import React from "react";
import { Card, CardContent, CardHeader, CardTitle, CardFooter } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import {
  Mail,
  Phone,
  Building2,
  Edit,
  Trash2,
  MapPin,
  Globe,
  Users,
  DollarSign,
  Eye,
} from "lucide-react";
import PhoneDisplay from "../shared/PhoneDisplay";

// Matching the stat card colors - semi-transparent backgrounds
const typeColors = {
  prospect: 'bg-blue-900/20 text-blue-300 border-blue-700',
  customer: 'bg-green-900/20 text-green-300 border-green-700',
  partner: 'bg-purple-900/20 text-purple-300 border-purple-700',
  competitor: 'bg-red-900/20 text-red-300 border-red-700',
  vendor: 'bg-amber-900/20 text-amber-300 border-amber-700',
  inactive: 'bg-gray-900/20 text-gray-300 border-gray-700'
};

export default function AccountCard({ account, assignedUserName, onEdit, onDelete, onViewDetails, onClick, isSelected, onSelect, user }) {
  return (
    <Card 
      className={`hover:shadow-lg transition-all duration-200 border-l-4 flex flex-col bg-slate-800 border-slate-700 hover:bg-slate-700/50 cursor-pointer ${
        isSelected ? 'ring-2 ring-blue-500 bg-slate-700/50' : ''
      }`}
      style={{ borderLeftColor: account.type === 'customer' ? '#10b981' : account.type === 'prospect' ? '#3b82f6' : '#8b5cf6' }}
      onClick={onClick}
    >
      <CardHeader className="pb-3">
        <div className="flex justify-between items-start">
          <div className="flex items-center gap-3 flex-1">
            <Checkbox
              checked={isSelected}
              onCheckedChange={onSelect}
              onClick={(e) => e.stopPropagation()}
              className="border-slate-600 data-[state=checked]:bg-blue-600 data-[state=checked]:border-blue-600"
            />
            <div className="flex-1">
              <CardTitle className="text-lg font-bold text-slate-100">
                {account.name}
              </CardTitle>
              <p className="text-sm font-normal text-slate-400 mt-1">
                {account.industry?.replace(/_/g, ' ')}
              </p>
            </div>
          </div>
        </div>
      </CardHeader>

      <CardContent className="flex-grow space-y-4">
        <div className="space-y-2 text-sm">
          <div className="flex items-center gap-2 text-slate-300">
            <Mail className="w-4 h-4 text-slate-500" />
            {account.email ? (
              <a href={`mailto:${account.email}`} className="hover:text-blue-400" onClick={(e) => e.stopPropagation()}>
                {account.email}
              </a>
            ) : (
              <span className="text-slate-500 italic">No email</span>
            )}
          </div>
          <div className="flex items-center gap-2 text-slate-300">
            <Phone className="w-4 h-4 text-slate-500" />
            {account.phone ? (
              <PhoneDisplay
                user={user}
                phone={account.phone}
                contactName={account.name}
                enableCalling={true}
                className="text-slate-300 hover:text-blue-400"
              />
            ) : (
              <span className="text-slate-500 italic">No phone</span>
            )}
          </div>
          <div className="flex items-center gap-2 text-slate-300">
            <Globe className="w-4 h-4 text-slate-500" />
            {account.website ? (
              <a href={account.website} target="_blank" rel="noopener noreferrer" className="hover:text-blue-400" onClick={(e) => e.stopPropagation()}>
                {account.website.replace(/^https?:\/\//, '')}
              </a>
            ) : (
              <span className="text-slate-500 italic">No website</span>
            )}
          </div>
          {account.city && (
            <div className="flex items-center gap-2 text-slate-300">
              <MapPin className="w-4 h-4 text-slate-500" />
              <span>{account.city}{account.state && `, ${account.state}`}</span>
            </div>
          )}
        </div>

        {(account.annual_revenue || account.employee_count) && (
          <div className="grid grid-cols-2 gap-4 pt-2 border-t border-slate-700">
            {account.annual_revenue && (
              <div className="text-center">
                <div className="flex items-center justify-center gap-1 text-slate-400 mb-1">
                  <DollarSign className="w-3 h-3" />
                  <span className="text-xs">Revenue</span>
                </div>
                <span className="text-sm font-semibold text-slate-200">
                  ${(account.annual_revenue / 1000000).toFixed(1)}M
                </span>
              </div>
            )}
            {account.employee_count && (
              <div className="text-center">
                <div className="flex items-center justify-center gap-1 text-slate-400 mb-1">
                  <Users className="w-3 h-3" />
                  <span className="text-xs">Employees</span>
                </div>
                <span className="text-sm font-semibold text-slate-200">{account.employee_count}</span>
              </div>
            )}
          </div>
        )}

        <div className="flex justify-between items-center pt-2 border-t border-slate-700">
          <Badge className={`${typeColors[account.type]} border capitalize`}>
            {account.type}
          </Badge>
          <span className="text-xs text-slate-500">
            {assignedUserName}
          </span>
        </div>
      </CardContent>

      <CardFooter className="flex justify-between items-center pt-4 border-t border-slate-700">
        <div className="flex gap-2 flex-wrap">
          <Button
            variant="outline"
            size="sm"
            onClick={(e) => { e.stopPropagation(); onEdit(account); }}
            className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600"
          >
            <Edit className="w-3 h-3 mr-1" />
            Edit
          </Button>
          <Button
            variant="outline"
            size="sm"
            onClick={(e) => { e.stopPropagation(); onViewDetails(account); }}
            className="bg-slate-700 border-slate-600 text-slate-400 hover:bg-slate-600 hover:text-slate-300"
          >
            <Eye className="w-3 h-3 mr-1" />
            View
          </Button>
        </div>
        <Button
          variant="ghost"
          size="icon"
          onClick={(e) => { e.stopPropagation(); onDelete(account.id); }}
          className="text-red-400 hover:text-red-300 hover:bg-slate-700"
        >
          <Trash2 className="w-4 h-4" />
        </Button>
      </CardFooter>
    </Card>
  );
}

import React from "react";
import UniversalDetailPanel from "../shared/UniversalDetailPanel";

export default function AccountDetailPanel({
  account,
  assignedUserName,
  open,
  onOpenChange,
  onEdit,
  onDelete,
  onRefresh,
  user
}) {
  if (!account) {
    return null;
  }

  return (
    <UniversalDetailPanel
      entity={account}
      entityType="account"
      open={open}
      onOpenChange={onOpenChange}
      onEdit={onEdit}
      onDelete={onDelete}
      user={user}
      displayData={{
        "Assigned To": (
          <p className="text-slate-200 font-medium mt-1">
            {assignedUserName || account.assigned_to || 'Unassigned'}
          </p>
        )
      }}
      customActions={[]}
      showNotes={true}
    />
  );
}

import React, { useState } from "react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { ChevronDown, UserCheck, Tag, Trash2 } from "lucide-react";
import LazyEmployeeSelector from "../shared/LazyEmployeeSelector";

export default function BulkActionsMenu({ 
  selectedCount, 
  onBulkTypeChange, 
  onBulkAssign,
  onBulkDelete,
  selectAllMode = false,
  totalCount = 0
}) {
  const [showTypeDialog, setShowTypeDialog] = useState(false);
  const [showAssignDialog, setShowAssignDialog] = useState(false);
  const [newType, setNewType] = useState("");
  const [newAssignee, setNewAssignee] = useState("");

  const displayCount = selectAllMode ? totalCount : selectedCount;
  const countLabel = selectAllMode ? `All ${displayCount}` : displayCount;

  const handleTypeChange = () => {
    if (newType) {
      onBulkTypeChange(newType);
      setShowTypeDialog(false);
      setNewType("");
    }
  };

  const handleAssign = () => {
    onBulkAssign(newAssignee);
    setShowAssignDialog(false);
    setNewAssignee("");
  };

  return (
    <>
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button variant="outline" className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600">
            Bulk Actions ({countLabel})
            <ChevronDown className="w-4 h-4 ml-2" />
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent className="bg-slate-800 border-slate-700">
          <DropdownMenuLabel className="text-slate-200">
            Actions for {selectAllMode ? `all ${displayCount}` : displayCount} account(s)
          </DropdownMenuLabel>
          <DropdownMenuSeparator className="bg-slate-700" />
          <DropdownMenuItem 
            onClick={() => setShowTypeDialog(true)}
            className="text-slate-200 hover:bg-slate-700 cursor-pointer"
          >
            <Tag className="w-4 h-4 mr-2" />
            Change Type
          </DropdownMenuItem>
          <DropdownMenuItem 
            onClick={() => setShowAssignDialog(true)}
            className="text-slate-200 hover:bg-slate-700 cursor-pointer"
          >
            <UserCheck className="w-4 h-4 mr-2" />
            Assign To
          </DropdownMenuItem>
          <DropdownMenuSeparator className="bg-slate-700" />
          <DropdownMenuItem 
            onClick={onBulkDelete}
            className="text-red-400 hover:bg-red-900/20 cursor-pointer"
          >
            <Trash2 className="w-4 h-4 mr-2" />
            Delete Selected
          </DropdownMenuItem>
        </DropdownMenuContent>
      </DropdownMenu>

      {/* Type Change Dialog */}
      <Dialog open={showTypeDialog} onOpenChange={setShowTypeDialog}>
        <DialogContent className="bg-slate-800 border-slate-700 text-slate-200">
          <DialogHeader>
            <DialogTitle>Change Type for {countLabel} Account(s)</DialogTitle>
            <DialogDescription className="text-slate-400">
              Select a new type to apply to {selectAllMode ? 'all selected' : 'the selected'} accounts.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div>
              <Label htmlFor="type" className="text-slate-200">New Type</Label>
              <Select value={newType} onValueChange={setNewType}>
                <SelectTrigger className="mt-2 bg-slate-700 border-slate-600 text-slate-200">
                  <SelectValue placeholder="Select type..." />
                </SelectTrigger>
                <SelectContent className="bg-slate-800 border-slate-700">
                  <SelectItem value="prospect" className="text-slate-200 hover:bg-slate-700">Prospect</SelectItem>
                  <SelectItem value="customer" className="text-slate-200 hover:bg-slate-700">Customer</SelectItem>
                  <SelectItem value="partner" className="text-slate-200 hover:bg-slate-700">Partner</SelectItem>
                  <SelectItem value="competitor" className="text-slate-200 hover:bg-slate-700">Competitor</SelectItem>
                  <SelectItem value="vendor" className="text-slate-200 hover:bg-slate-700">Vendor</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setShowTypeDialog(false)} className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600">
              Cancel
            </Button>
            <Button onClick={handleTypeChange} disabled={!newType} className="bg-blue-600 hover:bg-blue-700">
              Update Type
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Assignment Dialog */}
      <Dialog open={showAssignDialog} onOpenChange={setShowAssignDialog}>
        <DialogContent className="bg-slate-800 border-slate-700 text-slate-200">
          <DialogHeader>
            <DialogTitle>Assign {countLabel} Account(s)</DialogTitle>
            <DialogDescription className="text-slate-400">
              Select an employee to assign {selectAllMode ? 'all selected' : 'the selected'} accounts to.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div>
              <Label htmlFor="assignee" className="text-slate-200">Assign To</Label>
              <LazyEmployeeSelector
                value={newAssignee}
                onValueChange={setNewAssignee}
                placeholder="Select employee..."
                className="mt-2 bg-slate-700 border-slate-600 text-slate-200"
                contentClassName="bg-slate-800 border-slate-700"
                itemClassName="text-slate-200 hover:bg-slate-700"
                allowUnassigned={true}
                showLoadingState={true}
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setShowAssignDialog(false)} className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600">
              Cancel
            </Button>
            <Button onClick={handleAssign} className="bg-blue-600 hover:bg-blue-700">
              Assign Accounts
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </>
  );
}

import React, { useState } from "react";
import { Account } from "@/api/entities";
import { Lead } from "@/api/entities";
import { User } from "@/api/entities";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import AddressFields from "../shared/AddressFields";
import PhoneInput from "../shared/PhoneInput";
import { Loader2, Plus, X } from "lucide-react";
import { Checkbox } from "@/components/ui/checkbox";
import { toast } from "react-hot-toast";

export default function CreateAccountDialog({ open, onOpenChange, onAccountCreated }) {
  const [formData, setFormData] = useState({
    name: "",
    type: "prospect",
    industry: "",
    website: "",
    phone: "",
    email: "",
    annual_revenue: "",
    employee_count: "",
    address_1: "",
    address_2: "",
    city: "",
    state: "",
    zip: "",
    country: "United States",
    description: "",
    tags: [],
    is_test_data: false,
  });

  // Lead creation fields
  const [createLead, setCreateLead] = useState(false);
  const [leadData, setLeadData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    phone: "",
    job_title: "",
    source: "website",
    status: "new",
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  React.useEffect(() => {
    const loadUser = async () => {
      try {
        const user = await User.me();
        setCurrentUser(user);
      } catch (error) {
        console.error("Failed to load user:", error);
      }
    };
    loadUser();
  }, []);

  const handleChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleLeadChange = (field, value) => {
    setLeadData(prev => ({ ...prev, [field]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!currentUser) return;

    setIsSubmitting(true);
    try {
      const accountPayload = {
        ...formData,
        tenant_id: currentUser.tenant_id,
        annual_revenue: formData.annual_revenue ? parseFloat(formData.annual_revenue) : undefined,
        employee_count: formData.employee_count ? parseInt(formData.employee_count) : undefined,
      };

      const newAccount = await Account.create(accountPayload);

      // If user wants to create a lead, create it now
      if (createLead && leadData.first_name && leadData.last_name) {
        const leadPayload = {
          ...leadData,
          tenant_id: currentUser.tenant_id,
          account_id: newAccount.id,
          company: newAccount.name,
          assigned_to: currentUser.email,
        };
        await Lead.create(leadPayload);
        toast.success("Account and Lead created successfully!");
      } else {
        toast.success("Account created successfully!");
      }

      if (onAccountCreated) {
        onAccountCreated(newAccount);
      }
      onOpenChange(false);
    } catch (error) {
      console.error("Error creating account:", error);
      toast.error("Failed to create account. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!open) return null;

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="md:col-span-2">
          <Label htmlFor="acc-name" className="text-slate-300">
            Company Name <span className="text-red-400">*</span>
          </Label>
          <Input
            id="acc-name"
            value={formData.name}
            onChange={(e) => handleChange('name', e.target.value)}
            required
            className="bg-slate-700 border-slate-600 text-white"
            placeholder="Enter company name"
          />
        </div>

        <div>
          <Label htmlFor="acc-type" className="text-slate-300">Type</Label>
          <Select value={formData.type} onValueChange={(value) => handleChange('type', value)}>
            <SelectTrigger id="acc-type" className="bg-slate-700 border-slate-600 text-white">
              <SelectValue />
            </SelectTrigger>
            <SelectContent className="bg-slate-800 border-slate-600" style={{ zIndex: 2147483648 }}>
              <SelectItem value="prospect">Prospect</SelectItem>
              <SelectItem value="customer">Customer</SelectItem>
              <SelectItem value="partner">Partner</SelectItem>
              <SelectItem value="competitor">Competitor</SelectItem>
              <SelectItem value="vendor">Vendor</SelectItem>
            </SelectContent>
          </Select>
        </div>

        <div>
          <Label htmlFor="acc-industry" className="text-slate-300">Industry</Label>
          <Select value={formData.industry} onValueChange={(value) => handleChange('industry', value)}>
            <SelectTrigger id="acc-industry" className="bg-slate-700 border-slate-600 text-white">
              <SelectValue placeholder="Select industry" />
            </SelectTrigger>
            <SelectContent className="bg-slate-800 border-slate-600" style={{ zIndex: 2147483648 }}>
              <SelectItem value="aerospace_and_defense">Aerospace & Defense</SelectItem>
              <SelectItem value="agriculture">Agriculture</SelectItem>
              <SelectItem value="automotive">Automotive</SelectItem>
              <SelectItem value="banking_and_financial_services">Banking & Financial Services</SelectItem>
              <SelectItem value="construction">Construction</SelectItem>
              <SelectItem value="consumer_goods">Consumer Goods</SelectItem>
              <SelectItem value="education">Education</SelectItem>
              <SelectItem value="energy_and_utilities">Energy & Utilities</SelectItem>
              <SelectItem value="healthcare_and_life_sciences">Healthcare & Life Sciences</SelectItem>
              <SelectItem value="information_technology">Information Technology</SelectItem>
              <SelectItem value="manufacturing">Manufacturing</SelectItem>
              <SelectItem value="real_estate">Real Estate</SelectItem>
              <SelectItem value="retail_and_wholesale">Retail & Wholesale</SelectItem>
              <SelectItem value="telecommunications">Telecommunications</SelectItem>
              <SelectItem value="other">Other</SelectItem>
            </SelectContent>
          </Select>
        </div>

        <div>
          <Label htmlFor="acc-website" className="text-slate-300">Website</Label>
          <Input
            id="acc-website"
            type="url"
            value={formData.website}
            onChange={(e) => handleChange('website', e.target.value)}
            className="bg-slate-700 border-slate-600 text-white"
            placeholder="https://example.com"
          />
        </div>

        <div>
          <Label htmlFor="acc-phone" className="text-slate-300">Phone</Label>
          <PhoneInput
            id="acc-phone"
            value={formData.phone}
            onChange={(value) => handleChange('phone', value)}
            className="bg-slate-700 border-slate-600 text-white"
          />
        </div>

        <div>
          <Label htmlFor="acc-email" className="text-slate-300">Email</Label>
          <Input
            id="acc-email"
            type="email"
            value={formData.email}
            onChange={(e) => handleChange('email', e.target.value)}
            className="bg-slate-700 border-slate-600 text-white"
            placeholder="contact@company.com"
          />
        </div>

        <div>
          <Label htmlFor="acc-revenue" className="text-slate-300">Annual Revenue</Label>
          <Input
            id="acc-revenue"
            type="number"
            value={formData.annual_revenue}
            onChange={(e) => handleChange('annual_revenue', e.target.value)}
            className="bg-slate-700 border-slate-600 text-white"
            placeholder="0"
          />
        </div>

        <div>
          <Label htmlFor="acc-employees" className="text-slate-300">Employee Count</Label>
          <Input
            id="acc-employees"
            type="number"
            value={formData.employee_count}
            onChange={(e) => handleChange('employee_count', e.target.value)}
            className="bg-slate-700 border-slate-600 text-white"
            placeholder="0"
          />
        </div>
      </div>

      <AddressFields
        formData={formData}
        onChange={handleChange}
        className="bg-slate-700 border-slate-600 text-white"
      />

      <div>
        <Label htmlFor="acc-description" className="text-slate-300">Description</Label>
        <Textarea
          id="acc-description"
          value={formData.description}
          onChange={(e) => handleChange('description', e.target.value)}
          className="bg-slate-700 border-slate-600 text-white"
          rows={3}
          placeholder="Add notes about this account..."
        />
      </div>

      {/* Optional Lead Creation Section */}
      <div className="border-t border-slate-700 pt-4">
        <div className="flex items-center gap-2 mb-4">
          <Checkbox
            id="create-lead"
            checked={createLead}
            onCheckedChange={setCreateLead}
            className="border-slate-600"
          />
          <Label htmlFor="create-lead" className="text-slate-300 cursor-pointer">
            Create a Lead for this Account
          </Label>
        </div>

        {createLead && (
          <div className="space-y-4 pl-6 border-l-2 border-blue-600">
            <p className="text-sm text-slate-400">Add a contact person for this account</p>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="lead-first-name" className="text-slate-300">
                  First Name <span className="text-red-400">*</span>
                </Label>
                <Input
                  id="lead-first-name"
                  value={leadData.first_name}
                  onChange={(e) => handleLeadChange('first_name', e.target.value)}
                  required={createLead}
                  className="bg-slate-700 border-slate-600 text-white"
                  placeholder="John"
                />
              </div>

              <div>
                <Label htmlFor="lead-last-name" className="text-slate-300">
                  Last Name <span className="text-red-400">*</span>
                </Label>
                <Input
                  id="lead-last-name"
                  value={leadData.last_name}
                  onChange={(e) => handleLeadChange('last_name', e.target.value)}
                  required={createLead}
                  className="bg-slate-700 border-slate-600 text-white"
                  placeholder="Doe"
                />
              </div>

              <div>
                <Label htmlFor="lead-email" className="text-slate-300">Email</Label>
                <Input
                  id="lead-email"
                  type="email"
                  value={leadData.email}
                  onChange={(e) => handleLeadChange('email', e.target.value)}
                  className="bg-slate-700 border-slate-600 text-white"
                  placeholder="john.doe@company.com"
                />
              </div>

              <div>
                <Label htmlFor="lead-phone" className="text-slate-300">Phone</Label>
                <PhoneInput
                  id="lead-phone"
                  value={leadData.phone}
                  onChange={(value) => handleLeadChange('phone', value)}
                  className="bg-slate-700 border-slate-600 text-white"
                />
              </div>

              <div className="md:col-span-2">
                <Label htmlFor="lead-job-title" className="text-slate-300">Job Title</Label>
                <Input
                  id="lead-job-title"
                  value={leadData.job_title}
                  onChange={(e) => handleLeadChange('job_title', e.target.value)}
                  className="bg-slate-700 border-slate-600 text-white"
                  placeholder="Sales Manager"
                />
              </div>
            </div>
          </div>
        )}
      </div>

      {currentUser?.role === 'admin' && (
        <div className="flex items-center gap-2">
          <Checkbox
            id="test-data"
            checked={formData.is_test_data}
            onCheckedChange={(checked) => handleChange('is_test_data', checked)}
            className="border-slate-600"
          />
          <Label htmlFor="test-data" className="text-slate-300 cursor-pointer">
            Mark as test data
          </Label>
        </div>
      )}

      <div className="flex justify-end gap-3 pt-4 border-t border-slate-700">
        <Button
          type="button"
          variant="outline"
          onClick={() => onOpenChange(false)}
          className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600"
        >
          Cancel
        </Button>
        <Button
          type="submit"
          disabled={isSubmitting}
          className="bg-blue-600 hover:bg-blue-700 text-white"
        >
          {isSubmitting ? (
            <>
              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
              Creating...
            </>
          ) : (
            `Create Account${createLead ? ' & Lead' : ''}`
          )}
        </Button>
      </div>
    </form>
  );
}

