// Helper to strip tenant context from display
function stripTenantContext(content) {
  if (!content) return content;
  return content.replace(/^\[Client ID:.*?\]\n?/, '');
}

// Helper to strip markdown for TTS
function stripMarkdownForTTS(text) {
  if (!text) return '';
  return text
    .replace(/\*\*\*(.*?)\*\*\*/g, '$1') // Bold+italic
    .replace(/\*\*(.*?)\*\*/g, '$1')     // Bold
    .replace(/\*(.*?)\*/g, '$1')         // Italic
    .replace(/`(.*?)`/g, '$1')           // Inline code
    .replace(/\[(.*?)\]\(.*?\)/g, '$1')  // Links
    .replace(/#+\s/g, '')                // Headers
    .replace(/>\s/g, '')                 // Blockquotes
    .replace(/[-*+]\s/g, '')             // List markers (e.g., -, *, +)
    .replace(/^-+\s*$/gm, '')            // Horizontal rules (e.g., ---)
    .replace(/\n\s*\n/g, '\n\n')         // Reduce multiple empty lines to one
    .trim();
}

function Bubble({ role, content }) {
  const isUser = role === "user";
  const displayContent = isUser ? stripTenantContext(content) : content;

  return (
    <div className={`flex ${isUser ? "justify-end" : "justify-start"} my-1 gap-2`}>
      <div className={`${isUser ? "bg-blue-600 text-white" : "bg-slate-800 text-slate-200"} px-3 py-2 rounded-xl max-w-[80%]`}>
        {isUser ? (
          <p className="whitespace-pre-wrap">{displayContent}</p>
        ) : (
          <ReactMarkdown 
            className="prose prose-sm prose-invert max-w-none"
            components={{
              p: ({children}) => <p className="my-1 whitespace-pre-wrap">{children}</p>,
              strong: ({children}) => <strong className="font-bold text-slate-100">{children}</strong>,
              em: ({children}) => <em className="italic">{children}</em>,
              ul: ({children}) => <ul className="list-disc list-inside my-1">{children}</ul>,
              ol: ({children}) => <ol className="list-decimal list-inside my-1">{children}</ol>,
              li: ({children}) => <li className="my-0.5">{children}</li>,
              code: ({inline, children}) => 
                inline ? 
                  <code className="bg-slate-700 px-1 py-0.5 rounded text-sm">{children}</code> :
                  <code className="block bg-slate-700 p-2 rounded my-1">{children}</code>
            }}
          >
            {displayContent || ""}
          </ReactMarkdown>
        )}
      </div>
    </div>
  );
}

export default function AgentChat({ agentName = "crm_assistant", tenantId, tenantName, voiceEnabled = true }) {
  const [conversation, setConversation] = useState(null);
  const [messages, setMessages] = useState([]);
  const [sending, setSending] = useState(false);
  const [loading, setLoading] = useState(true);
  const [input, setInput] = useState("");
  const [micActive, setMicActive] = useState(false);
  const unsubRef = useRef(null);
  const [contextLoading, setContextLoading] = useState(false);
  const didContextRef = useRef(false);
  const audioRef = useRef(null);
  const urlRef = useRef(null);
  const lastMessageCountRef = useRef(0);

  useEffect(() => {
    if (!tenantId || typeof tenantId !== 'string' || !isValidId(tenantId)) {
      console.error('[AgentChat] Invalid or missing tenant ID:', tenantId);
    } else {
      console.log('[AgentChat] Active tenant context:', { tenantId, tenantName });
    }
  }, [tenantId, tenantName]);

  const whatsappUrl = useMemo(() => {
    try {
      return agentSDK.getWhatsAppConnectURL(agentName);
    } catch {
      return null;
    }
  }, [agentName]);

  // DEBUG: Log mic state changes in parent component
  useEffect(() => {
    console.log('[AgentChat] Current state:', { micActive, voiceEnabled, messageCount: messages.length });
  }, [micActive, voiceEnabled, messages.length]);

  const injectContextPrimer = useCallback(async (convo) => {
    if (!convo || !tenantId) {
      console.error('[AgentChat] Cannot inject context: missing conversation or tenant ID');
      return;
    }

    if (typeof tenantId !== 'string' || !isValidId(tenantId)) {
      console.error('[AgentChat] Invalid tenant ID format, blocking context injection:', tenantId);
      return;
    }

    try {
      setContextLoading(true);
      const me = await User.me().catch(() => null);
      if (!me) {
        console.error('[AgentChat] User not authenticated, cannot inject context');
        return;
      }

      const filter = { tenant_id: tenantId };

      console.log('[AgentChat] Loading context with STRICT tenant filter:', filter);

      const [recentActivities, openOpps, hotLeads, keyAccounts] = await Promise.all([
        Activity.filter({ ...filter, status: { $in: ["completed", "in-progress", "scheduled"] } }, "-created_date", 5).catch((e) => {
          console.error('[AgentChat] Failed to fetch activities for tenant:', tenantId, e);
          return [];
        }),
        Opportunity.filter({ ...filter, stage: { $nin: ["closed_won", "closed_lost"] } }, "-updated_date", 5).catch((e) => {
          console.error('[AgentChat] Failed to fetch opportunities for tenant:', tenantId, e);
          return [];
        }),
        Lead.filter({ ...filter, status: { $in: ["new", "contacted", "qualified"] } }, "-created_date", 5).catch((e) => {
          console.error('[AgentChat] Failed to fetch leads for tenant:', tenantId, e);
          return [];
        }),
        Account.filter(filter, "-updated_date", 3).catch((e) => {
          console.error('[AgentChat] Failed to fetch accounts for tenant:', tenantId, e);
          return [];
        }),
      ]);

      console.log('[AgentChat] Context loaded:', {
        tenant: tenantId,
        activities: recentActivities.length,
        opportunities: openOpps.length,
        leads: hotLeads.length,
        accounts: keyAccounts.length
      });

      const verifyTenant = (records, entityType) => {
        const wrongTenant = records.filter(r => r.tenant_id !== tenantId);
        if (wrongTenant.length > 0) {
          console.error(`[AgentChat] TENANT ISOLATION BREACH: ${wrongTenant.length} ${entityType} records from wrong tenant!`, {
            expected: tenantId,
            found: wrongTenant.map(r => ({ id: r.id, tenant_id: r.tenant_id }))
          });
          return records.filter(r => r.tenant_id === tenantId);
        }
        return records;
      };

      const safeActivities = verifyTenant(recentActivities, 'Activity');
      const safeOpps = verifyTenant(openOpps, 'Opportunity');
      const safeLeads = verifyTenant(hotLeads, 'Lead');
      const safeAccounts = verifyTenant(keyAccounts, 'Account');

      const lines = [];
      lines.push(`Context for ${me.email} (role: ${me.role})  Client: ${tenantName || tenantId}`);

      if (safeActivities.length) {
        lines.push(`Recent activities (${safeActivities.length}): ${safeActivities.map(a => a.subject).slice(0,3).join("; ")}`);
      }
      if (safeOpps.length) {
        const topOpps = safeOpps.slice(0,3).map(o => `${o.name} ($${o.amount || 0}, ${o.stage})`);
        lines.push(`Open opps (${safeOpps.length}): ${topOpps.join("; ")}`);
      }
      if (safeLeads.length) {
        const topLeads = safeLeads.slice(0,3).map(l => `${l.first_name} ${l.last_name}${l.company ? " @ " + l.company : ""}`);
        lines.push(`Active leads (${safeLeads.length}): ${topLeads.join("; ")}`);
      }
      if (safeAccounts.length) {
        const topAccts = safeAccounts.slice(0,3).map(a => a.name);
        lines.push(`Key accounts: ${topAccts.join("; ")}`);
      }

      if (lines.length === 1) {
        lines.push("No recent data found yetask me to create a lead or log an activity to get started.");
      }

      console.log('[AgentChat]  Context refreshed successfully for tenant:', tenantId);

      didContextRef.current = true;
    } catch (error) {
      console.error('[AgentChat] Failed to inject context primer:', error);
    } finally {
      setContextLoading(false);
    }
  }, [tenantId, tenantName]);

  const handleSend = useCallback(async (messageText) => {
    // Allow passing message directly (for voice input) or use state
    const text = messageText ? messageText.trim() : (input || "").trim();
    if (!text || !conversation || sending) return;

    if (!tenantId) {
      alert('Please select a client before sending messages');
      return;
    }

    setSending(true);
    try {
      // Add tenant context for the agent but it will be hidden in display
      const messageWithContext = `[Client ID: ${tenantId}${tenantName ? ` | Client Name: ${tenantName}` : ''}]\n${text}`;

      console.log('[AgentChat] Sending message with client context:', { tenantId, tenantName });

      await agentSDK.addMessage(conversation, { 
        role: "user", 
        content: messageWithContext
      });
      setInput("");
    } catch (e) {
      console.error("[AgentChat] Send failed:", e);
    } finally {
      setSending(false);
    }
  }, [input, conversation, sending, tenantId, tenantName]);

  // Listen for voice input results from MicButton
  useEffect(() => {
    const handleVoiceResult = (event) => {
      const { transcript } = event.detail || {};
      if (transcript && transcript.trim()) {
        console.log('[AgentChat] Voice result received, auto-sending:', transcript);
        // Send immediately with the transcript
        handleSend(transcript);
      }
    };

    window.addEventListener('chat:voice-result', handleVoiceResult);
    console.log('[AgentChat] Listening for voice input events');

    return () => {
      window.removeEventListener('chat:voice-result', handleVoiceResult);
    };
  }, [handleSend]);

  // Listen for mic state changes
  useEffect(() => {
    const handleMicChange = (event) => {
      const { active } = event.detail || {};
      console.log('[AgentChat] Mic state changed:', active);
      setMicActive(active);
    };

    window.addEventListener('chat:mic-active', handleMicChange);
    console.log('[AgentChat] Registered mic-active listener');

    return () => {
      window.removeEventListener('chat:mic-active', handleMicChange);
    };
  }, []);

  // AUTO-PLAY audio when new AI message arrives and mic is active
  useEffect(() => {
    if (!micActive || !messages.length) return;

    const lastMessage = messages[messages.length - 1];
    const isNewAIMessage = 
      lastMessage?.role === 'assistant' && 
      messages.length > lastMessageCountRef.current;

    if (isNewAIMessage) {
      lastMessageCountRef.current = messages.length;

      // Auto-play the audio
      const playAudio = async () => {
        try {
          // CRITICAL: Strip markdown and check if message has content
          const rawText = (lastMessage.content || '').trim();
          const messageText = stripMarkdownForTTS(rawText);

          if (!messageText) {
            console.warn('[AgentChat] Message content is empty after markdown stripping, skipping audio');
            window.dispatchEvent(new CustomEvent("chat:unlock-open"));
            return;
          }

          // Clean up previous audio
          if (audioRef.current) {
            try { audioRef.current.pause(); } catch (e) { console.warn("Error pausing previous audio", e); }
            audioRef.current = null;
          }
          if (urlRef.current) {
            try { URL.revokeObjectURL(urlRef.current); } catch (e) { console.warn("Error revoking previous audio URL", e); }
            urlRef.current = null;
          }

          console.log('[AgentChat] Auto-playing audio for new message:', messageText.substring(0, 50) + '...');
          console.log('[AgentChat] Message length:', messageText.length, 'characters');

          const resp = await generateElevenLabsSpeech({ 
            text: messageText, 
            voice_id: "21m00Tcm4TlvDq8ikWAM" 
          });

          const data = resp?.data;
          let blob;

          if (data?.audio_base64 && typeof data.audio_base64 === "string") {
            const decoded = Uint8Array.from(atob(data.audio_base64), c => c.charCodeAt(0));
            blob = new Blob([decoded], { type: "audio/mpeg" });
            console.log('[AgentChat] Audio blob created, size:', blob.size, 'bytes');
          }

          if (blob && blob.size > 0) {
            const url = URL.createObjectURL(blob);
            urlRef.current = url;
            const audio = new Audio(url);
            audio.preload = "auto";
            audioRef.current = audio;

            // Better event handling
            audio.onended = () => {
              console.log('[AgentChat] Audio playback ended naturally');
              // Add delay before unlocking to prevent premature restart
              setTimeout(() => {
                window.dispatchEvent(new CustomEvent("chat:unlock-open"));
              }, 500);
            };

            audio.onerror = (e) => {
              console.error('[AgentChat] Audio playback error:', e);
              setTimeout(() => {
                window.dispatchEvent(new CustomEvent("chat:unlock-open"));
              }, 500);
            };

            audio.onpause = () => {
              console.warn('[AgentChat] Audio was paused unexpectedly');
            };

            audio.onabort = () => {
              console.warn('[AgentChat] Audio was aborted');
            };

            // Lock the mic BEFORE playing
            window.dispatchEvent(new CustomEvent("chat:lock-open"));
            console.log('[AgentChat] Mic locked, starting audio playback');

            // Add small delay before playing to ensure lock takes effect
            await new Promise(resolve => setTimeout(resolve, 200));

            await audio.play();
            console.log('[AgentChat] Audio playback started, duration:', audio.duration || 'unknown', 'seconds');
          } else {
            console.warn('[AgentChat] No audio blob generated or blob is empty.');
            window.dispatchEvent(new CustomEvent("chat:unlock-open"));
          }
        } catch (error) {
          console.error('[AgentChat] Auto-play audio failed:', error);
          window.dispatchEvent(new CustomEvent("chat:unlock-open"));
        }
      };

      playAudio();
    }
  }, [messages, micActive]);


  useEffect(() => {
    let mounted = true;

    const init = async () => {
      setLoading(true);
      didContextRef.current = false;
      lastMessageCountRef.current = 0; // Reset message count on init

      try {
        const storageKey = `agent_conversation_${agentName}_${tenantId || 'default'}`;
        const savedConvId = localStorage.getItem(storageKey);

        let convo = null;

        if (savedConvId) {
          try {
            convo = await agentSDK.getConversation(savedConvId);
            console.log('[AgentChat] Loaded saved conversation:', savedConvId);
          } catch (error) {
            console.warn('[AgentChat] Saved conversation not found, creating new one:', error);
            localStorage.removeItem(storageKey);
          }
        }

        if (!convo) {
          convo = await agentSDK.createConversation({
            agent_name: agentName,
            metadata: {
              name: "Ai-SHA Executive Assistant",
              description: "Context-aware CRM assistant with memory",
              tenant_id: tenantId,
              tenant_name: tenantName
            }
          });
          localStorage.setItem(storageKey, convo.id);
          console.log('[AgentChat] Created new conversation:', convo.id, 'for tenant:', tenantId);

          try {
            await agentSDK.addMessage(convo, {
              role: "assistant",
              content: "Hi, how may I help?"
            });
          } catch (greetErr) {
            console.warn('[AgentChat] Failed to add greeting message:', greetErr);
          }
        }

        if (!mounted) return;
        setConversation(convo);

        const conversationMessages = (convo?.messages || []).filter(m => m.role !== 'system');

        if (conversationMessages.length === 0) {
          setMessages([{ role: 'assistant', content: 'Hi, how may I help?' }]);
        } else {
          setMessages(conversationMessages);
        }
        lastMessageCountRef.current = conversationMessages.length || 1; // Initialize for existing messages

        unsubRef.current = agentSDK.subscribeToConversation(convo.id, (data) => {
          setMessages((data.messages || []).filter(m => m.role !== 'system'));
        });

        if (!didContextRef.current) {
          await injectContextPrimer(convo);
        }
      } catch (e) {
        console.error("[AgentChat] init failed:", e);
      } finally {
        if (mounted) setLoading(false);
      }
    };

    init();
    return () => {
      mounted = false;
      if (unsubRef.current) {
        try { unsubRef.current(); } catch {}
      }
      // Clean up any playing audio when component unmounts
      if (audioRef.current) {
        try { audioRef.current.pause(); } catch {}
        audioRef.current = null;
      }
      if (urlRef.current) {
        try { URL.revokeObjectURL(urlRef.current); } catch {}
        urlRef.current = null;
      }
    };
  }, [agentName, tenantId, tenantName, injectContextPrimer]);


  if (loading) {
    return (
      <div className="h-[70vh] flex items-center justify-center">
        <Loader2 className="w-8 h-8 animate-spin text-blue-400" />
      </div>
    );
  }

  return (
    <div className="space-y-4">
      <Card className="bg-slate-800 border-slate-700 p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <div className="w-9 h-9 rounded-lg bg-slate-700 flex items-center justify-center">
              <Sparkles className="w-5 h-5 text-cyan-400" />
            </div>
            <div>
              <div className="text-slate-100 font-semibold">Ai-SHA</div>
              <div className="text-slate-400 text-sm">{tenantName || tenantId || 'No client selected'}</div>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <Button
              variant="outline"
              className="bg-slate-900 border-slate-600 text-slate-200 hover:bg-slate-700"
              onClick={() => conversation && injectContextPrimer(conversation)}
              disabled={contextLoading || !conversation || !tenantId}
            >
              <RefreshCw className={`w-4 h-4 mr-2 ${contextLoading ? "animate-spin" : ""}`} />
              {contextLoading ? "Refreshing" : "Refresh context"}
            </Button>
            <Button
              variant="outline"
              className="bg-slate-900 border-slate-600 text-red-400 hover:bg-slate-700 hover:text-red-300"
              onClick={async () => {
                if (window.confirm('Clear this conversation and start fresh?')) {
                  try {
                    if (unsubRef.current) unsubRef.current();

                    const newConvo = await agentSDK.createConversation({
                      agent_name: agentName,
                      metadata: {
                        name: "Ai-SHA Executive Assistant",
                        description: "Context-aware CRM assistant with memory",
                        tenant_id: tenantId,
                        tenant_name: tenantName
                      }
                    });

                    const storageKey = `agent_conversation_${agentName}_${tenantId || 'default'}`;
                    localStorage.setItem(storageKey, newConvo.id);

                    setConversation(newConvo);
                    setMessages([]);
                    didContextRef.current = false;
                    lastMessageCountRef.current = 0; // Reset message count after clear

                    try {
                      await agentSDK.addMessage(newConvo, {
                        role: "assistant",
                        content: "Hi, how may I help?"
                      });
                      setMessages([{ role: 'assistant', content: 'Hi, how may I help?' }]);
                    } catch (greetErr) {
                      console.warn('[AgentChat] Failed to add greeting message to cleared conversation:', greetErr);
                      setMessages([{ role: 'assistant', content: 'Hi, how may I help?' }]);
                    }
                    lastMessageCountRef.current = 1; // For the initial greeting message

                    unsubRef.current = agentSDK.subscribeToConversation(newConvo.id, (data) => {
                      setMessages((data.messages || []).filter(m => m.role !== 'system'));
                    });

                    await injectContextPrimer(newConvo);
                  } catch (e) {
                    console.error('Failed to clear conversation:', e);
                  }
                }
              }}
              disabled={!conversation}
            >
              <Trash2 className="w-4 h-4 mr-2" />
              Clear conversation
            </Button>
            {whatsappUrl && (
              <a href={whatsappUrl} target="_blank" rel="noreferrer">
                <Button variant="outline" className="bg-slate-900 border-slate-600 text-slate-200 hover:bg-slate-700">
                  <MessageSquare className="w-4 h-4 mr-2" />
                  WhatsApp
                  <ExternalLink className="w-3 h-3 ml-2" />
                </Button>
              </a>
            )}
          </div>
        </div>
      </Card>

      <Card className="bg-slate-800 border-slate-700 p-4 h-[60vh] flex flex-col">
        <div className="flex-1 overflow-y-auto pr-1">
          {messages?.length ? (
            messages.map((m, idx) => (
              <Bubble 
                key={idx} 
                role={m.role} 
                content={m.content} 
              />
            ))
          ) : (
            <div className="text-slate-400 text-sm">
              Say "What opportunities do I have open?" or "Create a lead for Jane Doe at Acme, title Marketing Manager"
            </div>
          )}
        </div>

        <div className="mt-3 flex items-center gap-2">
          <input
            className="flex-1 bg-slate-900 border border-slate-700 rounded-lg px-3 py-2 text-slate-200 outline-none focus:border-slate-500"
            placeholder="Type a message"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => { if (e.key === "Enter" && !e.shiftKey) { e.preventDefault(); handleSend(); } }}
            disabled={!tenantId}
          />
          {voiceEnabled && (
            <MicButton 
              disabled={!tenantId}
            />
          )}
          <Button onClick={() => handleSend()} disabled={sending || !input.trim() || !tenantId} className="bg-blue-600 hover:bg-blue-700">
            {sending ? <Loader2 className="w-4 h-4 animate-spin" /> : <Send className="w-4 h-4" />}
          </Button>
        </div>
      </Card>
    </div>
  );
}

import React, { useState, useRef, useEffect, useCallback } from 'react';
import { MessageCircle, X, Mic, MicOff, Volume2, VolumeX, Loader2 } from 'lucide-react';
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { toast } from "sonner";
import { generateDailyBriefing } from "@/api/functions";
import { generateElevenLabsSpeech } from "@/api/functions";
import { processChatCommand } from "@/api/functions";

export default function AIAssistantWidget({ user }) {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [speechRecognition, setSpeechRecognition] = useState(null);
  const [briefingEnabled, setBriefingEnabled] = useState(true);
  const [hasBriefedToday, setHasBriefingToday] = useState(false);
  const [currentAudio, setCurrentAudio] = useState(null);
  const [voiceEnabled, setVoiceEnabled] = useState(true);
  const [timeRemaining, setTimeRemaining] = useState(0);
  const [micPermissionStatus, setMicPermissionStatus] = useState('unknown');
  const [permissionError, setPermissionError] = useState('');
  const [isDetectingVoice, setIsDetectingVoice] = useState(false);
  const [currentTranscript, setCurrentTranscript] = useState('');
  const [isProcessing, setIsProcessing] = useState(false); // Add processing state to prevent loops

  const messagesEndRef = useRef(null);
  const recognitionRef = useRef(null);
  const timerIntervalRef = useRef(null);

  // Refs to hold mutable state/callback values for stable useEffect closures
  const isListeningRef = useRef(isListening);
  const currentTranscriptRef = useRef(currentTranscript);
  const handleSendMessageRef = useRef(() => {}); // Will be updated by useEffect
  const stopVoiceModeRef = useRef(() => {}); // Will be updated by useEffect
  const isProcessingRef = useRef(isProcessing); // New ref for isProcessing
  const isLoadingRef = useRef(isLoading); // New ref for isLoading
  const processVoiceMessageRef = useRef(() => {}); // New ref for processVoiceMessage

  // Update refs whenever the corresponding state/callback changes
  useEffect(() => { isListeningRef.current = isListening; }, [isListening]);
  useEffect(() => { currentTranscriptRef.current = currentTranscript; }, [currentTranscript]);
  useEffect(() => { isProcessingRef.current = isProcessing; }, [isProcessing]);
  useEffect(() => { isLoadingRef.current = isLoading; }, [isLoading]);


  // Format time remaining for display
  const formatTimeRemaining = useCallback((seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  }, []);

  const stopSpeaking = useCallback(() => {
    if (currentAudio) {
      currentAudio.pause();
      currentAudio.currentTime = 0;
      setCurrentAudio(null);
    }

    if ('speechSynthesis' in window) {
      window.speechSynthesis.cancel();
    }

    setIsSpeaking(false);
  }, [currentAudio]);

  // Separate fallback function to avoid repetition
  const fallbackToWebSpeech = useCallback((text) => {
    if (!voiceEnabled || !text || typeof text !== 'string' || text.trim().length === 0) {
      console.log(' Cannot speak (fallback): voiceEnabled =', voiceEnabled, 'text =', text);
      return;
    }

    if ('speechSynthesis' in window) {
      try {
        window.speechSynthesis.cancel();

        const utterance = new SpeechSynthesisUtterance(text);
        utterance.rate = 0.9;
        utterance.pitch = 1.1;
        utterance.volume = 0.8;

        // Try to find a female voice
        const voices = window.speechSynthesis.getVoices();
        const femaleVoice = voices.find(voice => 
          voice.name.toLowerCase().includes('female') || 
          voice.name.toLowerCase().includes('samantha') ||
          voice.name.toLowerCase().includes('karen') ||
          voice.name.toLowerCase().includes('susan')
        );

        if (femaleVoice) {
          utterance.voice = femaleVoice;
        }

        utterance.onstart = () => {
          console.log(' Web Speech API started');
          setIsSpeaking(true);
        };
        utterance.onend = () => {
          console.log(' Web Speech API ended');
          setIsSpeaking(false);
        };
        utterance.onerror = (error) => {
          console.error(' Web Speech API error:', error);
          setIsSpeaking(false);
        };

        window.speechSynthesis.speak(utterance);
        console.log(' Using browser TTS fallback');
      } catch (webSpeechError) {
        console.error(' Web Speech API also failed:', webSpeechError);
        setIsSpeaking(false);
      }
    } else {
      console.warn(' No speech synthesis available');
      setIsSpeaking(false);
    }
  }, [voiceEnabled]);

  const speakText = useCallback(async (text) => {
    if (!voiceEnabled || !text || typeof text !== 'string' || text.trim().length === 0) {
      console.log(' Cannot speak: voiceEnabled =', voiceEnabled, 'text =', text);
      return;
    }

    try {
      // Stop any current speech
      stopSpeaking();

      setIsSpeaking(true);

      // Try ElevenLabs first (premium voice - Rachel)
      console.log(' Calling ElevenLabs TTS with text:', text.substring(0, Math.min(text.length, 50)) + '...');

      const response = await generateElevenLabsSpeech({
        text: text.trim(), // Ensure text is properly passed and trimmed
        voice_id: '21m00Tcm4TlvDq8ikWAM' // Rachel - professional female voice
      });

      if (response && response.data && response.data.success) {
        // Play ElevenLabs audio
        console.log(' ElevenLabs response received, creating audio...');
        const audioBlob = new Blob([
          Uint8Array.from(atob(response.data.audio_base64), c => c.charCodeAt(0))
        ], { type: 'audio/mpeg' });

        const audioUrl = URL.createObjectURL(audioBlob);
        const audio = new Audio(audioUrl);

        setCurrentAudio(audio);

        audio.onended = () => {
          console.log(' Audio playback ended');
          setIsSpeaking(false);
          setCurrentAudio(null);
          URL.revokeObjectURL(audioUrl);
        };

        audio.onerror = (error) => {
          console.warn(' ElevenLabs audio playback failed:', error);
          setIsSpeaking(false);
          setCurrentAudio(null);
          URL.revokeObjectURL(audioUrl);
          fallbackToWebSpeech(text);
        };

        try {
          await audio.play();
          console.log(' Using ElevenLabs premium voice (Rachel)');
        } catch (playError) {
          console.warn(' Audio play failed:', playError);
          fallbackToWebSpeech(text);
        }

      } else {
        console.warn(' ElevenLabs response unsuccessful:', response?.data);
        fallbackToWebSpeech(text);
      }

    } catch (error) {
      console.warn(' ElevenLabs TTS failed:', error.message);
      setIsSpeaking(false);
      fallbackToWebSpeech(text);
    }
  }, [voiceEnabled, stopSpeaking, fallbackToWebSpeech]);

  const triggerDailyBriefing = useCallback(async () => {
    if (isLoading) return;

    setIsLoading(true);

    try {
      console.log(' Generating daily briefing...');

      const response = await generateDailyBriefing();

      if (response.data && response.data.success) {
        const briefingMessage = {
          role: 'assistant',
          content: response.data.briefing,
          isBriefing: true,
          timestamp: Date.now()
        };

        setMessages(prev => [...prev, briefingMessage]);

        // Mark as briefed today
        const today = new Date().toDateString();
        localStorage.setItem('lastBriefingDate', today);
        setHasBriefingToday(true);

        // Auto-speak the briefing
        await speakText(response.data.briefing);

        toast.success(' Daily briefing generated!');
      } else {
        throw new Error(response.data?.error || 'Failed to generate briefing');
      }
    } catch (error) {
      console.error('Daily briefing error:', error);
      const errorMessage = {
        role: 'assistant',
        content: 'I apologize, but I encountered an issue generating your daily briefing. Please try again.',
        timestamp: Date.now(),
        isError: true // Added isError for styling
      };
      setMessages(prev => [...prev, errorMessage]);
      toast.error('Daily briefing failed: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  }, [isLoading, speakText]);

  // Check microphone permissions (kept for permission indicator, though startVoiceMode handles acquisition)
  const checkMicrophonePermissions = useCallback(async () => {
    if (!navigator.permissions || !navigator.mediaDevices) {
      console.warn('Permissions API or MediaDevices not supported');
      setMicPermissionStatus('unknown');
      return 'unknown';
    }

    try {
      // Check current permission status
      const permissionStatus = await navigator.permissions.query({ name: 'microphone' });
      setMicPermissionStatus(permissionStatus.state);

      // Listen for permission changes
      permissionStatus.onchange = () => {
        setMicPermissionStatus(permissionStatus.state);
        if (permissionStatus.state === 'denied' && isListeningRef.current) { // Use ref here
          stopVoiceModeRef.current(); // Use ref here
          setPermissionError('Microphone access was revoked. Voice mode has been disabled.');
        }
      };

      return permissionStatus.state;
    } catch (error) {
      console.warn('Could not check microphone permissions:', error);
      setMicPermissionStatus('unknown');
      return 'unknown';
    }
  }, []);

  // UNUSED: Request microphone access (kept for manual request, though startVoiceMode handles acquisition)
  // const requestMicrophoneAccess = useCallback(async () => {
  //   setPermissionError('');
  //   
  //   try {
  //     const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
  //     stream.getTracks().forEach(track => track.stop());
  //     await checkMicrophonePermissions();
  //     toast.success(' Microphone access granted! Voice mode is ready.');
  //     return true;
  //   } catch (error) {
  //     console.error('Microphone access denied:', error);
  //     if (error.name === 'NotAllowedError') {
  //       setPermissionError('Microphone access denied. Please enable microphone permissions in your browser settings.');
  //       setMicPermissionStatus('denied');
  //     } else if (error.name === 'NotFoundError') {
  //       setPermissionError('No microphone found. Please connect a microphone and try again.');
  //     } else {
  //       setPermissionError('Could not access microphone. Please check your device settings.');
  //     }
  //     toast.error('Microphone access failed. Check permissions and try again.');
  //     return false;
  //   }
  // }, [checkMicrophonePermissions]);

  // Separate function to process voice messages
  const processVoiceMessage = useCallback(async (messageText) => {
    // Prevent duplicate processing
    if (isProcessingRef.current || isLoadingRef.current) { // Use refs for latest state
      console.warn(' Already processing or loading, skipping voice message');
      return;
    }

    setIsProcessing(true);
    setIsLoading(true);

    try {
      console.log(' Sending voice message to AI:', messageText);

      const response = await processChatCommand({
        message: messageText,
        user_context: {
          email: user.email,
          full_name: user.full_name || user.display_name,
          role: user.role,
          tenant_id: user.tenant_id
        }
      });

      // Check if we should still process (user might have stopped voice mode)
      if (!isListeningRef.current && !voiceEnabled) { // Use ref for isListening
        console.log(' User stopped voice mode or voice is disabled, not adding response or speaking.');
        return; // Early exit if voice mode is off and voice disabled
      }

      if (response?.data?.status === 'success' && response.data.response) {
        const assistantMessage = {
          role: 'assistant',
          content: response.data.response,
          timestamp: Date.now(),
          source: response.data.source || 'ai',
          isVoiceResponse: true
        };

        console.log(' Adding AI response to chat:', assistantMessage.content.substring(0, Math.min(assistantMessage.content.length, 100)) + '...');
        setMessages(prev => [...prev, assistantMessage]);

        // Only speak if voice is still enabled
        if (voiceEnabled) {
          console.log(' Speaking AI response...');
          setTimeout(() => {
            speakText(response.data.response);
          }, 300); // Small delay to allow UI update
        }

        toast.success(' AI responded to your voice message!');

      } else {
        throw new Error(response?.data?.response || 'No response received from AI');
      }

    } catch (error) {
      console.error(' Voice message processing failed:', error);

      // Only show error if voice is enabled or we are still "listening"
      if (voiceEnabled || isListeningRef.current) {
        const errorMessage = {
          role: 'assistant',
          content: `I apologize, but I encountered an error processing your voice message: ${error.message}. Please try again.`,
          timestamp: Date.now(),
          isError: true
        };
        setMessages(prev => [...prev, errorMessage]);
        toast.error('Voice processing failed: ' + error.message);
      }
    } finally {
      setIsLoading(false);
      setIsProcessing(false);
    }
  }, [user, voiceEnabled, speakText]);

  // Update processVoiceMessageRef whenever processVoiceMessage changes
  useEffect(() => { processVoiceMessageRef.current = processVoiceMessage; }, [processVoiceMessage]);


  // Update handleSendMessage to work better with voice
  const handleSendMessage = useCallback(async (messageText = null) => {
    const messageToSend = messageText || inputMessage.trim();
    if (!messageToSend || isLoadingRef.current || isProcessingRef.current) { // Use refs for latest state
      console.warn('Cannot send message: empty, loading, or already processing');
      return;
    }

    // Only add user message if it's not from voice (voice already added it)
    if (!messageText) { // If messageText is null, it's a typed message
      setInputMessage(''); // Clear input for typed messages

      const userMessage = {
        role: 'user',
        content: messageToSend,
        timestamp: Date.now(),
        isVoice: false // Mark as not from voice for distinction
      };

      setMessages(prev => [...prev, userMessage]);
    }

    // Process the message (reuse the voice processing logic)
    await processVoiceMessage(messageToSend);

  }, [inputMessage, processVoiceMessage]); // Dependencies updated

  // Update handleSendMessageRef whenever handleSendMessage changes
  useEffect(() => { handleSendMessageRef.current = handleSendMessage; }, [handleSendMessage]);

  const stopVoiceMode = useCallback(() => {
    console.log(' Stopping voice mode...');

    setIsListening(false);
    setTimeRemaining(0);
    setIsDetectingVoice(false);
    setCurrentTranscript('');

    // Clear timer
    if (timerIntervalRef.current) {
      clearInterval(timerIntervalRef.current);
      timerIntervalRef.current = null;
      console.log(' Timer cleared');
    }

    // Stop recognition
    if (recognitionRef.current) {
      try {
        recognitionRef.current.stop();
      } catch (error) {
        console.warn('Error stopping recognition:', error);
      }
    }

    stopSpeaking();
    toast.success(' Voice mode OFF');
  }, [stopSpeaking]);

  // Update stopVoiceModeRef whenever stopVoiceMode changes
  useEffect(() => { stopVoiceModeRef.current = stopVoiceMode; }, [stopVoiceMode]);

  const startVoiceMode = useCallback(async () => {
    console.log(' Starting voice mode...');

    // Check for speech recognition support first
    if (!speechRecognition) {
      toast.error('Voice recognition not supported in this browser');
      setPermissionError('Speech recognition is not supported in this browser. Please use Chrome, Edge, or Safari for voice commands.');
      return;
    }

    try {
      // Request microphone permission
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      stream.getTracks().forEach(track => track.stop()); // Stop immediately, we just needed permission

      // Clear any existing states
      setIsListening(true);
      setTimeRemaining(120); // 2 minutes
      setIsDetectingVoice(false);
      setCurrentTranscript('');
      setPermissionError(null);

      // Add greeting message
      const greetingMessage = {
        role: 'assistant',
        content: " Voice mode activated! I'm listening for 2 minutes. You can ask me about your contacts, leads, activities, or anything else in your CRM.",
        timestamp: Date.now(),
        isGreeting: true
      };
      setMessages(prev => [...prev, greetingMessage]);

      // Start the timer countdown
      console.log(' Starting timer countdown...');
      // Clear any existing timer
      if (timerIntervalRef.current) {
        clearInterval(timerIntervalRef.current);
      }
      timerIntervalRef.current = setInterval(() => {
        setTimeRemaining(prev => {
          console.log(' Timer tick, remaining:', prev - 1);
          if (prev <= 1) {
            console.log(' Timer expired');
            stopVoiceMode();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);

      // Stop any active speech before starting recognition
      stopSpeaking();

      // Start speech recognition
      console.log(' Starting speech recognition...');
      if (recognitionRef.current) {
        console.log(' Starting speech recognition...');
        recognitionRef.current.start();
        toast.success(' Voice mode ON - Speak now!');
      } else {
        console.error('Recognition ref is null, could not start.');
      }
    } catch (error) {
      console.error(' Failed to start voice mode:', error);
      toast.error('Failed to start voice recognition: ' + error.message);
      setPermissionError('Microphone access denied. Please allow access and try again.');
      // Ensure voice mode states are reset if start fails
      setIsListening(false);
      setTimeRemaining(0);
      setIsDetectingVoice(false);
      setCurrentTranscript('');
      if (timerIntervalRef.current) {
        clearInterval(timerIntervalRef.current);
        timerIntervalRef.current = null;
      }
    }
  }, [speechRecognition, stopVoiceMode, stopSpeaking]);


  const toggleVoiceMode = useCallback(() => {
    console.log(' Toggle voice mode - current state:', isListening);
    if (isListening) {
      stopVoiceMode();
    } else {
      startVoiceMode();
    }
  }, [isListening, stopVoiceMode, startVoiceMode]);

  // Simplified speech recognition setup
  useEffect(() => {
    if (typeof window === 'undefined') return;

    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

    if (SpeechRecognition) {
      const recognition = new SpeechRecognition();

      recognition.continuous = false; // Simplified: listen for one utterance then fires onend
      recognition.interimResults = true;
      recognition.lang = 'en-US';

      recognition.onresult = (event) => {
        if (!isListeningRef.current) { // Use ref for latest state
            console.log(' Ignoring result - not listening anymore (via onresult)');
            return;
        }

        console.log(' Speech result received');
        let finalTranscript = '';
        let interimTranscript = '';

        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            finalTranscript += transcript;
          } else {
            interimTranscript += transcript;
          }
        }

        // Show what we're hearing
        const displayText = finalTranscript || interimTranscript;
        if (displayText.trim()) {
          setCurrentTranscript(displayText.trim());
          setIsDetectingVoice(true);
        }

        // Process final result
        if (finalTranscript.trim() && finalTranscript.trim().length > 3) {
          console.log(' Final speech:', finalTranscript);

          // Clear transcript display after brief delay
          setTimeout(() => setCurrentTranscript(''), 500);

          // Add user message
          const userMessage = {
            role: 'user',
            content: finalTranscript.trim(),
            timestamp: Date.now(),
            isVoice: true
          };
          setMessages(prev => [...prev, userMessage]);

          // Process with AI
          // Use ref for handleSendMessage to ensure latest version is called
          setTimeout(() => {
            handleSendMessageRef.current(finalTranscript.trim());
          }, 200);
        }
      };

      recognition.onstart = () => {
        console.log(' Recognition started');
        setIsDetectingVoice(false);
      };

      recognition.onend = () => {
        console.log(' Recognition ended, listening:', isListeningRef.current, 'time remaining:', timeRemaining);
        setIsDetectingVoice(false);

        // Restart if we're still supposed to be listening (2-min timer active)
        if (isListeningRef.current && timeRemaining > 0) {
          setTimeout(() => {
            if (isListeningRef.current && recognitionRef.current) { // Double check again before restarting
              try {
                recognitionRef.current.start();
                console.log(' Recognition restarted successfully');
              } catch (error) {
                console.warn('Failed to restart recognition:', error);
              }
            }
          }, 100); // Small delay before attempting to restart
        } else if (recognitionRef.current) {
            // If we're not listening, ensure recognition is stopped
            try {
                recognitionRef.current.stop();
                console.log(' Recognition manually stopped via onend (not listening)');
            } catch(e) {
                console.warn('Error stopping recognition onend cleanup:', e);
            }
        }
      };

      recognition.onerror = (event) => {
        console.log(' Recognition error:', event.error);
        setIsDetectingVoice(false);

        if (event.error === 'not-allowed') {
          toast.error('Microphone access denied. Please enable permissions.');
          stopVoiceModeRef.current(); // Use ref here to ensure latest stopVoiceMode is called
          setPermissionError('Microphone access denied. Please allow access and try again.');
        } else if (event.error !== 'aborted' && event.error !== 'no-speech') {
          console.error(' Unexpected recognition error:', event.error);
          // For other errors, don't stop voice mode, let onend handle restart if still active
        }
      };

      recognitionRef.current = recognition;
      setSpeechRecognition(recognition);
      console.log(' Speech recognition initialized');
    }

    return () => {
      if (timerIntervalRef.current) {
        clearInterval(timerIntervalRef.current);
        timerIntervalRef.current = null;
      }
      if (recognitionRef.current) {
        try {
          recognitionRef.current.abort(); // Use abort for immediate cleanup on unmount
        } catch(e) {
          // Ignore errors during cleanup
        }
      }
    };
  }, [stopVoiceMode, timeRemaining, isListening, handleSendMessage]); // Dependencies updated for stability

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Check if user has been briefed today
  useEffect(() => {
    const lastBriefingDate = localStorage.getItem('lastBriefingDate');
    const today = new Date().toDateString();
    setHasBriefingToday(lastBriefingDate === today);
  }, []);

  // Auto-trigger briefing when widget opens (if enabled and not briefed today)
  useEffect(() => {
    if (isOpen && briefingEnabled && !hasBriefedToday && user) {
      const timer = setTimeout(() => {
        triggerDailyBriefing();
      }, 1000);
      return () => clearTimeout(timer);
    }
  }, [isOpen, briefingEnabled, hasBriefedToday, user, triggerDailyBriefing]);

  // Handle key press for Enter to send
  const handleKeyPress = useCallback((e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  }, [handleSendMessage]);

  // Helper function to get permission status icon and color
  const getPermissionIndicator = () => {
    switch (micPermissionStatus) {
      case 'granted':
        return { icon: '', color: 'text-green-600', text: 'Microphone ready' };
      case 'denied':
        return { icon: '', color: 'text-red-600', text: 'Microphone blocked' };
      case 'prompt':
        return { icon: '', color: 'text-yellow-600', text: 'Permission needed' };
      default:
        return { icon: '', color: 'text-gray-600', text: 'Checking...' };
    }
  };

  // Render messages with better key handling and styling
  const renderMessages = () => {
    return messages.map((message, index) => (
      <div key={`${message.timestamp}-${index}`} className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}>
        <div className={`max-w-[80%] p-3 rounded-lg ${
          message.role === 'user' 
            ? 'bg-blue-500 text-white rounded-br-sm' 
            : message.isBriefing
            ? 'bg-gradient-to-r from-purple-100 to-blue-100 border-l-4 border-purple-500 rounded-bl-sm'
            : message.isGreeting
            ? 'bg-gradient-to-r from-green-100 to-emerald-100 border-l-4 border-green-500 rounded-bl-sm'
            : message.isError // New condition for error messages
            ? 'bg-red-50 text-red-700 border border-red-200 rounded-bl-sm'
            : 'bg-gray-100 text-gray-800 rounded-bl-sm'
        }`}>
          {message.isBriefing && <Badge className="mb-2 text-xs">Daily Briefing</Badge>}
          {message.isGreeting && <Badge className="mb-2 text-xs bg-green-600">Voice Greeting</Badge>}
          {message.isVoice && <Badge className="mb-2 text-xs bg-blue-400 text-white">Voice Input</Badge>}
          {message.isVoiceResponse && <Badge className="mb-2 text-xs bg-emerald-400 text-white">Voice Response</Badge>}
          <p className="text-sm whitespace-pre-wrap break-words">{message.content}</p>
          {message.source && message.source !== 'ai' && ( // Only show if source is not 'ai'
            <div className="mt-2 text-xs opacity-75">
              <Badge variant="outline" className="text-xs">
                Source: {message.source}
              </Badge>
            </div>
          )}
        </div>
      </div>
    ));
  };


  if (!user) return null;

  const permissionIndicator = getPermissionIndicator();

  return (
    <>
      {/* Floating Action Button */}
      <div className="fixed bottom-6 right-6 z-50">
        <div className="relative">
          <Button
            onClick={() => setIsOpen(!isOpen)}
            className={`w-14 h-14 rounded-full shadow-lg hover:shadow-xl transition-all duration-300 ${
              isListening 
                ? 'bg-gradient-to-r from-red-500 to-pink-500 hover:from-red-600 hover:to-pink-600' 
                : 'bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700'
            } ${isDetectingVoice ? 'animate-pulse ring-4 ring-green-300' : ''}`}
          >
            {isOpen ? <X className="w-6 h-6" /> : <MessageCircle className="w-6 h-6" />}
          </Button>

          {/* Permission Status Indicator */}
          {micPermissionStatus === 'denied' && (
            <div className="absolute -top-2 -right-2 w-5 h-5 bg-red-500 rounded-full flex items-center justify-center text-white text-xs">
              !
            </div>
          )}
        </div>
      </div>

      {/* Chat Widget */}
      {isOpen && (
        <div className="fixed bottom-24 right-6 w-96 h-[500px] bg-white rounded-lg shadow-2xl border z-50 flex flex-col">
          {/* Header */}
          <CardHeader className="text-white rounded-t-lg p-4 bg-gradient-to-r from-purple-600 to-blue-600">
            <div className="flex items-center justify-between">
              <CardTitle className="text-lg font-semibold">
                 Ai-SHA Assistant
              </CardTitle>
              <div className="flex items-center gap-2">
                {isSpeaking && <Badge variant="secondary" className="text-xs">Speaking</Badge>}
                {isListening && timeRemaining > 0 && (
                  <Badge variant="secondary" className={`text-xs ${isDetectingVoice ? 'animate-pulse bg-green-200' : ''}`}>
                     {formatTimeRemaining(timeRemaining)}
                  </Badge>
                )}
                {isDetectingVoice && (
                  <Badge variant="secondary" className="text-xs bg-green-200 text-green-800 animate-pulse">
                     Hearing You!
                  </Badge>
                )}
                 <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setVoiceEnabled(!voiceEnabled)}
                  className="text-white hover:bg-white/20"
                >
                  {voiceEnabled ? <Volume2 className="w-4 h-4" /> : <VolumeX className="w-4 h-4" />}
                </Button>
              </div>
            </div>

            {/* Permission Status */}
            {recognitionRef.current && (
              <div className="mt-2 text-xs opacity-90 flex items-center gap-1">
                <span>{permissionIndicator.icon}</span>
                <span>{permissionIndicator.text}</span>
              </div>
            )}
          </CardHeader>

          {/* Voice Mode Indicator - New Placement */}
          {isListening && (
            <div className={`text-white p-3 text-center ${
              isDetectingVoice
                ? 'bg-gradient-to-r from-green-500 to-emerald-500 animate-pulse'
                : 'bg-gradient-to-r from-red-500 to-pink-500' 
            }`}>
              <div className="flex items-center justify-center gap-2">
                <div className={`w-3 h-3 bg-white rounded-full ${isDetectingVoice ? 'animate-pulse' : ''}`}></div>
                <span className="font-medium text-lg">
                   LISTENING - {Math.floor(timeRemaining / 60)}:{(timeRemaining % 60).toString().padStart(2, '0')}
                </span>
              </div>
              {currentTranscript && (
                <div className="mt-2 text-sm bg-white/20 rounded p-2">
                  "{currentTranscript}"
                </div>
              )}
              <Button 
                variant="ghost" 
                size="sm" 
                onClick={stopVoiceMode}
                className="mt-2 text-white hover:bg-white/20"
              >
                Stop Listening
              </Button>
            </div>
          )}

          {/* Messages */}
          <CardContent className="flex-1 overflow-y-auto p-4 space-y-3">
            {/* Permission Error Alert */}
            {permissionError && (
              <div className="bg-red-50 border border-red-200 rounded-lg p-3 mb-3">
                <div className="flex items-start gap-2">
                  <span className="text-red-500"></span>
                  <div className="text-red-700 text-xs">
                    <p className="font-semibold mb-1">Microphone Issue</p>
                    <p>{permissionError}</p>
                    {micPermissionStatus === 'denied' && (
                      <p className="mt-2">
                        <strong>Fix:</strong> Click the <span className="font-bold"></span> or <span className="font-bold"></span> icon in your browser's address bar  Allow microphone access  Refresh page
                      </p>
                    )}
                  </div>
                </div>
              </div>
            )}

            {messages.length === 0 && !permissionError ? (
              <div className="text-center text-gray-500 mt-8">
                <MessageCircle className="w-12 h-12 mx-auto mb-2 opacity-50" />
                <p>Hello! I'm Ai-SHA, your CRM assistant.</p>
                <p className="text-sm">Ask me about contacts, leads, or use voice mode!</p>
                <ul className="text-xs mt-2 space-y-1">
                  <li> <strong>Toggle voice mode</strong> for 2 minutes</li>
                  <li> Type commands</li>
                  <li> Ask about your CRM data</li>
                  <li> Get daily briefings</li>
                </ul>

                {recognitionRef.current && micPermissionStatus === 'prompt' && (
                  <div className="mt-3 p-2 bg-blue-50 rounded text-xs">
                    <p className="text-blue-700"> Click the microphone button to enable voice mode</p>
                  </div>
                )}
              </div>
            ) : (
              renderMessages() // Call the new renderMessages function here
            )}

            {isLoading && (
              <div className="flex justify-start">
                <div className="bg-gray-100 p-3 rounded-lg">
                  <div className="flex items-center space-x-2">
                    <Loader2 className="w-4 h-4 animate-spin" />
                    <span className="text-sm">Ai-SHA is thinking...</span>
                  </div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </CardContent>

          {/* Input */}
          <div className="p-4 border-t">
            <div className="flex gap-2">
              <Button
                variant={isListening ? "destructive" : micPermissionStatus === 'denied' ? "secondary" : "outline"}
                size="sm"
                onClick={toggleVoiceMode}
                disabled={isLoading || !recognitionRef.current || isSpeaking || (micPermissionStatus === 'denied' && !isListening) || isProcessing}
                className={`shrink-0 ${isDetectingVoice ? 'animate-pulse ring-2 ring-green-400' : ''}`}
                title={micPermissionStatus === 'denied' ? 'Microphone access blocked' : 'Toggle voice mode'}
              >
                {isDetectingVoice ? (
                  <div className="flex items-center">
                    <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse mr-1"></div>
                    <Mic className="w-4 h-4" />
                  </div>
                ) : isListening ? (
                  <MicOff className="w-4 h-4" />
                ) : (
                  <Mic className="w-4 h-4" />
                )}
              </Button>
              <Input
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder={
                  micPermissionStatus === 'denied'
                    ? "Microphone access blocked. Enable for voice."
                    : "Type a message or click mic for voice mode..."
                }
                disabled={isLoading || isListening || micPermissionStatus === 'denied' || isProcessing}
                className="flex-1"
              />
              <Button 
                onClick={() => handleSendMessage()} 
                disabled={!inputMessage.trim() || isLoading || isProcessing}
                size="sm"
              >
                {isLoading ? <Loader2 className="w-4 h-4 animate-spin" /> : 'Send'}
              </Button>
            </div>

            {/* Daily Briefing Button */}
            {briefingEnabled && !hasBriefedToday && (
              <div className="mt-2">
                <Button
                  onClick={triggerDailyBriefing}
                  disabled={isLoading || isProcessing}
                  variant="outline"
                  size="sm"
                  className="w-full"
                >
                  {isLoading ? (
                    <>
                      <Loader2 className="w-4 h-4 animate-spin mr-2" />
                      Generating briefing...
                    </>
                  ) : (
                    <>
                       Get Daily Briefing
                    </>
                  )}
                </Button>
              </div>
            )}
          </div>
        </div>
      )}
    </>
  );
}

import React from "react";
import { Button } from "@/components/ui/button";
import { Volume2, VolumeX, Loader2 } from "lucide-react";
import { generateElevenLabsSpeech } from "@/api/functions";

export default function AudioPlayerButton(props) {
  // AUTO-CLEAR old disable flag on mount
  React.useEffect(() => {
    try {
      if (typeof localStorage !== "undefined" && localStorage.getItem('disable_inline_audio') === 'true') {
        localStorage.removeItem('disable_inline_audio');
        console.log('[AudioPlayerButton] Cleared old disable_inline_audio flag from localStorage');
      }
      if (typeof window !== "undefined" && window.__AI_CONTEXT?.disable_inline_audio === true) {
        window.__AI_CONTEXT.disable_inline_audio = false;
        console.log('[AudioPlayerButton] Cleared global disable_inline_audio flag from window.__AI_CONTEXT');
      }
    } catch (e) {
      console.warn('[AudioPlayerButton] Could not clear disable flags:', e);
    }
  }, []);

  // Compute disabled flag without early-returning before hooks
  const disabledByProp = props?.hidden === true || props?.disableInlineAudio === true;
  const disabledByGlobal =
    (typeof window !== "undefined" && window.__AI_CONTEXT?.disable_inline_audio === true) ||
    (typeof localStorage !== "undefined" && localStorage.getItem("disable_inline_audio") === "true");
  const disabled = disabledByProp || disabledByGlobal;

  // Hooks must always be called in the same order
  const [loading, setLoading] = React.useState(false);
  const [playing, setPlaying] = React.useState(false);
  const audioRef = React.useRef(null);
  const urlRef = React.useRef(null);

  const cleanup = () => {
    if (audioRef.current) {
      try { audioRef.current.pause(); } catch (e) { /* ignore */ }
      audioRef.current.onended = null;
      audioRef.current.onpause = null;
      audioRef.current.onerror = null;
      audioRef.current = null;
    }
    if (urlRef.current) {
      try { URL.revokeObjectURL(urlRef.current); } catch (e) { /* ignore */ }
      urlRef.current = null;
    }
    setPlaying(false);
  };

  React.useEffect(() => () => cleanup(), []);

  // If disabled, render nothing (hooks already called above to satisfy rules)
  if (disabled) {
    return null;
  }

  const ensureAudio = async () => {
    if (audioRef.current) return audioRef.current;

    setLoading(true);
    try {
      const resp = await generateElevenLabsSpeech({ text: props.text, voice_id: props.voiceId || "21m00Tcm4TlvDq8ikWAM" });

      let blob;
      const headers = resp?.headers;
      const ct = (headers?.["content-type"] || headers?.get?.("content-type") || "").toLowerCase();
      const data = resp?.data;

      // Preferred: JSON with base64 from our backend
      if (data?.audio_base64 && typeof data.audio_base64 === "string") {
        const decoded = Uint8Array.from(atob(data.audio_base64), c => c.charCodeAt(0));
        blob = new Blob([decoded], { type: "audio/mpeg" });
      } else if (typeof data === "string" && data.length > 0) {
        // Some wrappers may return a string; try parse JSON or base64
        try {
          const parsed = JSON.parse(data);
          if (parsed?.audio_base64) {
            const decoded = Uint8Array.from(atob(parsed.audio_base64), c => c.charCodeAt(0));
            blob = new Blob([decoded], { type: "audio/mpeg" });
          }
        } catch {
          // If JSON.parse failed, try direct base64
          try {
            const decoded = Uint8Array.from(atob(data), c => c.charCodeAt(0));
            blob = new Blob([decoded], { type: "audio/mpeg" });
          } catch {
            // failed to parse as JSON or base64 string
          }
        }
      } else if (ct.includes("audio/") || data instanceof ArrayBuffer || ArrayBuffer.isView(data)) {
        // Raw audio fallback
        const bytes = data instanceof ArrayBuffer ? new Uint8Array(data) : (ArrayBuffer.isView(data) ? data : new Uint8Array([]));
        blob = new Blob([bytes], { type: "audio/mpeg" });
      }

      // Sanity: ensure we have some audio bytes (allow very short clips)
      if (!blob || blob.size <= 0) {
        throw new Error("Received empty audio");
      }

      const url = URL.createObjectURL(blob);
      urlRef.current = url;
      const audio = new Audio(url);
      audio.preload = "auto";

      audio.onended = () => {
        setPlaying(false);
        window.dispatchEvent(new CustomEvent("chat:unlock-open"));
      };
      audio.onpause = () => {
        if (playing) {
          setPlaying(false);
          window.dispatchEvent(new CustomEvent("chat:unlock-open"));
        }
      };
      audio.onerror = () => {
        setPlaying(false);
        window.dispatchEvent(new CustomEvent("chat:unlock-open"));
      };

      audioRef.current = audio;
      return audio;
    } finally {
      setLoading(false);
    }
  };

  const onClick = async () => {
    if (loading) return;

    // Toggle pause if already playing
    if (playing) {
      try { audioRef.current?.pause(); } catch (e) { /* ignore */ }
      setPlaying(false);
      window.dispatchEvent(new CustomEvent("chat:unlock-open"));
      return;
    }

    const audio = await ensureAudio();
    if (!audio) return;

    try {
      window.dispatchEvent(new CustomEvent("chat:lock-open"));
      await audio.play();
      setPlaying(true);
    } catch (e) {
      // If autoplay/gesture blocks, unlock and stop
      window.dispatchEvent(new CustomEvent("chat:unlock-open"));
      setPlaying(false);
    }
  };

  return (
    <Button variant="ghost" size="icon" onClick={onClick} className={props.className || ""} title="Speak message">
      {loading ? <Loader2 className="w-4 h-4 animate-spin text-slate-400" /> : (
        playing ? <VolumeX className="w-4 h-4 text-slate-300" /> : <Volume2 className="w-4 h-4 text-slate-300" />
      )}
    </Button>
  );
}

import React, { useState, useEffect, useRef } from "react";

export default function AvatarWidget({
  agentId,
  apiKey,
  onMessage,
  onNavigate
}) {
  const [isReady, setIsReady] = useState(false); // isReady state added as per outline
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);

  const widgetRef = useRef(null);

  // This useEffect now only handles global AI events to update listening/speaking states.
  // It no longer handles 'thinking' state as per the new design,
  // and 'idle' is used to correctly reset 'speaking'.
  useEffect(() => {
    const onSpeaking = () => setIsSpeaking(true);
    const onIdle = () => {
      // The original audio element dispatched 'ai:idle' on 'onEnded'.
      // With the audio element removed, 'ai:idle' must be dispatched by some other
      // mechanism when speaking stops, or 'isSpeaking' will remain true.
      // Assuming 'ai:idle' still functions as the event to signal the end of AI activity.
      setIsSpeaking(false);
    };
    const onListening = (e) => setIsListening(e.detail.isListening);

    window.addEventListener('ai:speaking', onSpeaking);
    window.addEventListener('ai:idle', onIdle);
    window.addEventListener('ai:listening', onListening);

    return () => {
      window.removeEventListener('ai:speaking', onSpeaking);
      window.removeEventListener('ai:idle', onIdle);
      window.removeEventListener('ai:listening', onListening);
    };
  }, []);

  return (
    <div
      id="ai-avatar-launcher"
      ref={widgetRef}
      className="fixed bottom-4 right-24 z-[10005]"
      style={{
        width: '80px',
        height: '80px',
        borderRadius: '50%',
      }}
    >
      {/* Animated Glow Ring - BEHIND everything */}
      <div
        className={`absolute inset-0 transition-all duration-300 ${
          isSpeaking ? 'animate-pulse' : ''
        }`}
        style={{
          borderRadius: '50%',
          background: isSpeaking
            ? 'radial-gradient(circle, rgba(34, 197, 94, 0.5) 0%, rgba(34, 197, 94, 0.2) 50%, transparent 70%)'
            : isListening
            ? 'radial-gradient(circle, rgba(59, 130, 246, 0.5) 0%, rgba(59, 130, 246, 0.2) 50%, transparent 70%)'
            : 'radial-gradient(circle, rgba(234, 179, 8, 0.4) 0%, rgba(234, 179, 8, 0.15) 50%, transparent 70%)',
          filter: 'blur(12px)',
          transform: isSpeaking ? 'scale(1.4)' : isListening ? 'scale(1.3)' : 'scale(1.2)',
        }}
      />

      {/* Colored Border Circle */}
      <div
        className="absolute inset-0 transition-all duration-300"
        style={{
          borderRadius: '50%',
          border: '3px solid',
          borderColor: isSpeaking
            ? 'rgb(34, 197, 94)' // green-500
            : isListening
            ? 'rgb(59, 130, 246)' // blue-500
            : 'rgb(234, 179, 8)', // yellow-500
          boxShadow: isSpeaking
            ? '0 0 25px rgba(34, 197, 94, 0.7), 0 0 50px rgba(34, 197, 94, 0.4), inset 0 0 20px rgba(34, 197, 94, 0.2)'
            : isListening
            ? '0 0 25px rgba(59, 130, 246, 0.7), 0 0 50px rgba(59, 130, 246, 0.4), inset 0 0 20px rgba(59, 130, 246, 0.2)'
            : '0 0 20px rgba(234, 179, 8, 0.5), 0 0 40px rgba(234, 179, 8, 0.3)',
        }}
      />

      {/* Avatar Image Container - FULLY CIRCULAR */}
      <div
        className="absolute flex items-center justify-center bg-slate-900 overflow-hidden"
        style={{
          top: '6px',
          left: '6px',
          right: '6px',
          bottom: '6px',
          borderRadius: '50%',
          width: 'calc(100% - 12px)',
          height: 'calc(100% - 12px)',
        }}
      >
        <img
          src="https://qtrypzzcjebvfcihiynt.supabase.co/storage/v1/object/public/base44-prod/public/68ad592dcffacef630b477d2/1fee90417_aisha_new_nobg.png"
          alt="AI Assistant"
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            borderRadius: '50%',
            transform: isSpeaking ? 'scale(1.08)' : 'scale(1)',
            transition: 'transform 0.3s ease',
          }}
        />
      </div>

      {/* Status Indicator Dot */}
      <div
        className="absolute bottom-0 right-0"
        style={{
          width: '16px',
          height: '16px',
          borderRadius: '50%',
          border: '2px solid rgb(15, 23, 42)',
          backgroundColor: isSpeaking
            ? 'rgb(34, 197, 94)' // green
            : isListening
            ? 'rgb(59, 130, 246)' // blue
            : 'rgb(234, 179, 8)', // yellow
          boxShadow: isSpeaking || isListening
            ? '0 0 10px currentColor, 0 0 20px currentColor'
            : '0 0 6px currentColor',
        }}
      />

      {/* Pulse animation rings for speaking */}
      {isSpeaking && (
        <>
          <div
            className="absolute inset-0 border-2 border-green-500 animate-ping"
            style={{
              borderRadius: '50%',
              opacity: 0.4,
              animationDuration: '1s'
            }}
          />
