      <div className="text-slate-400 text-sm">No web results yet. Try another query.</div>
    );
  }

  return (
    <div className="space-y-3">
      {results.map((item, idx) => {
        const isCompany = (item.type || "").toLowerCase().includes("company");
        const hasUrl = !!item.website || !!item.domain || !!item.linkedin;
        const primaryUrl = item.website || (item.domain ? `https://${item.domain}` : item.linkedin);

        return (
          <div
            key={idx}
            className="rounded-lg border border-slate-700 bg-slate-800/70 p-3 hover:bg-slate-800 transition-colors"
          >
            <div className="flex items-start justify-between gap-3">
              <div className="min-w-0">
                <div className="flex items-center gap-2">
                  {isCompany ? (
                    <Building2 className="w-4 h-4 text-blue-400" />
                  ) : (
                    <User className="w-4 h-4 text-purple-400" />
                  )}
                  <h4 className="text-slate-100 font-medium truncate">{item.name || item.title || "Unknown"}</h4>
                </div>
                {item.summary && (
                  <p className="mt-1 text-sm text-slate-300 line-clamp-3">{item.summary}</p>
                )}
                <div className="mt-2 flex flex-wrap gap-3 text-xs text-slate-400">
                  {item.location && (
                    <span className="inline-flex items-center gap-1">
                      <MapPin className="w-3 h-3" />
                      {item.location}
                    </span>
                  )}
                  {item.domain && (
                    <span className="inline-flex items-center gap-1">
                      <Globe className="w-3 h-3" />
                      {item.domain}
                    </span>
                  )}
                  {item.linkedin && (
                    <span className="inline-flex items-center gap-1">
                      <LinkIcon className="w-3 h-3" />
                      LinkedIn
                    </span>
                  )}
                </div>
              </div>

              <div className="shrink-0 flex flex-col items-end gap-2">
                {hasUrl && primaryUrl && (
                  <a
                    href={primaryUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="inline-flex items-center gap-1 text-xs text-slate-200 hover:text-white"
                  >
                    Open <ExternalLink className="w-3 h-3" />
                  </a>
                )}
                <div className="flex gap-2">
                  {onAddLead && (
                    <Button
                      size="sm"
                      className="bg-blue-600 hover:bg-blue-700 h-7 px-2 text-xs"
                      onClick={() => onAddLead(item)}
                      title="Create a Lead from this result"
                    >
                      Save as Lead
                    </Button>
                  )}
                  {onAddContact && (
                    <Button
                      size="sm"
                      className="bg-emerald-600 hover:bg-emerald-700 h-7 px-2 text-xs"
                      onClick={() => onAddContact(item)}
                      title="Create a Contact from this result"
                    >
                      Save as Contact
                    </Button>
                  )}
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

import React, { useState } from 'react';
import ReactMarkdown from 'react-markdown';
import { Button } from "@/components/ui/button";
import { Copy, Zap, CheckCircle2, AlertCircle, Loader2, ChevronRight, Clock } from 'lucide-react';
import { cn } from "@/lib/utils";
import { toast } from "sonner";

const FunctionDisplay = ({ toolCall }) => {
    const [expanded, setExpanded] = useState(false);
    const name = toolCall?.name || 'Function';
    const status = toolCall?.status || 'pending';
    const results = toolCall?.results;

    const parsedResults = (() => {
        if (!results) return null;
        try {
            return typeof results === 'string' ? JSON.parse(results) : results;
        } catch {
            return results;
        }
    })();

    const isError = results && (
        (typeof results === 'string' && /error|failed/i.test(results)) ||
        (parsedResults?.success === false)
    );

    const statusConfig = {
        pending: { icon: Clock, color: 'text-slate-400', text: 'Pending' },
        running: { icon: Loader2, color: 'text-slate-500', text: 'Running...', spin: true },
        in_progress: { icon: Loader2, color: 'text-slate-500', text: 'Running...', spin: true },
        completed: isError ? 
            { icon: AlertCircle, color: 'text-red-500', text: 'Failed' } : 
            { icon: CheckCircle2, color: 'text-green-600', text: 'Success' },
        success: { icon: CheckCircle2, color: 'text-green-600', text: 'Success' },
        failed: { icon: AlertCircle, color: 'text-red-500', text: 'Failed' },
        error: { icon: AlertCircle, color: 'text-red-500', text: 'Failed' }
    }[status] || { icon: Zap, color: 'text-slate-500', text: '' };

    const Icon = statusConfig.icon;
    const formattedName = name.split('.').reverse().join(' ').toLowerCase();

    return (
        <div className="mt-2 text-xs">
            <button
                onClick={() => setExpanded(!expanded)}
                className={cn(
                    "flex items-center gap-2 px-3 py-1.5 rounded-lg border transition-all",
                    "hover:bg-slate-50",
                    expanded ? "bg-slate-50 border-slate-300" : "bg-white border-slate-200"
                )}
            >
                <Icon className={cn("h-3 w-3", statusConfig.color, statusConfig.spin && "animate-spin")} />
                <span className="text-slate-700">{formattedName}</span>
                {statusConfig.text && (
                    <span className={cn("text-slate-500", isError && "text-red-600")}>
                         {statusConfig.text}
                    </span>
                )}
                {!statusConfig.spin && (toolCall.arguments_string || results) && (
                    <ChevronRight className={cn("h-3 w-3 text-slate-400 transition-transform ml-auto", 
                        expanded && "rotate-90")} />
                )}
            </button>

            {expanded && !statusConfig.spin && (
                <div className="mt-1.5 ml-3 pl-3 border-l-2 border-slate-200 space-y-2">
                    {toolCall.arguments_string && (
                        <div>
                            <div className="text-xs text-slate-500 mb-1">Parameters:</div>
                            <pre className="bg-slate-50 rounded-md p-2 text-xs text-slate-600 whitespace-pre-wrap">
                                {(() => {
                                    try {
                                        return JSON.stringify(JSON.parse(toolCall.arguments_string), null, 2);
                                    } catch {
                                        return toolCall.arguments_string;
                                    }
                                })()}
                            </pre>
                        </div>
                    )}
                    {parsedResults && (
                        <div>
                            <div className="text-xs text-slate-500 mb-1">Result:</div>
                            <pre className="bg-slate-50 rounded-md p-2 text-xs text-slate-600 whitespace-pre-wrap max-h-48 overflow-auto">
                                {typeof parsedResults === 'object' ? 
                                    JSON.stringify(parsedResults, null, 2) : parsedResults}
                            </pre>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

