                    key={i}
                    href={src}
                    target="_blank"
                    rel="noreferrer"
                    className="inline-flex items-center gap-1"
                    title={src}
                  >
                    <Badge variant="outline" className="bg-slate-800 border-slate-600 text-blue-300 hover:bg-slate-700">
