import React, { useState, useEffect } from "react";
import { ArchiveIndex } from "@/api/entities";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import {
  Archive,
  Search,
  Download,
  RotateCcw,
  Loader2,
  X,
  AlertTriangle,
} from "lucide-react";
import { toast } from "sonner";
import { format } from "date-fns";
import { retrieveArchiveFromR2 } from "@/api/functions";

export default function ArchiveIndexViewer({ tenantId, onClose, onRetrieved }) {
  const [archives, setArchives] = useState([]);
  const [loading, setLoading] = useState(true);
  const [retrieving, setRetrieving] = useState(false);
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);
  const [selectedArchive, setSelectedArchive] = useState(null);
  const [reactivateMode, setReactivateMode] = useState('all');

  const [searchTerm, setSearchTerm] = useState("");
  const [batchFilter, setBatchFilter] = useState("all");
  const [formatFilter, setFormatFilter] = useState("all");

  useEffect(() => {
    loadArchives();
  }, [tenantId]);

  const loadArchives = async () => {
    if (!tenantId) return;

    setLoading(true);
    try {
      const filter = {
        tenant_id: tenantId,
        entity_type: "BizDevSource"
      };

      const fetchedArchives = await ArchiveIndex.filter(filter, '-archived_at', 100);
      setArchives(fetchedArchives || []);
    } catch (error) {
      console.error("Failed to load archives:", error);
      toast.error("Failed to load archive index");
    } finally {
      setLoading(false);
    }
  };

  const handleRetrieveClick = (archive) => {
    setSelectedArchive(archive);
    setShowConfirmDialog(true);
  };

  const handleConfirmRetrieve = async () => {
    if (!selectedArchive) return;

    setRetrieving(true);
    setShowConfirmDialog(false);

    try {
      const response = await retrieveArchiveFromR2({
        archive_index_id: selectedArchive.id,
        reactivate_mode: reactivateMode
      });

      if (response.status === 200) {
        const result = response.data;
        toast.success(
          `Retrieved ${result.rehydrated_count} records from archive`,
          {
            description: result.skipped_count > 0 
              ? `${result.skipped_count} records were skipped (already exist)`
              : undefined
          }
        );

        // Callback to refresh the main page
        if (onRetrieved) {
          onRetrieved();
        }
      } else {
        throw new Error(response.data?.error || 'Retrieval failed');
      }
    } catch (error) {
      console.error("Archive retrieval failed:", error);
      toast.error("Failed to retrieve archive", {
        description: error.message || 'An error occurred'
      });
    } finally {
      setRetrieving(false);
      setSelectedArchive(null);
    }
  };

  const filteredArchives = archives.filter((archive) => {
    const matchesSearch = !searchTerm ||
      archive.batch_id?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      archive.source_description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      archive.archive_path?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesBatch = batchFilter === "all" || archive.batch_id === batchFilter;
    const matchesFormat = formatFilter === "all" || archive.file_format === formatFilter;

    return matchesSearch && matchesBatch && matchesFormat;
  });

  const uniqueBatches = [...new Set(archives.map(a => a.batch_id).filter(Boolean))];

  const formatFileSize = (bytes) => {
    if (!bytes) return "N/A";
    if (bytes < 1024) return `${bytes} B`;
    if (bytes < 1024 * 1024) return `${(bytes / 1024).toFixed(2)} KB`;
    return `${(bytes / (1024 * 1024)).toFixed(2)} MB`;
  };

  if (loading) {
    return (
      <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
        <div className="bg-slate-800 rounded-lg p-8">
          <Loader2 className="w-8 h-8 animate-spin text-blue-500 mx-auto" />
          <p className="text-slate-300 mt-4">Loading archives...</p>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
        <div className="bg-slate-800 rounded-lg max-w-6xl w-full max-h-[90vh] flex flex-col">
          <CardHeader className="border-b border-slate-700 flex-shrink-0">
            <div className="flex items-center justify-between">
              <CardTitle className="text-slate-100 flex items-center gap-2">
                <Archive className="w-5 h-5 text-blue-400" />
                Archive Index
              </CardTitle>
              <Button
                variant="ghost"
                size="icon"
                onClick={onClose}
                className="text-slate-400 hover:text-slate-300"
                disabled={retrieving}
              >
                <X className="w-5 h-5" />
              </Button>
            </div>
          </CardHeader>

          <CardContent className="p-6 flex-1 overflow-auto">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-slate-400" />
                <Input
                  placeholder="Search archives..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 bg-slate-700 border-slate-600 text-slate-100"
                />
              </div>
              <Select value={batchFilter} onValueChange={setBatchFilter}>
                <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-100">
                  <SelectValue placeholder="Filter by batch" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Batches</SelectItem>
                  {uniqueBatches.map(batch => (
                    <SelectItem key={batch} value={batch}>{batch}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <Select value={formatFilter} onValueChange={setFormatFilter}>
                <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-100">
                  <SelectValue placeholder="Filter by format" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Formats</SelectItem>
                  <SelectItem value="json">JSON</SelectItem>
                  <SelectItem value="csv">CSV</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {filteredArchives.length === 0 ? (
              <div className="text-center py-12">
                <Archive className="w-16 h-16 text-slate-600 mx-auto mb-4" />
                <h3 className="text-lg font-semibold text-slate-300 mb-2">
                  No archives found
                </h3>
                <p className="text-slate-400">
                  {archives.length === 0
                    ? "No BizDev sources have been archived yet."
                    : "Try adjusting your filters or search term."}
                </p>
              </div>
            ) : (
              <div className="space-y-3">
                {filteredArchives.map((archive) => (
                  <Card key={archive.id} className="bg-slate-700 border-slate-600 hover:border-blue-500 transition-colors">
                    <CardContent className="p-4">
                      <div className="flex items-start justify-between">
                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-2">
                            <h3 className="font-semibold text-slate-100">
                              {archive.source_description || "BizDev Archive"}
                            </h3>
                            <Badge variant="outline" className="text-xs">
                              {archive.file_format?.toUpperCase()}
                            </Badge>
                            {archive.is_accessible ? (
                              <Badge variant="outline" className="text-xs text-green-400 border-green-600">
                                Accessible
                              </Badge>
                            ) : (
                              <Badge variant="outline" className="text-xs text-red-400 border-red-600">
                                Unavailable
                              </Badge>
                            )}
                          </div>

                          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                            <div>
                              <p className="text-slate-400">Batch ID</p>
                              <p className="text-slate-200 font-mono text-xs">
                                {archive.batch_id || 'N/A'}
                              </p>
                            </div>
                            <div>
                              <p className="text-slate-400">Records</p>
                              <p className="text-slate-200 font-medium">
                                {archive.record_count}
                              </p>
                            </div>
                            <div>
                              <p className="text-slate-400">File Size</p>
                              <p className="text-slate-200 font-medium">
                                {formatFileSize(archive.file_size_bytes)}
                              </p>
                            </div>
                            <div>
                              <p className="text-slate-400">Archived</p>
                              <p className="text-slate-200">
                                {format(new Date(archive.archived_at), 'MMM d, yyyy')}
                              </p>
                            </div>
                          </div>

                          <div className="mt-2">
                            <p className="text-xs text-slate-400">
                              <span className="font-mono">{archive.archive_path}</span>
                            </p>
                            <p className="text-xs text-slate-500 mt-1">
                              Archived by: {archive.archived_by}
                            </p>
                          </div>
                        </div>

                        <div className="flex gap-2 ml-4">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleRetrieveClick(archive)}
                            disabled={!archive.is_accessible || retrieving}
                            className="border-green-600 text-green-400 hover:bg-green-900/30"
                          >
                            {retrieving && selectedArchive?.id === archive.id ? (
                              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                            ) : (
                              <RotateCcw className="w-4 h-4 mr-2" />
                            )}
                            Retrieve
                          </Button>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </CardContent>

          <div className="border-t border-slate-700 p-4 flex-shrink-0">
            <div className="flex items-center justify-between text-sm text-slate-400">
              <span>Showing {filteredArchives.length} of {archives.length} archives</span>
              <Button
                variant="outline"
                onClick={loadArchives}
                disabled={retrieving}
                className="border-slate-600 text-slate-300 hover:bg-slate-700"
              >
                Refresh
              </Button>
            </div>
          </div>
        </div>
      </div>

      {/* Confirmation Dialog */}
      <Dialog open={showConfirmDialog} onOpenChange={setShowConfirmDialog}>
        <DialogContent className="bg-slate-800 border-slate-700">
          <DialogHeader>
            <DialogTitle className="text-slate-100 flex items-center gap-2">
              <AlertTriangle className="w-5 h-5 text-yellow-500" />
              Confirm Archive Retrieval
            </DialogTitle>
            <DialogDescription className="text-slate-300">
              You are about to retrieve{" "}
              <span className="font-semibold text-slate-100">
                {selectedArchive?.record_count} records
              </span>{" "}
              from archive:
              <div className="mt-2 p-3 bg-slate-700 rounded-lg">
                <p className="text-sm font-medium text-slate-200">
                  {selectedArchive?.source_description}
                </p>
                <p className="text-xs text-slate-400 mt-1">
                  Batch: {selectedArchive?.batch_id}
                </p>
              </div>
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            <div>
              <label className="text-sm font-medium text-slate-300 mb-2 block">
                Reactivation Mode
              </label>
              <Select value={reactivateMode} onValueChange={setReactivateMode}>
                <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-100">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">
                    Reactivate existing + Create new
                  </SelectItem>
                  <SelectItem value="new_only">
                    Only create new records (skip existing)
                  </SelectItem>
                </SelectContent>
              </Select>
              <p className="text-xs text-slate-400 mt-2">
                {reactivateMode === 'all' 
                  ? "This will reactivate archived records and create any that don't exist."
                  : "This will only create records that don't already exist in the database."}
              </p>
            </div>
          </div>

          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setShowConfirmDialog(false)}
              className="border-slate-600 text-slate-300"
            >
              Cancel
            </Button>
            <Button
              onClick={handleConfirmRetrieve}
              className="bg-green-600 hover:bg-green-700"
            >
              <RotateCcw className="w-4 h-4 mr-2" />
              Retrieve Archive
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </>
  );
}

import React from "react";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  Building2,
  Mail,
  Phone,
  MapPin,
  Globe,
  ExternalLink,
  Pencil,
} from "lucide-react";

export default function BizDevSourceCard({ source, onEdit, onDelete, onViewDetails, onClick, isSelected, onSelect }) {
  const getStatusColor = (status) => {
    switch (status) {
      case "Active":
        return "bg-green-900/30 text-green-400 border-green-700";
      case "Promoted":
        return "bg-blue-900/30 text-blue-400 border-blue-700";
      case "Archived":
        return "bg-slate-700 text-slate-400 border-slate-600";
      default:
        return "bg-slate-700 text-slate-300 border-slate-600";
    }
  };

  const getLicenseStatusColor = (status) => {
    switch (status) {
      case "Active":
        return "bg-green-900/30 text-green-400 border-green-700";
      case "Suspended":
      case "Revoked":
        return "bg-red-900/30 text-red-400 border-red-700";
      case "Expired":
        return "bg-yellow-900/30 text-yellow-400 border-yellow-700";
      case "Unknown":
      case "Not Required":
        return "bg-slate-700 text-slate-400 border-slate-600";
      default:
        return "bg-slate-700 text-slate-300 border-slate-600";
    }
  };

  const linkedAccount = null;
  const statusColorClass = getStatusColor(source.status || 'Active');

  const handleCardClick = (e) => {
    // Don't open detail panel if clicking checkbox, button, or link
    if (
      e.target.type === 'checkbox' || 
      e.target.closest('button') || 
      e.target.closest('a') ||
      e.target.closest('input[type="checkbox"]')
    ) {
      return;
    }

    // Call the onClick handler to open detail panel
    if (onClick) {
      onClick(source);
    }
  };

  return (
    <Card
      className={`hover:shadow-lg transition-all duration-200 cursor-pointer bg-slate-800 border-slate-700 ${
        isSelected ? 'ring-2 ring-blue-500' : ''
      }`}
      onClick={handleCardClick}
    >
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between gap-4">
          {onSelect && (
            <div onClick={(e) => e.stopPropagation()} className="pt-1 pr-2">
              <input
                type="checkbox"
                checked={isSelected}
                onChange={(e) => {
                  e.stopPropagation();
                  onSelect(source.id);
                }}
                className="w-4 h-4 text-blue-600 bg-slate-700 border-slate-600 rounded focus:ring-blue-500"
              />
            </div>
          )}

          <div className="flex-1 space-y-1">
            <div className="flex items-start gap-3">
              <div className="w-10 h-10 rounded-lg bg-blue-900/30 border border-blue-700/50 flex items-center justify-center flex-shrink-0">
                <Building2 className="w-5 h-5 text-blue-400" />
              </div>
              <div className="flex-1 min-w-0">
                <h3 className="text-lg font-semibold text-slate-100">{source.company_name}</h3>
                {source.dba_name && (
                  <p className="text-sm text-slate-400">DBA: {source.dba_name}</p>
                )}
              </div>
            </div>
            <div className="flex items-center gap-2 flex-wrap ml-12">
              {source.license_status && source.license_status !== "Not Required" && (
                <Badge variant="outline" className={getLicenseStatusColor(source.license_status)}>
                  {source.license_status}
                </Badge>
              )}
              {source.batch_id && (
                <span className="flex items-center gap-1 text-sm text-slate-400">
                  Batch: <span className="text-slate-300">{source.batch_id}</span>
                </span>
              )}
              {source.industry && (
                <span className="flex items-center gap-1 text-sm text-slate-400">
                  Industry: <span className="text-slate-300">{source.industry}</span>
                </span>
              )}
            </div>
          </div>
          {onEdit && (
            <Button
              variant="ghost"
              size="icon"
              onClick={(e) => {
                e.stopPropagation();
                onEdit(source);
              }}
              className="text-slate-400 hover:text-blue-400 hover:bg-slate-700"
            >
              <Pencil className="w-4 h-4" />
            </Button>
          )}
        </div>
      </CardHeader>

      <CardContent className="flex-grow space-y-4">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-3 text-sm">
          {source.email && (
            <div className="flex items-center gap-2 text-slate-300">
              <Mail className="w-4 h-4 text-slate-400 flex-shrink-0" />
              <a 
                href={`mailto:${source.email}`} 
                className="hover:text-blue-400 transition-colors truncate"
                onClick={(e) => e.stopPropagation()}
              >
                {source.email}
              </a>
            </div>
          )}
          {source.phone_number && (
            <div className="flex items-center gap-2 text-slate-300">
              <Phone className="w-4 h-4 text-slate-400 flex-shrink-0" />
              <a 
                href={`tel:${source.phone_number}`} 
                className="hover:text-blue-400 transition-colors"
                onClick={(e) => e.stopPropagation()}
              >
                {source.phone_number}
              </a>
            </div>
          )}
          {source.city && source.state_province && (
            <div className="flex items-center gap-2 text-slate-300">
              <MapPin className="w-4 h-4 text-slate-400 flex-shrink-0" />
              <span className="truncate">{source.city}, {source.state_province}</span>
            </div>
          )}
          {source.website && (
            <div className="flex items-center gap-2 text-slate-300">
              <Globe className="w-4 h-4 text-slate-400 flex-shrink-0" />
              <a
                href={source.website}
                target="_blank"
                rel="noopener noreferrer"
                className="hover:text-blue-400 transition-colors truncate flex items-center gap-1"
                onClick={(e) => e.stopPropagation()}
              >
                {source.website.replace(/^https?:\/\/(www\.)?/, "")}
                <ExternalLink className="w-3 h-3" />
              </a>
            </div>
          )}
        </div>

        {linkedAccount && (
          <div className="flex items-center gap-2 text-sm">
            <Badge variant="outline" className="border-blue-600 text-blue-400">
              Linked to Account: {linkedAccount.name}
            </Badge>
          </div>
        )}

        <div className="flex justify-between items-center pt-2 border-t border-slate-700">
          <Badge variant="outline" className={statusColorClass}>
            {source.status || 'Active'}
          </Badge>
          <span className="text-xs text-slate-500">{source.source}</span>
        </div>
      </CardContent>
    </Card>
  );
}

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription, AlertTitle } from "@/components/ui/alert";
import {
  X,
  Building2,
  Mail,
  Phone,
  Globe,
  MapPin,
  FileText,
  TrendingUp,
  AlertCircle,
  CheckCircle2,
  Clock,
  Pencil,
  Archive,
  Loader2,
  ExternalLink,
  Plus,
  Calendar,
  Target,
  Users,
  CheckCircle,
  Info
} from "lucide-react";
import { format } from "date-fns";
import { toast } from "sonner";
import { promoteBizDevSourceToAccount } from "@/api/functions";
import { createPageUrl } from "@/utils";
import { Link } from "react-router-dom";
import { Opportunity } from "@/api/entities";
import { Activity } from "@/api/entities";
import { Lead } from "@/api/entities";

export default function BizDevSourceDetailPanel({ 
  bizDevSource, 
  accounts, 
  onClose, 
  onEdit, 
  onDelete,
  onArchive, 
  onPromote, 
  onUpdate, 
  onRefresh 
}) {
  const [promoting, setPromoting] = useState(false);
  const [showPromoteConfirm, setShowPromoteConfirm] = useState(false);
  const [linkedLeads, setLinkedLeads] = useState([]);
  const [creatingOpportunity, setCreatingOpportunity] = useState(false);
  const [linkedOpportunities, setLinkedOpportunities] = useState([]);
  const [currentSource, setCurrentSource] = useState(bizDevSource);

  // Update currentSource when bizDevSource prop changes
  useEffect(() => {
    if (bizDevSource) {
      setCurrentSource(bizDevSource);
    }
  }, [bizDevSource]);

  // Load linked data (leads and opportunities)
  useEffect(() => {
    const loadLinkedData = async () => {
      if (!currentSource?.id) return;

      try {
        // Load linked leads
        if (currentSource.lead_ids && currentSource.lead_ids.length > 0) {
          const leads = await Lead.list();
          const filtered = leads.filter(l => currentSource.lead_ids.includes(l.id));
          setLinkedLeads(filtered);
        } else {
          setLinkedLeads([]);
        }

        // Load linked opportunities (search by description containing source ID)
        const opps = await Opportunity.list();
        const linkedOpps = opps.filter(opp =>
          opp.description && opp.description.includes(`[BizDevSource:${currentSource.id}]`)
        );
        setLinkedOpportunities(linkedOpps);
      } catch (error) {
        console.error("Failed to load linked data:", error);
        toast.error("Failed to load linked data.");
      }
    };
    loadLinkedData();
  }, [currentSource?.id, currentSource?.lead_ids]);

  if (!currentSource) return null;

  const handlePromote = async () => {
    if (!currentSource?.id) {
      toast.error("Invalid BizDev Source");
      return;
    }

    setPromoting(true);
    setShowPromoteConfirm(false);

    try {
      const { data, status } = await promoteBizDevSourceToAccount({
        bizdev_source_id: currentSource.id
      });

      if (status === 200 && data.success) {
        const updatedSource = {
          ...currentSource,
          status: 'Promoted',
          account_id: data.account_id,
          account_name: data.account_name
        };

        setCurrentSource(updatedSource);

        if (onUpdate) onUpdate(updatedSource);
        if (onPromote) onPromote(updatedSource);
        if (onRefresh) onRefresh();

        if (data.already_promoted) {
          toast.info(data.message, {
            description: "This source is already linked to an account"
          });
        } else {
          toast.success(data.message, {
            description: `Promoted to Account: ${data.account_name}`
          });
        }
      } else {
        toast.error(data.error || 'Failed to promote BizDev Source');
      }
    } catch (error) {
      console.error('Promote error:', error);

      if (error.response?.status === 400) {
        toast.error("Cannot promote this source", {
          description: error.response?.data?.error || "This source may already be promoted"
        });
      } else {
        toast.error(error.message || 'Failed to promote BizDev Source');
      }
    } finally {
      setPromoting(false);
    }
  };

  const handleArchive = () => {
    const updatedSource = {
      ...currentSource,
      status: 'Archived',
      archived_at: new Date().toISOString()
    };

    setCurrentSource(updatedSource);
    if (onUpdate) onUpdate(updatedSource);

    toast.success("BizDev Source archived");
    onClose();
  };

  const getLicenseStatusColor = (status) => {
    const colors = {
      'Active': 'bg-green-100 text-green-800 border-green-300',
      'Suspended': 'bg-yellow-100 text-yellow-800 border-yellow-300',
      'Revoked': 'bg-red-100 text-red-800 border-red-300',
      'Expired': 'bg-orange-100 text-orange-800 border-orange-300',
      'Unknown': 'bg-gray-100 text-gray-800 border-gray-300',
      'Not Required': 'bg-blue-100 text-blue-800 border-blue-300'
    };
    return colors[status] || 'bg-gray-100 text-gray-800 border-gray-300';
  };

  const getStatusColor = (status) => {
    const colors = {
      'Active': 'bg-blue-100 text-blue-800 border-blue-300',
      'Promoted': 'bg-green-100 text-green-800 border-green-300',
      'Archived': 'bg-gray-100 text-gray-800 border-gray-300'
    };
    return colors[status] || 'bg-gray-100 text-gray-800 border-gray-300';
  };

  const isPromoted = currentSource.status === 'Promoted';
  const isArchived = currentSource.status === 'Archived';
  const canPromote = !isPromoted && !isArchived;
  const hasAccountLinked = Boolean(currentSource.account_id);


  const handleCreateOpportunity = async () => {
    if (!currentSource?.id) {
      toast.error("Invalid BizDev Source");
      return;
    }

    console.log('[BizDevSource] Creating opportunity from source:', currentSource.company_name);

    setCreatingOpportunity(true);
    try {
      const closeDate = new Date();
      closeDate.setDate(closeDate.getDate() + 30);
      const closeDateStr = closeDate.toISOString().split('T')[0];

      const oppPayload = {
        name: `${currentSource.company_name} - New Business Opportunity`,
        amount: 0,
        stage: "prospecting",
        close_date: closeDateStr,
        tenant_id: currentSource.tenant_id,
        description: `Opportunity created from BizDev Source: ${currentSource.source || 'Unknown Source'}\n` +
                     `Batch: ${currentSource.batch_id || 'N/A'}\n` +
                     `Company: ${currentSource.company_name}\n` +
                     `Contact: ${currentSource.email || currentSource.phone_number || 'No contact info'}\n` +
                     `[BizDevSource:${currentSource.id}]`,
        lead_source: "other",
        type: "new_business",
        probability: 10,
        is_test_data: false,
        // Don't set account_id yet - that happens after we win the business
      };

      console.log('[BizDevSource] Creating opportunity with payload:', oppPayload);

      const newOpp = await Opportunity.create(oppPayload);

      console.log('[BizDevSource] Opportunity created:', newOpp);

      // Create initial follow-up activity
      try {
        const activityDueDate = new Date();
        activityDueDate.setDate(activityDueDate.getDate() + 2);
        const activityDueDateStr = activityDueDate.toISOString().split('T')[0];

        await Activity.create({
          type: "call",
          subject: `Initial contact: ${currentSource.company_name}`,
          description: `Follow up on opportunity for ${currentSource.company_name}\n` +
                       `Contact: ${currentSource.email || currentSource.phone_number || 'No contact info'}\n` +
                       `Source: ${currentSource.source}`,
          status: "scheduled",
          priority: "high",
          related_to: "opportunity",
          related_id: newOpp.id,
          related_name: newOpp.name,
          due_date: activityDueDateStr,
          tenant_id: currentSource.tenant_id,
          is_test_data: false,
        });

        toast.success("Opportunity and initial activity created!");
      } catch (actError) {
        console.warn("Failed to create activity, but opportunity was created:", actError);
        toast.success("Opportunity created! (Activity creation failed)");
      }

      setTimeout(() => {
        window.location.href = createPageUrl(`Opportunities?id=${newOpp.id}`);
      }, 500);
    } catch (error) {
      console.error("Failed to create opportunity:", error);
      toast.error(`Failed to create opportunity: ${error.message || 'Unknown error'}`);
    } finally {
      setCreatingOpportunity(false);
    }
  };

  return (
    <div className="fixed inset-y-0 right-0 w-full md:w-2/3 lg:w-1/2 bg-slate-800 shadow-2xl z-50 overflow-y-auto border-l border-slate-700">
      <CardHeader className="border-b border-slate-700 sticky top-0 bg-slate-800 z-10">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-3">
            <Building2 className="w-6 h-6 text-blue-400" />
            <div>
              <CardTitle className="text-slate-100">{currentSource.company_name}</CardTitle>
              {currentSource.dba_name && (
                <p className="text-sm text-slate-400 mt-1">
                  DBA: {currentSource.dba_name}
                </p>
              )}
            </div>
          </div>
          <Button
            variant="ghost"
            size="icon"
            onClick={onClose}
            className="text-slate-400 hover:text-slate-300 hover:bg-slate-700"
          >
            <X className="w-5 h-5" />
          </Button>
        </div>

        <div className="flex flex-wrap gap-2 mt-4">
          <Badge className={`${getStatusColor(currentSource.status)} font-semibold`}>
            {currentSource.status}
          </Badge>
          {currentSource.license_status && (
            <Badge className={`${getLicenseStatusColor(currentSource.license_status)} font-semibold`}>
              License: {currentSource.license_status}
            </Badge>
          )}
          {currentSource.batch_id && (
            <Badge variant="outline" className="border-slate-600 text-slate-300">
              Batch: {currentSource.batch_id}
            </Badge>
          )}
          {currentSource.source && (
            <Badge variant="outline" className="border-slate-600 text-slate-300">
              Source: {currentSource.source}
            </Badge>
          )}
        </div>

        {/* Action Buttons */}
        <div className="flex flex-wrap gap-2 mt-4">
          {/* Create Opportunity - Always available for Active sources */}
          {!isArchived && (
            <Button
              onClick={handleCreateOpportunity}
              disabled={creatingOpportunity}
              className="bg-blue-600 hover:bg-blue-700"
            >
              {creatingOpportunity ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Creating...
                </>
              ) : (
                <>
                  <Target className="w-4 h-4 mr-2" />
                  Create Opportunity
                </>
              )}
            </Button>
          )}

          {/* Promote to Account - Only show after business is won */}
          {canPromote && (
            <Button
              variant="outline"
              onClick={() => setShowPromoteConfirm(true)}
              disabled={promoting}
              className="border-green-600 text-green-400 hover:bg-green-900/30"
              title="Promote to Account after winning business"
            >
              {promoting ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Promoting...
                </>
              ) : (
                <>
                  <TrendingUp className="w-4 h-4 mr-2" />
                  Promote to Account
                </>
              )}
            </Button>
          )}

          {/* View Linked Account - Show if already promoted */}
          {isPromoted && currentSource.account_id && (
            <Link to={createPageUrl(`Accounts?id=${currentSource.account_id}`)}>
              <Button variant="outline" className="border-slate-600 text-slate-300 hover:bg-slate-700">
                <ExternalLink className="w-4 h-4 mr-2" />
                View Linked Account
              </Button>
            </Link>
          )}

          {/* Edit and Archive */}
          {!isArchived && (
            <>
              <Button
                variant="outline"
                onClick={() => onEdit(currentSource)}
                className="border-slate-600 text-slate-300 hover:bg-slate-700"
              >
                <Pencil className="w-4 h-4 mr-2" />
                Edit
              </Button>

              <Button
                variant="outline"
                onClick={handleArchive}
                className="border-slate-600 text-slate-300 hover:bg-slate-700"
              >
                <Archive className="w-4 h-4 mr-2" />
                Archive
              </Button>
            </>
          )}
        </div>

        {/* Workflow Info - LIGHTENED BACKGROUND */}
        {/* Only show workflow hint if not archived, not promoted, and has linked leads */}
        {!isArchived && !isPromoted && currentSource.lead_ids && currentSource.lead_ids.length > 0 && ( 
          <Alert className="mt-4 bg-blue-50 border-blue-200">
            <Info className="h-4 w-4 text-blue-700" />
            <AlertTitle className="text-blue-900 font-semibold">Workflow</AlertTitle>
            <AlertDescription className="text-blue-800">
              Create an opportunity to start pursuing this prospect. Promote to Account after winning business.
            </AlertDescription>
          </Alert>
        )}
      </CardHeader>

      <div className="p-6 space-y-6">
        {/* Security Info */}
        <div className="flex items-center gap-2 text-xs text-slate-400">
          <Badge variant="outline" className="text-xs border-slate-600 text-slate-300">
            ID: {currentSource.id?.slice(0, 12)}...
          </Badge>
          {currentSource.batch_id && (
            <Badge variant="outline" className="text-xs border-slate-600 text-slate-300">
              Batch: {currentSource.batch_id}
            </Badge>
          )}
        </div>

        {/* Promote Confirmation Alert - LIGHTENED BACKGROUND */}
        {showPromoteConfirm && canPromote && ( 
          <Alert className="bg-green-50 border-green-200">
            <AlertCircle className="h-4 w-4 text-green-700" />
            <AlertTitle className="text-green-900 font-semibold">Promote to Account?</AlertTitle>
            <AlertDescription className="text-green-800">
              <p className="mb-3">
                This will create a permanent Account record for <strong>{currentSource.company_name}</strong>. 
                Use this when business is won and they become a customer.
              </p>
              <div className="mt-3 flex gap-2">
                <Button
                  size="sm"
                  onClick={handlePromote}
                  disabled={promoting}
                  className="bg-green-600 hover:bg-green-700 text-white"
                >
                  {promoting ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Promoting...
                    </>
                  ) : (
                    <>
                      <CheckCircle className="w-4 h-4 mr-2" />
                      Confirm Promotion
                    </>
                  )}
                </Button>
                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => setShowPromoteConfirm(false)}
                  className="border-green-300 text-green-700 hover:bg-green-50"
                >
                  Cancel
                </Button>
              </div>
            </AlertDescription>
          </Alert>
        )}

        {/* Already Promoted Alert - LIGHTENED BACKGROUND */}
        {isPromoted && (
