import React from "react";
import { format } from "date-fns";
import { Clock } from "lucide-react";
import { Badge } from "@/components/ui/badge";

// Matching the stat card colors - semi-transparent backgrounds
const statusColors = {
  scheduled: "bg-blue-900/20 text-blue-300 border-blue-700",
  in_progress: "bg-yellow-900/20 text-yellow-300 border-yellow-700",
  overdue: "bg-red-900/20 text-red-300 border-red-700",
  completed: "bg-emerald-900/20 text-emerald-300 border-emerald-700",
  cancelled: "bg-slate-900/20 text-slate-300 border-slate-700"
};

export default function DayView({ currentDate, activities, onActivityClick }) {
  const key = format(currentDate, "yyyy-MM-dd");
  const items = activities
    .filter(a => a._dateKey === key)
    .sort((a, b) => (a.due_time || "").localeCompare(b.due_time || ""));

  return (
    <div className="rounded-lg overflow-hidden border border-slate-800">
      <div className="bg-slate-800 text-slate-300 text-xs uppercase tracking-wide font-semibold p-2 border-b border-slate-800">
        {format(currentDate, "EEEE, MMMM d, yyyy")}
      </div>
      <div className="p-3 space-y-2 bg-slate-900 min-h-[240px]">
        {items.length === 0 && <div className="text-slate-500 text-sm italic">No activities scheduled.</div>}
        {items.map(a => (
          <button
            key={a.id}
            onClick={() => onActivityClick(a)}
            className="w-full text-left rounded-md px-3 py-2 bg-slate-800 hover:bg-slate-700 border border-slate-400"
          >
            <div className="flex items-center justify-between">
              <div className="text-slate-200 font-medium">{a.subject}</div>
              {a.due_time && (
                <div className="flex items-center gap-1 text-slate-400 text-sm">
                  <Clock className="w-3 h-3" />
                  {a.due_time}
                </div>
              )}
            </div>
            <div className="mt-1 flex gap-2">
              <Badge variant="outline" className="text-xs border-slate-600">
                {a.type}
              </Badge>
              {a.status && (
                <Badge className={`text-xs border ${statusColors[a.status] || 'border-slate-600'}`}>
                  {a.status}
                </Badge>
              )}
            </div>
          </button>
        ))}
      </div>
    </div>
  );
}

import React from "react";
import { format, startOfMonth, endOfMonth, startOfWeek, endOfWeek, addDays, isSameMonth, isToday } from "date-fns";
import { Badge } from "@/components/ui/badge";

// Matching the stat card colors - semi-transparent backgrounds
const statusColors = {
  scheduled: "bg-blue-900/20 text-blue-300 border-blue-700",
  in_progress: "bg-yellow-900/20 text-yellow-300 border-yellow-700",
  overdue: "bg-red-900/20 text-red-300 border-red-700",
  completed: "bg-emerald-900/20 text-emerald-300 border-emerald-700",
  cancelled: "bg-slate-900/20 text-slate-300 border-slate-700"
};

function DayCell({ date, inCurrentMonth, activitiesForDay, onActivityClick }) {
  return (
    <div className={`border border-slate-800 min-h-[120px] p-2 ${inCurrentMonth ? "bg-slate-900 calendar-cell-in" : "bg-slate-900/70 calendar-cell-out"}`}>
      <div className="flex items-center justify-between mb-1">
        <span className={`text-xs font-semibold ${isToday(date) ? "text-accent" : "text-slate-400"}`}>
          {format(date, "d")}
        </span>
        {isToday(date) && <span className="bg-amber-200 text-sky-600 px-1.5 py-0.5 rounded-full">Today</span>}
      </div>
      <div className="space-y-1">
        {activitiesForDay.slice(0, 4).map((a) =>
        <button
          key={a.id}
          onClick={() => onActivityClick(a)}
          className="w-full text-left rounded-md px-2 py-1 bg-slate-800 hover:bg-slate-700 border border-slate-400"
          title={a.subject}>

            <div className="text-[11px] text-slate-300 truncate">{a.subject}</div>
            {a.due_time && <div className="text-[10px] text-slate-500">{a.due_time}</div>}
            <div className="mt-1 flex flex-wrap gap-1">
              <Badge variant="outline" className="text-[10px] border-slate-600">{a.type}</Badge>
              {a.status &&
            <Badge
              className={`text-[10px] border ${statusColors[a.status] || 'border-slate-600'}`}>

                  {a.status}
                </Badge>
            }
            </div>
          </button>
        )}
        {activitiesForDay.length > 4 &&
        <div className="text-[11px] text-slate-500">+{activitiesForDay.length - 4} more</div>
        }
      </div>
    </div>);

}

export default function MonthGrid({ currentDate, activities, onActivityClick }) {
  const monthStart = startOfMonth(currentDate);
  const monthEnd = endOfMonth(monthStart);
  const gridStart = startOfWeek(monthStart, { weekStartsOn: 0 });
  const gridEnd = endOfWeek(monthEnd, { weekStartsOn: 0 });

  const rows = [];
  let day = gridStart;
  while (day <= gridEnd) {
    const days = [];
    for (let i = 0; i < 7; i++) {
      const dateStr = format(day, "yyyy-MM-dd");
      const acts = activities.filter((a) => a._dateKey === dateStr);
      days.push(
        <DayCell
          key={day.toISOString()}
          date={day}
          inCurrentMonth={isSameMonth(day, monthStart)}
          activitiesForDay={acts}
          onActivityClick={onActivityClick} />

      );
      day = addDays(day, 1);
    }
    rows.push(
      <div key={day.toISOString()} className="grid grid-cols-7 gap-px">
        {days}
      </div>
    );
  }

  const weekdays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

  return (
    <div className="rounded-lg overflow-hidden border border-slate-800">
      <div className="grid grid-cols-7">
        {weekdays.map((d) =>
        <div key={d} className="bg-slate-800 text-slate-300 text-xs uppercase tracking-wide font-semibold p-2 text-center border-b border-slate-800">
            {d}
          </div>
        )}
      </div>
      <div className="divide-y divide-slate-800">
        {rows}
      </div>
    </div>);

}

import React from "react";
import { format, startOfWeek, addDays, isToday } from "date-fns";

// Matching the stat card colors - semi-transparent backgrounds
const statusColors = {
  scheduled: "bg-blue-900/20 text-blue-300 border-blue-700",
  in_progress: "bg-yellow-900/20 text-yellow-300 border-yellow-700",
  overdue: "bg-red-900/20 text-red-300 border-red-700",
  completed: "bg-emerald-900/20 text-emerald-300 border-emerald-700",
  cancelled: "bg-slate-900/20 text-slate-300 border-slate-700"
};

export default function WeekView({ currentDate, activities, onActivityClick }) {
  const start = startOfWeek(currentDate, { weekStartsOn: 0 });
  const days = Array.from({ length: 7 }).map((_, i) => addDays(start, i));

  const getActs = (date) => {
    const key = format(date, "yyyy-MM-dd");
    return activities.filter(a => a._dateKey === key);
  };

  return (
    <div className="rounded-lg overflow-hidden border border-slate-800">
      <div className="grid grid-cols-7">
        {days.map(d => (
          <div key={d.toISOString()} className={`text-xs uppercase tracking-wide font-semibold p-2 text-center border-b border-slate-800 ${isToday(d) ? 'bg-indigo-600 text-white' : 'bg-slate-800 text-slate-300'}`}>
            <span>{format(d, "EEE dd")}</span>
          </div>
        ))}
      </div>
      <div className="grid grid-cols-7 gap-px">
        {days.map(d => (
          <div key={d.toISOString()} className={`min-h-[220px] p-2 border border-slate-800 ${isToday(d) ? 'bg-slate-800/50' : 'bg-slate-900'}`}>
            <div className="space-y-2">
              {getActs(d).map(a => (
                <button
                  key={a.id}
                  onClick={() => onActivityClick(a)}
                  className="w-full text-left rounded-md px-2 py-1 bg-slate-800 hover:bg-slate-700 border border-slate-400"
                  title={a.subject}
                >
                  <div className="text-[11px] text-slate-300 truncate">{a.subject}</div>
                  {a.due_time && <div className="text-[10px] text-slate-500">{a.due_time}</div>}
                  <div className="mt-1 flex flex-wrap gap-1">
                    {a.type && <span className="text-[10px] text-slate-400 border border-slate-600 rounded px-1">{a.type}</span>}
                    {a.status && (
                      <span className={`text-[10px] rounded px-1 border ${statusColors[a.status] || 'border-slate-600'}`}>
                        {a.status}
                      </span>
                    )}
                  </div>
                </button>
              ))}
              {getActs(d).length === 0 && <div className="text-[12px] text-slate-500 italic">No activities</div>}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

import React, { useMemo } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";
import { format, startOfMonth, endOfMonth, eachMonthOfInterval, subMonths } from "date-fns";

function ensurePlain(obj) {
  if (obj && typeof obj === "object" && typeof obj['hasOwnProperty'] !== "function") {
    return JSON.parse(JSON.stringify(obj));
  }
  return obj;
}

export default function CashFlowChart({ transactions }) {
  transactions = Array['isArray'](transactions) ? transactions.map(ensurePlain) : [];

  const chartData = useMemo(() => {
    if (!transactions || !Array['isArray'](transactions)) {
      return [];
    }

    const now = new Date();
    const last6Months = eachMonthOfInterval({
      start: subMonths(startOfMonth(now), 5),
      end: endOfMonth(now)
    });

    return last6Months.map(month => {
      const monthStart = startOfMonth(month);
      const monthEnd = endOfMonth(month);
      const monthLabel = format(month, "MMM yyyy");

      let income = 0;
      let expenses = 0;

      transactions.forEach(txn => {
        const plainTxn = ensurePlain(txn);
        const txnDate = new Date(plainTxn['transaction_date']);
        if (txnDate >= monthStart && txnDate <= monthEnd) {
          const amount = Number(plainTxn['amount']) || 0;
          if (plainTxn['transaction_type'] === "income") {
            income += amount;
          } else if (plainTxn['transaction_type'] === "expense") {
            expenses += amount;
          }
        }
      });

      return {
        month: monthLabel,
        income: Math.round(income * 100) / 100,
        expenses: Math.round(expenses * 100) / 100
      };
    });
  }, [transactions]);

  return (
    <Card className="bg-slate-800 border-slate-700 shadow-lg">
      <CardHeader>
        <CardTitle className="text-slate-100">Cash Flow Trend</CardTitle>
      </CardHeader>
      <CardContent>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
            <XAxis dataKey="month" stroke="#94a3b8" />
            <YAxis stroke="#94a3b8" />
            <Tooltip 
              contentStyle={{ 
                backgroundColor: '#1e293b', 
                border: '1px solid #475569',
                borderRadius: '8px',
                color: '#e2e8f0'
              }}
              formatter={(value) => `$${value.toLocaleString()}`}
            />
            <Legend />
            <Bar dataKey="income" fill="#22c55e" name="Income" />
            <Bar dataKey="expenses" fill="#ef4444" name="Expenses" />
          </BarChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
}

import React from "react";
