import React, { useState, useEffect } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription, DialogFooter } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Switch } from '@/components/ui/switch';
import { Label } from '@/components/ui/label';

export default function WidgetPickerModal({ open, onOpenChange, availableWidgets, currentPreferences, onSave }) {
  const [internalPreferences, setInternalPreferences] = useState({});

  useEffect(() => {
    // Sync internal state when the modal opens or preferences change
    if (open) {
      setInternalPreferences(currentPreferences || {});
    }
  }, [open, currentPreferences]);

  const handleToggle = (widgetId, isChecked) => {
    setInternalPreferences(prev => ({
      ...prev,
      [widgetId]: isChecked
    }));
  };

  const handleSave = () => {
    onSave(internalPreferences);
    onOpenChange(false);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="bg-slate-800 border-slate-700 text-slate-100">
        <DialogHeader>
          <DialogTitle>Customize Dashboard</DialogTitle>
          <DialogDescription className="text-slate-400">
            Choose which widgets to display on your dashboard.
          </DialogDescription>
        </DialogHeader>
        <div className="py-4 space-y-4 max-h-[60vh] overflow-y-auto">
          {availableWidgets.map(widget => (
            <div key={widget.id} className="flex items-center justify-between p-3 rounded-lg bg-slate-700/50">
              <Label htmlFor={`widget-toggle-${widget.id}`} className="font-medium">
                {widget.name}
              </Label>
              <Switch
                id={`widget-toggle-${widget.id}`}
                checked={internalPreferences[widget.id] ?? false}
                onCheckedChange={(isChecked) => handleToggle(widget.id, isChecked)}
                className="data-[state=checked]:bg-blue-500"
              />
            </div>
          ))}
        </div>
        <DialogFooter>
          <Button variant="ghost" onClick={() => onOpenChange(false)} className="text-slate-300 hover:bg-slate-700">Cancel</Button>
          <Button onClick={handleSave} className="bg-blue-600 hover:bg-blue-700">Save Preferences</Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Loader2, UploadCloud, AlertCircle, CheckCircle, CreditCard, X } from 'lucide-react'; // Changed Contact to CreditCard, added X
import { UploadFile, ExtractDataFromUploadedFile } from '@/api/integrations';
import { Contact as ContactEntity } from '@/api/entities';
import { useTenant } from '../shared/tenantContext';

const contactSchema = {
  type: 'object',
  properties: {
    first_name: { type: 'string' },
    last_name: { type: 'string' },
    job_title: { type: 'string' },
    company: { type: 'string' },
    phone: { type: 'string' },
    mobile: { type: 'string' },
    email: { type: 'string', format: 'email' },
    website: { type: 'string' },
    address_1: { type: 'string' },
    city: { type: 'string' },
    state: { type: 'string' },
    zip: { type: 'string' },
    country: { type: 'string' }
  },
  required: ['first_name', 'last_name']
};

export default function BusinessCardProcessor({ user, onCancel, onProcessingChange }) { // Added onCancel, onProcessingChange
  const [file, setFile] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const { selectedTenantId } = useTenant();

  // Effect to notify parent about processing status changes
  useEffect(() => {
    if (onProcessingChange) {
      onProcessingChange(isProcessing);
    }
  }, [isProcessing, onProcessingChange]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setResult(null);
    setError(null);
  };

  const handleProcess = async () => {
    if (!file) {
      setError('Please select a file first.');
      return;
    }

    if (!user) {
      setError("User not loaded. Cannot process.");
      return;
    }

    const tenantId = user.role === 'superadmin' ? selectedTenantId : user.tenant_id;
    if (!tenantId) {
      setError("Cannot determine tenant. Please ensure a tenant is selected or assigned.");
      return;
    }

    setIsProcessing(true);
    setError(null);
    setResult(null);

    try {
      const { file_url } = await UploadFile({ file });

      const extractionResult = await ExtractDataFromUploadedFile({
        file_url,
        json_schema: contactSchema,
      });

      if (extractionResult.status === 'success' && extractionResult.output) {
        const extractedData = { 
            ...extractionResult.output,
            tenant_id: tenantId,
            assigned_to: user.email,
            processed_by_ai_doc: true,
            ai_doc_source_type: 'business_card'
        };
        const newContact = await ContactEntity.create(extractedData);
        setResult({
          message: 'Successfully created contact!',
          contact: newContact
        });
      } else {
        throw new Error(extractionResult.details || 'Failed to extract data from the business card.');
      }
    } catch (e) {
      setError(e.message || 'An unknown error occurred during processing.');
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <Card className="shadow-lg border-0 bg-slate-800 border-slate-700 text-slate-300">
      <CardHeader className="border-b border-slate-700">
        <CardTitle className="flex items-center justify-between text-slate-100">
          <span className="flex items-center gap-2">
            <CreditCard className="w-6 h-6 text-blue-400" />
            Business Card Scanner
          </span>
          {onCancel && (
            <Button variant="ghost" onClick={onCancel} className="text-slate-400 hover:text-slate-200">
              <X className="w-5 h-5" />
            </Button>
          )}
        </CardTitle>
        <CardDescription className="text-slate-400">
          Upload a business card image and extract contact information automatically.
        </CardDescription>
      </CardHeader>
      <CardContent className="p-6"> {/* Added p-6 padding */}
        <div className="space-y-2">
          <label htmlFor="business-card-file" className="text-sm font-medium text-slate-200">Business Card Image</label>
          <Input
            id="business-card-file"
            type="file"
            accept="image/png, image/jpeg, image/jpg"
            onChange={handleFileChange}
            disabled={isProcessing}
            className="bg-slate-700 border-slate-600 text-slate-200 file:bg-slate-600 file:text-slate-200 file:border-slate-500"
          />
        </div>

        {error && (
          <div className="flex items-center gap-2 p-3 bg-red-900/30 border border-red-700/50 rounded-lg">
            <AlertCircle className="w-5 h-5 text-red-400" />
            <p className="text-sm text-red-300">{error}</p>
          </div>
        )}

        {result && (
          <div className="flex items-start gap-3 p-3 bg-green-900/30 border border-green-700/50 rounded-lg">
            <CheckCircle className="w-5 h-5 text-green-400 mt-1" />
            <div>
              <p className="font-semibold text-green-300">{result.message}</p>
              <div className="text-sm text-slate-300 mt-1">
                <p><strong>Name:</strong> {result.contact.first_name} {result.contact.last_name}</p>
                <p><strong>Company:</strong> {result.contact.company}</p>
                <p><strong>Email:</strong> {result.contact.email}</p>
                <p><strong>Phone:</strong> {result.contact.phone}</p>
              </div>
            </div>
          </div>
        )}
      </CardContent>
      <CardFooter className="flex justify-end gap-2"> {/* Added flex utilities for button alignment */}
        {onCancel && (
          <Button variant="ghost" onClick={onCancel} disabled={isProcessing} className="text-slate-400 hover:text-slate-200">
            Cancel
          </Button>
        )}
        <Button onClick={handleProcess} disabled={isProcessing || !file} className="bg-blue-600 hover:bg-blue-700">
          {isProcessing ? (
            <>
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              Processing...
            </>
          ) : (
            <>
              <UploadCloud className="mr-2 h-4 w-4" />
              Process Card
            </>
          )}
        </Button>
      </CardFooter>
    </Card>
  );
}

import React, { useState, useCallback } from 'react';
import { UploadFile, ExtractDataFromUploadedFile } from '@/api/integrations';
import { CashFlow } from '@/api/entities';
import { toast } from 'sonner';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Loader2, Upload, FileUp, CheckCircle, AlertTriangle, Save, Trash2, ArrowRightLeft } from 'lucide-react';
import { useTenant } from '../shared/tenantContext';

const cashFlowExtractionSchema = {
  type: "object",
  properties: {
    transactions: {
      type: "array",
      items: {
        type: "object",
        properties: {
          transaction_date: { type: "string", format: "date", description: "The date of the transaction in YYYY-MM-DD format." },
          description: { type: "string", description: "A detailed description of the transaction." },
          amount: { type: "number", description: "The transaction amount. Always a positive number." },
          transaction_type: { type: "string", enum: ["income", "expense"], description: "The type of transaction (income or expense)." }
        },
        required: ["transaction_date", "description", "amount", "transaction_type"]
      }
    }
  },
  required: ["transactions"]
};

const cashFlowCategories = [
    "sales_revenue", "recurring_revenue", "refund", "operating_expense", "marketing", 
    "equipment", "supplies", "utilities", "rent", "payroll", 
    "professional_services", "travel", "meals", "other"
];

export default function CashFlowExtractor({ onCancel, onProcessingChange, user }) {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('idle'); // idle, uploading, extracting, review, saving, success
  const [extractedData, setExtractedData] = useState([]);
  const [error, setError] = useState(null);
  const { selectedTenantId } = useTenant();

  // Handle file processing
  const handleProcessFile = useCallback(async (fileToProcess) => {
    if (!user) {
      setError("User information is not available.");
      setStatus('error');
      return;
    }

    onProcessingChange(true);
    setStatus('uploading');
    setError(null);
    try {
      const { file_url } = await UploadFile({ file: fileToProcess });

      setStatus('extracting');
      const extractionResult = await ExtractDataFromUploadedFile({ file_url, json_schema: cashFlowExtractionSchema });

      if (extractionResult.status === 'success' && extractionResult.output?.transactions) {
        setExtractedData(extractionResult.output.transactions.map(t => ({...t, category: 'other'})));
        setStatus('review');
      } else {
        throw new Error(extractionResult.details || "Failed to extract structured data from the document.");
      }
    } catch (err) {
      setError(err.message);
      setStatus('error');
    } finally {
      onProcessingChange(false);
    }
  }, [user, onProcessingChange]);

  // Handle file selection
  const handleFileChange = useCallback((event) => {
    const selectedFile = event.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      handleProcessFile(selectedFile);
    }
  }, [handleProcessFile]);

  const handleSaveTransactions = async () => {
    if (!user) {
      toast.error("Cannot save transactions. User not found.");
      return;
    }
    const tenantId = selectedTenantId || user.tenant_id;
    if (!tenantId) {
      toast.error("Cannot save transactions. Tenant not identified.");
      return;
    }

    onProcessingChange(true);
    setStatus('saving');
    try {
      const recordsToCreate = extractedData.map(item => ({
        ...item,
        tenant_id: tenantId,
        entry_method: 'document_extracted',
        is_editable: true,
      }));

      await CashFlow.bulkCreate(recordsToCreate);
      setStatus('success');
      toast.success(`${recordsToCreate.length} transactions saved successfully!`);
    } catch (err) {
      setError(err.message);
      setStatus('error');
    } finally {
      onProcessingChange(false);
    }
  };

  const handleRowChange = (index, field, value) => {
    const updatedData = [...extractedData];
    updatedData[index][field] = value;
    setExtractedData(updatedData);
  };

  const handleRemoveRow = (index) => {
    setExtractedData(extractedData.filter((_, i) => i !== index));
  };

  if (status === 'success') {
    return (
      <Card className="bg-slate-800 border-slate-700">
        <CardContent className="p-8 text-center space-y-4">
          <CheckCircle className="w-16 h-16 text-green-500 mx-auto" />
          <h3 className="text-2xl font-bold text-slate-100">Success!</h3>
          <p className="text-slate-300">{extractedData.length} transactions have been added to your Cash Flow module.</p>
          <Button onClick={onCancel} className="bg-blue-600 hover:bg-blue-700">Process Another Document</Button>
        </CardContent>
      </Card>
    );
  }

  if (status === 'review') {
    return (
      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="text-slate-100">Review Extracted Transactions</CardTitle>
          <CardDescription className="text-slate-400">Verify the data extracted from your document before saving it to your Cash Flow.</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="overflow-x-auto">
            <Table>
              <TableHeader>
                <TableRow className="border-slate-700">
                  <TableHead className="text-slate-300">Date</TableHead>
                  <TableHead className="text-slate-300">Description</TableHead>
                  <TableHead className="text-slate-300">Amount</TableHead>
                  <TableHead className="text-slate-300">Type</TableHead>
                  <TableHead className="text-slate-300">Category</TableHead>
                  <TableHead></TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {extractedData.map((item, index) => (
                  <TableRow key={index} className="border-slate-700">
                    <TableCell><Input type="date" value={item.transaction_date} onChange={(e) => handleRowChange(index, 'transaction_date', e.target.value)} className="bg-slate-700 border-slate-600 text-slate-200" /></TableCell>
                    <TableCell><Input value={item.description} onChange={(e) => handleRowChange(index, 'description', e.target.value)} className="bg-slate-700 border-slate-600 text-slate-200" /></TableCell>
                    <TableCell><Input type="number" value={item.amount} onChange={(e) => handleRowChange(index, 'amount', parseFloat(e.target.value))} className="bg-slate-700 border-slate-600 text-slate-200" /></TableCell>
                    <TableCell>
                      <Select value={item.transaction_type} onValueChange={(val) => handleRowChange(index, 'transaction_type', val)}>
                        <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200"><SelectValue /></SelectTrigger>
                        <SelectContent className="bg-slate-800 border-slate-700">
                          <SelectItem value="income" className="text-slate-200">Income</SelectItem>
                          <SelectItem value="expense" className="text-slate-200">Expense</SelectItem>
                        </SelectContent>
                      </Select>
                    </TableCell>
                    <TableCell>
                      <Select value={item.category} onValueChange={(val) => handleRowChange(index, 'category', val)}>
                        <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200"><SelectValue /></SelectTrigger>
                        <SelectContent className="bg-slate-800 border-slate-700">
                          {cashFlowCategories.map(cat => <SelectItem key={cat} value={cat} className="text-slate-200 capitalize">{cat.replace(/_/g, ' ')}</SelectItem>)}
                        </SelectContent>
                      </Select>
                    </TableCell>
                    <TableCell><Button variant="ghost" size="icon" onClick={() => handleRemoveRow(index)}><Trash2 className="w-4 h-4 text-red-500" /></Button></TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
          <div className="flex justify-end mt-6">
            <Button onClick={handleSaveTransactions} disabled={status === 'saving'} className="bg-green-600 hover:bg-green-700">
              {status === 'saving' ? <Loader2 className="w-4 h-4 mr-2 animate-spin" /> : <Save className="w-4 h-4 mr-2" />}
              Save Transactions
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-slate-800 border-slate-700">
      <CardHeader>
        <CardTitle className="text-slate-100">Upload Financial Document</CardTitle>
        <CardDescription className="text-slate-400">Upload a spreadsheet (XLSX, CSV), PDF, or Word document containing income and expense transactions.</CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        {status === 'idle' && (
          <div className="p-10 border-2 border-dashed rounded-lg text-center transition-colors border-slate-600 hover:border-slate-500 hover:bg-slate-700/50">
            <input
              type="file"
              accept=".xlsx,.csv,.pdf,.docx,.xls"
              onChange={handleFileChange}
              className="hidden"
              id="financial-file-input"
            />
            <label htmlFor="financial-file-input" className="cursor-pointer">
              <div className="flex flex-col items-center justify-center gap-2 text-slate-400">
                <FileUp className="w-8 h-8" />
                <p>Click to select your financial document.</p>
                <p className="text-xs">(XLSX, CSV, PDF, DOCX supported)</p>
              </div>
            </label>
          </div>
        )}

        {['uploading', 'extracting'].includes(status) && (
          <div className="text-center p-10">
            <Loader2 className="w-12 h-12 text-blue-500 animate-spin mx-auto" />
            <p className="mt-4 text-slate-300 text-lg font-semibold capitalize">{status}...</p>
            <p className="text-slate-400">Please wait while we process your document.</p>
          </div>
        )}

        {status === 'error' && (
          <div className="p-4 bg-red-900/20 border border-red-700/50 rounded-lg text-center">
            <AlertTriangle className="w-8 h-8 text-red-400 mx-auto mb-2" />
            <p className="text-red-300 font-semibold">An Error Occurred</p>
            <p className="text-red-400 text-sm mb-4">{error}</p>
            <Button onClick={onCancel}>Try Again</Button>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Loader2, UploadCloud, AlertCircle, FileJson, FileText, X } from 'lucide-react'; // Added X icon
import { UploadFile, ExtractDataFromUploadedFile } from '@/api/integrations';

const entitySchemas = {
  Contact: {
    type: 'object',
    properties: {
      first_name: { type: 'string' },
      last_name: { type: 'string' },
      email: { type: 'string', format: 'email' },
      phone: { type: 'string' },
      company: { type: 'string' }
    }
  },
  Lead: {
    type: 'object',
    properties: {
      first_name: { type: 'string' },
      last_name: { type: 'string' },
      email: { type: 'string', format: 'email' },
      phone: { type: 'string' },
      company: { type: 'string' },
      source: { type: 'string' }
    }
  },
};

export default function DocumentExtractor({ onCancel, onProcessingChange }) {
  const [file, setFile] = useState(null);
  const [entityType, setEntityType] = useState('Contact');
  const [isProcessing, setIsProcessing] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (onProcessingChange) {
      onProcessingChange(isProcessing);
    }
  }, [isProcessing, onProcessingChange]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setResult(null);
    setError(null);
  };

  const handleProcess = async () => {
    if (!file) {
      setError('Please select a file first.');
      return;
    }
    setIsProcessing(true);
    setError(null);
    setResult(null);

    try {
      const { file_url } = await UploadFile({ file });

      const extractionResult = await ExtractDataFromUploadedFile({
        file_url,
        json_schema: entitySchemas[entityType],
      });

      if (extractionResult.status === 'success') {
        setResult(extractionResult.output);
      } else {
        throw new Error(extractionResult.details || 'Failed to extract data.');
      }
    } catch (e) {
      setError(e.message || 'An unknown error occurred.');
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <Card className="bg-slate-800 border-slate-700 text-slate-300 shadow-lg border-0">
      <CardHeader className="border-b border-slate-700">
        <CardTitle className="flex items-center justify-between text-slate-100">
          <span className="flex items-center gap-2">
            <FileText className="w-6 h-6 text-emerald-400" />
            Document Extractor
          </span>
          <Button variant="ghost" onClick={onCancel} className="text-slate-400 hover:text-slate-200">
            <X className="w-5 h-5" />
          </Button>
        </CardTitle>
        <CardDescription className="text-slate-400">
          Upload documents like receipts, invoices, or contracts to extract structured data.
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4 p-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
                <label htmlFor="document-file" className="text-sm font-medium text-slate-200">Document File</label>
                <Input
                id="document-file"
                type="file"
                accept=".pdf,.txt,.docx,.md"
                onChange={handleFileChange}
                disabled={isProcessing}
                className="bg-slate-700 border-slate-600 text-slate-200 file:bg-slate-600 file:text-slate-200 file:border-slate-500"
                />
            </div>
            <div className="space-y-2">
                <label htmlFor="entity-type" className="text-sm font-medium text-slate-200">Target Entity</label>
                <Select value={entityType} onValueChange={setEntityType} disabled={isProcessing}>
                <SelectTrigger id="entity-type" className="bg-slate-700 border-slate-600 text-slate-200">
                    <SelectValue />
                </SelectTrigger>
                <SelectContent className="bg-slate-800 border-slate-700 text-slate-200">
                    <SelectItem value="Contact" className="focus:bg-slate-700">Contact</SelectItem>
                    <SelectItem value="Lead" className="focus:bg-slate-700">Lead</SelectItem>
                </SelectContent>
                </Select>
            </div>
        </div>

        {error && (
          <div className="flex items-center gap-2 p-3 bg-red-900/30 border border-red-700/50 rounded-lg">
            <AlertCircle className="w-5 h-5 text-red-400" />
            <p className="text-sm text-red-300">{error}</p>
          </div>
        )}

        {result && (
          <div className="space-y-2">
            <h3 className="text-md font-semibold flex items-center gap-2 text-slate-100">
              <FileJson className="w-5 h-5 text-green-400" />
              Extracted Data
            </h3>
            <Textarea
              readOnly
              value={JSON.stringify(result, null, 2)}
              className="font-mono text-xs h-48 bg-slate-900 border-slate-700 text-slate-300"
            />
          </div>
        )}
      </CardContent>
      <CardFooter className="flex justify-end gap-2"> {/* Changed to flex and gap for buttons */}
        <Button 
          variant="outline" 
          onClick={onCancel} 
          disabled={isProcessing} 
          className="bg-slate-700 text-slate-200 border-slate-600 hover:bg-slate-600"
        >
          Cancel
        </Button>
        <Button onClick={handleProcess} disabled={isProcessing || !file} className="bg-purple-600 hover:bg-purple-700">
          {isProcessing ? (
            <>
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              Extracting...
            </>
          ) : (
            <>
              <UploadCloud className="mr-2 h-4 w-4" />
              Extract Data
            </>
          )}
        </Button>
      </CardFooter>
    </Card>
  );
}

import React, { useState } from "react";
import { Sheet, SheetContent, SheetHeader, SheetTitle, SheetDescription } from "@/components/ui/sheet";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import {
  User, Mail, Phone, Building2, Briefcase, Calendar,
  MapPin, Shield, Clock, Edit, Trash2, Users, Link2, Loader2, RefreshCw
} from "lucide-react";
import PhoneDisplay from "../shared/PhoneDisplay";
import EmployeePermissionsDialog from "./EmployeePermissionsDialog";
import { User as UserEntity } from "@/api/entities";
import { linkEmployeeToCRMUser } from "@/api/functions";
import { syncEmployeeUserPermissions } from "@/api/functions";
import { toast } from "sonner";

export default function EmployeeDetailPanel({
  employee,
  open,
  onOpenChange,
  onEdit,
  onDelete,
  user,
  onManagePermissions
}) {
  // Ensure hooks are always called (moved above any early return)
  const [__showAccessDialog, __setShowAccessDialog] = React.useState(false);
  const [__currentUser, __setCurrentUser] = React.useState(null);
  const [linking, setLinking] = React.useState(false);
  const [isSyncing, setIsSyncing] = useState(false); // New state as per outline

  React.useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const me = await UserEntity.me();
        if (mounted) __setCurrentUser(me);
      } catch (error) {
        console.error("Failed to fetch current user for EmployeeDetailPanel:", error);
        if (mounted) __setCurrentUser(null);
      }
    })();
    return () => { mounted = false; };
  }, []);

  const __canManageEmployeeAccess = React.useMemo(() => {
    if (!__currentUser) return false;
    if (__currentUser.role === "admin" || __currentUser.role === "superadmin") return true;
    return __currentUser.tier === "Tier3" || __currentUser.tier === "Tier4";
  }, [__currentUser]);

  const handleLinkCRMUser = async () => {
    if (!employee?.email) {
      toast.error("Employee must have an email address to link to a CRM user");
      return;
    }

    setLinking(true);
    try {
      const response = await linkEmployeeToCRMUser({
        employee_id: employee.id,
        employee_email: employee.email
      });

      if (response.status === 200 && response.data?.success) {
        toast.success(response.data.message || `Successfully linked to CRM user: ${employee.email}`);

        // Refresh the page to show updated data
        setTimeout(() => {
          window.location.reload();
        }, 1000);
      } else {
        const errorMsg = response.data?.error || 'Failed to link to CRM user';
        toast.error(errorMsg);
      }

    } catch (error) {
      let msg = "Failed to link to CRM user";
      try {
        if (error?.message) msg = error.message;
      } catch (e) {}
      console.error("Link error:", msg);
      toast.error(msg);
    } finally {
      setLinking(false);
    }
  };

  // New function as per outline
  const handleSyncPermissions = async () => {
    if (!employee?.user_email) {
      toast.error("This employee is not linked to a CRM user");
      return;
    }

    setIsSyncing(true);
    try {
      const response = await syncEmployeeUserPermissions({ employee_id: employee.id });
      if (response.status === 200 && response.data?.success) {
        toast.success("Permissions synced successfully");
        // Trigger refresh to show updated data, as employee prop might not reflect changes immediately
        window.location.reload(); 
      } else {
        throw new Error(response.data?.error || "Failed to sync permissions");
      }
    } catch (error) {
      let msg = "Failed to sync permissions";
      try {
        if (error?.message) msg = error.message;
      } catch (e) {}
      console.error("Sync error:", msg);
      toast.error(msg);
    } finally {
      setIsSyncing(false);
    }
  };

  const statusColors = {
    active: "bg-green-100 text-green-700 border-green-200",
    inactive: "bg-gray-100 text-gray-700 border-gray-200",
    terminated: "bg-red-100 text-red-700 border-red-200",
    on_leave: "bg-yellow-100 text-yellow-700 border-yellow-200"
  };

  const employmentTypeColors = {
    full_time: "bg-blue-100 text-blue-700 border-blue-200",
    part_time: "bg-purple-100 text-purple-700 border-purple-200",
    contractor: "bg-orange-100 text-orange-700 border-orange-200",
    seasonal: "bg-teal-100 text-teal-700 border-teal-200"
  };

  const formatDate = (dateString) => {
    if (!dateString) return "N/A";
    return new Date(dateString).toLocaleDateString();
  };

  const formatDepartment = (dept) => {
    if (!dept) return "N/A";
    return dept.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  const canManage = React.useMemo(() => {
    if (!user) return false;
    if (user.role === "superadmin" || user.role === "admin") return !!employee?.user_email;
    return (user.tier === "Tier3" || user.tier === "Tier4") && !!employee?.user_email;
  }, [user, employee]);

  // After hooks are set up, we can safely early-return
  if (!employee) return null;

  return (
    <Sheet open={open} onOpenChange={onOpenChange}>
      <SheetContent className="w-full sm:max-w-lg overflow-y-auto bg-slate-900 border-slate-700 text-slate-100">
        <SheetHeader className="space-y-3">
          <div className="flex items-start justify-between">
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 bg-slate-700 rounded-full flex items-center justify-center">
                <User className="w-6 h-6 text-slate-300" />
              </div>
              <div>
                <SheetTitle className="text-xl font-bold text-slate-100">
                  {employee.first_name} {employee.last_name}
                </SheetTitle>
                <SheetDescription className="text-slate-400">
                  {employee.job_title}  {formatDepartment(employee.department)}
                </SheetDescription>
              </div>
            </div>
            <div className="flex gap-2">
              {canManage && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => onManagePermissions && onManagePermissions(employee)}
                  className="bg-slate-800 border-slate-600 text-indigo-300 hover:bg-slate-700"
                  title="Manage Permissions"
                >
                  <Shield className="w-4 h-4" />
                </Button>
              )}
              <Button
                variant="outline"
                size="sm"
                onClick={() => onEdit(employee)}
                className="bg-slate-800 border-slate-600 text-slate-200 hover:bg-slate-700"
              >
                <Edit className="w-4 h-4" />
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => onDelete(employee.id)}
                className="bg-slate-800 border-slate-600 text-red-400 hover:bg-slate-700"
              >
                <Trash2 className="w-4 h-4" />
              </Button>
            </div>
          </div>
          <div className="flex gap-2 flex-wrap">
            <Badge className={statusColors[employee.employment_status] || statusColors.active}>
              {employee.employment_status?.replace(/_/g, ' ')}
            </Badge>
            <Badge className={employmentTypeColors[employee.employment_type] || employmentTypeColors.full_time}>
              {employee.employment_type?.replace(/_/g, ' ')}
            </Badge>
          </div>
        </SheetHeader>

        {/* Link CRM User button - show if has email but no user_email */}
        {employee.email && !employee.user_email && __canManageEmployeeAccess && (
          <div className="mt-3 mb-6">
            <Button
              variant="outline"
              className="w-full bg-blue-900/30 border-blue-700 text-blue-300 hover:bg-blue-900/50 hover:text-blue-200"
              onClick={handleLinkCRMUser}
              disabled={linking}
            >
              {linking ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Linking...
                </>
              ) : (
                <>
                  <Link2 className="w-4 h-4 mr-2" />
                  Link to CRM User
                </>
              )}
            </Button>
            <p className="text-xs text-slate-400 mt-2">
              This will link this employee to an existing CRM user account with email: {employee.email}
            </p>
          </div>
        )}

        {/* Manage Access and Sync Permissions buttons (visible to Admin/Superadmin or Tier3/4) */}
        {__canManageEmployeeAccess && (
          <div className="mt-3 mb-6 flex flex-wrap gap-2"> {/* Added flex gap for buttons */}
            <Button
              variant="outline"
              className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600 hover:text-white"
              onClick={() => __setShowAccessDialog(true)}
            >
              <Shield className="w-4 h-4 mr-2" />
              Manage Access
            </Button>

            {/* Sync Permissions button - show if employee has CRM access */}
            {employee?.has_crm_access && employee?.user_email && (
              <Button
                variant="outline"
                className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600 hover:text-white"
                onClick={handleSyncPermissions}
                disabled={isSyncing}
              >
                {isSyncing ? (
                  <>
                    <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                    Syncing...
                  </>
                ) : (
                  <>
                    <RefreshCw className="w-4 h-4 mr-2" />
                    Sync Permissions
                  </>
                )}
              </Button>
            )}
          </div>
        )}

        <Separator className="my-6 bg-slate-700" />

        <div className="space-y-6">
          {/* Contact Information */}
          <Card className="bg-slate-800 border-slate-700">
            <CardHeader className="pb-3">
              <CardTitle className="text-base font-semibold text-slate-200 flex items-center gap-2">
                <Mail className="w-4 h-4 text-blue-400" />
                Contact Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              <div className="grid grid-cols-3 gap-2 items-center">
                <span className="text-sm text-slate-400">Email:</span>
                <span className="col-span-2 text-sm text-slate-200">{employee.email || "N/A"}</span>
              </div>
              <div className="grid grid-cols-3 gap-2 items-center">
                <span className="text-sm text-slate-400">Phone:</span>
                <div className="col-span-2">
                  {employee.phone ? (
                    <PhoneDisplay user={user} phone={employee.phone} contactName={`${employee.first_name} ${employee.last_name}`} />
                  ) : (
                    <span className="text-sm text-slate-500 italic">No phone</span>
                  )}
                </div>
              </div>
              <div className="grid grid-cols-3 gap-2 items-center">
                <span className="text-sm text-slate-400">Mobile:</span>
                <div className="col-span-2">
                  {employee.mobile ? (
                    <PhoneDisplay user={user} phone={employee.mobile} contactName={`${employee.first_name} ${employee.last_name}`} />
                  ) : (
                    <span className="text-sm text-slate-500 italic">No mobile</span>
                  )}
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Employment Details */}
          <Card className="bg-slate-800 border-slate-700">
            <CardHeader className="pb-3">
              <CardTitle className="text-base font-semibold text-slate-200 flex items-center gap-2">
                <Briefcase className="w-4 h-4 text-green-400" />
                Employment Details
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              <div className="grid grid-cols-3 gap-2 items-center">
                <span className="text-sm text-slate-400">Employee ID:</span>
                <span className="col-span-2 text-sm text-slate-200">{employee.employee_number || "N/A"}</span>
              </div>
              <div className="grid grid-cols-3 gap-2 items-center">
                <span className="text-sm text-slate-400">Hire Date:</span>
                <span className="col-span-2 text-sm text-slate-200">{formatDate(employee.hire_date)}</span>
              </div>
              <div className="grid grid-cols-3 gap-2 items-center">
                <span className="text-sm text-slate-400">Hourly Rate:</span>
                <span className="col-span-2 text-sm text-slate-200">{employee.hourly_rate ? `$${employee.hourly_rate}/hr` : "N/A"}</span>
              </div>
            </CardContent>
          </Card>

          {/* CRM Access & Role Level */}
          {employee.has_crm_access && employee.user_email && (
            <Card className="bg-slate-800 border-slate-700">
              <CardHeader className="pb-3">
                <CardTitle className="text-base font-semibold text-slate-200 flex items-center gap-2">
                  <Shield className="w-4 h-4 text-indigo-400" />
                  CRM Access & Permissions
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="grid grid-cols-3 gap-2 items-center">
                  <span className="text-sm text-slate-400">CRM User:</span>
                  <span className="col-span-2 text-sm text-slate-200">{employee.user_email}</span>
                </div>
                <div className="grid grid-cols-3 gap-2 items-center">
                  <span className="text-sm text-slate-400">Role Level:</span>
                  <span className="col-span-2">
                    {employee.crm_user_employee_role === 'manager' ? (
                      <Badge className="bg-purple-900/30 text-purple-300 border-purple-700">
                        Manager
                      </Badge>
                    ) : employee.crm_user_employee_role === 'employee' ? (
                      <Badge className="bg-blue-900/30 text-blue-300 border-blue-700">
                        Employee
                      </Badge>
                    ) : (
                      <span className="text-sm text-slate-500 italic">Not Set</span>
                    )}
                  </span>
                </div>
                <div className="grid grid-cols-3 gap-2 items-center">
                  <span className="text-sm text-slate-400">Access Level:</span>
                  <span className="col-span-2">
                    {employee.crm_user_access_level === 'read_write' ? (
                      <Badge className="bg-green-900/30 text-green-300 border-green-700">
                        Read/Write
                      </Badge>
                    ) : employee.crm_user_access_level === 'read' ? (
                      <Badge className="bg-yellow-900/30 text-yellow-300 border-yellow-700">
                        Read Only
                      </Badge>
                    ) : (
                      <span className="text-sm text-slate-500 italic">Not Set</span>
                    )}
                  </span>
                </div>
                <div className="grid grid-cols-3 gap-2 items-center">
                  <span className="text-sm text-slate-400">Description:</span>
                  <span className="col-span-2 text-xs text-slate-400">
                    {employee.crm_user_employee_role === 'manager'
                      ? 'Can view and manage all team records'
                      : 'Can only view and manage their own records'}
                  </span>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Address Information */}
          {(employee.address_1 || employee.city || employee.state) && (
            <Card className="bg-slate-800 border-slate-700">
              <CardHeader className="pb-3">
                <CardTitle className="text-base font-semibold text-slate-200 flex items-center gap-2">
                  <MapPin className="w-4 h-4 text-purple-400" />
                  Address
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-2">
                <div className="text-sm text-slate-200">
                  {employee.address_1 && <div>{employee.address_1}</div>}
                  {employee.address_2 && <div>{employee.address_2}</div>}
                  {(employee.city || employee.state || employee.zip) && (
                    <div>
                      {employee.city && employee.city}
                      {employee.city && employee.state && ", "}
                      {employee.state && employee.state}
                      {employee.zip && ` ${employee.zip}`}
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          )}

          {/* Emergency Contact */}
          {(employee.emergency_contact_name || employee.emergency_contact_phone) && (
            <Card className="bg-slate-800 border-slate-700">
              <CardHeader className="pb-3">
                <CardTitle className="text-base font-semibold text-slate-200 flex items-center gap-2">
                  <Shield className="w-4 h-4 text-red-400" />
                  Emergency Contact
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="grid grid-cols-3 gap-2 items-center">
                  <span className="text-sm text-slate-400">Name:</span>
                  <span className="col-span-2 text-sm text-slate-200">{employee.emergency_contact_name || "N/A"}</span>
                </div>
                <div className="grid grid-cols-3 gap-2 items-center">
                  <span className="text-sm text-slate-400">Phone:</span>
                  <div className="col-span-2">
                    {employee.emergency_contact_phone ? (
                      <PhoneDisplay user={user} phone={employee.emergency_contact_phone} contactName={employee.emergency_contact_name} />
                    ) : (
                      <span className="text-sm text-slate-500 italic">No phone</span>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Skills & Notes */}
          {(employee.skills?.length > 0 || employee.notes) && (
            <Card className="bg-slate-800 border-slate-700">
              <CardHeader className="pb-3">
                <CardTitle className="text-base font-semibold text-slate-200 flex items-center gap-2">
                  <Users className="w-4 h-4 text-orange-400" />
                  Additional Information
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                {employee.skills?.length > 0 && (
                  <div>
                    <span className="text-sm text-slate-400 block mb-2">Skills:</span>
                    <div className="flex gap-2 flex-wrap">
                      {employee.skills.map((skill, index) => (
                        <Badge key={index} variant="outline" className="bg-slate-700 border-slate-600 text-slate-300 text-xs">
                          {skill}
                        </Badge>
                      ))}
                    </div>
                  </div>
                )}
                {employee.notes && (
                  <div>
                    <span className="text-sm text-slate-400 block mb-2">Notes:</span>
                    <p className="text-sm text-slate-200 bg-slate-700/50 p-3 rounded border border-slate-600">
                      {employee.notes}
                    </p>
                  </div>
                )}
              </CardContent>
            </Card>
          )}

          {/* Metadata */}
          <Card className="bg-slate-800 border-slate-700">
            <CardHeader className="pb-3">
              <CardTitle className="text-base font-semibold text-slate-200 flex items-center gap-2">
                <Clock className="w-4 h-4 text-slate-400" />
                Record Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              <div className="grid grid-cols-3 gap-2 items-center">
                <span className="text-sm text-slate-400">Created:</span>
                <span className="col-span-2 text-sm text-slate-200">{formatDate(employee.created_date)}</span>
              </div>
              <div className="grid grid-cols-3 gap-2 items-center">
                <span className="text-sm text-slate-400">Updated:</span>
                <span className="col-span-2 text-sm text-slate-200">{formatDate(employee.updated_date)}</span>
              </div>
              <div className="grid grid-cols-3 gap-2 items-center">
                <span className="text-sm text-slate-400">Created By:</span>
                <span className="col-span-2 text-sm text-slate-200">{employee.created_by || "System"}</span>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Access dialog mounted with current employee record */}
        <EmployeePermissionsDialog
          open={__showAccessDialog}
          onOpenChange={__setShowAccessDialog}
          employee={employee}
          editorUser={__currentUser}
          onSuccess={() => {
            __setShowAccessDialog(false);
          }}
        />
      </SheetContent>
    </Sheet>
  );
}

import React, { useState } from "react";
import { User } from "@/api/entities";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
// Removed: Switch component is replaced by native checkbox
import { Card, CardContent } from "@/components/ui/card";
// Removed Alert and AlertDescription as messages are now handled by toast
// import { Alert, AlertDescription } from "@/components/ui/alert";
import { CheckCircle2, Send } from "lucide-react";
import { useTenant } from "../shared/tenantContext";
import { requestUserInvite } from "@/api/functions";
import { inviteUser } from "@/api/functions";
import { saveEmployee } from "@/api/functions";
import { toast } from "sonner";
// Removed: ResendInviteButton is removed from this form's outline
