                </DropdownMenuItem>
                {onConvert && lead.status !== 'converted' && (
                  <DropdownMenuItem onClick={(e) => { e.stopPropagation(); onConvert(lead); }} className="hover:bg-slate-700">
                    <UserCheck className="w-4 h-4 mr-2" />
