                <DropdownMenuItem onClick={(e) => { e.stopPropagation(); onDelete(lead.id); }} className="text-red-400 hover:bg-slate-700 focus:text-red-400">
                  <Trash2 className="w-4 h-4 mr-2" />
                  Delete
