import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { AlertTriangle, CheckCircle, XCircle, Info, Loader2, RefreshCw } from 'lucide-react';
import { analyzeDataQuality } from '@/api/functions';

export default function DataQualityReport({ tenantFilter }) {
  const [loading, setLoading] = useState(true);
  const [qualityData, setQualityData] = useState(null);
  const [error, setError] = useState(null);

  const loadQualityData = async () => {
    setLoading(true);
    setError(null);
    try {
      console.log('DataQualityReport: Analyzing with filter:', tenantFilter);

      // Extract tenant_id from tenantFilter for the backend call
      const tenant_id = tenantFilter?.tenant_id || null;

      const result = await analyzeDataQuality({ tenant_id });
      console.log('DataQualityReport: Raw result:', result);

      // Check if we got a valid response
      if (!result || !result.data) {
        throw new Error('No data received from analysis');
      }

      const data = result.data;
      console.log('DataQualityReport: Analysis data:', data);

      // Validate the data structure
      if (!data.report) {
        throw new Error('Invalid response format: missing report data');
      }

      // Calculate overall score from entity scores
      const report = data.report;
      const entityScores = {
        Contacts: 100,
        Accounts: 100,
        Leads: 100,
        Opportunities: 100
      };

      // Calculate scores based on issues
      if (report.contacts) {
        const contactIssues = report.contacts.issues_percentage || 0;
        entityScores.Contacts = Math.round(100 - contactIssues);
      }
      if (report.accounts) {
        const accountIssues = report.accounts.issues_percentage || 0;
        entityScores.Accounts = Math.round(100 - accountIssues);
      }
      if (report.leads) {
        const leadIssues = report.leads.issues_percentage || 0;
        entityScores.Leads = Math.round(100 - leadIssues);
      }

      // Calculate overall score
      const scores = Object.values(entityScores);
      const overallScore = Math.round(scores.reduce((a, b) => a + b, 0) / scores.length);

      // Format issues for display
      const issues = {
        Contacts: [],
        Accounts: [],
        Leads: [],
        Opportunities: []
      };

      // Process contacts issues
      if (report.contacts?.issues) {
        const contactIssues = report.contacts.issues;
        if (contactIssues.missing_first_name > 0) {
          issues.Contacts.push({
            field: 'First Name',
            issue: 'Missing first name',
            count: contactIssues.missing_first_name
          });
        }
        if (contactIssues.missing_last_name > 0) {
          issues.Contacts.push({
            field: 'Last Name',
            issue: 'Missing last name',
            count: contactIssues.missing_last_name
          });
        }
        if (contactIssues.invalid_email > 0) {
          issues.Contacts.push({
            field: 'Email',
            issue: 'Invalid email format',
            count: contactIssues.invalid_email
          });
        }
        if (contactIssues.missing_contact_info > 0) {
          issues.Contacts.push({
            field: 'Contact Info',
            issue: 'Missing both phone and email',
            count: contactIssues.missing_contact_info
          });
        }
        if (contactIssues.invalid_name_characters > 0) {
          issues.Contacts.push({
            field: 'Name',
            issue: 'Invalid characters in name',
            count: contactIssues.invalid_name_characters
          });
        }
      }

      // Process accounts issues
      if (report.accounts?.issues) {
        const accountIssues = report.accounts.issues;
        if (accountIssues.invalid_email > 0) {
          issues.Accounts.push({
            field: 'Email',
            issue: 'Invalid email format',
            count: accountIssues.invalid_email
          });
        }
        if (accountIssues.missing_contact_info > 0) {
          issues.Accounts.push({
            field: 'Contact Info',
            issue: 'Missing both phone and email',
            count: accountIssues.missing_contact_info
          });
        }
        if (accountIssues.invalid_name_characters > 0) {
          issues.Accounts.push({
            field: 'Name',
            issue: 'Invalid characters in name',
            count: accountIssues.invalid_name_characters
          });
        }
      }

      // Process leads issues
      if (report.leads?.issues) {
        const leadIssues = report.leads.issues;
        if (leadIssues.missing_first_name > 0) {
          issues.Leads.push({
            field: 'First Name',
            issue: 'Missing first name',
            count: leadIssues.missing_first_name
          });
        }
        if (leadIssues.missing_last_name > 0) {
          issues.Leads.push({
            field: 'Last Name',
            issue: 'Missing last name',
            count: leadIssues.missing_last_name
          });
        }
        if (leadIssues.invalid_email > 0) {
          issues.Leads.push({
            field: 'Email',
            issue: 'Invalid email format',
            count: leadIssues.invalid_email
          });
        }
        if (leadIssues.missing_contact_info > 0) {
          issues.Leads.push({
            field: 'Contact Info',
            issue: 'Missing both phone and email',
            count: leadIssues.missing_contact_info
          });
        }
        if (leadIssues.invalid_name_characters > 0) {
          issues.Leads.push({
            field: 'Name',
            issue: 'Invalid characters in name',
            count: leadIssues.invalid_name_characters
          });
        }
      }

      // Generate recommendations
      const recommendations = [];
      if (overallScore < 80) {
        recommendations.push('Review and clean up records with missing or invalid data');
      }
      if (Object.values(issues).some(arr => arr.some(i => i.field.includes('Email')))) {
        recommendations.push('Validate email addresses to ensure they follow proper format');
      }
      if (Object.values(issues).some(arr => arr.some(i => i.field.includes('Name')))) {
        recommendations.push('Ensure all contacts and leads have complete name information');
      }
      if (Object.values(issues).some(arr => arr.some(i => i.issue.includes('contact info')))) {
        recommendations.push('Add phone numbers or emails for records missing contact information');
      }

      setQualityData({
        overallScore,
        entityScores,
        issues,
        recommendations
      });

    } catch (error) {
      console.error('Error analyzing data quality:', error);
      setError(error.message);
      setQualityData(null);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadQualityData();
  }, [tenantFilter]);

  if (loading) {
    return (
      <div className="flex items-center justify-center p-12">
        <Loader2 className="w-8 h-8 animate-spin text-blue-400" />
        <span className="ml-3 text-slate-400">Analyzing data quality...</span>
      </div>
    );
  }

  if (error || !qualityData) {
    return (
      <Card className="bg-slate-800 border-slate-700">
        <CardContent className="p-12 text-center">
          <AlertTriangle className="w-12 h-12 text-orange-400 mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-slate-300 mb-2">Unable to Load Data Quality Report</h3>
          <p className="text-slate-400 mb-4">{error || 'Please try again later.'}</p>
          <Button onClick={loadQualityData} variant="outline" className="bg-slate-700 hover:bg-slate-600 text-slate-200 border-slate-600">
            <RefreshCw className="w-4 h-4 mr-2" />
            Retry
          </Button>
        </CardContent>
      </Card>
    );
  }

  const getScoreColor = (score) => {
    if (score >= 80) return 'text-green-400';
    if (score >= 60) return 'text-yellow-400';
    return 'text-red-400';
  };

  const getScoreBadge = (score) => {
    if (score >= 80) return <Badge className="bg-green-900/30 text-green-400 border-green-700">Excellent</Badge>;
    if (score >= 60) return <Badge className="bg-yellow-900/30 text-yellow-400 border-yellow-700">Good</Badge>;
    return <Badge className="bg-red-900/30 text-red-400 border-red-700">Needs Improvement</Badge>;
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-slate-100">Data Quality Report</h2>
          <p className="text-slate-400">Comprehensive analysis of your CRM data quality</p>
        </div>
        <Button onClick={loadQualityData} variant="outline" className="bg-slate-700 hover:bg-slate-600 text-slate-200 border-slate-600">
          <RefreshCw className="w-4 h-4 mr-2" />
          Refresh
        </Button>
      </div>

      {/* Overall Score */}
      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="text-slate-100">Overall Data Quality Score</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center justify-between">
            <div>
              <div className={`text-6xl font-bold ${getScoreColor(qualityData.overallScore)}`}>
                {qualityData.overallScore}%
              </div>
              <div className="mt-2">
                {getScoreBadge(qualityData.overallScore)}
              </div>
            </div>
            <div className="text-right text-slate-400">
              <p className="text-sm">Last analyzed: {new Date().toLocaleString()}</p>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Entity Scores */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {Object.entries(qualityData.entityScores || {}).map(([entity, score]) => (
          <Card key={entity} className="bg-slate-800 border-slate-700">
            <CardHeader>
              <CardTitle className="text-sm text-slate-300">{entity}</CardTitle>
            </CardHeader>
            <CardContent>
              <div className={`text-3xl font-bold ${getScoreColor(score)}`}>
                {score}%
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Issues by Entity */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {Object.entries(qualityData.issues || {}).map(([entity, entityIssues]) => (
          <Card key={entity} className="bg-slate-800 border-slate-700">
            <CardHeader>
              <CardTitle className="text-slate-100">{entity} Issues</CardTitle>
              <CardDescription className="text-slate-400">
                {entityIssues.length} issue{entityIssues.length !== 1 ? 's' : ''} found
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {entityIssues.length === 0 ? (
                  <div className="flex items-center gap-2 text-green-400">
                    <CheckCircle className="w-4 h-4" />
                    <span className="text-sm">No issues found</span>
                  </div>
                ) : (
                  entityIssues.map((issue, idx) => (
                    <div key={idx} className="flex items-start gap-3 p-3 bg-slate-700/50 rounded-lg">
                      <AlertTriangle className="w-4 h-4 text-yellow-400 mt-0.5" />
                      <div className="flex-1">
                        <p className="text-sm text-slate-200 font-medium">{issue.field}</p>
                        <p className="text-xs text-slate-400 mt-1">{issue.issue}</p>
                        <p className="text-xs text-slate-500 mt-1">
                          Affected records: {issue.count}
                        </p>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Recommendations */}
      {qualityData.recommendations && qualityData.recommendations.length > 0 && (
        <Card className="bg-slate-800 border-slate-700">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-slate-100">
              <Info className="w-5 h-5 text-blue-400" />
              Recommendations
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {qualityData.recommendations.map((rec, idx) => (
                <div key={idx} className="border-l-4 border-blue-400 pl-4 py-2">
                  <p className="text-sm text-slate-200">{rec}</p>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

import React, { useState, useEffect, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, BarChart, Bar } from 'recharts';
import { TrendingUp, Calendar, Users, Target, DollarSign, Loader2 } from 'lucide-react';
import { Contact, Account, Lead, Opportunity, Activity } from '@/api/entities';
import { User } from '@/api/entities';
import { useTenant } from '../shared/tenantContext';

export default function HistoricalTrends({ tenantFilter }) {
  const [loading, setLoading] = useState(true);
  const [timeRange, setTimeRange] = useState('30');
  const [trendsData, setTrendsData] = useState([]);
  const [summaryStats, setSummaryStats] = useState({
    totalContacts: 0,
    totalLeads: 0,
    totalOpportunities: 0,
    totalValue: 0
  });

  const loadTrendsData = useCallback(async () => {
    setLoading(true);
    try {
      console.log('HistoricalTrends: Using filter:', tenantFilter);

      // Calculate date range
      const endDate = new Date();
      endDate.setHours(23, 59, 59, 999); // Ensure endDate includes the entire current day
      const startDate = new Date();
      startDate.setDate(endDate.getDate() - parseInt(timeRange) + 1); // +1 to include startDate itself in the range
      startDate.setHours(0, 0, 0, 0); // Ensure startDate includes the very beginning of the day

      console.log('HistoricalTrends: Date range:', {
        startDate: startDate.toISOString(),
        endDate: endDate.toISOString()
      });

      // Load data with tenant filter
      const effectiveFilter = { ...tenantFilter };
      if (!('is_test_data' in effectiveFilter)) {
        effectiveFilter.is_test_data = { $ne: true };
      }

      const [contacts, leads, opportunities] = await Promise.all([
        Contact.filter(effectiveFilter).catch(() => []),
        Lead.filter(effectiveFilter).catch(() => []),
        Opportunity.filter(effectiveFilter).catch(() => [])
      ]);

      console.log('HistoricalTrends: Fetched data:', {
        contacts: contacts.length,
        leads: leads.length,
        opportunities: opportunities.length
      });

      // Log sample dates from fetched data
      if (contacts.length > 0) {
        console.log('HistoricalTrends: Sample contact dates:', {
          first: contacts[0]?.created_date,
          second: contacts[1]?.created_date,
          third: contacts[2]?.created_date
        });
      }
      if (leads.length > 0) {
        console.log('HistoricalTrends: Sample lead dates:', {
          first: leads[0]?.created_date,
          second: leads[1]?.created_date,
          third: leads[2]?.created_date
        });
      }
      if (opportunities.length > 0) {
        console.log('HistoricalTrends: Sample opportunity dates:', {
          first: opportunities[0]?.created_date,
          second: opportunities[1]?.created_date,
          third: opportunities[2]?.created_date
        });
      }

      // Filter data by date range for the trend
      const filterByDateRange = (items, dateField = 'created_date') => {
        return items.filter((item, index) => {
          if (!item[dateField]) {
            if (index < 5) console.log(`HistoricalTrends: Missing date field: '${dateField}' for item ID: ${item.id}`);
            return false;
          }
          const itemDate = new Date(item[dateField]);
          // Adjust itemDate to its specific day's start for comparison with startDate, and end for endDate
          const itemDateDayStart = new Date(itemDate.getFullYear(), itemDate.getMonth(), itemDate.getDate(), 0, 0, 0, 0);
          const itemDateDayEnd = new Date(itemDate.getFullYear(), itemDate.getMonth(), itemDate.getDate(), 23, 59, 59, 999);


          const inRange = itemDateDayStart >= startDate && itemDateDayEnd <= endDate;

          if (!inRange && index < 5) { // Log only a few examples to avoid console spam
            console.log('HistoricalTrends: Item date out of range:', {
              itemId: item.id,
              itemDate: itemDate.toISOString(),
              itemDateDayStart: itemDateDayStart.toISOString(),
              itemDateDayEnd: itemDateDayEnd.toISOString(),
              rangeStart: startDate.toISOString(),
              rangeEnd: endDate.toISOString(),
              isBeforeStart: itemDateDayStart < startDate,
              isAfterEnd: itemDateDayEnd > endDate,
            });
          }

          return inRange;
        });
      };

      const filteredContacts = filterByDateRange(contacts, 'created_date');
      const filteredLeads = filterByDateRange(leads, 'created_date');
      const filteredOpportunities = filterByDateRange(opportunities, 'created_date');

      console.log('HistoricalTrends: Filtered by date range:', {
        contacts: filteredContacts.length,
        leads: filteredLeads.length,
        opportunities: filteredOpportunities.length,
        dateRange: `${startDate.toLocaleDateString()} to ${endDate.toLocaleDateString()}`
      });

      // Calculate summary stats (for the selected period)
      const totalValue = filteredOpportunities.reduce((sum, opp) => sum + (opp.amount || 0), 0);
      setSummaryStats({
        totalContacts: filteredContacts.length,
        totalLeads: filteredLeads.length,
        totalOpportunities: filteredOpportunities.length,
        totalValue
      });

      // Group data by day for trend analysis
      const dailyData = {};
      const dayCount = parseInt(timeRange);

      // Initialize all days with zero values
      for (let i = 0; i < dayCount; i++) {
        const date = new Date(endDate); // Start from endDate and go backwards
        date.setDate(endDate.getDate() - i);
        const dateStr = date.toISOString().split('T')[0];
        dailyData[dateStr] = {
          date: dateStr,
          contacts: 0,
          leads: 0,
          opportunities: 0,
          value: 0
        };
      }

      // Count contacts by day
      filteredContacts.forEach(contact => {
        const dateStr = new Date(contact.created_date).toISOString().split('T')[0];
        if (dailyData[dateStr]) {
          dailyData[dateStr].contacts++;
        }
      });

      // Count leads by day
      filteredLeads.forEach(lead => {
        const dateStr = new Date(lead.created_date).toISOString().split('T')[0];
        if (dailyData[dateStr]) {
          dailyData[dateStr].leads++;
        }
      });

      // Count opportunities by day
      filteredOpportunities.forEach(opportunity => {
        const dateStr = new Date(opportunity.created_date).toISOString().split('T')[0];
        if (dailyData[dateStr]) {
          dailyData[dateStr].opportunities++;
          dailyData[dateStr].value += (opportunity.amount || 0);
        }
      });

      // Convert to array and sort by date
      const trendsArray = Object.values(dailyData).sort((a, b) => new Date(a.date) - new Date(b.date));

      // Format dates for display
      const formattedTrends = trendsArray.map(item => ({
        ...item,
        displayDate: new Date(item.date).toLocaleDateString('en-US', { 
          month: 'short', 
          day: 'numeric' 
        })
      }));

      console.log('HistoricalTrends: Final trends data sample (last 5 days):', formattedTrends.slice(-5));

      setTrendsData(formattedTrends);
    } catch (error) {
      console.error('Error loading trends data:', error);
      setTrendsData([]);
    } finally {
      setLoading(false);
    }
  }, [tenantFilter, timeRange]);

  useEffect(() => {
    loadTrendsData();
  }, [loadTrendsData]);

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-center p-12">
          <Loader2 className="w-8 h-8 animate-spin text-blue-600" />
          <span className="ml-3 text-slate-600">Loading historical trends...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-slate-100">Historical Trends</h2>
