          <CardHeader>
            <CardTitle className="text-slate-100">Daily Activity Trends</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={trendsData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                <XAxis dataKey="displayDate" tick={{ fontSize: 12, fill: '#94a3b8' }} />
                <YAxis tick={{ fontSize: 12, fill: '#94a3b8' }} />
                <Tooltip 
                  contentStyle={{ 
                    backgroundColor: '#1e293b', 
                    border: '1px solid #475569', 
                    borderRadius: '8px', 
                    color: '#f1f5f9' 
                  }} 
                />
                <Legend wrapperStyle={{ color: '#f1f5f9' }} />
                <Line type="monotone" dataKey="contacts" stroke="#3b82f6" strokeWidth={2} name="Contacts" />
                <Line type="monotone" dataKey="leads" stroke="#10b981" strokeWidth={2} name="Leads" />
                <Line type="monotone" dataKey="opportunities" stroke="#f59e0b" strokeWidth={2} name="Opportunities" />
