          </CardContent>
        </Card>
      </div>
    </div>
  );
}

import React, { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Users,
  Building,
  Star,
  Target,
  DollarSign,
  Activity,
} from "lucide-react";
import {
  PieChart,
  Pie,
  Cell,
  Tooltip,
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Legend,
} from "recharts";
import { Contact, Account, Lead, Opportunity, Activity as ActivityEntity } from "@/api/entities";
import TrendIndicator from './TrendIndicator';
import { getDashboardStats } from "@/api/functions";

const COLORS = ['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6'];

export default function OverviewStats({ tenantFilter }) {
  const [stats, setStats] = useState({
    contacts: 0,
    accounts: 0,
    leads: 0,
    opportunities: 0,
    activities: 0,
    pipelineValue: 0
  });

  const [trends, setTrends] = useState({
    contacts: null,
    accounts: null,
    leads: null,
    opportunities: null,
    activities: null,
    pipelineValue: null,
  });

  const [chartData, setChartData] = useState({
    leadSources: [],
    opportunityStages: []
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        console.log('OverviewStats: Fetching stats with filter:', tenantFilter);

        // Use the same function as Dashboard to ensure consistency
        const response = await getDashboardStats({ tenantFilter });

        // Ensure test data is excluded unless explicitly included for direct entity fetches
        const effectiveFilter = { ...tenantFilter };
        if (!('is_test_data' in effectiveFilter)) {
          effectiveFilter.is_test_data = { $ne: true };
        }

        // Fetch additional data for charts and specific stats that getDashboardStats might not provide as totals
        const [allLeads, allOpportunities, allAccounts] = await Promise.all([
          Lead.filter(effectiveFilter),
          Opportunity.filter(effectiveFilter),
          Account.filter(effectiveFilter)
        ]);

        if (response.data && response.data.stats) {
          const dashboardStats = response.data.stats;

          console.log('OverviewStats: Received dashboard stats from getDashboardStats:', dashboardStats);
          console.log('OverviewStats: Chart data counts from direct entity fetches:', {
            leads: allLeads.length,
            opportunities: allOpportunities.length,
            accounts: allAccounts.length
          });

          setStats({
            contacts: dashboardStats.totalContacts || 0,
            accounts: allAccounts.length, // Use actual count from direct fetch for accuracy
            leads: allLeads.length,       // Use actual total leads from direct fetch
            opportunities: allOpportunities.length, // Use actual total opportunities from direct fetch
            activities: dashboardStats.activitiesLogged || 0, // Assuming this aligns with 'Activities This Month' contextually from dashboard
            pipelineValue: dashboardStats.pipelineValue || 0
          });

          // Set trends if available
          if (dashboardStats.trends) {
            setTrends(dashboardStats.trends);
          }
        } else {
          console.warn("OverviewStats: No stats data received from getDashboardStats.");
        }

        // Prepare chart data
        // Initialize all possible lead sources
        const allLeadSources = {
          'website': 0,
          'referral': 0,
          'cold_call': 0,
          'email': 0,
          'social_media': 0,
          'trade_show': 0,
          'advertising': 0,
          'other': 0
        };

        allLeads.forEach(lead => {
          const source = (lead.source || '').toLowerCase();
          const key = Object.prototype.hasOwnProperty.call(allLeadSources, source) ? source : 'other';
          allLeadSources[key]++;
        });

        // Initialize all possible opportunity stages
        const allOpportunityStages = {
          'prospecting': 0,
          'qualification': 0,
          'proposal': 0,
          'negotiation': 0,
          'closed_won': 0,
          'closed_lost': 0
        };

        allOpportunities.forEach(opp => {
          const stage = (opp.stage || '').toLowerCase();
          const key = Object.prototype.hasOwnProperty.call(allOpportunityStages, stage) ? stage : 'prospecting';
          allOpportunityStages[key]++;
        });

        setChartData({
          leadSources: Object.entries(allLeadSources).map(([name, value]) => ({ 
            name: name.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase()), 
            value,
            originalKey: name
          })),
          opportunityStages: Object.entries(allOpportunityStages).map(([name, value]) => ({ 
            name: name.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase()), 
            value,
            originalKey: name
          })),
        });

      } catch (error) {
        console.error("Error fetching overview stats:", error);
      }
    };

    fetchStats();
  }, [tenantFilter]);

  const statItems = [
    { title: "Total Contacts", value: stats.contacts, icon: Users, color: "bg-blue-500", trend: trends.contacts },
    { title: "Active Accounts", value: stats.accounts, icon: Building, color: "bg-emerald-500", trend: trends.accounts },
    { title: "Total Leads", value: stats.leads, icon: Star, color: "bg-purple-500", trend: trends.leads },
    { title: "Opportunities", value: stats.opportunities, icon: Target, color: "bg-orange-500", trend: trends.opportunities },
    { title: "Pipeline Value", value: `$${stats.pipelineValue.toLocaleString()}`, icon: DollarSign, color: "bg-amber-500", trend: trends.pipelineValue },
    { title: "Activities This Month", value: stats.activities, icon: Activity, color: "bg-indigo-500", trend: trends.activities }
  ];

  return (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {statItems.map((stat, index) => (
          <Card key={index} className="relative overflow-hidden bg-slate-800 border-slate-700 shadow-lg hover:shadow-xl transition-all duration-300">
            <div className={`absolute top-0 right-0 w-32 h-32 ${stat.color} opacity-5 rounded-full transform translate-x-8 -translate-y-8`} />
            <CardHeader className="flex flex-row items-center justify-between pb-2">
              <CardTitle className="text-sm font-medium text-slate-400">
                {stat.title}
              </CardTitle>
              <div className={`p-2 rounded-lg ${stat.color} bg-opacity-10`}>
                <stat.icon className={`w-4 h-4 ${stat.color.replace('bg-', 'text-')}`} />
              </div>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-slate-100 mb-1">
                {stat.value}
              </div>
              <TrendIndicator percentage={stat.trend} />
            </CardContent>
          </Card>
        ))}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card className="shadow-lg bg-slate-800 border-slate-700">
          <CardHeader>
            <CardTitle className="text-slate-100">Lead Sources Distribution</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie
                  data={chartData.leadSources.filter(item => item.value > 0)} // Only show slices with data
                  cx="50%"
                  cy="50%"
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                  label={({ name, percent }) => {
                    // Only show labels for slices that are 5% or larger to avoid overlap
                    if (percent >= 0.05) {
                      return `${name} ${(percent * 100).toFixed(0)}%`;
                    }
                    return '';
                  }}
                >
                  {chartData.leadSources.filter(item => item.value > 0).map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip contentStyle={{ backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '8px', color: '#f1f5f9' }} />
                <Legend
                  align="center"
                  verticalAlign="bottom"
                  layout="horizontal"
                  iconType="circle"
                  wrapperStyle={{ 
                    paddingTop: '15px',
                    fontSize: '12px' 
                  }}
                  payload={chartData.leadSources.map((item, index) => {
                    const filteredItems = chartData.leadSources.filter(d => d.value > 0);
                    const itemInFiltered = filteredItems.find(fItem => Object.prototype.hasOwnProperty.call(fItem, 'originalKey') && fItem.originalKey === item.originalKey);
                    const color = item.value > 0 
                      ? COLORS[filteredItems.indexOf(itemInFiltered) % COLORS.length] 
                      : '#64748b'; // Grey out items with 0 value
                    return {
                      value: item.name,
                      type: 'circle',
                      color: color,
                      payload: item // Keep the original item data in payload
                    };
                  })}
                  formatter={(value, entry) => {
                    const item = entry.payload;
                    const totalWithData = chartData.leadSources.reduce((sum, item) => sum + (item.value > 0 ? item.value : 0), 0);
                    const percent = totalWithData > 0 && item.value > 0 ? ((item.value / totalWithData) * 100).toFixed(0) : 0;
                    return (
                      <span style={{ 
                        color: entry.color, // match slice color; grey for zero
                        fontStyle: item.value === 0 ? 'italic' : 'normal'
                      }}>
                        {value} ({item.value > 0 ? `${percent}%` : '0'})
                      </span>
                    );
                  }}
                />
              </PieChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>

        <Card className="shadow-lg bg-slate-800 border-slate-700">
          <CardHeader>
            <CardTitle className="text-slate-100">Sales Pipeline by Stage</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={chartData.opportunityStages.filter(item => item.value > 0)}>
                <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                <XAxis
                  dataKey="name"
                  tick={{ fontSize: 12, fill: '#94a3b8' }}
                  angle={-45}
                  textAnchor="end"
                  height={80}
                />
                <YAxis tick={{ fontSize: 12, fill: '#94a3b8' }} />
                <Tooltip contentStyle={{ backgroundColor: '#1e293b', border: '1px solid #475569', borderRadius: '8px', color: '#f1f5f9' }} />
                <Bar dataKey="value" fill="#3b82f6" radius={[4, 4, 0, 0]} />
              </BarChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

import React, { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Activity as ActivityIcon, Clock, CheckCircle, Calendar, Zap, Lightbulb } from "lucide-react"; // Added Lightbulb and Renamed Activity to ActivityIcon to avoid conflict
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  LineChart,
  Line,
  Legend
} from "recharts";
import { startOfWeek, format, subWeeks, isThisWeek, isToday } from "date-fns";
import { Activity, User } from "@/api/entities"; // Assuming User is also exported from here

const COLORS = ['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6'];

export default function ProductivityAnalytics({ tenantFilter }) {
  const [activities, setActivities] = useState([]);
  const [activitiesPerUser, setActivitiesPerUser] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchProductivityData = async () => {
      setIsLoading(true);
      try {
        // Assuming Activity and User from "@/api/entities" have a similar .filter method as the mocks
        const [rawActivities, users] = await Promise.all([
          Activity.filter(tenantFilter),
          User.filter(tenantFilter)
        ]);

        setActivities(rawActivities); // Set the main activities state for overall calculations

        // Process data for activitiesPerUser
        const userMap = users.reduce((acc, user) => {
          acc[user.id] = user.name;
          return acc;
        }, {});

        const activitiesByUser = rawActivities.reduce((acc, activity) => {
          if (!activity.userId) return acc; // Skip if no user ID
          if (!acc[activity.userId]) {
            acc[activity.userId] = {
              name: userMap[activity.userId] || `Unknown User (${activity.userId})`,
              total: 0,
              completed: 0,
            };
          }
          acc[activity.userId].total++;
          if (activity.status === 'completed') {
            acc[activity.userId].completed++;
          }
          return acc;
        }, {});

        const processedActivitiesPerUser = Object.entries(activitiesByUser).map(([userId, data]) => ({
          userId: userId,
          name: data.name,
          totalActivities: data.total,
          completedActivities: data.completed,
          completionRate: data.total > 0 ? Math.round((data.completed / data.total) * 100) : 0,
        }));
        setActivitiesPerUser(processedActivitiesPerUser);

      } catch (error) {
        console.error("Failed to fetch productivity data:", error);
        setActivities([]); // Clear activities on error
        setActivitiesPerUser([]);
      } finally {
        setIsLoading(false);
      }
    };

    fetchProductivityData();
  }, [tenantFilter]);

  // Calculate key productivity metrics
  const completedActivities = activities.filter(act => act.status === 'completed');
  const completionRate = activities.length > 0 ? (completedActivities.length / activities.length) * 100 : 0;
  const todaysActivities = activities.filter(act => act.due_date && isToday(new Date(act.due_date)));
  const thisWeekActivities = activities.filter(act => act.due_date && isThisWeek(new Date(act.due_date)));

  // Activity type distribution
  const getActivityTypeData = () => {
    const types = {};
    activities.forEach(activity => {
      const type = activity.type || 'other';
      if (!types[type]) {
        types[type] = { total: 0, completed: 0 };
      }
      types[type].total++;
      if (activity.status === 'completed') {
        types[type].completed++;
      }
    });

    return Object.entries(types).map(([name, data]) => ({
      type: name,
      total: data.total,
      completed: data.completed,
      completionRate: data.total > 0 ? Math.round((data.completed / data.total) * 100) : 0
    }));
  };

  // Weekly activity trends
  const getWeeklyActivityTrend = () => {
    const weeklyData = [];
    // Go back 7 weeks from the current week
    for (let i = 7; i >= 0; i--) {
      const weekStart = startOfWeek(subWeeks(new Date(), i), { weekStartsOn: 1 }); // Monday start
      // To get activities strictly within a week, define weekEnd as the start of the next week
      const weekEnd = startOfWeek(subWeeks(new Date(), i - 1), { weekStartsOn: 1 }); 
      const weekName = format(weekStart, 'MMM dd'); // Display the start date of the week

      const weekActivities = activities.filter(activity => {
        const actCreatedDate = new Date(activity.created_date);
        return actCreatedDate >= weekStart && actCreatedDate < weekEnd;
      });

      const completed = weekActivities.filter(act => act.status === 'completed').length;

      weeklyData.push({
        week: weekName,
        activities: weekActivities.length,
        completed: completed,
        completionRate: weekActivities.length > 0 ? Math.round((completed / weekActivities.length) * 100) : 0
      });
    }
    return weeklyData;
  };

  // Priority distribution
  const getPriorityDistribution = () => {
    const priorities = {};
    activities.forEach(activity => {
      const priority = activity.priority || 'normal';
      priorities[priority] = (priorities[priority] || 0) + 1;
    });
    return Object.entries(priorities).map(([name, value]) => ({ name, value }));
  };

  // Activity efficiency metrics
  const getEfficiencyMetrics = () => {
    const metrics = {
      onTime: 0,
      late: 0,
      upcoming: 0
    };

    activities.forEach(activity => {
      if (!activity.due_date) return;

      const dueDate = new Date(activity.due_date);
      const now = new Date();

      if (activity.status === 'completed' && dueDate >= now) { // Completed on or before due date
        metrics.onTime++;
      } else if (activity.status === 'completed' && dueDate < now) { // Completed but late
        metrics.late++; // Count as late even if completed
      } else if (activity.status !== 'completed' && dueDate < now) { // Not completed and overdue
        metrics.late++;
      } else if (activity.status !== 'completed' && dueDate >= now) { // Not completed and upcoming
        metrics.upcoming++;
      }
    });

    return [
      { name: 'On Time', value: metrics.onTime },
      { name: 'Overdue', value: metrics.late },
      { name: 'Upcoming', value: metrics.upcoming }
    ];
  };

  const typeData = getActivityTypeData();
  const weeklyData = getWeeklyActivityTrend();
  const priorityData = getPriorityDistribution();
  const efficiencyData = getEfficiencyMetrics();

  // Calculate insights statistics
  const stats = {
    highestCompletionDate: null,
    completionRate: completionRate,
    mostProductiveType: 'N/A',
    overdueCount: 0,
  };

  if (weeklyData.length > 0) {
    const highestWeek = weeklyData.reduce((max, week) =>
      (week.completionRate === undefined ? -1 : week.completionRate) > (max.completionRate === undefined ? -1 : max.completionRate) ? week : max,
      { completionRate: -1 } // Initial value for max.completionRate if all are undefined
    );
    if (highestWeek.week && highestWeek.completionRate !== -1) {
      stats.highestCompletionDate = highestWeek.week;
    }
  }

  if (typeData.length > 0) {
    const mostProductive = typeData.reduce((max, type) =>
      (type.completionRate === undefined ? -1 : type.completionRate) > (max.completionRate === undefined ? -1 : max.completionRate) ? type : max,
      { completionRate: -1 }
    );
    if (mostProductive.type && mostProductive.completionRate !== -1) {
      stats.mostProductiveType = mostProductive.type;
    }
  }

  const overdueEntry = efficiencyData.find(e => e.name === 'Overdue');
  if (overdueEntry) {
    stats.overdueCount = overdueEntry.value;
  }

  const insights = [
    {
      type: 'completion',
      bgColor: 'bg-blue-50',
      textColor: 'text-blue-900',
      borderColor: 'border-blue-200',
      message: stats.highestCompletionDate
        ? `Your highest completion rate was during the week of ${stats.highestCompletionDate}. Schedule important tasks during these periods.`
        : 'Track your activities to see completion patterns.'
    },
    {
      type: 'improvement',
      bgColor: 'bg-green-50',
      textColor: 'text-green-900',
      borderColor: 'border-green-200',
      message: stats.completionRate < 70 
        ? `Your completion rate of ${stats.completionRate.toFixed(1)}% needs improvement. Consider reducing task volume or extending deadlines.`
        : `Great job! Your ${stats.completionRate.toFixed(1)}% completion rate shows strong execution.`
    },
    {
      type: 'focus',
      bgColor: 'bg-purple-50',
      textColor: 'text-purple-900',
      borderColor: 'border-purple-200',
      message: stats.mostProductiveType !== 'N/A'
        ? `Most productive activity type: "${stats.mostProductiveType}". Focus more energy on high-performing tasks.`
        : 'Complete more activities to identify your most productive task types.'
    },
    {
      type: 'deadline',
      bgColor: 'bg-amber-50',
      textColor: 'text-amber-900',
      borderColor: 'border-amber-200',
      message: stats.overdueCount > 0
        ? `You have ${stats.overdueCount} overdue tasks. Consider using time-blocking and setting realistic deadlines to improve efficiency.`
        : 'No overdue tasks! Your deadline management is excellent.'
    }
  ];

  if (isLoading) {
    return (
      <Card className="shadow-lg bg-slate-800 border-slate-700">
        <CardContent className="p-6 text-center text-slate-400">
          <p>Loading productivity data...</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Key Productivity Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card className="shadow-lg bg-slate-800 border-slate-700">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-slate-400">Total Activities</p>
                <p className="text-2xl font-bold text-slate-100">{activities.length}</p>
              </div>
              <ActivityIcon className="w-8 h-8 text-blue-400" /> {/* Changed to ActivityIcon */}
            </div>
          </CardContent>
        </Card>

        <Card className="shadow-lg bg-slate-800 border-slate-700">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-slate-400">Completion Rate</p>
                <p className="text-2xl font-bold text-slate-100">{completionRate.toFixed(1)}%</p>
              </div>
              <CheckCircle className="w-8 h-8 text-green-400" />
            </div>
          </CardContent>
        </Card>

        <Card className="shadow-lg bg-slate-800 border-slate-700">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-slate-400">Today's Tasks</p>
                <p className="text-2xl font-bold text-slate-100">{todaysActivities.length}</p>
              </div>
              <Calendar className="w-8 h-8 text-purple-400" />
            </div>
          </CardContent>
        </Card>

        <Card className="shadow-lg bg-slate-800 border-slate-700">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-slate-400">This Week</p>
                <p className="text-2xl font-bold text-slate-100">{thisWeekActivities.length}</p>
              </div>
              <Zap className="w-8 h-8 text-orange-400" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card className="shadow-lg bg-slate-800 border-slate-700">
          <CardHeader>
            <CardTitle className="text-lg text-slate-100">Weekly Activity Trend</CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={weeklyData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#475569" />
                <XAxis 
                  dataKey="week" 
                  tick={{ fontSize: 12, fill: '#94a3b8' }}
                />
                <YAxis tick={{ fontSize: 12, fill: '#94a3b8' }} />
                <Tooltip 
                  contentStyle={{ 
                    backgroundColor: '#1e293b', 
                    border: '1px solid #475569', 
                    borderRadius: '8px',
                    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
                    color: '#f1f5f9'
                  }}
                />
                <Line type="monotone" dataKey="activities" stroke="#3b82f6" strokeWidth={2} name="Total" />
