export default function AIExecutionLogViewer() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);

  const loadLogs = useCallback(async () => {
    setLoading(true);
    try {
      // Expanded list to capture all AI-related executions (and cron runner visibility)
      const functionNames = [
        // Core AI pipeline
        "mcpHandler",
        "generateAIPlan",
        "executeAIPlan",
        "processAICommand",
        "processChatCommand",
        // Chat/LLM wrappers
        "aiRun",
        "aiToken",
        "invokeTenantLLM",
        "invokeSystemOpenAI",
        "testSystemOpenAI",
        "generateEntitySummary",
        // Voice / MCP servers
        "voiceCommand",
        "handleVoiceCommand",
        "mcpServer",
        "mcpServerPublic",
        "mcpServerSimple",
        "mcpServerDebug",
        // Cron+Schedulers
        "cronHeartbeat",
        "cronHeartbeat.duration",
        "cronJobRunner",
        "processScheduledAICalls",
        "processScheduledAIEmails"
      ];

      // Use backend function to avoid entity GET network errors and respect admin auth
      const { data } = await listPerformanceLogs({ limit: 50, functionNames });
      setLogs(Array.isArray(data?.logs) ? data.logs : []);
    } catch (error) {
      console.error("Failed to load execution logs:", error);
      // Fallback to empty logs and keep UI responsive
      setLogs([]);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadLogs();
  }, [loadLogs]);

  // Auto-refresh every 30s to keep logs up-to-date
  useEffect(() => {
    const id = setInterval(() => {
      loadLogs();
    }, 30000);
    return () => clearInterval(id);
  }, [loadLogs]);

  return (
    <div className="space-y-6">
      <Alert className="bg-blue-900/30 border-blue-700/50">
        <Bot className="h-4 w-4 text-blue-400" />
        <AlertDescription className="text-blue-300">
          View detailed logs of AI function calls and responses for debugging and optimization.
        </AlertDescription>
      </Alert>

      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-slate-100">
            <Bot className="w-5 h-5 text-purple-400" />
            AI Execution Logs
          </CardTitle>
          <CardDescription className="text-slate-400">
            Recent AI function invocations
          </CardDescription>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="flex justify-center p-8">
              <Loader2 className="w-6 h-6 animate-spin text-blue-400" />
            </div>
          ) : logs.length === 0 ? (
            <div className="text-center p-8 text-slate-400">
              No AI execution logs available
            </div>
          ) : (
            <div className="space-y-3">
              {logs.map((log, idx) => (
                <div key={idx} className="p-4 bg-slate-900 rounded-lg border border-slate-700">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center gap-3">
                        <Badge variant="outline" className="bg-slate-700 text-slate-300 border-slate-600">
                          {log.function_name}
                        </Badge>
                        <Badge className={log.status === 'success' ? 'bg-green-600' : 'bg-red-600'}>
                          {log.status}
                        </Badge>
                      </div>
                      <div className="mt-2 text-sm text-slate-300">
                        Response Time: {log.response_time_ms}ms
                      </div>
                      <div className="mt-1 text-xs text-slate-500">
                        {new Date(log.created_date).toLocaleString()}
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription, CardFooter } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { Badge } from '@/components/ui/badge';
import { Loader2, KeyRound, CheckCircle, AlertCircle, Save, Plug } from 'lucide-react';
import { User } from '@/api/entities';
import { testSystemOpenAI } from '@/api/functions';
import { toast } from "sonner";

export default function AdminOpenAISettings() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [localSettings, setLocalSettings] = useState({
    openai_api_key: '',
    model: 'gpt-4o-mini',
    max_tokens: 1000,
    temperature: 0.7,
    enabled: false,
  });
  const [isSaving, setIsSaving] = useState(false);
  const [isTesting, setIsTesting] = useState(false);
  const [testResult, setTestResult] = useState(null);
  const [showKey, setShowKey] = useState(false);

  useEffect(() => {
    async function loadUser() {
      try {
        const userData = await User.me();
        setUser(userData);
        if (userData.system_openai_settings) {
          setLocalSettings(userData.system_openai_settings);
        }
      } catch (error) {
        console.error("Failed to load user:", error);
      } finally {
        setLoading(false);
      }
    }
    loadUser();
  }, []);

  const handleSettingChange = (field, value) => {
    setLocalSettings(prev => ({ ...prev, [field]: value }));
  };

  const handleSaveSettings = async () => {
    setIsSaving(true);
    try {
      await User.updateMyUserData({ system_openai_settings: localSettings });
      toast.success("OpenAI settings saved successfully!");
    } catch (error) {
      toast.error("Failed to save settings.");
      console.error(error);
    } finally {
      setIsSaving(false);
    }
  };

  const handleTestConnection = async () => {
    if (!localSettings.openai_api_key) {
      toast.error("Please enter an OpenAI API key to test.");
      return;
    }
    setIsTesting(true);
    setTestResult(null);
    try {
      const response = await testSystemOpenAI({
        api_key: localSettings.openai_api_key,
        model: localSettings.model
      });
      setTestResult(response.data);
      if (response.data.success) {
        toast.success("OpenAI connection successful!");
        setLocalSettings(prev => ({ ...prev, enabled: true }));
      } else {
        toast.error(`Test failed: ${response.data.error}`);
      }
    } catch (error) {
      // Improved error handling to show specific backend message
      const errorMessage = error.response?.data?.error || "The request failed. Please check the function logs.";
      setTestResult({ success: false, error: errorMessage });
      toast.error(errorMessage, {
        description: error.response?.data?.details || "No further details available.",
      });
    } finally {
      setIsTesting(false);
    }
  };

  if (loading) {
    return <div className="flex justify-center p-8"><Loader2 className="w-6 h-6 animate-spin text-slate-400" /></div>;
  }

  return (
    <div className="space-y-6">
      <Card className="bg-slate-700/50 border-slate-600">
        <CardHeader>
          <CardTitle className="text-slate-100">System OpenAI Integration</CardTitle>
          <CardDescription className="text-slate-400">
            Configure your personal OpenAI API key to power AI features across the platform. This will be used when tenants don't have their own OpenAI integration configured.
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="space-y-2">
            <Label htmlFor="openai_api_key" className="text-slate-200">OpenAI API Key</Label>
            <div className="flex items-center gap-2">
              <Input
                id="openai_api_key"
                type={showKey ? 'text' : 'password'}
                value={localSettings.openai_api_key}
                onChange={(e) => handleSettingChange('openai_api_key', e.target.value)}
                placeholder="sk-..."
                className="bg-slate-700 border-slate-600 text-slate-200 placeholder:text-slate-400 focus:border-slate-500"
              />
              <Button variant="ghost" size="icon" onClick={() => setShowKey(!showKey)} className="text-slate-400 hover:text-slate-200">
                <KeyRound className="w-4 h-4" />
              </Button>
            </div>
            <a href="https://platform.openai.com/api-keys" target="_blank" rel="noopener noreferrer" className="text-xs text-blue-400 hover:underline">
              Get your API key from OpenAI Platform
            </a>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="space-y-2">
              <Label htmlFor="model" className="text-slate-200">Model</Label>
              <Select value={localSettings.model} onValueChange={(value) => handleSettingChange('model', value)}>
                <SelectTrigger id="model" className="bg-slate-700 border-slate-600 text-slate-200">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent className="bg-slate-800 border-slate-700 text-slate-200">
                  <SelectItem value="gpt-4o-mini" className="focus:bg-slate-700">GPT-4o Mini</SelectItem>
                  <SelectItem value="gpt-4o" className="focus:bg-slate-700">GPT-4o</SelectItem>
                  <SelectItem value="gpt-4-turbo" className="focus:bg-slate-700">GPT-4 Turbo</SelectItem>
                  <SelectItem value="gpt-3.5-turbo" className="focus:bg-slate-700">GPT-3.5 Turbo</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="max_tokens" className="text-slate-200">Max Tokens</Label>
              <Input
                id="max_tokens"
                type="number"
                value={localSettings.max_tokens}
                onChange={(e) => handleSettingChange('max_tokens', parseInt(e.target.value, 10))}
                className="bg-slate-700 border-slate-600 text-slate-200"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="temperature" className="text-slate-200">Temperature</Label>
              <Input
                id="temperature"
                type="number"
                step="0.1"
                min="0"
                max="2"
                value={localSettings.temperature}
                onChange={(e) => handleSettingChange('temperature', parseFloat(e.target.value))}
                className="bg-slate-700 border-slate-600 text-slate-200"
              />
            </div>
          </div>

          <div className="flex items-center space-x-2 pt-4">
            <Switch
              id="enabled"
              checked={localSettings.enabled}
              onCheckedChange={(checked) => handleSettingChange('enabled', checked)}
            />
            <Label htmlFor="enabled" className="text-slate-200">Enable system OpenAI integration</Label>
            {localSettings.enabled && <Badge className="bg-green-600">Enabled</Badge>}
          </div>

          {testResult && (
            <Alert className={testResult.success ? "bg-green-900/30 border-green-700/50" : "bg-red-900/30 border-red-700/50"}>
              <AlertTitle className="flex items-center gap-2">
                {testResult.success ? <CheckCircle className="text-green-400" /> : <AlertCircle className="text-red-400" />}
                <span className={testResult.success ? "text-green-200" : "text-red-200"}>
                  {testResult.success ? "Connection Successful" : "Connection Failed"}
                </span>
              </AlertTitle>
              <AlertDescription className={testResult.success ? "text-green-300" : "text-red-300"}>
                {testResult.success ? testResult.message : testResult.error}
              </AlertDescription>
            </Alert>
          )}

        </CardContent>
        <CardFooter className="flex gap-2 bg-slate-800/50 py-4 px-6 border-t border-slate-600">
          <Button onClick={handleSaveSettings} disabled={isSaving || isTesting}>
            {isSaving ? <Loader2 className="w-4 h-4 animate-spin mr-2" /> : <Save className="w-4 h-4 mr-2" />}
            Save Settings
          </Button>
          <Button variant="outline" onClick={handleTestConnection} disabled={isTesting || isSaving}>
            {isTesting ? <Loader2 className="w-4 h-4 animate-spin mr-2" /> : <Plug className="w-4 h-4 mr-2" />}
            Test Connection
          </Button>
        </CardFooter>
      </Card>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import {
  Plus,
  Copy,
  Eye,
  EyeOff,
  Trash2,
  Key,
  Calendar,
  BarChart3,
  AlertTriangle,
  RefreshCw,
  User // Added User icon for created_by
} from 'lucide-react';
import { ApiKey } from '@/api/entities';
import { format } from 'date-fns';
import toast from 'react-hot-toast';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";

export default function ApiKeyManager() {
  const [apiKeys, setApiKeys] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showNewKeyDialog, setShowNewKeyDialog] = useState(false);
  const [visibleKeys, setVisibleKeys] = useState(new Set());
  const [justCreatedKey, setJustCreatedKey] = useState(null); // Track newly created keys

  // New key form state
  const [newKey, setNewKey] = useState({
    key_name: '',
    description: '',
    key_value: ''
  });

  useEffect(() => {
    loadApiKeys();
  }, []);

  const loadApiKeys = async () => {
    try {
      const keys = await ApiKey.list('-created_date');
      // Sort by created_date descending to be sure
      keys.sort((a, b) => new Date(b.created_date) - new Date(a.created_date));
      setApiKeys(keys);
    } catch (error) {
      console.error('Failed to load API keys:', error);
      toast.error('Failed to load API keys');
    } finally {
      setLoading(false);
    }
  };

  const generateRandomKey = () => {
    // Generate a secure random key with app prefix
    const prefix = 'aisha';
    const timestamp = Date.now().toString(36);
    const random = Array.from(crypto.getRandomValues(new Uint8Array(16)))
      .map(b => b.toString(16).padStart(2, '0'))
      .join('');

    return `${prefix}_${timestamp}_${random}`;
  };

  const handleCreateKey = async () => {
    if (!newKey.key_name.trim()) {
      toast.error('Key name is required');
      return;
    }

    try {
      const keyData = {
        ...newKey,
        key_value: newKey.key_value || generateRandomKey()
      };

      const createdKey = await ApiKey.create(keyData);
      setApiKeys([createdKey, ...apiKeys]);
      setNewKey({ key_name: '', description: '', key_value: '' });
      setShowNewKeyDialog(false);

      // Make the newly created key immediately visible
      setJustCreatedKey(createdKey.id);
      setVisibleKeys(prev => new Set(prev).add(createdKey.id));

      toast.success('API key created successfully! The key is now visible for you to copy.');
    } catch (error) {
      console.error('Failed to create API key:', error);
      toast.error('Failed to create API key');
    }
  };

  const handleDeleteKey = async (keyId) => {
    try {
      await ApiKey.delete(keyId);
      setApiKeys(apiKeys.filter(key => key.id !== keyId));
      toast.success('API key deleted');
    } catch (error) {
      console.error('Failed to delete API key:', error);
      toast.error('Failed to delete API key');
    }
  };

  const toggleKeyVisibility = (keyId) => {
    const newVisible = new Set(visibleKeys);
    if (newVisible.has(keyId)) {
      newVisible.delete(keyId);
    } else {
      newVisible.add(keyId);
    }
    setVisibleKeys(newVisible);
  };

  const copyToClipboard = async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      toast.success('Copied to clipboard');
    } catch (error) {
      toast.error('Failed to copy');
    }
  };

  const maskKey = (key) => {
    if (key.length <= 8) return '*'.repeat(key.length);
    return key.substring(0, 4) + '*'.repeat(key.length - 8) + key.substring(key.length - 4);
  };

  if (loading) {
    return (
      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Key className="w-5 h-5 text-amber-400" />
            <span className="text-slate-100">API Keys</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex justify-center p-8">
            <RefreshCw className="w-6 h-6 animate-spin text-slate-400" />
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Key className="w-5 h-5 text-amber-400" />
              <span className="text-slate-100">API Keys</span>
            </div>
            <Dialog open={showNewKeyDialog} onOpenChange={setShowNewKeyDialog}>
              <DialogTrigger asChild>
                <Button className="bg-blue-600 hover:bg-blue-700">
                  <Plus className="w-4 h-4 mr-2" />
                  Generate New Key
                </Button>
              </DialogTrigger>
              <DialogContent className="bg-slate-800 border-slate-700">
                <DialogHeader>
                  <DialogTitle className="text-slate-100">Generate New API Key</DialogTitle>
                  <DialogDescription className="text-slate-400">
                    Create a new API key for external applications to access your CRM functions.
                  </DialogDescription>
                </DialogHeader>
                <div className="space-y-4">
                  <div>
                    <Label htmlFor="key_name" className="text-slate-200">Key Name *</Label>
                    <Input
                      id="key_name"
                      value={newKey.key_name}
                      onChange={(e) => setNewKey({...newKey, key_name: e.target.value})}
                      placeholder="e.g., Dialogflow Scheduler"
                      className="bg-slate-700 border-slate-600 text-slate-200 placeholder:text-slate-400 focus:border-slate-500"
                    />
                  </div>
                  <div>
                    <Label htmlFor="description" className="text-slate-200">Description</Label>
                    <Textarea
                      id="description"
                      value={newKey.description}
                      onChange={(e) => setNewKey({...newKey, description: e.target.value})}
                      placeholder="What this key will be used for..."
                      className="bg-slate-700 border-slate-600 text-slate-200 placeholder:text-slate-400 focus:border-slate-500"
                    />
                  </div>
                  <div>
                    <Label htmlFor="key_value" className="text-slate-200">Custom Key (Optional)</Label>
                    <Input
                      id="key_value"
                      value={newKey.key_value}
                      onChange={(e) => setNewKey({...newKey, key_value: e.target.value})}
                      placeholder="Leave blank to auto-generate"
                      className="bg-slate-700 border-slate-600 text-slate-200 placeholder:text-slate-400 focus:border-slate-500"
                    />
                    <p className="text-sm text-slate-400 mt-1">
                      Leave blank to generate a secure random key
                    </p>
                  </div>
                </div>
                <DialogFooter>
                  <Button variant="outline" onClick={() => setShowNewKeyDialog(false)} className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600">
                    Cancel
                  </Button>
                  <Button onClick={handleCreateKey} className="bg-blue-600 hover:bg-blue-700">
                    Generate Key
                  </Button>
                </DialogFooter>
              </DialogContent>
            </Dialog>
          </CardTitle>
          <CardDescription className="text-slate-400">
            Manage API keys for external integrations and automation
          </CardDescription>
        </CardHeader>
        <CardContent>
          {apiKeys.length === 0 ? (
            <div className="text-center py-8 text-slate-400">
              <Key className="w-12 h-12 mx-auto mb-4 text-slate-500" />
              <p className="font-medium text-slate-300">No API keys created yet</p>
              <p className="text-sm">Generate your first API key to get started</p>
            </div>
          ) : (
            <div className="space-y-4">
              {apiKeys.map((key) => (
                <Card
                  key={key.id}
                  className={`border-l-4 ${
                    justCreatedKey === key.id
                      ? 'border-l-green-500 bg-green-900/20'
                      : 'border-l-blue-500 bg-slate-700'
                  }`}
                >
                  <CardContent className="pt-4">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          <h3 className="font-semibold text-slate-100">{key.key_name}</h3>
                          <Badge variant={key.is_active ? 'default' : 'secondary'} className={key.is_active ? 'bg-green-600 text-white' : 'bg-slate-600 text-slate-300'}>
                            {key.is_active ? 'Active' : 'Inactive'}
                          </Badge>
                          {justCreatedKey === key.id && (
                            <Badge className="bg-green-600 text-white">
                              Just Created - Copy Now!
                            </Badge>
                          )}
                        </div>

                        {key.description && (
                          <p className="text-sm text-slate-400 mb-3">{key.description}</p>
                        )}

                        <div className={`font-mono text-sm p-2 rounded border flex items-center justify-between ${
                          justCreatedKey === key.id ? 'bg-green-900/30 border-green-600' : 'bg-slate-600 border-slate-500'
                        }`}>
                          <span className="break-all text-slate-200">
                            {visibleKeys.has(key.id) ? key.key_value : maskKey(key.key_value)}
                          </span>
                          <div className="flex items-center gap-1 ml-2">
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => toggleKeyVisibility(key.id)}
                              className="text-slate-300 hover:text-slate-100 hover:bg-slate-500"
                            >
                              {visibleKeys.has(key.id) ? (
                                <EyeOff className="w-4 h-4" />
                              ) : (
                                <Eye className="w-4 h-4" />
                              )}
                            </Button>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => {
                                copyToClipboard(key.key_value);
                                if (justCreatedKey === key.id) {
                                  setJustCreatedKey(null); // Remove highlight after copying
                                }
                              }}
                              className={`text-slate-300 hover:text-slate-100 ${justCreatedKey === key.id ? 'bg-green-700 hover:bg-green-600' : 'hover:bg-slate-500'}`}
                            >
                              <Copy className="w-4 h-4" />
                            </Button>
                          </div>
                        </div>

                        <div className="flex flex-wrap items-center gap-x-4 gap-y-1 mt-3 text-xs text-slate-400">
                          <div className="flex items-center gap-1">
                            <Calendar className="w-3 h-3" />
                            Created: {format(new Date(key.created_date), 'MMM d, yyyy HH:mm')}
                          </div>
                          {key.created_by && (
                            <div className="flex items-center gap-1">
                              <User className="w-3 h-3" />
                              Creator: {key.created_by}
                            </div>
                          )}
                          {key.usage_count > 0 && (
                            <div className="flex items-center gap-1">
                              <BarChart3 className="w-3 h-3" />
                              Used: {key.usage_count} times
                            </div>
                          )}
                          {key.last_used && (
                            <div className="flex items-center gap-1">
                              <Calendar className="w-3 h-3" />
                              Last used: {format(new Date(key.last_used), 'MMM d, yyyy')}
                            </div>
                          )}
                        </div>
                      </div>

                      <AlertDialog>
                        <AlertDialogTrigger asChild>
                          <Button variant="ghost" size="sm" className="text-red-400 hover:text-red-300 hover:bg-slate-600">
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent className="bg-slate-800 border-slate-700">
                          <AlertDialogHeader>
                            <AlertDialogTitle className="flex items-center gap-2 text-slate-100">
                              <AlertTriangle className="w-5 h-5 text-red-400" />
                              Delete API Key
                            </AlertDialogTitle>
                            <AlertDialogDescription className="text-slate-400">
                              Are you sure you want to delete the API key "{key.key_name}"?
                              This action cannot be undone and will break any integrations using this key.
                            </AlertDialogDescription>
                          </AlertDialogHeader>
                          <AlertDialogFooter>
                            <AlertDialogCancel className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600">Cancel</AlertDialogCancel>
                            <AlertDialogAction
                              onClick={() => handleDeleteKey(key.id)}
                              className="bg-red-600 hover:bg-red-700"
                            >
                              Delete Key
                            </AlertDialogAction>
                          </AlertDialogFooter>
                        </AlertDialogContent>
                      </AlertDialog>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { SubscriptionPlan, Subscription, User, Tenant } from '@/api/entities';
import { Card, CardContent, CardHeader, CardTitle, CardFooter, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Check, Crown, Loader2, AlertCircle, Users, ExternalLink, Building2, Plug, Eye, EyeOff, Save, CheckCircle } from 'lucide-react';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { createCheckoutSession } from '@/api/functions';
import { createBillingPortalSession } from '@/api/functions';
import { toast } from "sonner";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { testStripeConnection } from "@/api/functions";

const AdminStripeConfig = ({ user, onUpdate }) => {
  const [config, setConfig] = useState({ secret_key: '', publishable_key: '', webhook_secret: '', is_connected: false });
  const [showSecret, setShowSecret] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [isTesting, setIsTesting] = useState(false);
  const [testResult, setTestResult] = useState(null);

  useEffect(() => {
    if (user?.system_stripe_settings) {
      setConfig(user.system_stripe_settings);
      setTestResult({
        success: user.system_stripe_settings.is_connected,
        message: user.system_stripe_settings.is_connected ? 'Connection is active.' : 'Not tested.',
      });
    }
  }, [user]);

  const handleSave = async () => {
    setIsSaving(true);
    try {
      await User.updateMyUserData({ system_stripe_settings: { ...config, is_connected: testResult?.success || false } });
      toast.success("Stripe configuration saved.");
      onUpdate();
    } catch (error) {
      toast.error("Failed to save Stripe configuration.");
      console.error(error);
    } finally {
      setIsSaving(false);
    }
  };

  const handleTest = async () => {
    if (!config.secret_key) {
      toast.error("Please enter a Stripe Secret Key to test.");
      return;
    }
    setIsTesting(true);
    setTestResult(null);
    try {
      const { data } = await testStripeConnection({ apiKey: config.secret_key });
      setTestResult(data);
      if (data.success) {
        toast.success("Stripe connection successful!");
        // Auto-save the connection status
        await User.updateMyUserData({ system_stripe_settings: { ...config, is_connected: true } });
        onUpdate();
      } else {
        toast.error(`Connection failed: ${data.message}`);
        await User.updateMyUserData({ system_stripe_settings: { ...config, is_connected: false } });
        onUpdate();
      }
    } catch (error) {
      const message = error.response?.data?.message || error.message;
      setTestResult({ success: false, message });
      toast.error(`Connection failed: ${message}`);
      await User.updateMyUserData({ system_stripe_settings: { ...config, is_connected: false } });
      onUpdate();
    } finally {
      setIsTesting(false);
    }
  };

  return (
    <Card className="bg-slate-800 border-slate-700 mt-8">
      <CardHeader>
        <CardTitle className="text-slate-100">System Stripe Configuration</CardTitle>
        <CardDescription className="text-slate-400">
          Connect your Stripe account to process payments for tenant subscriptions. These keys are stored securely.
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="space-y-2">
          <Label htmlFor="stripe-secret" className="text-slate-300">Stripe Secret Key</Label>
          <div className="relative">
            <Input
              id="stripe-secret"
              type={showSecret ? 'text' : 'password'}
              value={config.secret_key || ''}
              onChange={(e) => setConfig(prev => ({ ...prev, secret_key: e.target.value }))}
              placeholder="sk_live_..."
              className="bg-slate-700 border-slate-600 text-slate-200"
            />
            <Button
              variant="ghost"
              size="icon"
              className="absolute right-2 top-1/2 -translate-y-1/2 h-7 w-7 text-slate-400 hover:text-slate-200"
              onClick={() => setShowSecret(!showSecret)}
            >
              {showSecret ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
            </Button>
          </div>
        </div>
        <div className="space-y-2">
          <Label htmlFor="stripe-publishable" className="text-slate-300">Stripe Publishable Key</Label>
          <Input
            id="stripe-publishable"
            value={config.publishable_key || ''}
            onChange={(e) => setConfig(prev => ({ ...prev, publishable_key: e.target.value }))}
            placeholder="pk_live_..."
            className="bg-slate-700 border-slate-600 text-slate-200"
          />
        </div>
        <div className="space-y-2">
          <Label htmlFor="stripe-webhook" className="text-slate-300">Stripe Webhook Secret</Label>
          <Input
            id="stripe-webhook"
            value={config.webhook_secret || ''}
            onChange={(e) => setConfig(prev => ({ ...prev, webhook_secret: e.target.value }))}
            placeholder="whsec_..."
            className="bg-slate-700 border-slate-600 text-slate-200"
          />
           <p className="text-xs text-slate-500">
            Find this in your Stripe Dashboard under Developers  Webhooks. Use the endpoint for `/functions/handleStripeWebhook`.
          </p>
        </div>

        {testResult && (
          <Alert className={testResult.success ? 'bg-green-900/30 border-green-700/50' : 'bg-red-900/30 border-red-700/50'}>
            {testResult.success ? <CheckCircle className="h-4 w-4 text-green-400" /> : <AlertCircle className="h-4 w-4 text-red-400" />}
            <AlertDescription className={testResult.success ? 'text-green-300' : 'text-red-300'}>
              {testResult.message}
            </AlertDescription>
          </Alert>
        )}

      </CardContent>
      <CardFooter className="flex gap-2">
        <Button onClick={handleSave} disabled={isSaving || isTesting} className="bg-blue-600 hover:bg-blue-700">
          {isSaving ? <Loader2 className="w-4 h-4 mr-2 animate-spin" /> : <Save className="w-4 h-4 mr-2" />}
          Save
        </Button>
        <Button variant="outline" onClick={handleTest} disabled={isSaving || isTesting} className="bg-slate-700 border-slate-600 hover:bg-slate-600">
          {isTesting ? <Loader2 className="w-4 h-4 mr-2 animate-spin" /> : <Plug className="w-4 h-4 mr-2" />}
          Test Connection
        </Button>
      </CardFooter>
    </Card>
  );
};


export default function BillingSettings() {
  const [plans, setPlans] = useState([]);
  const [currentSubscription, setCurrentSubscription] = useState(null);
  const [allSubscriptions, setAllSubscriptions] = useState([]);
  const [allTenants, setAllTenants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    try {
      const [plansData, userData] = await Promise.all([
        SubscriptionPlan.list('display_order'),
        User.me()
      ]);
      setPlans(plansData);
      setUser(userData);

      // Admin-specific data
      if (userData?.role === 'admin' || userData?.role === 'superadmin') {
        const [subsData, tenantsData] = await Promise.all([
            Subscription.list(),
            Tenant.list()
        ]);
        setAllSubscriptions(subsData);
        setAllTenants(tenantsData);
      }

      // User-specific data (for both admins and regular users)
      if (userData?.tenant_id) {
        const subscriptions = await Subscription.filter({ tenant_id: userData.tenant_id });
        if (subscriptions.length > 0) {
          setCurrentSubscription(subscriptions[0]);
        }
      }
    } catch (error) {
      console.error("Error fetching billing data:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubscribe = async (plan) => {
    if (!user?.tenant_id) {
      alert("Tenant information is missing. Cannot subscribe.");
      return;
    }
    setIsProcessing(true);
    try {
      const { data } = await createCheckoutSession({ 
        priceId: plan.stripe_price_id,
        tenantId: user.tenant_id
      });
      if (data.url) {
        window.location.href = data.url;
      }
    } catch (error) {
      console.error("Error creating checkout session:", error);
      alert("Could not initiate subscription. Please try again.");
    } finally {
      setIsProcessing(false);
    }
  };

  const handleManageSubscription = async (customerId) => {
    if (!customerId) {
      alert("Could not find customer billing information.");
      return;
    }
    setIsProcessing(true);
    try {
      const { data } = await createBillingPortalSession({ 
        customerId: customerId 
      });
      if (data.url) {
        window.location.href = data.url;
      }
    } catch (error) {
      console.error("Error creating billing portal session:", error);
      alert("Could not open billing portal. Please try again.");
    } finally {
      setIsProcessing(false);
    }
  };

  const getTenantSubscription = (tenantId) => {
    return allSubscriptions.find(sub => sub.tenant_id === tenantId);
  };

  const getPlanName = (planId) => {
    const plan = plans.find(p => p.id === planId);
    return plan ? plan.name : 'Unknown Plan';
  };

  if (loading) {
    return <div className="flex justify-center items-center p-10"><Loader2 className="w-8 h-8 animate-spin text-slate-400" /></div>;
  }

  if (user?.role === 'admin' || user?.role === 'superadmin') {
    return (
      <div className="space-y-8">
        <AdminStripeConfig user={user} onUpdate={fetchData} />

        {/* Available Subscription Plans */}
        <Card className="bg-slate-800 border-slate-700">
          <CardHeader>
            <CardTitle className="text-slate-100">Available Subscription Plans</CardTitle>
            <CardDescription className="text-slate-400">
              These are the plans available to your tenants. Manage plan details through your Stripe dashboard.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              {plans.map((plan) => (
                <Card key={plan.id} className={`flex flex-col bg-slate-700 border-slate-600 ${plan.name === 'Professional' ? 'border-blue-500 relative' : ''}`}>
                  {plan.name === 'Professional' && (
                    <div className="absolute top-3 right-3">
                      <Crown className="w-5 h-5 text-yellow-500" />
                    </div>
                  )}
                  <CardHeader>
                    <h3 className="text-xl font-bold text-slate-100">{plan.name}</h3>
                    <p className="text-slate-400 text-sm">{plan.description}</p>
                  </CardHeader>
                  <CardContent className="flex-1">
                    <div className="mb-6">
                      <span className="text-4xl font-bold text-slate-100">${plan.price_monthly}</span>
                      <span className="text-slate-400">/month</span>
                    </div>
                    <ul className="space-y-3">
                      {plan.features?.map((feature, i) => (
                        <li key={i} className="flex items-center gap-3">
                          <Check className="w-5 h-5 text-green-500 flex-shrink-0" />
                          <span className="text-sm text-slate-300">{feature}</span>
                        </li>
                      ))}
                    </ul>
                  </CardContent>
                  <CardFooter>
                    <Button className="w-full bg-slate-600 hover:bg-slate-500" disabled>
                      Plan Configuration
                    </Button>
                  </CardFooter>
                </Card>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Tenant Subscription Management */}
        <Card className="bg-slate-800 border-slate-700">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-slate-100">
              <Users className="w-5 h-5 text-blue-600" />
              Tenant Subscription Management
            </CardTitle>
            <CardDescription className="text-slate-400">
              View and manage subscriptions for all tenants in the system.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Table>
              <TableHeader>
                <TableRow className="border-slate-700">
                  <TableHead className="text-slate-300">Tenant</TableHead>
                  <TableHead className="text-slate-300">Current Plan</TableHead>
                  <TableHead className="text-slate-300">Status</TableHead>
                  <TableHead className="text-slate-300">Renews On</TableHead>
                  <TableHead className="text-slate-300">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {allTenants.map(tenant => {
                  const subscription = getTenantSubscription(tenant.id);
                  return (
                    <TableRow key={tenant.id} className="border-slate-700">
                      <TableCell>
                        <div className="flex items-center gap-2">
                          <Building2 className="w-4 h-4 text-slate-500" />
                          <div>
                            <div className="font-medium text-slate-200">{tenant.name}</div>
                            {tenant.domain && (
                              <div className="text-xs text-slate-500">{tenant.domain}</div>
                            )}
                          </div>
                        </div>
                      </TableCell>
                      <TableCell>
                        {subscription ? (
                          <Badge variant="default" className="bg-blue-100 text-blue-800">
                            {getPlanName(subscription.plan_id)}
                          </Badge>
                        ) : (
                          <Badge variant="secondary" className="bg-slate-600 text-slate-300">No Active Plan</Badge>
                        )}
                      </TableCell>
                      <TableCell>
                        {subscription ? (
                          <Badge variant={subscription.status === 'active' ? 'default' : 'secondary'} 
                                 className={subscription.status === 'active' ? 'bg-green-100 text-green-800' : 'bg-slate-600 text-slate-300'}>
                            {subscription.status}
                          </Badge>
                        ) : (
                          <Badge variant="outline" className="bg-slate-700 border-slate-600 text-slate-300">Unsubscribed</Badge>
                        )}
                      </TableCell>
                      <TableCell className="text-slate-300">
                        {subscription?.current_period_end ? 
                          new Date(subscription.current_period_end).toLocaleDateString() : 'N/A'}
                      </TableCell>
                      <TableCell>
                        {subscription ? (
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleManageSubscription(subscription.stripe_customer_id)}
                            disabled={isProcessing}
                            className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600"
                          >
                            {isProcessing ? (
                              <Loader2 className="w-3 h-3 mr-2 animate-spin" />
                            ) : (
                              <ExternalLink className="w-3 h-3 mr-2" />
                            )}
                            Manage
                          </Button>
                        ) : (
                          <span className="text-xs text-slate-500">No subscription</span>
                        )}
                      </TableCell>
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
            {allTenants.length === 0 && (
              <div className="text-center py-8 text-slate-500">
                No tenants found in the system.
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    );
  }

  // View for Regular Users
  const currentPlan = plans.find(p => p.id === currentSubscription?.plan_id);

  return (
    <div className="space-y-6">
      {currentSubscription && currentPlan ? (
        <Card className="bg-slate-800 border-slate-700">
          <CardHeader>
            <CardTitle className="text-slate-100">Your Subscription</CardTitle>
            <CardDescription className="text-slate-400">Manage your current plan and billing details.</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="p-4 border rounded-lg bg-slate-700/50 border-slate-600">
              <h3 className="text-lg font-semibold text-blue-400">{currentPlan.name}</h3>
              <p className="text-slate-300">You are currently subscribed to the {currentPlan.name} plan.</p>
              <div className="mt-2 text-sm">
                Status: <Badge className={currentSubscription.status === 'active' ? 'bg-green-600 text-white' : 'bg-slate-600 text-slate-300'}>{currentSubscription.status}</Badge>
              </div>
              {currentSubscription.current_period_end && (
                <div className="mt-1 text-sm text-slate-400">
                  Your plan renews on {new Date(currentSubscription.current_period_end).toLocaleDateString()}.
                </div>
              )}
            </div>
            <Button 
                onClick={() => handleManageSubscription(currentSubscription.stripe_customer_id)}
                disabled={isProcessing}
                className="bg-blue-600 hover:bg-blue-700"
            >
                {isProcessing ? <Loader2 className="w-4 h-4 animate-spin mr-2" /> : null}
                Manage Billing & Subscription
            </Button>
          </CardContent>
        </Card>
      ) : (
        <>
            <Alert className="bg-red-900/30 border-red-700/50">
                <AlertCircle className="h-4 w-4 text-red-400" />
                <AlertTitle className="text-red-200">No Active Subscription</AlertTitle>
                <AlertDescription className="text-red-300">
                Please choose a plan below to activate your CRM features.
                </AlertDescription>
            </Alert>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {plans.map((plan) => (
                <Card key={plan.id} className={`flex flex-col bg-slate-700 border-slate-600 ${plan.name === 'Professional' ? 'border-blue-500' : ''}`}>
                <CardHeader>
                    <div className="flex justify-between items-center">
                    <h3 className="text-xl font-bold text-slate-100">{plan.name}</h3>
                    {plan.name === 'Professional' && <Crown className="w-5 h-5 text-yellow-500" />}
                    </div>
                    <p className="text-slate-400 text-sm">{plan.description}</p>
                </CardHeader>
                <CardContent className="flex-1">
                    <div className="mb-6">
                    <span className="text-4xl font-bold text-slate-100">${plan.price_monthly}</span>
                    <span className="text-slate-400">/month</span>
                    </div>
                    <ul className="space-y-3">
                    {plan.features?.map((feature, i) => (
                        <li key={i} className="flex items-center gap-3">
                        <Check className="w-5 h-5 text-green-500" />
                        <span className="text-sm text-slate-300">{feature}</span>
                        </li>
                    ))}
                    </ul>
                </CardContent>
                <CardFooter>
                    <Button 
                        className="w-full bg-blue-600 hover:bg-blue-700" 
                        onClick={() => handleSubscribe(plan)}
                        disabled={isProcessing}
                    >
                        {isProcessing ? <Loader2 className="w-4 h-4 animate-spin mr-2" /> : null}
                        Choose Plan
                    </Button>
                </CardFooter>
                </Card>
            ))}
            </div>
        </>
      )}
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { Tenant } from '@/api/entities';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Loader2, Trash2, AlertTriangle } from 'lucide-react';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { deleteTenantWithData } from '@/api/functions';
import { toast } from 'sonner';

export default function ClientOffboarding() {
  const [tenants, setTenants] = useState([]);
  const [selectedTenantId, setSelectedTenantId] = useState("");
  const [deleting, setDeleting] = useState(false);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadTenants();
  }, []);

  const loadTenants = async () => {
    try {
      const tenantList = await Tenant.list();
      setTenants(tenantList);
    } catch (error) {
      console.error("Failed to load tenants:", error);
      toast.error("Failed to load tenants");
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteTenant = async () => {
    if (!selectedTenantId) {
      alert("Please select a tenant first");
      return;
    }

    const tenant = tenants.find(t => t.id === selectedTenantId);
    if (!tenant) {
      alert("Tenant not found");
      return;
    }

    const confirmation1 = confirm(
      ` DELETE ALL DATA FOR "${tenant.name}"?\n\n` +
      `This will permanently delete:\n` +
      ` All Contacts\n` +
      ` All Accounts\n` +
      ` All Leads\n` +
      ` All Opportunities\n` +
      ` All Activities\n` +
      ` All Notes\n` +
      ` All Settings\n` +
      ` The Tenant itself\n\n` +
      `Users will be unassigned (not deleted).\n\n` +
      `This action CANNOT BE UNDONE.`
    );

    if (!confirmation1) return;

    const confirmation2 = prompt(
      `Type the tenant name "${tenant.name}" to confirm deletion:`
    );

    if (confirmation2 !== tenant.name) {
      alert("Tenant name did not match. Deletion cancelled.");
      return;
    }

    setDeleting(true);
    setResult(null);

    try {
      const response = await deleteTenantWithData({ tenantId: selectedTenantId });

      if (response.data.status === 'success') {
        setResult({
          type: 'success',
          message: response.data.message
        });
        setSelectedTenantId("");
        toast.success('Client offboarded successfully');

        // Reload tenant list
        loadTenants();
      } else {
        setResult({
          type: 'error',
          message: response.data.message || 'Deletion failed'
        });
        toast.error('Failed to offboard client');
      }
    } catch (error) {
      console.error("Deletion error:", error);
      setResult({
        type: 'error',
        message: error.message || 'An error occurred during deletion'
      });
      toast.error('An error occurred during offboarding');
    } finally {
      setDeleting(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center p-8">
        <Loader2 className="w-8 h-8 animate-spin text-blue-400" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div>
        <h3 className="text-lg font-semibold text-slate-100">Client Offboarding</h3>
        <p className="text-sm text-slate-400 mt-1">
          Permanently delete all data for clients who are offboarding. This action cannot be undone.
        </p>
      </div>

      {result && (
        <Alert variant={result.type === 'success' ? 'default' : 'destructive'}>
          <AlertTitle>{result.type === 'success' ? 'Success' : 'Error'}</AlertTitle>
          <AlertDescription>{result.message}</AlertDescription>
        </Alert>
      )}

      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="text-slate-100 flex items-center gap-2">
            <Trash2 className="w-5 h-5 text-red-400" />
            Delete Client Data
          </CardTitle>
          <CardDescription className="text-slate-400">
            Permanently delete all data for a client who is offboarding.
            This removes all contacts, accounts, leads, opportunities, activities, and settings.
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div>
            <Label className="text-slate-300 mb-2 block">Select Client to Delete</Label>
            <Select value={selectedTenantId} onValueChange={setSelectedTenantId} disabled={deleting}>
              <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200">
                <SelectValue placeholder="Choose a client..." />
              </SelectTrigger>
              <SelectContent className="bg-slate-800 border-slate-700">
                {tenants.map(tenant => (
                  <SelectItem key={tenant.id} value={tenant.id} className="text-slate-200">
                    {tenant.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <Alert variant="destructive" className="bg-red-900/20 border-red-700">
            <AlertTriangle className="h-4 w-4" />
            <AlertTitle>Warning</AlertTitle>
            <AlertDescription>
              This will permanently delete ALL data for the selected client. This action cannot be undone.
              Users will be unassigned but not deleted.
            </AlertDescription>
          </Alert>

          <Button
            onClick={handleDeleteTenant}
            disabled={!selectedTenantId || deleting}
            variant="destructive"
            className="w-full bg-red-600 hover:bg-red-700"
          >
            {deleting ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Deleting Client Data...
              </>
            ) : (
              <>
                <Trash2 className="w-4 h-4 mr-2" />
                Delete Client & All Data
              </>
            )}
          </Button>
        </CardContent>
      </Card>
    </div>
  );
}

import React, { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Alert, AlertTitle, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/components/ui/use-toast";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import {
  Play,
  CheckCircle,
  XCircle,
  Loader2,
  ListChecks,
  ChevronDown,
  ChevronRight,
  RefreshCw,
  Activity,
  AlertTriangle,
} from "lucide-react";
import { runComponentTests } from "@/api/functions";

const availableComponents = [
  { id: "ContactManagement", name: "Contact Management", description: "Tests contact CRUD operations and data handling.", lastUpdated: "2024-07-20" },
  { id: "AccountManagement", name: "Account Management", description: "Tests account creation, updates, and associations.", lastUpdated: "2024-07-20" },
  { id: "LeadManagement", name: "Lead Management", description: "Tests lead processing and conversion workflows.", lastUpdated: "2024-07-20" },
  { id: "OpportunityManagement", name: "Opportunity Management", description: "Tests sales pipeline and opportunity tracking.", lastUpdated: "2024-07-20" },
  { id: "ActivityTracking", name: "Activity Tracking", description: "Tests activity creation and status management.", lastUpdated: "2024-07-21" },
  { id: "ReportsAndAnalytics", name: "Reports & Analytics", description: "Tests dashboard statistics and reporting accuracy.", lastUpdated: "2024-07-20" },
  { id: "UserTagging", name: "User Tagging", description: "Tests adding, verifying, and removing user tags.", lastUpdated: "2024-07-22" }
];

export default function QATestRunner() {
    const [testResults, setTestResults] = useState(null);
    const [runningTest, setRunningTest] = useState(null);
    const [selectedComponent, setSelectedComponent] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [expandedTests, setExpandedTests] = useState({});
    const [systemStatus, setSystemStatus] = useState(null);
    const [statusLoading, setStatusLoading] = useState(true);
    const [statusError, setStatusError] = useState(null);
    const { toast } = useToast();

    useEffect(() => {
        loadSystemStatus();
    }, []);

    const loadSystemStatus = async () => {
        setStatusLoading(true);
        setStatusError(null);
        try {
            const controller = new AbortController();
            const timeoutId = setTimeout(() => controller.abort(), 10000);

            try {
                const { checkBackendStatus } = await import("@/api/functions");
                const { data } = await checkBackendStatus({}, { signal: controller.signal });

                clearTimeout(timeoutId);
                setSystemStatus(data);
            } catch (functionError) {
                clearTimeout(timeoutId);
                console.warn("Backend status check function not available:", functionError);

                setStatusError("Function not available");
                setSystemStatus({
                    overall_status: 'unknown',
                    authenticated: true, 
                    error: `Backend status check function is not available.`,
                    timestamp: new Date().toISOString(),
                    endpoints: {
                        general: { 
                            status: 'unknown', 
                            details: 'Status check function not deployed or failed to load.' 
                        }
                    }
                });
            }

        } catch (error) {
            console.warn("System status check failed:", error);

            let errorMessage = "Status check unavailable";
            if (error.name === 'AbortError') {
                errorMessage = "Status check timed out";
            } else if (error.message?.includes('Rate limit') || error.response?.status === 429) {
                errorMessage = "Rate limit exceeded";
            } else if (error.response?.status >= 500) {
                errorMessage = "Backend service temporarily unavailable";
            }

            setStatusError(errorMessage);

            setSystemStatus({
                overall_status: 'unknown',
                authenticated: true, 
                error: `Backend status check is not accessible: ${errorMessage}.`,
                timestamp: new Date().toISOString(),
                endpoints: {
                    general: { 
                        status: 'unknown', 
                        details: 'Status check function unavailable or failed.' 
                    }
                }
            });
        } finally {
            setStatusLoading(false);
        }
    };

    const handleRunTests = async () => {
        if (!selectedComponent) {
            toast({
                variant: "destructive",
                title: "No Component Selected",
                description: "Please select a component to test.",
            });
            return;
        }

        setIsLoading(true);
        setRunningTest(selectedComponent);
        setTestResults(null);
        try {
            const { data } = await runComponentTests({ componentName: selectedComponent });

            setTestResults({
                componentName: data.component_name || selectedComponent,
                results: data.report_data || [], // Directly use the results array
                error: data.error || (data.report_data?.length === 0 ? "The test ran but produced no specific result items. This can happen if test dependencies are not met." : null)
            });

            toast({
                title: "Tests Complete",
                description: `${data.component_name || selectedComponent} tests finished.`,
            });
        } catch (error) {
            console.error("Error running component tests:", error);
            toast({
                variant: "destructive",
                title: "Test Execution Error",
                description: error.message || "An unexpected error occurred.",
            });
        } finally {
            setIsLoading(false);
            setRunningTest(null);
        }
    };

    const toggleExpand = (index) => {
        setExpandedTests(prev => ({ ...prev, [index]: !prev[index] }));
    };

    const getStatusColor = (status) => {
        if (status === 'ok' || status === 'healthy') return 'bg-green-500';
        if (status === 'warning' || status === 'degraded') return 'bg-yellow-500';
        if (status === 'error' || status === 'down') return 'bg-red-500';
        return 'bg-gray-500';
    };

    const getStatusText = (status) => {
        if (status === 'ok') return 'Operational';
        if (status === 'unknown') return 'Unknown';
        return status?.charAt(0).toUpperCase() + status?.slice(1) || 'Unknown';
    };

    return (
        <div className="space-y-6">
            {/* System Status Overview */}
            <Card className="bg-slate-800 border-slate-700">
                <CardHeader>
                    <CardTitle className="flex items-center gap-2 text-slate-100">
                        <Activity className="w-5 h-5 text-blue-400" />
                        System Status
                        <Button
                            variant="ghost"
                            size="sm"
                            onClick={loadSystemStatus}
                            disabled={statusLoading}
                            className="p-1 h-auto text-slate-300 hover:text-slate-100 hover:bg-slate-600"
                        >
                            {statusLoading ? (
                                <Loader2 className="w-4 h-4 animate-spin" />
                            ) : (
                                <RefreshCw className="w-4 h-4" />
                            )}
                        </Button>
                    </CardTitle>
                </CardHeader>
                <CardContent>
                    {statusLoading ? (
                        <div className="flex items-center justify-center p-4">
                            <Loader2 className="w-6 h-6 animate-spin mr-2 text-slate-400" />
                            <span className="text-slate-300">Checking system status...</span>
                        </div>
                    ) : statusError ? (
                        <div className="p-4 bg-yellow-900/30 text-yellow-300 border border-yellow-700/50 rounded-md">
                            <div className="flex items-center gap-2 mb-2">
                                <AlertTriangle className="w-4 h-4" />
                                <span className="font-medium">Status Check Issue</span>
                            </div>
                            <p className="text-sm">
                                {statusError}. The application should still function normally. This is often expected in staging or limited deployment environments.
                            </p>
                        </div>
                    ) : systemStatus ? (
                        <div>
                            <div className="flex items-center gap-2 mb-4">
                                <div className={`w-3 h-3 rounded-full ${getStatusColor(systemStatus.overall_status)}`}></div>
                                <span className="text-lg font-semibold text-slate-200">{getStatusText(systemStatus.overall_status)}</span>
                            </div>
                            <p className="text-sm text-slate-400">{systemStatus.error || "All systems operational."}</p>
                        </div>
                    ) : null}
                </CardContent>
            </Card>

            {/* Component Test Runner */}
            <Card className="bg-slate-800 border-slate-700">
                <CardHeader>
                    <CardTitle className="flex items-center gap-2 text-slate-100">
                        <ListChecks className="w-5 h-5 text-blue-400" />
                        Internal Component Test Runner
                    </CardTitle>
                    <CardDescription className="text-slate-400">
                        Run automated tests against key CRM components to verify data handling and rendering.
                    </CardDescription>
                </CardHeader>
                <CardContent>
                    <div className="space-y-4">
                        <div>
                            <Select onValueChange={setSelectedComponent} value={selectedComponent}>
                                <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200">
                                    <SelectValue placeholder="Select a component to test..." />
                                </SelectTrigger>
                                <SelectContent className="bg-slate-800 border-slate-700">
                                    {availableComponents.map(comp => (
                                        <SelectItem key={comp.id} value={comp.id} className="text-slate-200 hover:bg-slate-700">
                                            <div className="flex flex-col">
                                                <span className="font-medium">{comp.name}</span>
                                                <span className="text-xs text-slate-400">{comp.description} (Updated: {comp.lastUpdated})</span>
                                            </div>
                                        </SelectItem>
                                    ))}
                                </SelectContent>
                            </Select>
                        </div>
                        <Button
                            onClick={handleRunTests}
                            disabled={isLoading || !selectedComponent}
                            className="w-full bg-blue-600 hover:bg-blue-700"
                        >
                            {isLoading && runningTest === selectedComponent ? (
                                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                            ) : (
                                <Play className="w-4 h-4 mr-2" />
                            )}
                            {isLoading && runningTest === selectedComponent
                                ? `Running ${selectedComponent} Tests...`
                                : `Test ${selectedComponent || 'Selected'} Module`}
                        </Button>

                        {selectedComponent && (
                            <div className="p-3 bg-blue-900/30 rounded-lg border border-blue-700/50">
                                <h4 className="font-medium text-blue-200">
                                    {availableComponents.find(c => c.id === selectedComponent)?.name}
                                </h4>
                                <p className="text-sm text-blue-300">
                                    {availableComponents.find(c => c.id === selectedComponent)?.description}
                                </p>
                            </div>
                        )}
                    </div>
                </CardContent>
            </Card>

            {/* Test Results */}
            {testResults && (
                <Card className="bg-slate-800 border-slate-700">
                    <CardHeader>
                        <CardTitle className="text-slate-100">Test Results for: {testResults.componentName}</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-3">
                        {testResults.results && Array.isArray(testResults.results) && testResults.results.length > 0 ? (
                            testResults.results.map((result, index) => (
                                <Card key={index} className="bg-slate-700 border-slate-600">
                                    <CardHeader
                                        className="flex flex-row items-center justify-between cursor-pointer p-4"
                                        onClick={() => toggleExpand(index)}
                                    >
                                        <div className="flex items-center gap-3">
                                            {result.status === "Success" ? (
                                                <CheckCircle className="w-5 h-5 text-green-500" />
                                            ) : result.status === "Error" ? (
                                                <XCircle className="w-5 h-5 text-red-500" />
                                            ) : result.status === "Warning" ? (
                                                <AlertTriangle className="w-5 h-5 text-yellow-500" />
                                            ) : (
                                                <></>
                                            )}
                                            <p className="font-medium text-slate-200">{result.entity ? `${result.entity}: ` : ''}{typeof result.details === 'string' && result.details.length > 100 ? result.details.substring(0, 100) + '...' : typeof result.details === 'string' ? result.details : JSON.stringify(result.details)}</p>
                                        </div>
                                        <div className="flex items-center gap-3">
                                            <Badge variant={result.status === "Success" ? "default" : result.status === "Error" ? "destructive" : "secondary"} className="capitalize">
                                                {result.status}
                                            </Badge>
                                            {expandedTests[index] ? <ChevronDown className="w-4 h-4 text-slate-300" /> : <ChevronRight className="w-4 h-4 text-slate-300" />}
                                        </div>
                                    </CardHeader>
                                    {expandedTests[index] && (
                                        <CardContent className="p-4 pt-0">
                                            <pre className="text-xs bg-slate-900 text-slate-200 p-3 rounded-md overflow-x-auto">
                                                {JSON.stringify(result.details, null, 2)}
                                            </pre>
                                        </CardContent>
                                    )}
                                </Card>
                            ))
                        ) : (
                            <div className="text-center py-4">
                                <p className="text-slate-400">No test results available or invalid test data format.</p>
                                {testResults.error && (
                                    <Alert variant="default" className="mt-4 text-left bg-blue-900/30 border-blue-700/50">
                                        <AlertTriangle className="h-4 w-4 text-blue-400" />
                                        <AlertTitle className="text-blue-200">Note</AlertTitle>
                                        <AlertDescription className="text-blue-300">
                                            {testResults.error}
                                        </AlertDescription>
                                    </Alert>
                                )}
                            </div>
                        )}
                    </CardContent>
                </Card>
            )}
        </div>
    );
}

