import { Switch } from '@/components/ui/switch';
import { Loader2, Send } from 'lucide-react';
import { useToast } from '@/components/ui/use-toast';
import { inviteUser } from '@/api/functions';

export default function InviteUserDialog({ open, onOpenChange, onSuccess, tenants, currentUser }) {
  const { toast } = useToast();
  const [formData, setFormData] = useState({
    email: '',
    full_name: '',
    role: 'user',
    employee_role: '', // NEW: Add employee role
    tenant_id: '',
    can_use_softphone: false,
    access_level: 'read_write',
    phone: '',
    navigation_permissions: {},
  });
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.email || !formData.full_name || !formData.role) {
      toast({ variant: "destructive", title: "Validation Error", description: "Email, name, and role are required." });
      return;
    }

    setSubmitting(true);
    try {
      const payload = {
        email: formData.email,
        full_name: formData.full_name,
        role: formData.role,
        employee_role: formData.employee_role === 'none' ? null : formData.employee_role || null, // NEW: Handle 'none' value
        tenant_id: formData.tenant_id || null,
        requested_access: formData.access_level || 'read_write',
        can_use_softphone: formData.can_use_softphone || false,
        phone: formData.phone || null,
        permissions: {
          navigation_permissions: formData.navigation_permissions || {}
        }
      };

      const response = await inviteUser(payload);

      if (response?.status === 200 && response?.data?.success) {
        const data = response.data;

        if (data.requires_manual_invite) {
          toast({ 
            title: "Invitation Notifications Sent", 
            description: `${formData.email} will receive a welcome email. Please complete the invite via base44 platform (Dashboard  Users  Invite User).`,
            duration: 8000
          });
        } else {
          toast({ 
            title: "User Invited Successfully", 
            description: data.message || `${formData.email} has been added to the system.`
          });
        }

        onOpenChange(false);
        if (onSuccess) onSuccess();
      } else {
        const errorMsg = response?.data?.error || response?.data?.message || 'Failed to process invitation';
        toast({ 
          variant: "destructive", 
          title: "Invitation Failed", 
          description: errorMsg 
        });
      }
    } catch (error) {
      console.error('Error inviting user:', error);
      toast({ 
        variant: "destructive", 
        title: "Error", 
        description: error?.message || 'An error occurred'
      });
    } finally {
      setSubmitting(false);
    }
  };

  const onCancel = () => {
    setFormData({
      email: '',
      full_name: '',
      role: 'user',
      employee_role: '', // NEW
      tenant_id: '',
      can_use_softphone: false,
      access_level: 'read_write',
      phone: '',
      navigation_permissions: {},
    });
    onOpenChange(false);
  };

  const handleNavigationChange = (permissionKey, value) => {
    setFormData(prev => ({
      ...prev,
      navigation_permissions: {
        ...prev.navigation_permissions,
        [permissionKey]: value,
      },
    }));
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto bg-slate-800 border-slate-700">
        <DialogHeader>
          <DialogTitle className="text-slate-100">Invite New User</DialogTitle>
          <DialogDescription className="text-slate-400">
            Send an invitation to join your CRM workspace
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="email" className="text-slate-200">Email address</Label>
            <Input
              id="email"
              type="email"
              value={formData.email}
              onChange={(e) => setFormData(prev => ({ ...prev, email: e.target.value }))}
              placeholder="Enter email address"
              required
              className="bg-slate-700 border-slate-600 text-slate-200 placeholder:text-slate-400"
            />
          </div>

          <div>
            <Label htmlFor="full_name" className="text-slate-200">Full Name</Label>
            <Input
              id="full_name"
              type="text"
              value={formData.full_name}
              onChange={(e) => setFormData(prev => ({ ...prev, full_name: e.target.value }))}
              placeholder="Enter full name"
              required
              className="bg-slate-700 border-slate-600 text-slate-200 placeholder:text-slate-400"
            />
          </div>

          <div>
            <Label htmlFor="phone" className="text-slate-200">Phone Number (Optional)</Label>
            <Input
              id="phone"
              type="tel"
              value={formData.phone}
              onChange={(e) => setFormData(prev => ({ ...prev, phone: e.target.value }))}
              placeholder="+1234567890"
              className="bg-slate-700 border-slate-600 text-slate-200 placeholder:text-slate-400"
            />
            <p className="text-xs text-slate-500 mt-1">Include country code for SMS notification (e.g., +1 for US)</p>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="role" className="text-slate-200">Role</Label>
              <Select value={formData.role} onValueChange={(value) => setFormData(prev => ({ ...prev, role: value }))}>
                <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200">
                  <SelectValue placeholder="Select access level" />
                </SelectTrigger>
                <SelectContent className="bg-slate-800 border-slate-600 text-slate-200">
                  <SelectItem value="admin">Admin - Can manage the app</SelectItem>
                  <SelectItem value="power-user">Power User - Advanced features</SelectItem>
                  <SelectItem value="user">User - Can use the app</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="employee_role" className="text-slate-200">Employee Role</Label>
              <Select 
                value={formData.employee_role || 'none'} 
                onValueChange={(value) => setFormData(prev => ({ ...prev, employee_role: value === 'none' ? '' : value }))}
              >
                <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200">
                  <SelectValue placeholder="Select employee role" />
                </SelectTrigger>
                <SelectContent className="bg-slate-800 border-slate-600 text-slate-200">
                  <SelectItem value="none">None (Use Base44 Role)</SelectItem>
                  <SelectItem value="manager">Manager (Full Visibility)</SelectItem>
                  <SelectItem value="employee">Employee (Own Records)</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          {tenants && tenants.length > 0 && (
            <div>
              <Label htmlFor="tenant" className="text-slate-200">Client (Optional)</Label>
              <Select value={formData.tenant_id} onValueChange={(value) => setFormData(prev => ({ ...prev, tenant_id: value }))}>
                <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200">
                  <SelectValue placeholder="Select client" />
                </SelectTrigger>
                <SelectContent className="bg-slate-800 border-slate-600 text-slate-200">
                  <SelectItem value="no-client">No specific client</SelectItem>
                  {tenants.map(tenant => (
                    <SelectItem key={tenant.id} value={tenant.id}>
                      {tenant.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          )}

          <div>
            <Label htmlFor="access_level" className="text-slate-200">Access Level</Label>
            <Select value={formData.access_level} onValueChange={(value) => setFormData(prev => ({ ...prev, access_level: value }))}>
              <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200">
                <SelectValue placeholder="Select access level" />
              </SelectTrigger>
              <SelectContent className="bg-slate-800 border-slate-600 text-slate-200">
                <SelectItem value="read_write">Read & Write</SelectItem>
                <SelectItem value="read_only">Read Only</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="flex items-center space-x-2">
            <Switch
              id="can_use_softphone"
              checked={formData.can_use_softphone}
              onCheckedChange={(checked) => setFormData(prev => ({ ...prev, can_use_softphone: checked }))}
              className="data-[state=checked]:bg-orange-500"
            />
            <Label htmlFor="can_use_softphone" className="text-slate-200">Can use Softphone</Label>
          </div>

          <div className="p-3 bg-slate-700 border border-slate-600 rounded-lg text-slate-300">
            <h3 className="font-semibold mb-2">Navigation Permissions (Advanced)</h3>
            <p className="text-sm text-slate-400">
              Granular control over specific application routes. (UI not fully implemented in this example)
            </p>
            {/* Example: A simple checkbox for a specific nav item */}
            {/* <div className="flex items-center space-x-2 mt-2">
              <Switch
                id="nav_dashboard"
                checked={formData.navigation_permissions.dashboard || false}
                onCheckedChange={(checked) => handleNavigationChange('dashboard', checked)}
              />
              <Label htmlFor="nav_dashboard">Access Dashboard</Label>
            </div> */}
          </div>

          <DialogFooter>
            <Button
              type="button"
              variant="outline"
              onClick={onCancel}
              disabled={submitting}
              className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={submitting}
              className="bg-orange-500 hover:bg-orange-600 text-white"
            >
              {submitting ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Sending...
                </>
              ) : (
                <>
                  <Send className="w-4 h-4 mr-2" />
                  Send Invitation
                </>
              )}
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  Brain, 
  Loader2, 
  RefreshCw, 
  TrendingUp, 
  DollarSign,
  Users,
  Zap,
  AlertTriangle,
  CheckCircle
} from "lucide-react";
import { Tenant, TenantIntegration } from "@/api/entities";
import { Alert, AlertDescription } from "@/components/ui/alert";

export default function LLMUsageMonitor() {
  const [tenants, setTenants] = useState([]);
  const [integrations, setIntegrations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalTenants: 0,
    tenantsWithOwnLLM: 0,
    tenantsUsingSystemLLM: 0,
    llmTypes: {}
  });

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    setLoading(true);
    try {
      const [tenantsData, integrationsData] = await Promise.all([
        Tenant.list(),
        TenantIntegration.filter({
          integration_type: ['openai_llm', 'anthropic_llm', 'azure_openai_llm']
        })
      ]);

      setTenants(tenantsData);
      setIntegrations(integrationsData);

      // Calculate statistics
      const activeLLMIntegrations = integrationsData.filter(i => i.is_active);
      const tenantsWithLLM = new Set(activeLLMIntegrations.map(i => i.tenant_id));

      const llmTypeCounts = {};
      activeLLMIntegrations.forEach(integration => {
        llmTypeCounts[integration.integration_type] = (llmTypeCounts[integration.integration_type] || 0) + 1;
      });

      setStats({
        totalTenants: tenantsData.length,
        tenantsWithOwnLLM: tenantsWithLLM.size,
        tenantsUsingSystemLLM: tenantsData.length - tenantsWithLLM.size,
        llmTypes: llmTypeCounts
      });

    } catch (error) {
      console.error('Error loading LLM usage data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getTenantLLMInfo = (tenantId) => {
    const tenantIntegrations = integrations.filter(i => 
      i.tenant_id === tenantId && i.is_active
    );

    if (tenantIntegrations.length === 0) {
      return { type: 'system', provider: 'System Default', status: 'using_system' };
    }

    const integration = tenantIntegrations[0]; // Use first active integration
    return {
      type: 'tenant',
      provider: integration.integration_type.replace('_', ' ').replace('llm', 'LLM').toUpperCase(),
      status: integration.sync_status,
      lastSync: integration.last_sync,
      integrationName: integration.integration_name
    };
  };

  const getStatusBadge = (status, type) => {
    if (type === 'system') {
      return <Badge className="bg-blue-700 text-blue-100 hover:bg-blue-700">Using Your Credits</Badge>;
    }

    switch (status) {
      case 'connected':
        return <Badge className="bg-green-700 text-green-100 hover:bg-green-700">Own API Key</Badge>;
      case 'error':
        return <Badge variant="destructive">Error</Badge>;
      case 'pending':
        return <Badge variant="secondary" className="bg-slate-600 text-slate-200 hover:bg-slate-600">Configuring</Badge>;
      case 'disconnected':
        return <Badge variant="outline" className="border-slate-500 text-slate-300">Disconnected</Badge>;
      default:
        return <Badge variant="secondary" className="bg-slate-600 text-slate-200 hover:bg-slate-600">{status}</Badge>;
    }
  };

  if (loading) {
    return (
      <Card className="bg-slate-800 border-slate-700">
        <CardContent className="p-8 text-center">
          <Loader2 className="w-8 h-8 animate-spin mx-auto mb-4 text-slate-400" />
          <p className="text-slate-300">Loading LLM usage data...</p>
        </CardContent>
      </Card>
    );
  }

  const selfServiceRate = stats.totalTenants > 0 ? Math.round((stats.tenantsWithOwnLLM / stats.totalTenants) * 100) : 0;

  return (
    <div className="space-y-6">
      {/* Overview Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card className="bg-slate-800 border-slate-700">
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <Users className="w-5 h-5 text-blue-600" />
              <div>
                <p className="text-2xl font-bold text-slate-100">{stats.totalTenants}</p>
                <p className="text-sm text-slate-400">Total Tenants</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="bg-slate-800 border-slate-700">
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <CheckCircle className="w-5 h-5 text-green-600" />
              <div>
                <p className="text-2xl font-bold text-slate-100">{stats.tenantsWithOwnLLM}</p>
                <p className="text-sm text-slate-400">Own API Keys</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="bg-slate-800 border-slate-700">
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <DollarSign className="w-5 h-5 text-amber-600" />
              <div>
                <p className="text-2xl font-bold text-slate-100">{stats.tenantsUsingSystemLLM}</p>
                <p className="text-sm text-slate-400">Using Your Credits</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="bg-slate-800 border-slate-700">
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <TrendingUp className="w-5 h-5 text-slate-600" />
              <div>
                <p className="text-2xl font-bold text-slate-100">{selfServiceRate}%</p>
                <p className="text-sm text-slate-400">Self-Service Rate</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Cost Impact Alert */}
      {stats.tenantsUsingSystemLLM > 0 && (
        <Alert className="border-amber-600/50 bg-amber-900/30">
          <AlertTriangle className="h-4 w-4 text-amber-400" />
          <AlertDescription className="text-amber-300">
            <strong>{stats.tenantsUsingSystemLLM} tenants</strong> are currently using your system LLM credits. 
            Consider encouraging them to set up their own API keys in <strong>Settings &gt; Integrations</strong> to reduce your costs.
          </AlertDescription>
        </Alert>
      )}

      {/* Main Data Table */}
      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <div className="flex justify-between items-center">
            <CardTitle className="flex items-center gap-2 text-slate-100">
              <Brain className="w-5 h-5 text-purple-600" />
              LLM Usage by Tenant
            </CardTitle>
            <Button variant="outline" size="sm" onClick={loadData} className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600">
              <RefreshCw className="w-4 h-4 mr-2" />
              Refresh
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow className="border-slate-700">
                <TableHead className="text-slate-300">Tenant</TableHead>
                <TableHead className="text-slate-300">LLM Provider</TableHead>
                <TableHead className="text-slate-300">Status</TableHead>
                <TableHead className="text-slate-300">Configuration</TableHead>
                <TableHead className="text-slate-300">Cost Impact</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {tenants.map(tenant => {
                const llmInfo = getTenantLLMInfo(tenant.id);
                return (
                  <TableRow key={tenant.id} className="border-slate-700">
                    <TableCell>
                      <div>
                        <div className="font-medium text-slate-200">{tenant.name}</div>
                        {tenant.domain && (
                          <div className="text-sm text-slate-400">{tenant.domain}</div>
                        )}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <Brain className="w-4 h-4 text-slate-400" />
                        <span className="text-slate-200">{llmInfo.provider}</span>
                      </div>
                    </TableCell>
                    <TableCell>
                      {getStatusBadge(llmInfo.status, llmInfo.type)}
                    </TableCell>
                    <TableCell>
                      {llmInfo.type === 'tenant' ? (
                        <div className="text-sm">
                          <div className="text-slate-200">{llmInfo.integrationName}</div>
                          {llmInfo.lastSync && (
                            <div className="text-slate-400">
                              Last sync: {new Date(llmInfo.lastSync).toLocaleDateString()}
                            </div>
                          )}
                        </div>
                      ) : (
                        <div className="text-sm text-slate-400">Default system configuration</div>
                      )}
                    </TableCell>
                    <TableCell>
                      {llmInfo.type === 'system' ? (
                        <div className="flex items-center gap-1 text-amber-400">
                          <DollarSign className="w-3 h-3" />
                          <span className="text-sm">Your cost</span>
                        </div>
                      ) : (
                        <div className="flex items-center gap-1 text-green-400">
                          <CheckCircle className="w-3 h-3" />
                          <span className="text-sm">Their cost</span>
                        </div>
                      )}
                    </TableCell>
                  </TableRow>
                );
              })}
              {tenants.length === 0 && (
                <TableRow>
                  <TableCell colSpan={5} className="text-center py-8 text-slate-400">
                    No tenants found.
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* LLM Provider Breakdown */}
      {Object.keys(stats.llmTypes).length > 0 && (
        <Card className="bg-slate-800 border-slate-700">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-slate-100">
              <Zap className="w-5 h-5 text-blue-600" />
              LLM Provider Breakdown
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {Object.entries(stats.llmTypes).map(([provider, count]) => (
                <div key={provider} className="p-4 border rounded-lg border-slate-600 bg-slate-700/30">
                  <div className="text-lg font-semibold text-slate-200">
                    {provider.replace('_', ' ').replace('llm', 'LLM').toUpperCase()}
                  </div>
                  <div className="text-2xl font-bold text-blue-400">{count}</div>
                  <div className="text-sm text-slate-400">
                    {count === 1 ? 'tenant' : 'tenants'}
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

import React, { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Loader2, Search, AlertCircle, CheckCircle2 } from "lucide-react";
import { diagnoseLeadVisibility } from "@/api/functions";
import { fixLeadVisibility } from "@/api/functions";
import { toast } from "sonner";

export default function LeadVisibilityDiagnostic() {
  const [leadId, setLeadId] = useState("");
  const [userEmail, setUserEmail] = useState("andrei.byfield@gmail.com");
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [fixing, setFixing] = useState(false);

  const handleDiagnose = async () => {
    if (!leadId || !userEmail) {
      toast.error("Please enter both lead ID and user email");
      return;
    }

    setLoading(true);
    setResult(null);

    try {
      const response = await diagnoseLeadVisibility({ leadId, userEmail });

      if (response.data) {
        setResult(response.data);
      } else {
        toast.error("Failed to diagnose lead visibility");
      }
    } catch (error) {
      console.error("Error diagnosing:", error);
      toast.error(error.message || "Failed to diagnose");
    } finally {
      setLoading(false);
    }
  };

  const handleFix = async () => {
    if (!result) {
      toast.error("Run diagnosis first");
      return;
    }

    // Get the CORRECT tenant ID (from the lead)
    const correctTenantId = result.lead_info.tenant_id;

    if (correctTenantId === 'NOT SET ') {
      toast.error("Lead has no tenant_id set");
      return;
    }

    setFixing(true);
    try {
      const response = await fixLeadVisibility({
        leadId: result.lead_info.id, // Use lead ID from the result
        userEmail: result.target_user_info.email, // Use user email from the result
        tenantId: correctTenantId
      });

      if (response.data?.success) {
        toast.success("Fixed! User's tenant_id updated to match the lead.");
        // Re-diagnose to confirm
        setTimeout(() => handleDiagnose(), 1000);
      } else {
        toast.error("Failed to apply fixes");
      }
    } catch (error) {
      console.error("Error fixing:", error);
      toast.error(error.message || "Failed to fix");
    } finally {
      setFixing(false);
    }
  };

  return (
    <Card className="bg-slate-800 border-slate-700">
      <CardHeader>
        <CardTitle className="text-slate-100">Lead Visibility Diagnostic</CardTitle>
        <p className="text-sm text-slate-400">
          Diagnose why a user cannot see a specific lead
        </p>
      </CardHeader>
      <CardContent className="space-y-4">
        <div>
          <Label className="text-slate-300">Lead ID</Label>
          <Input
            value={leadId}
            onChange={(e) => setLeadId(e.target.value)}
            placeholder="Enter lead ID or unique_id (e.g., LEAD-000001)"
            className="bg-slate-700 border-slate-600 text-slate-200"
          />
        </div>

        <div>
          <Label className="text-slate-300">User Email</Label>
          <Input
            value={userEmail}
            onChange={(e) => setUserEmail(e.target.value)}
            placeholder="user@example.com"
            className="bg-slate-700 border-slate-600 text-slate-200"
          />
        </div>

        <Button
          onClick={handleDiagnose}
          disabled={loading}
          className="bg-blue-600 hover:bg-blue-700 w-full"
        >
          {loading ? (
            <>
              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
              Diagnosing...
            </>
          ) : (
            <>
              <Search className="w-4 h-4 mr-2" />
              Diagnose
            </>
          )}
        </Button>

        {result && (
