            console.error('Error checking R2 config:', error);
            toast.error('Failed to check R2 configuration: ' + error.message);
        } finally {
            setChecking(false);
        }
    };

    return (
        <Card>
            <CardHeader>
                <CardTitle className="flex items-center gap-2">
                    <RefreshCw className="w-5 h-5 text-blue-600" />
