export default function SyncHealthMonitor({ tenantId = null }) {
  const [healthLogs, setHealthLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  // Removed stats state as it's no longer used in the new UI

  const loadHealthData = useCallback(async () => {
    setLoading(true);
    try {
      // Load last 30 sync health records
      const filter = tenantId ? { tenant_id: tenantId } : {};
      const logs = await SyncHealth.filter(filter, '-start_time', 30);
      setHealthLogs(logs);

      // Removed statistics calculation as it's no longer used
    } catch (error) {
      console.error("Failed to load sync health data:", error);
    } finally {
      setLoading(false);
    }
  }, [tenantId]);

  useEffect(() => {
    loadHealthData();
  }, [loadHealthData]);

  const getStatusBadge = (status) => {
    let classes = "";
    switch (status) {
      case 'completed': classes = 'bg-emerald-900/30 text-emerald-400 border-emerald-700/50'; break;
      case 'partial': classes = 'bg-yellow-900/30 text-yellow-400 border-yellow-700/50'; break;
      case 'failed': classes = 'bg-red-900/30 text-red-400 border-red-700/50'; break;
      case 'running': classes = 'bg-blue-900/30 text-blue-400 border-blue-700/50'; break;
      default: classes = 'bg-slate-700 text-slate-300 border-slate-600'; break;
    }
    return <Badge variant="outline" className={classes}>{status}</Badge>;
  };

  // Removed chartData preparation as charts are no longer used

  return (
    <div className="space-y-6">
      <Alert className="bg-blue-900/30 border-blue-700/50">
        <Activity className="h-4 w-4 text-blue-400" />
        <AlertDescription className="text-blue-300">
          Monitor automated data synchronization jobs and their health status across all tenants.
        </AlertDescription>
      </Alert>

      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-slate-100">
            <RefreshCw className="w-5 h-5 text-emerald-400" />
            Recent Sync Operations
          </CardTitle>
          <CardDescription className="text-slate-400">
            Last 10 synchronization jobs
          </CardDescription>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="flex justify-center p-8">
              <Loader2 className="w-6 h-6 animate-spin text-blue-400" />
            </div>
          ) : healthLogs.length === 0 ? (
            <div className="text-center p-8 text-slate-400">
              No sync operations recorded yet
            </div>
          ) : (
            <div className="space-y-3">
              {healthLogs.slice(0, 10).map((log) => ( // Limiting to last 10 as per CardDescription
                <div key={log.id} className="p-4 bg-slate-900 rounded-lg border border-slate-700">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center gap-3">
                        <Badge variant="outline" className="bg-slate-700 text-slate-300 border-slate-600">
                          {log.mode || "N/A"} {/* Using log.mode instead of log.sync_type */}
                        </Badge>
                        {getStatusBadge(log.status)}
                      </div>
                      <div className="mt-2 text-sm text-slate-300">
                        {log.records_updated || 0} records updated
                        {/* Assuming records_processed maps to records_updated as per entity structure */}
                      </div>
                      <div className="mt-1 text-xs text-slate-500">
                        Duration: {log.duration_ms ? `${log.duration_ms}ms` : 'N/A'} | {new Date(log.start_time).toLocaleString()}
                      </div>
                    </div>
                  </div>
                  {log.error_count > 0 && (
                    <Alert className="mt-3 bg-red-900/30 border-red-700/50">
                      <AlertCircle className="h-4 w-4 text-red-400" />
                      <AlertDescription className="text-red-300">
                        {log.error_count} error(s) occurred during sync
                      </AlertDescription>
                    </Alert>
                  )}
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

import React, { useState, useEffect, useCallback } from "react";
import { Announcement, Tenant, User, Notification } from "@/api/entities"; // Added User and Notification
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/components/ui/use-toast";
import { Plus, Edit, Trash2, Megaphone, Loader2, Info, AlertTriangle, ShieldAlert } from "lucide-react";

export default function SystemAnnouncements() {
    const [announcements, setAnnouncements] = useState([]);
    const [tenants, setTenants] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showForm, setShowForm] = useState(false);
    const [editingAnnouncement, setEditingAnnouncement] = useState(null);
    const [newAnnouncement, setNewAnnouncement] = useState({
        title: "",
        message: "",
        type: "info",
        target_tenant_id: "all",
        is_active: true,
    });
    const { toast } = useToast();

    const loadData = useCallback(async () => {
        setLoading(true);
        try {
            const [announcementsData, tenantsData] = await Promise.all([
                Announcement.list("-created_date"),
                Tenant.list()
            ]);
            setAnnouncements(announcementsData);
            setTenants(tenantsData);
        } catch (error) {
            toast({ variant: "destructive", title: "Failed to load data", description: error.message });
        } finally {
            setLoading(false);
        }
    }, [toast]);

    useEffect(() => {
        loadData();
    }, [loadData]);

    const handleInputChange = (field, value) => {
        if (editingAnnouncement) {
            setEditingAnnouncement(prev => ({ ...prev, [field]: value }));
        } else {
            setNewAnnouncement(prev => ({ ...prev, [field]: value }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const data = editingAnnouncement || newAnnouncement;
        try {
            if (editingAnnouncement) {
                await Announcement.update(editingAnnouncement.id, data);
                toast({ title: "Success", description: "Announcement updated." });
            } else {
                const newAnn = await Announcement.create(data);
                toast({ title: "Success", description: "Announcement created." });

                // After creating the announcement, create notifications for users
                try {
                    let targetUsers = [];
                    if (data.target_tenant_id === 'all') {
                        targetUsers = await User.list();
                    } else {
                        targetUsers = await User.filter({ tenant_id: data.target_tenant_id });
                    }

                    if (targetUsers.length > 0) {
                        const notificationRecords = targetUsers.map(user => ({
                            user_email: user.email,
                            title: ` ${data.title}`,
                            description: data.message.substring(0, 100) + (data.message.length > 100 ? '...' : ''),
                            link: '/Dashboard', // Link to dashboard when clicked
                            icon: 'Megaphone'
                        }));
                        await Notification.bulkCreate(notificationRecords);
                        console.log(`Created ${notificationRecords.length} notifications for announcement.`);
                    }
                } catch (notificationError) {
                    console.error("Failed to create notifications for announcement:", notificationError);
                    // Don't block the main success message, but log the error.
                    toast({
                        variant: "destructive",
                        title: "Notification Failure",
                        description: "The announcement was created, but failed to send notifications to users."
                    });
                }
            }
            resetForm();
            loadData();
        } catch (error) {
            toast({ variant: "destructive", title: "Operation Failed", description: error.message });
        }
    };

    const handleEdit = (announcement) => {
        setEditingAnnouncement(announcement);
        setShowForm(true);
    };

    const handleDelete = async (id) => {
        if (confirm("Are you sure you want to delete this announcement?")) {
            try {
                await Announcement.delete(id);
                toast({ title: "Success", description: "Announcement deleted." });
                loadData();
            } catch (error) {
                toast({ variant: "destructive", title: "Delete Failed", description: error.message });
            }
        }
    };

    const handleToggleActive = async (announcement) => {
        try {
            await Announcement.update(announcement.id, { is_active: !announcement.is_active });
            toast({ title: "Status Updated" });
            loadData();
        } catch(error) {
            toast({ variant: "destructive", title: "Update Failed", description: error.message });
        }
    };

    const resetForm = () => {
        setShowForm(false);
        setEditingAnnouncement(null);
        setNewAnnouncement({ title: "", message: "", type: "info", target_tenant_id: "all", is_active: true });
    };

    const typeConfig = {
        info: { color: "bg-blue-100 text-blue-800", icon: Info },
        warning: { color: "bg-yellow-100 text-yellow-800", icon: AlertTriangle },
        critical: { color: "bg-red-100 text-red-800", icon: ShieldAlert },
    };

    return (
        <div className="space-y-6">
            <Card className="shadow-lg bg-slate-800 border-slate-700">
                <CardHeader>
                    <CardTitle className="flex items-center gap-2 text-slate-100">
                        <Megaphone className="w-6 h-6 text-blue-400" />
                        System Announcements
                    </CardTitle>
                    <CardDescription className="text-slate-400">Create and manage global or tenant-specific announcements.</CardDescription>
                </CardHeader>
                <CardContent>
                    <Button onClick={() => { setShowForm(!showForm); setEditingAnnouncement(null); }} className="bg-blue-600 hover:bg-blue-700">
                        <Plus className="w-4 h-4 mr-2" /> {showForm ? "Cancel" : "Create New Announcement"}
                    </Button>

                    {showForm && (
                        <form onSubmit={handleSubmit} className="mt-6 space-y-4 p-4 border rounded-lg bg-slate-700/50 border-slate-600">
                            <h3 className="font-medium text-lg text-slate-100">{editingAnnouncement ? "Edit Announcement" : "New Announcement"}</h3>
                            <div>
                                <Label htmlFor="title" className="text-slate-200">Title</Label>
                                <Input id="title" value={editingAnnouncement?.title || newAnnouncement.title} onChange={(e) => handleInputChange('title', e.target.value)} required className="bg-slate-700 border-slate-600 text-slate-200 placeholder:text-slate-400 focus:border-slate-500" />
                            </div>
                            <div>
                                <Label htmlFor="message" className="text-slate-200">Message</Label>
                                <Textarea id="message" value={editingAnnouncement?.message || newAnnouncement.message} onChange={(e) => handleInputChange('message', e.target.value)} required className="bg-slate-700 border-slate-600 text-slate-200 placeholder:text-slate-400 focus:border-slate-500" />
                            </div>
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                <div>
                                    <Label htmlFor="type" className="text-slate-200">Type</Label>
                                    <Select value={editingAnnouncement?.type || newAnnouncement.type} onValueChange={(value) => handleInputChange('type', value)}>
                                        <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200">
                                            <SelectValue />
                                        </SelectTrigger>
                                        <SelectContent className="bg-slate-800 border-slate-700">
                                            <SelectItem value="info" className="text-slate-200 hover:bg-slate-700 focus:bg-slate-700">Info</SelectItem>
                                            <SelectItem value="warning" className="text-slate-200 hover:bg-slate-700 focus:bg-slate-700">Warning</SelectItem>
                                            <SelectItem value="critical" className="text-slate-200 hover:bg-slate-700 focus:bg-slate-700">Critical</SelectItem>
                                        </SelectContent>
                                    </Select>
                                </div>
                                <div>
                                    <Label htmlFor="target" className="text-slate-200">Target</Label>
                                    <Select value={editingAnnouncement?.target_tenant_id || newAnnouncement.target_tenant_id} onValueChange={(value) => handleInputChange('target_tenant_id', value)}>
                                        <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200">
                                            <SelectValue />
                                        </SelectTrigger>
                                        <SelectContent className="bg-slate-800 border-slate-700">
                                            <SelectItem value="all" className="text-slate-200 hover:bg-slate-700 focus:bg-slate-700">All Tenants</SelectItem>
                                            {tenants.map(t => <SelectItem key={t.id} value={t.id} className="text-slate-200 hover:bg-slate-700 focus:bg-slate-700">{t.name}</SelectItem>)}
                                        </SelectContent>
                                    </Select>
                                </div>
                            </div>
                            <div className="flex items-center justify-between">
                                <Button type="submit" className="bg-blue-600 hover:bg-blue-700">{editingAnnouncement ? "Update Announcement" : "Create Announcement"}</Button>
                                <div className="flex items-center space-x-2">
                                    <Label htmlFor="is_active" className="text-slate-200">Active</Label>
                                    <Switch id="is_active" checked={editingAnnouncement?.is_active ?? newAnnouncement.is_active} onCheckedChange={(checked) => handleInputChange('is_active', checked)} />
                                </div>
                            </div>
                        </form>
                    )}
                </CardContent>
            </Card>

            <Card className="shadow-lg bg-slate-800 border-slate-700">
                <CardHeader><CardTitle className="text-slate-100">Existing Announcements</CardTitle></CardHeader>
                <CardContent>
                    {loading ? (
                        <div className="flex justify-center items-center py-8"><Loader2 className="w-8 h-8 animate-spin text-blue-400" /></div>
                    ) : (
                        <Table>
                            <TableHeader>
                                <TableRow className="border-slate-700">
                                    <TableHead className="text-slate-300">Status</TableHead>
                                    <TableHead className="text-slate-300">Type</TableHead>
                                    <TableHead className="text-slate-300">Title</TableHead>
                                    <TableHead className="text-slate-300">Target</TableHead>
                                    <TableHead className="text-slate-300">Actions</TableHead>
                                </TableRow>
                            </TableHeader>
                            <TableBody>
                                {announcements.map(ann => {
                                    const TypeIcon = typeConfig[ann.type].icon;
                                    return (
                                        <TableRow key={ann.id} className="border-slate-700">
                                            <TableCell>
                                                <Switch checked={ann.is_active} onCheckedChange={() => handleToggleActive(ann)} />
                                            </TableCell>
                                            <TableCell>
                                                <Badge className={typeConfig[ann.type].color}>
                                                    <TypeIcon className="w-4 h-4 mr-1.5" />
                                                    {ann.type}
                                                </Badge>
                                            </TableCell>
                                            <TableCell>
                                                <p className="font-medium text-slate-200">{ann.title}</p>
                                                <p className="text-sm text-slate-400 truncate max-w-xs">{ann.message}</p>
                                            </TableCell>
                                            <TableCell className="text-slate-200">{ann.target_tenant_id === 'all' ? 'All Tenants' : tenants.find(t => t.id === ann.target_tenant_id)?.name || 'N/A'}</TableCell>
                                            <TableCell className="space-x-2">
                                                <Button variant="ghost" size="icon" onClick={() => handleEdit(ann)} className="text-slate-400 hover:text-slate-200 hover:bg-slate-700"><Edit className="w-4 h-4" /></Button>
                                                <Button variant="ghost" size="icon" className="text-red-400 hover:text-red-300 hover:bg-slate-700" onClick={() => handleDelete(ann.id)}><Trash2 className="w-4 h-4" /></Button>
                                            </TableCell>
                                        </TableRow>
                                    );
                                })}
                            </TableBody>
                        </Table>
                    )}
                </CardContent>
            </Card>
        </div>
    );
}

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription, AlertTitle } from "@/components/ui/alert";
import { 
  Activity, 
  AlertCircle, 
  CheckCircle2, 
  XCircle, 
  Clock,
  RefreshCw,
  TrendingUp,
  Database
} from "lucide-react";
import { useErrorLog } from "../shared/ErrorLogger";

const BACKEND_URL = import.meta.env.VITE_BACKEND_URL || 'http://localhost:3001';

export default function SystemHealthDashboard() {
  const { errors, getRecentErrors, getCriticalErrors, clearErrors } = useErrorLog();
  const [backendStatus, setBackendStatus] = useState('checking');
  const [lastCheck, setLastCheck] = useState(null);

  const checkHealth = async () => {
    setBackendStatus('checking');
    try {
      const response = await fetch(`${BACKEND_URL}/health`);
      if (response.ok) {
        const data = await response.json();
        setBackendStatus(data.status === 'ok' ? 'healthy' : 'unhealthy');
      } else {
        setBackendStatus('unhealthy');
      }
      setLastCheck(new Date());
    } catch (error) {
      console.error('Backend health check failed:', error);
      setBackendStatus('unhealthy');
      setLastCheck(new Date());
    }
  };

  useEffect(() => {
    checkHealth();
    const interval = setInterval(checkHealth, 60000); // Check every minute
    return () => clearInterval(interval);
  }, []);

  const recentErrors = getRecentErrors(5);
  const criticalErrors = getCriticalErrors();

  const statusIcon = {
    healthy: <CheckCircle2 className="w-5 h-5 text-green-500" />,
    checking: <Clock className="w-5 h-5 text-yellow-500 animate-spin" />,
    unhealthy: <XCircle className="w-5 h-5 text-red-500" />
  };

  const statusColor = {
    healthy: 'bg-green-100 text-green-800 border-green-300',
    checking: 'bg-yellow-100 text-yellow-800 border-yellow-300',
    unhealthy: 'bg-red-100 text-red-800 border-red-300'
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold text-slate-100">System Health</h2>
        <Button 
          onClick={checkHealth} 
          variant="outline" 
          size="sm" 
          className="bg-slate-700 border-slate-600 text-slate-200"
          disabled={backendStatus === 'checking'}
        >
          <RefreshCw className={`w-4 h-4 mr-2 ${backendStatus === 'checking' ? 'animate-spin' : ''}`} />
          {backendStatus === 'checking' ? 'Checking...' : 'Refresh'}
        </Button>
      </div>

      {/* System Status */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card className="bg-slate-800 border-slate-700">
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium text-slate-300 flex items-center gap-2">
              <Database className="w-4 h-4" />
              Backend Status
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center gap-2">
              {statusIcon[backendStatus]}
              <Badge className={statusColor[backendStatus]}>
                {backendStatus === 'healthy' ? 'Operational' : 
                 backendStatus === 'checking' ? 'Checking...' : 'Issues Detected'}
              </Badge>
            </div>
            {lastCheck && (
              <p className="text-xs text-slate-400 mt-2">
                Last checked: {lastCheck.toLocaleTimeString()}
              </p>
            )}
          </CardContent>
        </Card>

        <Card className="bg-slate-800 border-slate-700">
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium text-slate-300 flex items-center gap-2">
              <Activity className="w-4 h-4" />
              Error Rate
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-slate-100">
              {errors.length}
            </div>
            <p className="text-xs text-slate-400 mt-1">
              errors in last hour
            </p>
          </CardContent>
        </Card>

        <Card className="bg-slate-800 border-slate-700">
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium text-slate-300 flex items-center gap-2">
              <AlertCircle className="w-4 h-4" />
              Critical Issues
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-red-400">
              {criticalErrors.length}
            </div>
            <p className="text-xs text-slate-400 mt-1">
              require attention
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Critical Errors */}
      {criticalErrors.length > 0 && (
        <Alert variant="destructive" className="bg-red-900/20 border-red-700">
          <AlertCircle className="h-4 w-4" />
          <AlertTitle>Critical Issues Detected</AlertTitle>
          <AlertDescription>
            {criticalErrors.length} critical error(s) require immediate attention.
          </AlertDescription>
        </Alert>
      )}

      {/* Recent Errors */}
      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-slate-100">Recent Errors</CardTitle>
            {errors.length > 0 && (
              <Button onClick={clearErrors} variant="outline" size="sm" className="bg-slate-700 border-slate-600 text-slate-200">
                Clear All
              </Button>
            )}
          </div>
        </CardHeader>
        <CardContent>
          {recentErrors.length === 0 ? (
            <div className="text-center py-8 text-slate-400">
              <CheckCircle2 className="w-12 h-12 mx-auto mb-2 text-green-500" />
              <p>No errors logged</p>
            </div>
          ) : (
            <div className="space-y-3">
              {recentErrors.map((error) => (
                <div key={error.id} className="p-3 bg-slate-700/50 rounded-lg border border-slate-600">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-1">
                        <Badge className={
                          error.severity === 'critical' ? 'bg-red-600' :
                          error.severity === 'warning' ? 'bg-yellow-600' :
                          'bg-slate-600'
                        }>
                          {error.severity}
                        </Badge>
                        <span className="text-sm font-medium text-slate-200">{error.component}</span>
                      </div>
                      <p className="text-sm text-slate-300">{error.message}</p>
                      {error.actionable && (
                        <p className="text-xs text-blue-400 mt-1">
                           {error.actionable}
                        </p>
                      )}
                    </div>
                    <span className="text-xs text-slate-500">
                      {new Date(error.timestamp).toLocaleTimeString()}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { SystemLog } from '@/api/entities';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Loader2, RefreshCw, Trash2, Search, Filter } from "lucide-react";
import { toast } from "sonner";

const BACKEND_URL = import.meta.env.VITE_AISHACRM_BACKEND_URL || 'http://localhost:3001';

export default function SystemLogsViewer() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [clearing, setClearing] = useState(false);
  const [filterLevel, setFilterLevel] = useState('all');
  const [filterSource, setFilterSource] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [uniqueSources, setUniqueSources] = useState([]);

  const loadLogs = async () => {
    setLoading(true);
    try {
      // Try backend first (for local dev with Supabase Cloud)
      const response = await fetch(`${BACKEND_URL}/api/system/logs?tenant_id=test-tenant&limit=200`);

      if (!response.ok) {
        throw new Error('Backend not available, trying Base44...');
      }

      const data = await response.json();
      let allLogs = data.data || data;

      // If backend returns empty, try Base44 as fallback
      if (!allLogs || allLogs.length === 0) {
        console.log('No logs from backend, trying Base44...');
        allLogs = await SystemLog.list('-created_date', 200);
      }

      // Extract unique sources
      const sources = [...new Set(allLogs.map(log => log.source).filter(Boolean))];
      setUniqueSources(sources);

      // Apply filters
      if (filterLevel !== 'all') {
        allLogs = allLogs.filter(log => log.level === filterLevel);
      }

      if (filterSource !== 'all') {
        allLogs = allLogs.filter(log => log.source === filterSource);
      }

      if (searchTerm) {
        const term = searchTerm.toLowerCase();
        allLogs = allLogs.filter(log => 
          log.message?.toLowerCase().includes(term) ||
          log.source?.toLowerCase().includes(term) ||
          log.user_email?.toLowerCase().includes(term)
        );
      }

      setLogs(allLogs);
    } catch (error) {
      console.error('Failed to load logs:', error);
      toast.error('Failed to load system logs');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadLogs();
  }, [filterLevel, filterSource, searchTerm]);

  const handleClearLogs = async () => {
    if (!confirm('Are you sure you want to delete all logs? This action cannot be undone.')) {
      return;
    }

    setClearing(true);
    try {
      // Delete all logs (you might want to add a backend function for this)
      for (const log of logs) {
        await SystemLog.delete(log.id);
      }
      toast.success('All logs cleared');
      loadLogs();
    } catch (error) {
      console.error('Failed to clear logs:', error);
      toast.error('Failed to clear logs');
    } finally {
      setClearing(false);
    }
  };

  const getLevelBadgeColor = (level) => {
    switch (level) {
      case 'ERROR': return 'bg-red-600 text-white hover:bg-red-700';
      case 'WARNING': return 'bg-yellow-600 text-white hover:bg-yellow-700';
      case 'INFO': return 'bg-blue-600 text-white hover:bg-blue-700';
      case 'DEBUG': return 'bg-gray-600 text-white hover:bg-gray-700';
      default: return 'bg-slate-600 text-white hover:bg-slate-700';
    }
  };

  return (
    <div className="space-y-4">
      {/* Filters */}
      <Card className="bg-slate-800 border-slate-700">
        <CardContent className="p-4">
          <div className="flex flex-wrap gap-4 items-end">
            <div className="flex-1 min-w-[200px]">
              <label className="text-sm text-slate-300 mb-2 block">Search</label>
              <div className="relative">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-400" />
                <Input
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  placeholder="Search logs..."
                  className="pl-10 bg-slate-700 border-slate-600 text-slate-200"
                />
              </div>
            </div>

            <div className="w-[180px]">
              <label className="text-sm text-slate-300 mb-2 block">Level</label>
              <Select value={filterLevel} onValueChange={setFilterLevel}>
                <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200">
                  <SelectValue placeholder="All levels" />
                </SelectTrigger>
                <SelectContent className="bg-slate-800 border-slate-700">
                  <SelectItem value="all">All Levels</SelectItem>
                  <SelectItem value="ERROR">Error</SelectItem>
                  <SelectItem value="WARNING">Warning</SelectItem>
                  <SelectItem value="INFO">Info</SelectItem>
                  <SelectItem value="DEBUG">Debug</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="w-[200px]">
              <label className="text-sm text-slate-300 mb-2 block">Source</label>
              <Select value={filterSource} onValueChange={setFilterSource}>
                <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200">
                  <SelectValue placeholder="All sources" />
                </SelectTrigger>
                <SelectContent className="bg-slate-800 border-slate-700">
                  <SelectItem value="all">All Sources</SelectItem>
                  {uniqueSources.map(source => (
                    <SelectItem key={source} value={source}>{source}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <Button
              variant="outline"
              onClick={loadLogs}
              disabled={loading}
              className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600"
            >
              {loading ? <Loader2 className="w-4 h-4 animate-spin" /> : <RefreshCw className="w-4 h-4" />}
            </Button>

            <Button
              variant="destructive"
              onClick={handleClearLogs}
              disabled={loading || clearing || logs.length === 0}
              className="bg-red-600 hover:bg-red-700"
            >
              {clearing ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Clearing...
                </>
              ) : (
                <>
                  <Trash2 className="w-4 h-4 mr-2" />
                  Clear All
                </>
              )}
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Log Count */}
      <div className="flex items-center justify-between text-sm text-slate-400">
        <span>{logs.length} log{logs.length !== 1 ? 's' : ''} found</span>
        {(filterLevel !== 'all' || filterSource !== 'all' || searchTerm) && (
          <Button
            variant="ghost"
            size="sm"
            onClick={() => {
              setFilterLevel('all');
              setFilterSource('all');
              setSearchTerm('');
            }}
            className="text-blue-400 hover:text-blue-300"
          >
            Clear Filters
          </Button>
        )}
      </div>

      {/* Logs List */}
      <div className="space-y-2">
        {loading ? (
          <div className="flex items-center justify-center p-12">
            <Loader2 className="w-8 h-8 animate-spin text-blue-400" />
            <span className="ml-3 text-slate-300">Loading logs...</span>
          </div>
        ) : logs.length === 0 ? (
          <Card className="bg-slate-800 border-slate-700">
            <CardContent className="p-12 text-center">
              <p className="text-slate-400">No logs found</p>
              <p className="text-sm text-slate-500 mt-2">
                Logs will appear here as events occur in the application
              </p>
            </CardContent>
          </Card>
        ) : (
          logs.map((log) => (
            <Card key={log.id} className="bg-slate-800 border-slate-700 hover:border-slate-600 transition-colors">
              <CardContent className="p-4">
                <div className="flex items-start justify-between gap-4">
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2 mb-2">
                      <Badge className={getLevelBadgeColor(log.level)}>
                        {log.level}
                      </Badge>
                      <Badge variant="outline" className="bg-slate-700 text-slate-300 border-slate-600">
                        {log.source}
                      </Badge>
                      <span className="text-xs text-slate-500">
                        {new Date(log.created_date).toLocaleString()}
                      </span>
                    </div>

                    <p className="text-slate-200 text-sm mb-2">{log.message}</p>

                    <div className="flex flex-wrap gap-x-4 gap-y-1 text-xs text-slate-400">
                      {log.user_email && (
                        <span>User: {log.user_email}</span>
                      )}
                      {log.tenant_id && (
                        <span>Tenant: {log.tenant_id.substring(0, 8)}...</span>
                      )}
                    </div>

                    {log.metadata && Object.keys(log.metadata).length > 0 && (
                      <details className="mt-2">
                        <summary className="text-xs text-blue-400 cursor-pointer hover:text-blue-300">
                          Show metadata
                        </summary>
                        <pre className="mt-2 p-2 bg-slate-900 rounded text-xs text-slate-300 overflow-x-auto">
                          {JSON.stringify(log.metadata, null, 2)}
                        </pre>
                      </details>
                    )}

                    {log.stack_trace && (
                      <details className="mt-2">
                        <summary className="text-xs text-red-400 cursor-pointer hover:text-red-300">
                          Show stack trace
                        </summary>
                        <pre className="mt-2 p-2 bg-slate-900 rounded text-xs text-red-300 overflow-x-auto">
                          {log.stack_trace}
                        </pre>
                      </details>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Loader2, Trash2, AlertTriangle, CheckCircle } from "lucide-react";
import { toast } from "sonner";
import { cleanupTestRecords } from "@/api/functions";
import { useApiManager } from "../shared/ApiManager";

export default function TestDataManager() {
  const [cleaning, setCleaning] = useState(false);
  const [lastCleanup, setLastCleanup] = useState(null);
  const { clearCache } = useApiManager();

  const handleCleanup = async () => {
    if (!confirm("Are you sure you want to delete ALL test data? This action cannot be undone.")) {
      return;
    }

    setCleaning(true);
    try {
      const result = await cleanupTestRecords();
      console.log("Cleanup result:", result);

      // Clear the frontend cache to force re-fetching on other pages
      clearCache();
      console.log("Frontend API cache cleared.");

      setLastCleanup(new Date().toISOString());
      toast.success("Test data cleaned up successfully! Dashboard stats will refresh on next load.");

    } catch (error) {
      console.error("Cleanup failed:", error);
      toast.error("Failed to cleanup test data. Please try again.");
    } finally {
      setCleaning(false);
    }
  };

  return (
    <Card className="bg-slate-800 border-slate-700">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 text-slate-100">
          <Trash2 className="w-6 h-6 text-orange-600" />
          Test Data Management
        </CardTitle>
        <CardDescription className="text-slate-400">
          Cleanup test records from your system to ensure data accuracy for production.
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        <Alert variant="destructive" className="bg-red-900/30 border-red-700/50">
          <AlertTriangle className="h-4 w-4 text-red-400" />
          <AlertDescription className="text-red-500 text-sm [&_p]:leading-relaxed">
            <strong>Production Deployment Warning:</strong> Before publishing your app to production, 
            make sure to clean up all test data. Test data can confuse real users and affect analytics.
          </AlertDescription>
        </Alert>

        <div className="space-y-4">
          <div>
            <h3 className="font-semibold mb-2 text-slate-200">What gets cleaned up:</h3>
            <ul className="list-disc list-inside text-sm text-slate-400 space-y-1">
              <li>All contacts marked as test data</li>
              <li>All accounts marked as test data</li>
              <li>All leads marked as test data</li>
              <li>All opportunities marked as test data</li>
              <li>All activities marked as test data</li>
              <li>Associated notes and documents</li>
            </ul>
          </div>

          {lastCleanup && (
            <Alert className="bg-green-900/30 border-green-700/50">
              <CheckCircle className="h-4 w-4 text-green-400" />
              <AlertDescription className="text-green-300">
                Last cleanup performed: {new Date(lastCleanup).toLocaleString()}
              </AlertDescription>
            </Alert>
          )}

          <Button
            onClick={handleCleanup}
            disabled={cleaning}
            variant="destructive"
            className="w-full bg-red-600 hover:bg-red-700 text-white"
          >
            {cleaning ? (
              <>
                <Loader2 className="w-4 h-4 animate-spin mr-2" />
                Cleaning up test data...
              </>
            ) : (
              <>
                <Trash2 className="w-4 h-4 mr-2" />
                Clean Up All Test Data
              </>
            )}
          </Button>

          <div className="text-xs text-slate-400 bg-slate-700/50 p-3 rounded border border-slate-600">
            <strong>Pro Tip:</strong> During development, always create test records with the "Test Data" 
            checkbox enabled. This makes cleanup much easier before going live.
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

