export default function TimezoneSettings({ user, onUpdate }) {
  const [settings, setSettings] = useState({
    timezone: 'America/New_York',
    date_format: 'MM/dd/yyyy',
    time_format: '12h'
  });
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState("");

  useEffect(() => {
    if (user) {
      setSettings({
        timezone: user.timezone || 'America/New_York',
        date_format: user.date_format || 'MM/dd/yyyy',
        time_format: user.time_format || '12h'
      });
    }
  }, [user]);

  const handleSave = async () => {
    setSaving(true);
    try {
      await User.updateMyUserData(settings);
      setMessage("Settings saved successfully!");
      setTimeout(() => setMessage(""), 3000);
      if (onUpdate) onUpdate();
    } catch (error) {
      setMessage("Failed to save settings");
      setTimeout(() => setMessage(""), 3000);
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="space-y-6">
      {message && (
        <Alert className="bg-blue-900/30 border-blue-700/50">
          <AlertDescription className="text-blue-300">{message}</AlertDescription>
        </Alert>
      )}

      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-slate-100">
            <Globe className="w-5 h-5 text-blue-400" />
            Regional Settings
          </CardTitle>
          <CardDescription className="text-slate-400">
            Configure your timezone and date/time display preferences
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="space-y-2">
            <Label className="text-slate-200">Timezone</Label>
            <Select
              value={settings.timezone}
              onValueChange={(value) => setSettings({ ...settings, timezone: value })}
            >
              <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200">
                <SelectValue placeholder="Select timezone" />
              </SelectTrigger>
              <SelectContent className="bg-slate-800 border-slate-700">
                {COMMON_TIMEZONES.map(tz => (
                  <SelectItem key={tz.value} value={tz.value} className="text-slate-200 hover:bg-slate-700 focus:bg-slate-700">
                    {tz.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <Label className="text-slate-200">Date Format</Label>
            <Select
              value={settings.date_format}
              onValueChange={(value) => setSettings({ ...settings, date_format: value })}
            >
              <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200">
                <SelectValue placeholder="Select date format" />
              </SelectTrigger>
              <SelectContent className="bg-slate-800 border-slate-700">
                <SelectItem value="MM/dd/yyyy" className="text-slate-200 hover:bg-slate-700 focus:bg-slate-700">MM/dd/yyyy (US)</SelectItem>
                <SelectItem value="dd/MM/yyyy" className="text-slate-200 hover:bg-slate-700 focus:bg-slate-700">dd/MM/yyyy (UK/EU)</SelectItem>
                <SelectItem value="yyyy-MM-dd" className="text-slate-200 hover:bg-slate-700 focus:bg-slate-700">yyyy-MM-dd (ISO)</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <Label className="text-slate-200">Time Format</Label>
            <Select
              value={settings.time_format}
              onValueChange={(value) => setSettings({ ...settings, time_format: value })}
            >
              <SelectTrigger className="bg-slate-700 border-slate-600 text-slate-200">
                <SelectValue placeholder="Select time format" />
              </SelectTrigger>
              <SelectContent className="bg-slate-800 border-slate-700">
                <SelectItem value="12h" className="text-slate-200 hover:bg-slate-700 focus:bg-slate-700">12-hour (1:30 PM)</SelectItem>
                <SelectItem value="24h" className="text-slate-200 hover:bg-slate-700 focus:bg-slate-700">24-hour (13:30)</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="flex justify-end pt-4">
            <Button
              onClick={handleSave}
              disabled={saving}
              className="bg-blue-600 hover:bg-blue-700 text-white"
            >
              {saving ? <Loader2 className="w-4 h-4 mr-2 animate-spin" /> : <Save className="w-4 h-4 mr-2" />}
              Save Settings
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

import React, { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { User } from "@/api/entities";
import { User as UserIcon, Loader2, RefreshCw, ShieldCheck } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { cleanupUserData } from "@/api/functions";

export default function UserInfo() {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [cleaning, setCleaning] = useState(false);

  useEffect(() => {
    loadCurrentUser();
  }, []);

  const loadCurrentUser = async () => {
    setLoading(true);
    try {
      const user = await User.me();
      setCurrentUser(user);
    } catch (error) {
      console.error("Error loading user:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleCleanupUserData = async () => {
    if (!confirm("This will reset your custom branding and permission settings to fix display issues. Your role will NOT be changed. Continue?")) {
      return;
    }

    setCleaning(true);
    try {
      const response = await cleanupUserData();
      const result = response.data;

      if (result.status === 'success') {
        alert("User data cleaned successfully! Please refresh the page.");
        loadCurrentUser();
      } else {
        alert("Error cleaning user data: " + result.message);
      }
    } catch (error) {
      console.error("Error calling cleanup function:", error);
      const errorMessage = error.response?.data?.message || error.message || "An unknown error occurred.";
      alert("Error cleaning user data: " + errorMessage);
    } finally {
      setCleaning(false);
    }
  };

  if (loading) {
    return (
      <Card className="bg-slate-800 border-slate-700">
        <CardContent className="p-8 text-center">
          <Loader2 className="w-8 h-8 animate-spin mx-auto mb-4 text-slate-400" />
          <p className="text-slate-300">Loading Your Profile...</p>
        </CardContent>
      </Card>);

  }

  return (
    <div className="space-y-6">
      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-slate-100">
            <UserIcon className="w-5 h-5 text-blue-400" />
            My Profile
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div>
            <strong className="text-slate-200">Name:</strong> 
            <span className="text-slate-300 ml-2">{currentUser?.full_name || "Not set"}</span>
          </div>
          <div>
            <strong className="text-slate-200">Email:</strong> 
            <span className="text-slate-300 ml-2">{currentUser?.email || "Not set"}</span>
          </div>
          <div>
            <strong className="text-slate-200">Role:</strong> 
            <Badge variant="destructive" className="ml-2 capitalize bg-red-600 text-white hover:bg-red-700">
              {currentUser?.role === 'power-user' ? 'Power User' : currentUser?.role || 'user'}
            </Badge>
          </div>

          {currentUser?.role === 'admin' &&
          <Alert variant="default" className="bg-green-900/30 border-green-700/50">
                <ShieldCheck className="h-4 w-4 text-green-400" />
                <AlertDescription className="text-emerald-600 text-sm [&_p]:leading-relaxed">
                    <strong>You are the App Owner.</strong> Your Admin role is protected and gives you full control over the CRM. You can assign Power-User and User roles to your team members.
                </AlertDescription>
            </Alert>
          }
        </CardContent>
      </Card>

      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
            <CardTitle className="text-slate-100">Data Maintenance</CardTitle>
        </CardHeader>
        <CardContent>
          <Alert className="bg-blue-900/30 border-blue-700/50">
            <RefreshCw className="h-4 w-4 text-blue-400" />
            <AlertDescription className="text-blue-300">
              <div className="flex justify-between items-center">
                <span className="text-blue-600">Fix display issues or errors in the base44 dashboard by cleaning your user data:</span>
                <Button
                  onClick={handleCleanupUserData}
                  size="sm"
                  disabled={cleaning}
                  className="bg-blue-600 hover:bg-blue-700 whitespace-nowrap">

                  {cleaning ? <Loader2 className="w-4 h-4 animate-spin mr-2" /> : <RefreshCw className="w-4 h-4 mr-2" />}
                  {cleaning ? 'Cleaning...' : 'Clean My Data'}
                </Button>
              </div>
            </AlertDescription>
          </Alert>
        </CardContent>
      </Card>
    </div>);

}

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter, DialogDescription } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Loader2, PlusCircle, ShieldCheck, Users } from "lucide-react";
import { toast } from "sonner";
import { inviteUser } from "@/api/functions";
import { Tenant } from "@/api/entities";

const InviteUserModal = ({ tenants, onInvite, onCancel }) => {
    const [fullName, setFullName] = useState('');
    const [email, setEmail] = useState('');
    const [role, setRole] = useState('user');
    const [tenantId, setTenantId] = useState('');
    const [sending, setSending] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!email || !fullName) {
            toast.error("Full Name and Email are required.");
            return;
        }
        setSending(true);
        try {
            await onInvite({ fullName, email, role, tenantId: tenantId || null });
            onCancel(); // Close modal on success
        } catch (error) {
            console.error("Invite error from modal:", error);
            // Error is already toasted in the parent component
        } finally {
            setSending(false);
        }
    };

    return (
        <Dialog open={true} onOpenChange={onCancel}>
            <DialogContent>
                <DialogHeader>
                    <DialogTitle>Invite New User</DialogTitle>
                    <DialogDescription>
                        An invitation email will be sent to the user to set up their account.
                    </DialogDescription>
                </DialogHeader>
                <form onSubmit={handleSubmit} className="space-y-4 pt-4">
                    <div>
                        <Label htmlFor="full-name">Full Name</Label>
                        <Input 
                            id="full-name" 
                            value={fullName} 
                            onChange={(e) => setFullName(e.target.value)} 
                            required 
                        />
                    </div>
                    <div>
                        <Label htmlFor="email">Email</Label>
                        <Input 
                            id="email" 
                            type="email" 
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)} 
                            required 
                        />
                    </div>
                    <div>
                        <Label htmlFor="role">Role</Label>
                        <Select value={role} onValueChange={setRole}>
                            <SelectTrigger id="role">
                                <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                                <SelectItem value="user">User</SelectItem>
                                <SelectItem value="power-user">Power User</SelectItem>
                                <SelectItem value="admin">Admin</SelectItem>
                            </SelectContent>
                        </Select>
                    </div>
                    <div>
                        <Label htmlFor="tenant">Assign to Tenant (optional)</Label>
                        <Select value={tenantId} onValueChange={setTenantId}>
                            <SelectTrigger id="tenant">
                                <SelectValue placeholder="No specific tenant" />
                            </SelectTrigger>
                            <SelectContent>
                                <SelectItem value={null}>No specific tenant</SelectItem>
                                {tenants.map(t => <SelectItem key={t.id} value={t.id}>{t.name}</SelectItem>)}
                            </SelectContent>
                        </Select>
                    </div>
                    <DialogFooter>
                        <Button type="button" variant="outline" onClick={onCancel}>Cancel</Button>
                        <Button type="submit" disabled={sending}>
                            {sending ? (
                                <>
                                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                                    Sending...
                                </>
                            ) : 'Send Invitation'}
                        </Button>
                    </DialogFooter>
                </form>
            </DialogContent>
        </Dialog>
    );
};

export default function UserManagement() {
    const [showInviteModal, setShowInviteModal] = useState(false);
    const [tenants, setTenants] = useState([]);

    useEffect(() => {
        const loadTenants = async () => {
            try {
                const tenantsData = await Tenant.list();
                setTenants(tenantsData);
            } catch (error) {
                console.error("Failed to load tenants:", error);
                toast.error("Could not load tenant list for invitations.");
            }
        };
        loadTenants();
    }, []);

    const handleInvite = async (invitationData) => {
        try {
            const { error } = await inviteUser(invitationData);
            if (error) {
                throw new Error(error);
            }
            toast.success(`Invitation sent to ${invitationData.email}`);
        } catch (err) {
            console.error("Failed to send invitation:", err);
            toast.error(`Invitation failed: ${err.message}`);
            // Re-throw to keep modal open on failure
            throw err;
        }
    };

    return (
        <>
            <Card>
                <CardHeader>
                    <div className="flex items-center justify-between">
                        <div>
                            <CardTitle className="flex items-center gap-2">
                                <Users className="w-6 h-6 text-blue-600" />
                                Invite Users
                            </CardTitle>
                            <CardDescription>
                                Add new users to the platform by sending an email invitation.
                            </CardDescription>
                        </div>
                        <Button onClick={() => setShowInviteModal(true)}>
                            <PlusCircle className="mr-2 h-4 w-4" />
                            Invite User
                        </Button>
                    </div>
                </CardHeader>
                <CardContent>
                     <div className="p-4 bg-slate-50 border rounded-lg">
                        <div className="flex items-start gap-3">
                            <ShieldCheck className="w-5 h-5 text-slate-500 mt-1" />
                            <div>
                                <h4 className="font-semibold text-slate-800">Role Information</h4>
                                <ul className="list-disc pl-5 mt-1 text-sm text-slate-600 space-y-1">
                                    <li><strong>Admin:</strong> Can manage users, tenants, and all system settings.</li>
                                    <li><strong>Power User:</strong> Can view and manage all data within their assigned tenant.</li>
                                    <li><strong>User:</strong> Has standard access to CRM features based on assigned permissions.</li>
                                </ul>
                            </div>
                        </div>
                    </div>
                </CardContent>
            </Card>
            {showInviteModal && (
                <InviteUserModal
                    tenants={tenants}
                    onInvite={handleInvite}
                    onCancel={() => setShowInviteModal(false)}
                />
            )}
        </>
    );
}

import React, { useState, useEffect, useCallback } from "react";
import { User } from "@/api/entities";
import { Tenant } from "@/api/entities";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Loader2, Save, AlertCircle, Users2, Shield, Eye, PencilLine } from "lucide-react";
import { useTenant } from "../shared/tenantContext";
import NavigationPermissions from "./NavigationPermissions";
import TagInput from "../shared/TagInput";
import { toast } from "sonner";

export default function UserPermissions({ userEmail, onClose }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const { selectedTenantId } = useTenant();

  const [displayName, setDisplayName] = useState("");
  const [selectedTenant, setSelectedTenant] = useState("");
  const [tenants, setTenants] = useState([]);
  const [role, setRole] = useState("user");
  const [employeeRole, setEmployeeRole] = useState("");
  const [accessLevel, setAccessLevel] = useState("read_write");
  const [crmAccess, setCrmAccess] = useState(true);
  const [canUseSoftphone, setCanUseSoftphone] = useState(false);
  const [dashboardScope, setDashboardScope] = useState("own");
  const [tags, setTags] = useState([]);
  const [assignedManager, setAssignedManager] = useState("");
  const [managers, setManagers] = useState([]);

  const [navigationPermissions, setNavigationPermissions] = useState({
    Dashboard: true,
    Contacts: true,
    Accounts: true,
    Leads: true,
    Opportunities: true,
    Activities: true,
    Calendar: true,
    BizDevSources: true,
    CashFlow: true,
    Employees: false,
    Reports: false,
    Integrations: true,
    Settings: false,
    Documentation: true,
    AICampaigns: false,
    Agent: true,
    DocumentProcessing: false,
    DocumentManagement: false,
    PaymentPortal: false,
    Utilities: false
  });

  const loadUserData = useCallback(async () => {
    try {
      setLoading(true);
      const users = await User.list();
      const foundUser = users.find(u => u.email === userEmail);

      if (!foundUser) {
        setError("User not found");
        return;
      }

      console.log("Loaded user:", foundUser);
      console.log("User's stored navigation_permissions:", foundUser.navigation_permissions);

      setUser(foundUser);
      setDisplayName(foundUser.display_name || foundUser.full_name || "");
      setSelectedTenant(foundUser.tenant_id || "");
      setRole(foundUser.role || "user");
      setEmployeeRole(foundUser.employee_role || "");
      setAccessLevel(foundUser.access_level || "read_write");
      setCrmAccess(foundUser.crm_access !== false);
      setCanUseSoftphone(foundUser.permissions?.can_use_softphone || false);
      setDashboardScope(foundUser.permissions?.dashboard_scope || "own");
      setTags(foundUser.tags || []);
      setAssignedManager(foundUser.assigned_manager || "");

      if (foundUser.navigation_permissions) {
        console.log("Setting navigation permissions from user:", foundUser.navigation_permissions);
        setNavigationPermissions(prev => ({
          ...prev,
          ...foundUser.navigation_permissions
        }));
      }
    } catch (err) {
      console.error("Failed to load user:", err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, [userEmail]);

  const loadTenants = useCallback(async () => {
    try {
      const fetchedTenants = await Tenant.list();
      setTenants(fetchedTenants || []);
    } catch (err) {
      console.warn("Could not load tenants:", err);
    }
  }, []);

  const loadManagers = useCallback(async () => {
    try {
      const users = await User.list();
      const managerUsers = users.filter(u => 
        u.employee_role === 'manager' || 
        u.role === 'admin' || 
        u.role === 'superadmin'
      );
      setManagers(managerUsers);
    } catch (err) {
      console.warn("Could not load managers:", err);
    }
  }, []);

  useEffect(() => {
    loadUserData();
    loadTenants();
    loadManagers();
  }, [loadUserData, loadTenants, loadManagers]);

  const handleSave = async () => {
    if (!user) return;

    setSaving(true);
    setError(null);

    try {
      console.log("Saving navigation_permissions:", navigationPermissions);

      const updates = {
        display_name: displayName,
        tenant_id: selectedTenant || null,
        role,
        employee_role: employeeRole || null,
        access_level: accessLevel,
        crm_access: crmAccess,
        tags,
        assigned_manager: assignedManager || null,
        permissions: {
          ...(user.permissions || {}),
          can_use_softphone: canUseSoftphone,
          dashboard_scope: dashboardScope
        },
        navigation_permissions: navigationPermissions
      };

      console.log("Full update payload:", JSON.stringify(updates, null, 2));

      await User.update(user.id, updates);

      // Reload the user to verify the save
      const updatedUsers = await User.list();
      const updatedUser = updatedUsers.find(u => u.email === userEmail);
      console.log("After save, user navigation_permissions:", updatedUser?.navigation_permissions);

      toast.success("User permissions updated successfully");

      if (onClose) {
        onClose();
      }
    } catch (err) {
      console.error("Failed to save user permissions:", err);
      setError(err.message);
      toast.error("Failed to update permissions: " + err.message);
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center p-8">
        <Loader2 className="w-8 h-8 animate-spin text-blue-500" />
      </div>
    );
  }

  if (error && !user) {
    return (
      <Alert variant="destructive" className="m-4">
        <AlertCircle className="h-4 w-4" />
        <AlertDescription>{error}</AlertDescription>
      </Alert>
    );
  }

  return (
    <div className="space-y-6 p-6 bg-slate-900">
      {error && (
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      {/* User Info Header */}
      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="text-slate-100 flex items-center gap-2">
            <Users2 className="w-5 h-5" />
            User Access Settings  {userEmail}
          </CardTitle>
          <CardDescription className="text-slate-400">
            Configure user roles, access levels, and permissions
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Display Name */}
          <div>
            <Label className="text-slate-200">Display Name</Label>
            <Input
              value={displayName}
              onChange={(e) => setDisplayName(e.target.value)}
              placeholder="e.g., John Doe"
              className="bg-slate-900 border-slate-700 text-slate-100"
            />
          </div>

          {/* Client/Tenant */}
          <div>
            <Label className="text-slate-200">Client</Label>
            <Select value={selectedTenant} onValueChange={setSelectedTenant}>
              <SelectTrigger className="bg-slate-900 border-slate-700 text-slate-200">
                <SelectValue placeholder="Select tenant" />
              </SelectTrigger>
              <SelectContent className="bg-slate-900 border-slate-700 text-slate-200">
                <SelectItem value={null}>No Tenant (System Admin)</SelectItem>
                {tenants.map((t) => (
                  <SelectItem key={t.id} value={t.id}>
                    {t.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Role & Employee Role Row */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label className="text-slate-200">Base44 Role</Label>
              <Select value={role} onValueChange={setRole}>
                <SelectTrigger className="bg-slate-900 border-slate-700 text-slate-200">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent className="bg-slate-900 border-slate-700 text-slate-200">
                  <SelectItem value="superadmin">Super Admin</SelectItem>
                  <SelectItem value="admin">Admin</SelectItem>
                  <SelectItem value="power-user">Power User</SelectItem>
                  <SelectItem value="user">User</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label className="text-slate-200">Employee Role</Label>
              <Select value={employeeRole || 'none'} onValueChange={(val) => setEmployeeRole(val === 'none' ? '' : val)}>
                <SelectTrigger className="bg-slate-900 border-slate-700 text-slate-200">
                  <SelectValue placeholder="Select employee role" />
                </SelectTrigger>
                <SelectContent className="bg-slate-900 border-slate-700 text-slate-200">
                  <SelectItem value="none">None (Use Base44 Role)</SelectItem>
                  <SelectItem value="manager">
                    <div className="flex flex-col">
                      <span className="font-semibold">Manager</span>
                      <span className="text-xs text-slate-400">Full tenant visibility</span>
                    </div>
                  </SelectItem>
                  <SelectItem value="employee">
                    <div className="flex flex-col">
                      <span className="font-semibold">Employee</span>
                      <span className="text-xs text-slate-400">Own records only</span>
                    </div>
                  </SelectItem>
                </SelectContent>
              </Select>
              <p className="text-xs text-slate-400 mt-1">
                Two-tier RBAC: Managers see all team data, Employees see only their own records.
              </p>
            </div>
          </div>

          {/* Tags */}
          <div>
            <Label className="text-slate-200">Tags</Label>
            <TagInput
              tags={tags}
              onChange={setTags}
              placeholder="e.g., Sales, Manager"
              className="bg-slate-900 border-slate-700 text-slate-100"
            />
          </div>

          {/* Softphone */}
          <div className="flex items-center justify-between p-3 bg-slate-900 rounded-lg border border-slate-700">
            <Label className="text-slate-200">Can use Softphone</Label>
            <Switch
              checked={canUseSoftphone}
              onCheckedChange={setCanUseSoftphone}
            />
          </div>

          {/* CRM Access */}
          <div className="flex items-center justify-between p-3 bg-slate-900 rounded-lg border border-slate-700">
            <Label className="text-slate-200">CRM Access</Label>
            <Switch
              checked={crmAccess}
              onCheckedChange={setCrmAccess}
            />
          </div>

          {/* Access Level & Dashboard Scope Row */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label className="text-slate-200 flex items-center gap-2">
                <Eye className="w-4 h-4" />
                Access Level
              </Label>
              <Select value={accessLevel} onValueChange={setAccessLevel}>
                <SelectTrigger className="bg-slate-900 border-slate-700 text-slate-200">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent className="bg-slate-900 border-slate-700 text-slate-200">
                  <SelectItem value="read">Read</SelectItem>
                  <SelectItem value="read_write">Read/Write</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label className="text-slate-200 flex items-center gap-2">
                <PencilLine className="w-4 h-4" />
                Dashboard Data Scope
              </Label>
              <Select value={dashboardScope} onValueChange={setDashboardScope}>
                <SelectTrigger className="bg-slate-900 border-slate-700 text-slate-200">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent className="bg-slate-900 border-slate-700 text-slate-200">
                  <SelectItem value="own">Own Records Only</SelectItem>
                  <SelectItem value="aggregated">Aggregated (tenant)</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          {/* Manager Assignment */}
          {employeeRole && (
            <div>
              <Label className="text-slate-200">Manager</Label>
              <Select value={assignedManager} onValueChange={setAssignedManager}>
                <SelectTrigger className="bg-slate-900 border-slate-700 text-slate-200">
                  <SelectValue placeholder="Select manager" />
                </SelectTrigger>
                <SelectContent className="bg-slate-900 border-slate-700 text-slate-200">
                  <SelectItem value={null}>No Manager</SelectItem>
                  {managers.map((m) => (
                    <SelectItem key={m.id} value={m.email}>
                      {m.display_name || m.full_name || m.email}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <p className="text-xs text-slate-400 mt-1">
                A manager will be able to view this employee's data and their own team.
              </p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Navigation Permissions */}
      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="text-slate-100">Navigation Permissions</CardTitle>
          <CardDescription className="text-slate-400">
            {navigationPermissions ? Object.keys(navigationPermissions).filter(k => navigationPermissions[k]).length : 0} enabled
          </CardDescription>
        </CardHeader>
        <CardContent>
          <NavigationPermissions
            value={navigationPermissions}
            onChange={(newPerms) => {
              console.log("NavigationPermissions onChange called with:", newPerms);
              setNavigationPermissions(newPerms);
            }}
          />
        </CardContent>
      </Card>

      {/* Save Button */}
      <div className="flex justify-end gap-3">
        <Button
          variant="outline"
          onClick={onClose}
          className="bg-slate-800 border-slate-700 text-slate-200 hover:bg-slate-700"
        >
          Cancel
        </Button>
        <Button
          onClick={handleSave}
          disabled={saving}
          className="bg-blue-600 hover:bg-blue-700 text-white"
        >
          {saving ? (
            <>
              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
              Saving...
            </>
          ) : (
            <>
              <Save className="w-4 h-4 mr-2" />
              Save Changes
            </>
          )}
        </Button>
      </div>
    </div>
  );
}

import React, { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Loader2, Search, CheckCircle2, XCircle, AlertTriangle } from "lucide-react";
import { checkUserRecord } from "@/api/functions";
import { toast } from "sonner";

export default function UserRecordDiagnostic() {
  const [email, setEmail] = useState("andrei.byfield@gmail.com");
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);

  const handleCheck = async () => {
    if (!email) {
      toast.error("Please enter an email");
      return;
    }

    setLoading(true);
    setResult(null);

    try {
      const response = await checkUserRecord({ email });

      if (response.data) {
        setResult(response.data);
      } else {
        toast.error("Failed to check user record");
      }
    } catch (error) {
      console.error("Error checking user:", error);
      toast.error(error.message || "Failed to check user");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Card className="bg-slate-800 border-slate-700">
      <CardHeader>
        <CardTitle className="text-slate-100">User Record Diagnostic</CardTitle>
        <p className="text-sm text-slate-400">
          Check what's actually stored in a User's database record
        </p>
      </CardHeader>
      <CardContent className="space-y-4">
        <div>
          <Label className="text-slate-300">Email Address</Label>
          <Input
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="user@example.com"
            className="bg-slate-700 border-slate-600 text-slate-200"
          />
        </div>

        <Button
          onClick={handleCheck}
          disabled={loading}
          className="bg-blue-600 hover:bg-blue-700 w-full"
        >
          {loading ? (
            <>
              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
              Checking...
            </>
          ) : (
            <>
              <Search className="w-4 h-4 mr-2" />
              Check
            </>
          )}
        </Button>

        {result && (
          <div className="space-y-4 mt-4">
            {/* User Database Record */}
            <div className="bg-slate-700/50 p-4 rounded">
              <h4 className="font-semibold text-slate-200 mb-3">User Database Record</h4>
              <div className="grid grid-cols-2 gap-3 text-sm">
                <span className="text-slate-400">Email:</span>
                <span className="text-slate-200 font-mono text-xs">{result.user_record.email}</span>

                <span className="text-slate-400">Full Name:</span>
                <span className="text-slate-200">{result.user_record.full_name}</span>

                <span className="text-slate-400">Role:</span>
                <span className="text-cyan-400 font-mono font-bold">{result.user_record.role}</span>

                <span className="text-slate-400">Employee Role:</span>
                <span className="text-cyan-400 font-mono">{result.user_record.employee_role || 'null'}</span>

                <span className="text-slate-400">Tenant ID:</span>
                <span className="text-slate-200 font-mono text-xs">{result.user_record.tenant_id}</span>

                <span className="text-slate-400">Access Level:</span>
                <span className="text-slate-200">{result.user_record.access_level || 'N/A'}</span>
              </div>
            </div>

            {/* RLS Evaluation */}
            <div className="bg-slate-700/50 p-4 rounded">
              <h4 className="font-semibold text-slate-200 mb-3">RLS Evaluation</h4>
              <div className="space-y-2 text-sm">
                <div className="flex items-center justify-between">
                  <span className="text-slate-400">Is Admin/Superadmin:</span>
                  {result.rls_evaluation.is_admin ? 
                    <CheckCircle2 className="w-4 h-4 text-green-400" /> : 
                    <XCircle className="w-4 h-4 text-red-400" />
                  }
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-slate-400">Is Power User (role = "power-user"):</span>
                  {result.rls_evaluation.is_power_user ? 
                    <CheckCircle2 className="w-4 h-4 text-green-400" /> : 
                    <XCircle className="w-4 h-4 text-red-400" />
                  }
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-slate-400">Is Manager (employee_role):</span>
                  {result.rls_evaluation.is_manager ? 
                    <CheckCircle2 className="w-4 h-4 text-green-400" /> : 
                    <XCircle className="w-4 h-4 text-red-400" />
                  }
                </div>
              </div>
            </div>

            {/* Tenant Leads */}
            <div className="bg-slate-700/50 p-4 rounded">
              <h4 className="font-semibold text-slate-200 mb-3">
                Leads in Tenant ({result.tenant_leads_count})
              </h4>
              {result.tenant_leads.length > 0 ? (
                <div className="space-y-2">
                  {result.tenant_leads.map((lead) => (
                    <div key={lead.id} className="bg-slate-800/50 p-2 rounded text-xs">
                      <div className="font-semibold text-slate-200">{lead.name}</div>
                      <div className="text-slate-400">ID: {lead.unique_id}</div>
                      <div className="text-slate-400">Assigned: {lead.assigned_to || 'Unassigned'}</div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-slate-400 text-xs">No leads in this tenant</p>
              )}
            </div>

            {/* Verdict */}
            <Alert className={result.should_see_leads ? "bg-green-900/20 border-green-700" : "bg-red-900/20 border-red-700"}>
              {result.should_see_leads ? 
                <CheckCircle2 className="h-4 w-4 text-green-400" /> : 
                <AlertTriangle className="h-4 w-4 text-red-400" />
              }
              <AlertDescription className={result.should_see_leads ? "text-green-300" : "text-red-300"}>
                <div className="font-semibold mb-1">
                  {result.should_see_leads ? 
                    " User SHOULD see all tenant leads" : 
                    " User CANNOT see tenant leads"
                  }
                </div>
                {!result.should_see_leads && (
                  <div className="text-xs mt-2">
                    User needs one of: role="admin/superadmin/power-user" OR employee_role="manager"
                  </div>
                )}
              </AlertDescription>
            </Alert>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

import React, { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Webhook } from "@/api/entities";
import { AlertCircle, Loader2 } from "lucide-react";

const WEBHOOK_EVENTS = [
  { value: "contact.created", label: "Contact Created" },
  { value: "contact.updated", label: "Contact Updated" },
  { value: "contact.deleted", label: "Contact Deleted" },
  { value: "account.created", label: "Account Created" },
  { value: "account.updated", label: "Account Updated" },
  { value: "account.deleted", label: "Account Deleted" },
  { value: "lead.created", label: "Lead Created" },
  { value: "lead.updated", label: "Lead Updated" },
  { value: "lead.deleted", label: "Lead Deleted" },
  { value: "opportunity.created", label: "Opportunity Created" },
  { value: "opportunity.updated", label: "Opportunity Updated" },
  { value: "opportunity.deleted", label: "Opportunity Deleted" },
  { value: "activity.created", label: "Activity Created" },
  { value: "activity.updated", label: "Activity Updated" },
  { value: "activity.deleted", label: "Activity Deleted" },
];

export default function WebhookForm({ webhook, onSubmitSuccess, onCancel }) {
  const [formData, setFormData] = useState({
    event_name: "",
    target_url: "",
    description: "",
    is_active: true,
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (webhook) {
      setFormData({
        event_name: webhook.event_name || "",
        target_url: webhook.target_url || "",
        description: webhook.description || "",
        is_active: webhook.is_active !== false,
      });
    }
  }, [webhook]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError("");

    try {
      // Validate URL format
      try {
        new URL(formData.target_url);
      } catch {
        throw new Error("Please enter a valid URL");
      }

      if (webhook) {
        await Webhook.update(webhook.id, formData);
      } else {
        await Webhook.create(formData);
      }

      onSubmitSuccess();
    } catch (err) {
      console.error("Webhook form error:", err);
      setError(err.message || "Failed to save webhook");
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChange = (field) => (value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      {error && (
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      <div className="space-y-2">
        <Label htmlFor="event_name">Event Type</Label>
        <Select value={formData.event_name} onValueChange={handleChange("event_name")}>
          <SelectTrigger>
            <SelectValue placeholder="Select an event type" />
          </SelectTrigger>
          <SelectContent>
            {WEBHOOK_EVENTS.map((event) => (
              <SelectItem key={event.value} value={event.value}>
                {event.label}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      <div className="space-y-2">
        <Label htmlFor="target_url">Target URL</Label>
        <Input
          id="target_url"
          type="url"
          placeholder="https://your-n8n-instance.com/webhook/..."
          value={formData.target_url}
          onChange={(e) => setFormData(prev => ({ ...prev, target_url: e.target.value }))}
          required
        />
        <p className="text-sm text-slate-500">
          The webhook endpoint URL where events will be sent
        </p>
      </div>

      <div className="space-y-2">
        <Label htmlFor="description">Description</Label>
        <Textarea
          id="description"
          placeholder="Describe what this webhook is used for..."
          value={formData.description}
          onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
          rows={3}
        />
      </div>

      <Alert>
        <AlertCircle className="h-4 w-4" />
        <AlertDescription>
          Webhooks are secured with your N8N_SHARED_SECRET. Make sure your receiving endpoint validates the signature in the 'x-webhook-signature' header.
        </AlertDescription>
      </Alert>

      <div className="flex justify-end gap-3 pt-4">
        <Button type="button" variant="outline" onClick={onCancel}>
          Cancel
        </Button>
        <Button type="submit" disabled={isSubmitting || !formData.event_name || !formData.target_url}>
          {isSubmitting ? (
            <>
              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
              {webhook ? "Updating..." : "Creating..."}
            </>
          ) : (
            webhook ? "Update Webhook" : "Create Webhook"
          )}
        </Button>
      </div>
    </form>
  );
}

import React, { useState, useEffect, useCallback } from "react";
