import React, { createContext, useContext, useCallback, useRef } from 'react';

const ApiContext = createContext(null);

export const ApiProvider = ({ children }) => {
  const cacheRef = useRef(new Map());
  const pendingRequestsRef = useRef(new Map());

  const cachedRequest = useCallback(async (entityName, methodName, params, fetcher) => {
    const cacheKey = `${entityName}.${methodName}:${JSON.stringify(params)}`;

    if (cacheRef.current.has(cacheKey)) {
      const cached = cacheRef.current.get(cacheKey);
      const age = Date.now() - cached.timestamp;
      if (age < 30000) {
        return cached.data;
      }
    }

    if (pendingRequestsRef.current.has(cacheKey)) {
      return pendingRequestsRef.current.get(cacheKey);
    }

    const promise = fetcher()
      .then(data => {
        cacheRef.current.set(cacheKey, { data, timestamp: Date.now() });
        pendingRequestsRef.current.delete(cacheKey);
        return data;
      })
      .catch(error => {
        pendingRequestsRef.current.delete(cacheKey);
        throw error;
      });

    pendingRequestsRef.current.set(cacheKey, promise);
    return promise;
  }, []);

  const clearCache = useCallback((pattern) => {
    if (!pattern) {
      cacheRef.current.clear();
      pendingRequestsRef.current.clear();
      return;
    }

    const keysToDelete = [];
    for (const key of cacheRef.current.keys()) {
      if (key.includes(pattern)) {
        keysToDelete.push(key);
      }
    }
    keysToDelete.forEach(key => cacheRef.current.delete(key));
  }, []);

  return (
    <ApiContext.Provider value={{ cachedRequest, clearCache }}>
      {children}
    </ApiContext.Provider>
  );
};

export const useApiManager = () => {
  const context = useContext(ApiContext);
  if (!context) {
    throw new Error('useApiManager must be used within ApiProvider');
  }
  return context;
};

import React, { createContext, useContext, useRef, useCallback } from 'react';

const ApiOptimizerContext = createContext(null);

export const ApiOptimizerProvider = ({ children }) => {
  const requestQueueRef = useRef([]);
  const processingRef = useRef(false);
  const batchTimeoutRef = useRef(null);

  const queueRequest = useCallback((request) => {
    return new Promise((resolve, reject) => {
      requestQueueRef.current.push({ request, resolve, reject });

      if (batchTimeoutRef.current) {
        clearTimeout(batchTimeoutRef.current);
      }

      batchTimeoutRef.current = setTimeout(() => {
        processQueue();
      }, 50);
    });
  }, []);

  const processQueue = useCallback(async () => {
    if (processingRef.current || requestQueueRef.current.length === 0) {
      return;
    }

    processingRef.current = true;
    const batch = requestQueueRef.current.splice(0, 5);

    for (const { request, resolve, reject } of batch) {
      try {
        const result = await request();
        resolve(result);
      } catch (error) {
        reject(error);
      }
    }

    processingRef.current = false;

    if (requestQueueRef.current.length > 0) {
      setTimeout(processQueue, 100);
    }
  }, []);

  return (
    <ApiOptimizerContext.Provider value={{ queueRequest }}>
      {children}
    </ApiOptimizerContext.Provider>
  );
};

export const useApiOptimizer = () => {
  const context = useContext(ApiOptimizerContext);
  if (!context) {
    return { queueRequest: (req) => req() };
  }
  return context;
};

import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Loader2, CheckCircle, AlertTriangle, FileDigit } from 'lucide-react';
import { Account } from '@/api/entities';
import { Contact } from '@/api/entities';
import { Lead } from '@/api/entities';
import { generateUniqueId } from '@/api/functions';

export default function BackfillUniqueIdsClient({ tenantId }) {
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [progress, setProgress] = useState({ current: 0, total: 0 });

  const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));

  const updateWithRetry = async (EntityClass, recordId, data, maxRetries = 3) => {
    for (let attempt = 0; attempt < maxRetries; attempt++) {
      try {
        await EntityClass.update(recordId, data);
        return { success: true };
      } catch (error) {
        const isRateLimit = error?.response?.status === 429 || error?.status === 429;

        if (isRateLimit && attempt < maxRetries - 1) {
          // Exponential backoff: 5s, 10s, 20s
          const waitTime = 5000 * Math.pow(2, attempt);
          console.log(`Rate limited. Waiting ${waitTime/1000}s before retry ${attempt + 1}...`);
          await sleep(waitTime);
          continue;
        }

        return { success: false, error: error.message };
      }
    }
    return { success: false, error: 'Max retries exceeded' };
  };

  const backfillEntity = async (EntityClass, entityType, prefix) => {
    try {
      setProgress({ current: 0, total: 0 });

      // Get all records for tenant
      const allRecords = await EntityClass.filter({ tenant_id: tenantId });
      const recordsWithoutId = allRecords.filter(r => !r.unique_id);

      if (recordsWithoutId.length === 0) {
        return {
          success: true,
          message: `All ${entityType} records already have unique_ids`,
          updated: 0
        };
      }

      setProgress({ current: 0, total: recordsWithoutId.length });

      // Find highest existing number
      const existingIds = allRecords
        .filter(r => r.unique_id && r.unique_id.startsWith(`${prefix}-`))
        .map(r => {
          const parts = r.unique_id.split('-');
          const num = parseInt(parts[parts.length - 1]);
          return isNaN(num) ? 0 : num;
        });

      let nextNumber = existingIds.length > 0 ? Math.max(...existingIds) + 1 : 1;
      let updated = 0;
      let failed = 0;

      // Process ONE record at a time with LONG delays
      for (const record of recordsWithoutId) {
        const unique_id = `${prefix}-${String(nextNumber).padStart(6, '0')}`;

        const updateResult = await updateWithRetry(EntityClass, record.id, { unique_id });

        if (updateResult.success) {
          updated++;
          nextNumber++;
        } else {
          failed++;
          console.error(`Failed to update ${entityType} ${record.id}:`, updateResult.error);
        }

        setProgress({ current: updated + failed, total: recordsWithoutId.length });

        // CRITICAL: 3 second delay between EVERY update to avoid rate limits
        await sleep(3000);
      }

      return {
        success: true,
        message: `Successfully backfilled ${updated} ${entityType} records${failed > 0 ? ` (${failed} failed)` : ''}`,
        updated,
        failed,
        next_id: `${prefix}-${String(nextNumber).padStart(6, '0')}`
      };

    } catch (error) {
      return {
        success: false,
        message: error.message
      };
    }
  };

  const handleBackfill = async (entityType) => {
    setLoading(true);
    setResult(null);
    setProgress({ current: 0, total: 0 });

    try {
      let res;
      switch (entityType) {
        case 'Account':
          res = await backfillEntity(Account, 'Account', 'ACCT');
          break;
        case 'Contact':
          res = await backfillEntity(Contact, 'Contact', 'CONT');
          break;
        case 'Lead':
          res = await backfillEntity(Lead, 'Lead', 'LEAD');
          break;
      }

      setResult(res);
    } catch (error) {
      setResult({
        success: false,
        message: error.message
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Card className="bg-slate-800 border-slate-700">
      <CardHeader>
        <CardTitle className="text-slate-100 flex items-center gap-2">
          <FileDigit className="w-5 h-5 text-blue-400" />
          Backfill Unique IDs
        </CardTitle>
        <p className="text-slate-400 text-sm">
          Generate unique IDs (CONT-000001, ACCT-000001, LEAD-000001) for records that are missing them
        </p>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="flex flex-wrap gap-3">
          <Button
            onClick={() => handleBackfill('Account')}
            disabled={loading}
            className="bg-blue-600 hover:bg-blue-700"
          >
            {loading ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Processing...
              </>
            ) : (
              'Backfill Accounts'
            )}
          </Button>

          <Button
            onClick={() => handleBackfill('Contact')}
            disabled={loading}
            className="bg-green-600 hover:bg-green-700"
          >
            {loading ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Processing...
              </>
            ) : (
              'Backfill Contacts'
            )}
          </Button>

          <Button
            onClick={() => handleBackfill('Lead')}
            disabled={loading}
            className="bg-yellow-600 hover:bg-yellow-700"
          >
            {loading ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                Processing...
              </>
            ) : (
              'Backfill Leads'
            )}
          </Button>
        </div>

        {loading && progress.total > 0 && (
          <div className="space-y-2">
            <div className="flex justify-between text-sm text-slate-400">
              <span>Progress: {progress.current} / {progress.total}</span>
              <span>{Math.round((progress.current / progress.total) * 100)}%</span>
            </div>
            <div className="w-full bg-slate-700 rounded-full h-2">
              <div 
                className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                style={{ width: `${(progress.current / progress.total) * 100}%` }}
              />
            </div>
            <p className="text-xs text-slate-500 italic">
              Processing slowly to avoid rate limits (3 seconds per record)...
            </p>
          </div>
        )}

        {result && (
          <div className={`rounded-lg p-4 ${
            result.success 
              ? 'bg-green-900/20 border border-green-700/50' 
              : 'bg-red-900/20 border border-red-700/50'
          }`}>
            <div className="flex items-start gap-3">
              {result.success ? (
                <CheckCircle className="w-5 h-5 text-green-400 flex-shrink-0 mt-0.5" />
              ) : (
                <AlertTriangle className="w-5 h-5 text-red-400 flex-shrink-0 mt-0.5" />
              )}
              <div className="flex-1">
                <p className={result.success ? 'text-green-100' : 'text-red-100'}>
                  {result.message}
                </p>
                {result.updated > 0 && (
                  <p className="text-slate-400 text-sm mt-1">
                    Updated {result.updated} records. Next ID: <code className="bg-slate-700 px-2 py-0.5 rounded">{result.next_id}</code>
                  </p>
                )}
              </div>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

