import React from 'react';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from '@/components/ui/alert-dialog';
import { AlertTriangle } from 'lucide-react';

/**
 * Reusable confirmation dialog component
 * 
 * Usage:
 * const [confirmDialog, setConfirmDialog] = useState(null);
 * 
 * <ConfirmDialog
 *   open={confirmDialog !== null}
 *   onConfirm={() => { confirmDialog?.onConfirm(); setConfirmDialog(null); }}
 *   onCancel={() => setConfirmDialog(null)}
 *   title={confirmDialog?.title}
 *   description={confirmDialog?.description}
 *   variant={confirmDialog?.variant}
 * />
 * 
 * // To show dialog:
 * setConfirmDialog({
 *   title: "Delete Item?",
 *   description: "This action cannot be undone.",
 *   variant: "destructive",
 *   onConfirm: () => { // perform action }
 * });
 */
export default function ConfirmDialog({
  open,
  onConfirm,
  onCancel,
  title = "Are you sure?",
  description = "This action cannot be undone.",
  variant = "default", // "default" | "destructive"
  confirmText = "Confirm",
  cancelText = "Cancel"
}) {
  return (
    <AlertDialog open={open} onOpenChange={(isOpen) => !isOpen && onCancel()}>
      <AlertDialogContent>
        <AlertDialogHeader>
          <AlertDialogTitle className="flex items-center gap-2">
            {variant === "destructive" && (
              <AlertTriangle className="h-5 w-5 text-destructive" />
            )}
            {title}
          </AlertDialogTitle>
          <AlertDialogDescription>
            {description}
          </AlertDialogDescription>
        </AlertDialogHeader>
        <AlertDialogFooter>
          <AlertDialogCancel onClick={onCancel}>
            {cancelText}
          </AlertDialogCancel>
          <AlertDialogAction
            onClick={onConfirm}
            className={variant === "destructive" ? "bg-destructive hover:bg-destructive/90" : ""}
          >
            {confirmText}
          </AlertDialogAction>
        </AlertDialogFooter>
      </AlertDialogContent>
    </AlertDialog>
  );
}

/**
 * Hook for easier confirmation dialog management
 * 
 * Usage:
 * const { ConfirmDialog, confirm } = useConfirmDialog();
 * 
 * const handleDelete = async () => {
 *   const confirmed = await confirm({
 *     title: "Delete Item?",
 *     description: "This action cannot be undone.",
 *     variant: "destructive"
 *   });
 *   
 *   if (confirmed) {
 *     // perform delete
 *   }
 * };
 * 
 * return (
 *   <>
 *     <ConfirmDialog />
 *     <Button onClick={handleDelete}>Delete</Button>
 *   </>
 * );
 */
export function useConfirmDialog() {
  const [dialogState, setDialogState] = React.useState(null);

  const confirm = React.useCallback((options) => {
    return new Promise((resolve) => {
      setDialogState({
        ...options,
        onConfirm: () => {
          setDialogState(null);
          resolve(true);
        },
        onCancel: () => {
          setDialogState(null);
          resolve(false);
        }
      });
    });
  }, []);

  const ConfirmDialogComponent = React.useCallback(() => {
    if (!dialogState) return null;

    return (
      <ConfirmDialog
        open={true}
        onConfirm={dialogState.onConfirm}
        onCancel={dialogState.onCancel}
        title={dialogState.title}
        description={dialogState.description}
        variant={dialogState.variant}
        confirmText={dialogState.confirmText}
        cancelText={dialogState.cancelText}
      />
    );
  }, [dialogState]);

  return { ConfirmDialog: ConfirmDialogComponent, confirm };
}

import React, { useState, useEffect } from "react";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Button } from "@/components/ui/button";
import { Command, CommandEmpty, CommandGroup, CommandInput, CommandItem, CommandList } from "@/components/ui/command";
import { Check, ChevronsUpDown, User, Plus } from "lucide-react";
import { cn } from "@/lib/utils";

export default function ContactSelector({ 
  contacts = [], 
  value, 
  onValueChange, 
  placeholder = "Select contact...", 
  disabled = false, 
  className = "", 
  contentClassName = "", 
  itemClassName = "",
  required = false
}) {
  const [open, setOpen] = useState(false);
  const [searchValue, setSearchValue] = useState("");

  const selectedContact = contacts.find((contact) => contact.id === value);

  // Filter contacts based on search
  const filteredContacts = contacts.filter(contact => {
    const fullName = `${contact.first_name || ''} ${contact.last_name || ''}`.trim();
    const email = contact.email || '';
    const searchLower = searchValue.toLowerCase();
    return fullName.toLowerCase().includes(searchLower) || 
           email.toLowerCase().includes(searchLower);
  });

  const getDisplayName = (contact) => {
    const fullName = `${contact.first_name || ''} ${contact.last_name || ''}`.trim();
    return fullName || contact.email || 'Unnamed Contact';
  };

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          role="combobox"
          aria-expanded={open}
          disabled={disabled}
          className={cn("w-full justify-between", className)}
        >
          {selectedContact ? (
            <div className="flex items-center gap-2 truncate">
              <User className="w-4 h-4 text-slate-500 flex-shrink-0" />
              <span className="truncate">{getDisplayName(selectedContact)}</span>
            </div>
          ) : (
            <span className="text-slate-500">{placeholder}</span>
          )}
          <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent className={cn("w-[--radix-popover-trigger-width] p-0", contentClassName)}>
        <Command>
          <CommandInput 
            placeholder="Search contacts..." 
            value={searchValue}
            onValueChange={setSearchValue}
          />
          <CommandList>
            <CommandEmpty>
              <div className="py-6 text-center text-sm">
                <p>No contacts found matching "{searchValue}"</p>
              </div>
            </CommandEmpty>
            <CommandGroup>
              {!required && (
                <CommandItem
                  value="__none__"
                  onSelect={() => {
                    onValueChange(null);
                    setOpen(false);
                  }}
                  className={cn("cursor-pointer", itemClassName)}
                >
                  <Check className={cn("mr-2 h-4 w-4", !value ? "opacity-100" : "opacity-0")} />
                  <span className="text-muted-foreground">No contact</span>
                </CommandItem>
              )}
              {filteredContacts.map((contact) => (
                <CommandItem
                  key={contact.id}
                  value={getDisplayName(contact)}
                  onSelect={() => {
                    onValueChange(contact.id === value ? null : contact.id);
                    setOpen(false);
                  }}
                  className={cn("cursor-pointer", itemClassName)}
                >
                  <Check
                    className={cn(
                      "mr-2 h-4 w-4",
                      value === contact.id ? "opacity-100" : "opacity-0"
                    )}
                  />
                  <div className="flex items-center gap-2">
                    <User className="w-4 h-4 text-slate-500" />
                    <div className="flex flex-col">
                      <span className="font-medium">{getDisplayName(contact)}</span>
                      {contact.email && (
                        <span className="text-xs text-muted-foreground">
                          {contact.email}
                        </span>
                      )}
                      {contact.company && (
                        <span className="text-xs text-muted-foreground">
                          {contact.company}
                        </span>
                      )}
                    </div>
                  </div>
                </CommandItem>
              ))}
            </CommandGroup>
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );
}

import React, { useEffect, useRef } from 'react';
import { cronJobRunner } from '@/api/functions';
import { User } from '@/api/entities';
import { useErrorLog, handleApiError, createError } from './ErrorLogger';

export default function CronHeartbeat() {
  const lastRunRef = useRef(null);
  const isRunningRef = useRef(false);
  const failureCountRef = useRef(0);
  const MAX_FAILURES = 3;
  const { logError } = useErrorLog();

  useEffect(() => {
    const checkAndRunCronJobs = async () => {
      if (isRunningRef.current || failureCountRef.current >= MAX_FAILURES) {
        return;
      }

      const now = Date.now();
      if (lastRunRef.current && now - lastRunRef.current < 5 * 60 * 1000) {
        return;
      }

      try {
        const user = await User.me();

        if (user?.role !== 'admin' && user?.role !== 'superadmin') {
          return;
        }

        isRunningRef.current = true;
        lastRunRef.current = now;

        const timeoutPromise = new Promise((_, reject) => 
          setTimeout(() => reject(new Error('Cron execution timeout')), 30000)
        );

        const cronPromise = cronJobRunner({}).catch(err => {
          throw err;
        });

        await Promise.race([cronPromise, timeoutPromise]);

        failureCountRef.current = 0;

      } catch (error) {
        failureCountRef.current++;

        if (failureCountRef.current >= MAX_FAILURES && logError) {
          logError(createError('Cron System', 'Cron jobs disabled after multiple failures', {
            severity: 'critical',
            actionable: 'Check Settings  System Health. Page refresh required to resume.',
            details: error?.message
          }));
        } else if (logError) {
          logError(handleApiError('Cron System', error));
        }
      } finally {
        isRunningRef.current = false;
      }
    };

    const initialTimeout = setTimeout(checkAndRunCronJobs, 10000);
    const interval = setInterval(checkAndRunCronJobs, 5 * 60 * 1000);

    return () => {
      clearTimeout(initialTimeout);
      clearInterval(interval);
    };
  }, [logError]);

  return null;
}

import React, { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription, DialogTrigger } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Download, Upload, Loader2 } from 'lucide-react';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import CsvImportDialog from './CsvImportDialog';
import * as Entities from "@/api/entities";

const EXPORTABLE_ENTITIES = ["Lead", "Contact", "Account", "Opportunity", "Activity", "Employee"];
const IMPORTABLE_ENTITIES = ["Lead", "Contact", "Account", "Opportunity", "Activity", "Employee"];

function ExportTab() {
  const [isExporting, setIsExporting] = useState(null);

  const downloadCsv = async (entityName) => {
    setIsExporting(entityName);
    try {
      const Entity = Entities[entityName];
      if (!Entity) throw new Error(`Invalid entity: ${entityName}`);

      const data = await Entity.list();

      if (!data || data.length === 0) {
        alert(`No data available to export for ${entityName}.`);
        return;
      }

      const headers = Object.keys(data[0]);
      const replacer = (key, value) => value === null ? '' : value;

      const csvContent = [
        headers.join(','),
        ...data.map(row => headers.map(header => JSON.stringify(row[header], replacer).replace(/,/g, '')).join(','))
      ].join('\n');

      const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
      const link = document.createElement('a');
      const url = URL.createObjectURL(blob);
      link.setAttribute('href', url);
      link.setAttribute('download', `${entityName.toLowerCase()}-export.csv`);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (error) {
      console.error("Error exporting to CSV:", error);
      alert(`An error occurred during export: ${error.message}`);
    } finally {
      setIsExporting(null);
    }
  };

  return (
    <div className="space-y-4">
      <p className="text-sm text-slate-600">
        Select a data type to download a CSV file of all its records.
      </p>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {EXPORTABLE_ENTITIES.map(entityName => (
          <Button
            key={entityName}
            variant="outline"
            onClick={() => downloadCsv(entityName)}
            disabled={isExporting}
            className="justify-start"
          >
            {isExporting === entityName ? (
              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
            ) : (
              <Download className="w-4 h-4 mr-2" />
            )}
            Export All {entityName}s
          </Button>
        ))}
      </div>
    </div>
  );
}

function ImportTab() {
  const [selectedEntity, setSelectedEntity] = useState('');
  const [showImportDialog, setShowImportDialog] = useState(false);

  const handleStartImport = () => {
    if (selectedEntity) {
      setShowImportDialog(true);
    } else {
      alert("Please select a data type to import.");
    }
  };

  return (
    <div className="space-y-4">
      <p className="text-sm text-slate-600">
        Select the type of data you want to import, then you will be prompted to upload your CSV file.
      </p>
      <div className="flex items-end gap-4">
        <div className="flex-grow">
          <Label htmlFor="entity-select">Data Type</Label>
          <Select value={selectedEntity} onValueChange={setSelectedEntity}>
            <SelectTrigger id="entity-select">
              <SelectValue placeholder="Choose data type..." />
            </SelectTrigger>
            <SelectContent>
              {IMPORTABLE_ENTITIES.map(name => (
                <SelectItem key={name} value={name}>{name}</SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
        <Button onClick={handleStartImport} disabled={!selectedEntity}>
          <Upload className="w-4 h-4 mr-2" />
          Start Import
        </Button>
      </div>

      {showImportDialog && selectedEntity && (
        <CsvImportDialog
          open={showImportDialog}
          onOpenChange={setShowImportDialog}
          entity={Entities[selectedEntity]}
          schema={Entities[selectedEntity]?.schema()}
          onSuccess={() => {
            alert(`${selectedEntity}s imported successfully! The page will now refresh to show the new data.`);
            window.location.reload();
          }}
        />
      )}
    </div>
  );
}

export default function DataManagementDialog({ children }) {
  return (
    <Dialog>
      <DialogTrigger asChild>{children}</DialogTrigger>
      <DialogContent className="max-w-2xl">
        <DialogHeader>
          <DialogTitle>Data Management</DialogTitle>
          <DialogDescription>
            Import data from a CSV file or export existing data.
          </DialogDescription>
        </DialogHeader>
        <Tabs defaultValue="import">
