            <TabsTrigger value="import">Import</TabsTrigger>
            <TabsTrigger value="export">Export</TabsTrigger>
          </TabsList>
          <TabsContent value="import" className="pt-4">
            <ImportTab />
          </TabsContent>
          <TabsContent value="export" className="pt-4">
            <ExportTab />
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

