          {employees.map((emp) => (
            <SelectItem 
              key={emp.id} 
              value={emp.user_email}
              className="hover:bg-slate-700"
            >
              {emp.first_name} {emp.last_name}
            </SelectItem>
          ))}
        </SelectContent>
      </Select>
    </div>
  );
}

import React, { useState, useEffect, useMemo } from 'react';
import { Employee } from '@/api/entities';
import { User } from '@/api/entities';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Loader2 } from 'lucide-react';

export default function EmployeeSelector({
  value,
  onValueChange,
  placeholder = "Select employee...",
  className = "",
  disabled = false,
  includeUnassigned = true,
  ...props
}) {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadData = async () => {
      try {
        const currentUser = await User.me();

        // Build filter based on user's tenant
        let filter = {};
        if (currentUser.tenant_id) {
          filter.tenant_id = currentUser.tenant_id;
        }

        // Load active employees
        filter.is_active = true;

        const employeesData = await Employee.filter(filter);
        setEmployees(employeesData || []);
      } catch (error) {
        console.error('Failed to load employees:', error);
        setEmployees([]);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  // Sort employees by name
  const sortedEmployees = useMemo(() => {
    return [...employees].sort((a, b) => {
      const nameA = `${a.first_name} ${a.last_name}`.toLowerCase();
      const nameB = `${b.first_name} ${b.last_name}`.toLowerCase();
      return nameA.localeCompare(nameB);
    });
  }, [employees]);

  if (loading) {
    return (
      <div className={`flex items-center gap-2 p-2 bg-slate-700 border border-slate-600 rounded-md ${className}`}>
        <Loader2 className="w-4 h-4 animate-spin text-slate-400" />
        <span className="text-sm text-slate-400">Loading employees...</span>
      </div>
    );
  }

  return (
    <Select value={value || ''} onValueChange={onValueChange} disabled={disabled} {...props}>
      <SelectTrigger className={className}>
        <SelectValue placeholder={placeholder} />
      </SelectTrigger>
      <SelectContent className="bg-slate-800 border-slate-700 z-[2147483010]">
        {includeUnassigned && (
          <SelectItem value={null} className="text-slate-200 hover:bg-slate-700">
            Unassigned
          </SelectItem>
        )}
        {sortedEmployees.map((employee) => (
          <SelectItem 
            key={employee.id} 
            value={employee.email || employee.user_email} 
            className="text-slate-200 hover:bg-slate-700"
          >
            {employee.first_name} {employee.last_name}
            {employee.job_title && <span className="text-slate-400 text-xs ml-2">({employee.job_title})</span>}
          </SelectItem>
        ))}
        {sortedEmployees.length === 0 && (
          <SelectItem value="__no_employees__" disabled className="text-slate-500">
            No employees found
          </SelectItem>
        )}
      </SelectContent>
    </Select>
  );
}

import React from 'react';
import { AlertTriangle } from 'lucide-react';
import { Button } from '@/components/ui/button';

class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      hasError: false, 
      error: null, 
      errorInfo: null 
    };
  }

  static getDerivedStateFromError(error) {
    // Update state so the next render will show the fallback UI
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    // Log error to console in development
    if (import.meta.env.DEV) {
      console.error('ErrorBoundary caught an error:', error, errorInfo);
    }
    
    // Store error details in state
    this.setState({
      error,
      errorInfo
    });

    // You can also log the error to an error reporting service here
    // Example: logErrorToService(error, errorInfo);
  }

  handleReset = () => {
    this.setState({ 
      hasError: false, 
      error: null, 
      errorInfo: null 
    });
  };

  handleReload = () => {
    window.location.reload();
  };

  render() {
    if (this.state.hasError) {
      // Custom fallback UI
      return (
        <div className="flex items-center justify-center min-h-screen bg-gray-50">
          <div className="max-w-md w-full bg-white shadow-lg rounded-lg p-6">
            <div className="flex items-center justify-center w-12 h-12 mx-auto bg-red-100 rounded-full">
              <AlertTriangle className="w-6 h-6 text-red-600" />
            </div>
            
            <h2 className="mt-4 text-xl font-semibold text-center text-gray-900">
              Something went wrong
            </h2>
            
            <p className="mt-2 text-sm text-center text-gray-600">
              We're sorry, but something unexpected happened. Please try again.
            </p>

            {import.meta.env.DEV && this.state.error && (
              <div className="mt-4 p-3 bg-gray-100 rounded text-xs overflow-auto max-h-40">
                <p className="font-semibold text-red-600">{this.state.error.toString()}</p>
                {this.state.errorInfo && (
                  <pre className="mt-2 text-gray-700 whitespace-pre-wrap">
                    {this.state.errorInfo.componentStack}
                  </pre>
                )}
              </div>
            )}

            <div className="mt-6 flex gap-3">
              <Button 
                onClick={this.handleReset}
                variant="outline"
                className="flex-1"
              >
                Try Again
              </Button>
              <Button 
                onClick={this.handleReload}
                className="flex-1"
              >
                Reload Page
              </Button>
            </div>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;

import { useState, useEffect, createContext, useContext } from 'react';
import { User } from '@/api/entities';

// Client-side error log storage
const errorLog = [];
const MAX_ERRORS = 100;

const ErrorLogContext = createContext();

export function useErrorLog() {
  return useContext(ErrorLogContext);
}

export function ErrorLogProvider({ children }) {
  const [errors, setErrors] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    User.me().then(setUser).catch(() => {});
  }, []);

  const logError = (error) => {
    const errorEntry = {
      id: Date.now() + Math.random(),
      timestamp: new Date().toISOString(),
      message: error.message || 'Unknown error',
      component: error.component || 'Unknown',
      status: error.status,
      userEmail: user?.email,
      severity: error.severity || 'error',
      actionable: error.actionable || null,
      details: error.details || null
    };

    errorLog.unshift(errorEntry);
    if (errorLog.length > MAX_ERRORS) {
      errorLog.pop();
    }

    setErrors([...errorLog]);

    // Also log to console for developers
    console.error(`[${errorEntry.component}]`, errorEntry.message, errorEntry.details);
  };

  const clearErrors = () => {
    errorLog.length = 0;
    setErrors([]);
  };

  const getRecentErrors = (count = 10) => {
    return errorLog.slice(0, count);
  };

  const getCriticalErrors = () => {
    return errorLog.filter(e => e.severity === 'critical');
  };

  return (
    <ErrorLogContext.Provider value={{ 
      errors, 
      logError, 
      clearErrors, 
      getRecentErrors,
      getCriticalErrors
    }}>
      {children}
    </ErrorLogContext.Provider>
  );
}

// Helper to create structured errors
export function createError(component, message, options = {}) {
  return {
    component,
    message,
    status: options.status,
    severity: options.severity || 'error',
    actionable: options.actionable,
    details: options.details
  };
}

// HTTP status code helpers
export function handleApiError(component, error) {
  const status = error?.response?.status || error?.status;

  const errorMap = {
    403: {
      message: 'Permission denied',
      severity: 'warning',
      actionable: 'Check user permissions in Settings  User Management'
    },
    429: {
      message: 'Rate limit exceeded',
      severity: 'warning',
      actionable: 'System is busy. Wait a moment and try again.'
    },
    500: {
      message: 'Server error',
      severity: 'critical',
      actionable: 'System error occurred. Contact support if this persists.'
    },
    502: {
      message: 'Backend unavailable',
      severity: 'critical',
      actionable: 'Backend service is down. Try again in a few minutes.'
    },
    504: {
      message: 'Request timeout',
      severity: 'warning',
      actionable: 'Operation took too long. Try again or contact support.'
    }
  };

  const errorInfo = errorMap[status] || {
    message: error?.message || 'Operation failed',
    severity: 'error',
    actionable: 'An unexpected error occurred. Please try again.'
  };

  return createError(component, errorInfo.message, {
    status,
    severity: errorInfo.severity,
    actionable: errorInfo.actionable,
    details: error?.message
  });
}

import React from "react";
import { Button } from "@/components/ui/button";
