export default function GlobalDetailViewer({ recordInfo, open, onClose }) {
  const [editMode, setEditMode] = useState(false);
  const [editedRecord, setEditedRecord] = useState({});
  const [saving, setSaving] = useState(false);

  if (!open || !recordInfo) return null;

  const { record, entityType } = recordInfo;

  const title = record.name || `${record.first_name || ''} ${record.last_name || ''}`.trim();
  const Icon = entityIcons[entityType] || <User className="w-5 h-5" />;

  const formatDate = (dateString) => {
    try {
      return format(new Date(dateString), 'PPp');
    } catch {
      return dateString;
    }
  };

  const handleEdit = () => {
    setEditMode(true);
    setEditedRecord({ ...record });
  };

  const handleCancel = () => {
    setEditMode(false);
    setEditedRecord({});
  };

  const handleValueChange = (field, value) => {
    setEditedRecord(prev => ({ ...prev, [field]: value }));
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      const Model = entityType === 'Contact' ? Contact :
                   entityType === 'Lead' ? Lead :
                   entityType === 'Account' ? Account :
                   Opportunity;

      // Only send changed fields
      const changes = {};
      Object.keys(editedRecord).forEach(key => {
        if (editedRecord[key] !== record[key] && !key.startsWith('_') && key !== 'id' && key !== 'created_date' && key !== 'updated_date') {
          changes[key] = editedRecord[key];
        }
      });

      if (Object.keys(changes).length > 0) {
        await Model.update(record.id, changes);
        // Update the record in the viewer
        Object.assign(record, changes);
        console.log(`Updated ${entityType}:`, changes);
      }

      setEditMode(false);
    } catch (error) {
      console.error('Failed to update record:', error);
      alert(`Failed to update record: ${error.message}`);
    } finally {
      setSaving(false);
    }
  };

  const currentRecord = editMode ? editedRecord : record;

  return (
    <div 
      className="fixed inset-0 bg-black/60 z-50 flex justify-end"
      onClick={onClose}
    >
      <div 
        className="w-full max-w-md h-full bg-slate-800 shadow-2xl border-l border-slate-700 flex flex-col"
        onClick={(e) => e.stopPropagation()}
      >
        <CardHeader className="flex flex-row items-center justify-between p-4 border-b border-slate-700">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 flex items-center justify-center rounded-lg bg-slate-700">
              {Icon}
            </div>
            <div>
              <CardTitle className="text-lg text-slate-100">{title}</CardTitle>
              <Badge variant="secondary" className="mt-1 bg-slate-900 border-slate-700 text-slate-300">{entityType}</Badge>
            </div>
          </div>
          <div className="flex items-center gap-2">
            {!editMode ? (
              <Button variant="ghost" size="sm" onClick={handleEdit} className="text-slate-400 hover:text-slate-100">
                <Edit3 className="w-4 h-4" />
              </Button>
            ) : (
              <>
                <Button variant="ghost" size="sm" onClick={handleCancel} className="text-slate-400 hover:text-slate-100">
                  <RotateCcw className="w-4 h-4" />
                </Button>
                <Button variant="ghost" size="sm" onClick={handleSave} disabled={saving} className="text-green-400 hover:text-green-300">
                  <Save className="w-4 h-4" />
                </Button>
              </>
            )}
            <Button variant="ghost" size="icon" onClick={onClose} className="text-slate-400 hover:text-slate-100">
              <X className="w-5 h-5" />
            </Button>
          </div>
        </CardHeader>
        <CardContent className="p-4 overflow-y-auto flex-1">
          <dl className="space-y-1">
            <DetailRow label="First Name" value={currentRecord.first_name} field="first_name" editMode={editMode} onValueChange={handleValueChange} />
            <DetailRow label="Last Name" value={currentRecord.last_name} field="last_name" editMode={editMode} onValueChange={handleValueChange} />
