import React, { useState, useEffect, useCallback } from "react";
import { Note } from "@/api/entities";
import { User } from "@/api/entities";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import {
  Plus,
  StickyNote,
  Trash2,
  Clock,
  User as UserIcon,
  Loader2,
  AlertCircle,
  CheckCircle2
} from "lucide-react";
import { motion } from "framer-motion";
import { format } from "date-fns";

const noteTypes = [
  { value: "general", label: "General", color: "text-slate-400" },
  { value: "call_log", label: "Call Log", color: "text-blue-400" },
  { value: "meeting", label: "Meeting", color: "text-emerald-400" },
  { value: "email", label: "Email", color: "text-purple-400" },
  { value: "follow_up", label: "Follow-up", color: "text-yellow-400" },
  { value: "important", label: "Important", color: "text-red-400" },
];

const getTypeColor = (type) => {
  return noteTypes.find(t => t.value === type)?.color || "text-slate-400";
};

export default function NotesSection({ relatedTo, relatedId, className = "" }) {
  const [notes, setNotes] = useState([]);
  const [loadingNotes, setLoadingNotes] = useState(true);
  const [currentUser, setCurrentUser] = useState(null);
  const [userLoading, setUserLoading] = useState(true);

  const [newNote, setNewNote] = useState({
    title: "",
    content: "",
    type: "general",
  });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [loading, setLoading] = useState(false); // For the add note button

  useEffect(() => {
    loadUser();
  }, []);

  const loadUser = async () => {
    setUserLoading(true);
    try {
      const user = await User.me();
      setCurrentUser(user);

      if (!user.tenant_id) {
        console.warn("User does not have a tenant_id assigned. Please contact your administrator.");
      }
    } catch (error) {
      console.error("Error loading user:", error);
      setError("Failed to load user information.");
    } finally {
      setUserLoading(false);
    }
  };

  const loadNotes = useCallback(async () => {
    if (!currentUser?.tenant_id) return;
    setLoadingNotes(true);
    setError(null);
    try {
      const notesList = await Note.filter(
        { related_to: relatedTo, related_id: relatedId },
        '-created_date'
      );
      setNotes(notesList);
    } catch (error) {
      console.error("Error loading notes:", error);
      setError("Failed to load notes. Please try again.");
    } finally {
      setLoadingNotes(false);
    }
  }, [relatedTo, relatedId, currentUser?.tenant_id]);

  useEffect(() => {
    if (currentUser && currentUser.tenant_id) {
      loadNotes();
    }
  }, [relatedId, currentUser, loadNotes]);

  const handleAddNote = async () => {
    setError(null);
    setSuccess(null);

    if (!currentUser?.tenant_id) {
      setError("Error: User information not loaded or tenant not assigned. Please contact your administrator.");
      return;
    }

    if (!newNote.title.trim()) {
      setError("Please enter a title for the note.");
      return;
    }

    if (!newNote.content.trim()) {
      setError("Please enter content for the note.");
      return;
    }

    setLoading(true);
    try {
      const notePayload = {
        ...newNote,
        title: newNote.title.trim(),
        content: newNote.content.trim(),
        tenant_id: currentUser.tenant_id,
        related_to: relatedTo,
        related_id: relatedId,
        is_private: false, // Default value as the form doesn't support it
        tags: [], // Default value as the form doesn't support it
      };

      await Note.create(notePayload);
      setNewNote({ title: "", content: "", type: "general" });
      setSuccess("Note added successfully!");
      await loadNotes();
    } catch (error) {
      console.error("Error adding note:", error);
      setError(`Error adding note: ${error.message || 'Please try again or contact support.'}`);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteNote = async (noteId) => {
    if (confirm("Are you sure you want to delete this note?")) {
      setError(null);
      setSuccess(null);
      try {
        await Note.delete(noteId);
        setSuccess("Note deleted successfully!");
        await loadNotes();
      } catch (error) {
        console.error("Error deleting note:", error);
        setError("Error deleting note. Please try again.");
      }
    }
  };

  return (
    <div className={`space-y-4 ${className}`}>
      {/* Add Note Form */}
      <div className="bg-slate-700/30 p-4 rounded-lg border border-slate-600">
        <div className="space-y-3">
          <Input
            placeholder="Note title..."
            value={newNote.title}
            onChange={(e) => setNewNote(prev => ({ ...prev, title: e.target.value }))}
            className="bg-slate-700 border-slate-600 text-slate-200 placeholder:text-slate-400 focus:border-slate-500"
          />
          <Textarea
            placeholder="Write your note here..."
            value={newNote.content}
            onChange={(e) => setNewNote(prev => ({ ...prev, content: e.target.value }))}
            rows={3}
            className="bg-slate-700 border-slate-600 text-slate-200 placeholder:text-slate-400 focus:border-slate-500"
          />
          <div className="flex justify-between items-center">
            <Select
              value={newNote.type}
              onValueChange={(value) => setNewNote(prev => ({ ...prev, type: value }))}
            >
              <SelectTrigger className="w-40 bg-slate-700 border-slate-600 text-slate-200">
                <SelectValue placeholder="Note type" />
              </SelectTrigger>
              <SelectContent className="bg-slate-800 border-slate-700">
                {noteTypes.map(type => (
                  <SelectItem key={type.value} value={type.value} className="text-slate-200 hover:bg-slate-700">
                    {type.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            <Button
              onClick={handleAddNote}
              disabled={!newNote.title.trim() || !newNote.content.trim() || loading || !currentUser?.tenant_id}
              size="sm"
              className="bg-blue-600 hover:bg-blue-700 text-white"
            >
              {loading ? (
                <Loader2 className="w-4 h-4 animate-spin mr-2" />
              ) : (
                <Plus className="w-4 h-4 mr-2" />
              )}
              Add Note
            </Button>
          </div>
        </div>
      </div>

      {/* Notes List */}
      {loadingNotes ? (
        <div className="space-y-3">
          {[...Array(3)].map((_, i) => (
            <div key={i} className="bg-slate-700/30 rounded-lg p-4 border border-slate-600">
              <div className="animate-pulse">
                <div className="h-4 bg-slate-600 rounded w-1/3 mb-2"></div>
                <div className="h-3 bg-slate-600 rounded w-full mb-1"></div>
                <div className="h-3 bg-slate-600 rounded w-2/3"></div>
              </div>
            </div>
          ))}
        </div>
      ) : notes.length > 0 ? (
        <div className="space-y-3">
          {notes.map(note => (
            <motion.div
              key={note.id}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              className="bg-slate-700/30 rounded-lg p-4 border border-slate-600 hover:bg-slate-700/50 transition-colors"
            >
              <div className="flex justify-between items-start mb-2">
                <h4 className="font-medium text-slate-200">{note.title}</h4>
                <div className="flex items-center gap-2">
                  <Badge
                    variant="outline"
                    className={`text-xs ${getTypeColor(note.type)} border-slate-600`}
                  >
                    {noteTypes.find(t => t.value === note.type)?.label || note.type}
                  </Badge>
                  {!note.is_private && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => handleDeleteNote(note.id)}
                      className="h-6 w-6 p-0 text-slate-400 hover:text-red-400 hover:bg-red-900/20"
                    >
                      <Trash2 className="w-3 h-3" />
                    </Button>
                  )}
                </div>
              </div>
              <p className="text-sm text-slate-300 mb-2 whitespace-pre-wrap">{note.content}</p>
              <div className="flex justify-between items-center text-xs text-slate-500">
                <div className="flex items-center gap-1">
                  <UserIcon className="w-3 h-3" />
                  <span>
                    {note.created_by === currentUser?.email ? 'You' : note.created_by}
                  </span>
                </div>
                <div className="flex items-center gap-1">
                  <Clock className="w-3 h-3" />
                  <span>
                    {format(new Date(note.created_date), 'MMM d, yyyy h:mm a')}
                  </span>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      ) : (
        <div className="text-center py-8 bg-slate-700/30 rounded-lg border border-slate-600">
          <StickyNote className="w-8 h-8 mx-auto mb-2 text-slate-500" />
          <p className="text-slate-400">No notes yet</p>
          <p className="text-sm text-slate-500">Add your first note above</p>
        </div>
      )}

      {error && (
        <Alert variant="destructive" className="bg-red-900/20 border-red-700/50 text-red-300">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      {success && (
        <Alert className="bg-green-900/20 border-green-700/50 text-green-300">
          <CheckCircle2 className="h-4 w-4" />
          <AlertDescription>{success}</AlertDescription>
        </Alert>
      )}
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { Sheet, SheetContent, SheetHeader, SheetTitle, SheetTrigger } from "@/components/ui/sheet";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Bell, Loader2, Users, Star, Target, Calendar, AlertTriangle } from "lucide-react";
import { Notification } from "@/api/entities";
import { User } from "@/api/entities";
import { formatDistanceToNow } from "date-fns";

const iconMap = {
  bell: Bell,
  users: Users,
  star: Star,
  target: Target,
  calendar: Calendar,
  alert: AlertTriangle,
};

export default function NotificationPanel() {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isOpen, setIsOpen] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);

  useEffect(() => {
    loadNotifications();
  }, []);

  const loadNotifications = async () => {
    try {
      setLoading(true);
      const user = await User.me();
      const fetchedNotifications = await Notification.filter(
        { recipient_id: user.id },
        '-created_date',
        50
      );
      setNotifications(fetchedNotifications);

      const unread = fetchedNotifications.filter(n => !n.is_read).length;
      setUnreadCount(unread);
    } catch (error) {
      console.error("Failed to load notifications:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleNotificationClick = async (notification) => {
    if (!notification.is_read) {
      try {
        await Notification.update(notification.id, { is_read: true });
        setNotifications(prev => 
          prev.map(n => n.id === notification.id ? { ...n, is_read: true } : n)
        );
        setUnreadCount(prev => Math.max(0, prev - 1));
      } catch (error) {
        console.error("Failed to mark notification as read:", error);
      }
    }

    if (notification.action_url) {
      window.location.href = notification.action_url;
    }
  };

  const markAllAsRead = async () => {
    try {
      const unreadNotifications = notifications.filter(n => !n.is_read);

      await Promise.all(
        unreadNotifications.map(n => 
          Notification.update(n.id, { is_read: true })
        )
      );

      setNotifications(prev => 
        prev.map(n => ({ ...n, is_read: true }))
      );
      setUnreadCount(0);
    } catch (error) {
      console.error("Failed to mark all notifications as read:", error);
    }
  };

  return (
    <Sheet open={isOpen} onOpenChange={setIsOpen}>
      <SheetTrigger asChild>
        <Button variant="ghost" size="icon" className="relative text-slate-400 hover:text-slate-200 hover:bg-slate-800">
          <Bell className="w-5 h-5" />
          {unreadCount > 0 && (
            <Badge className="absolute -top-1 -right-1 w-5 h-5 flex items-center justify-center p-0 text-xs bg-red-600 text-white border-2 border-slate-900">
              {unreadCount > 9 ? '9+' : unreadCount}
            </Badge>
          )}
        </Button>
      </SheetTrigger>
      <SheetContent className="w-80 bg-slate-800 border-slate-700 text-slate-200">
        <SheetHeader className="border-b border-slate-700 pb-4">
          <SheetTitle className="flex items-center justify-between text-slate-100">
            <span>Notifications</span>
            {unreadCount > 0 && (
              <Button
                variant="ghost"
                size="sm"
                onClick={markAllAsRead}
                className="text-blue-400 hover:text-blue-300 hover:bg-slate-700"
              >
                Mark all read
              </Button>
            )}
          </SheetTitle>
        </SheetHeader>

        <div className="mt-4 space-y-2 max-h-[70vh] overflow-y-auto">
          {loading ? (
            <div className="flex items-center justify-center py-8">
              <Loader2 className="w-6 h-6 animate-spin text-blue-400" />
              <span className="ml-2 text-slate-400">Loading notifications...</span>
            </div>
          ) : notifications.length === 0 ? (
            <div className="flex flex-col items-center justify-center py-8 text-slate-500">
              <Bell className="w-12 h-12 mb-2 text-slate-600" />
              <p>No notifications yet</p>
              <p className="text-sm">You'll see important updates here</p>
            </div>
          ) : (
            notifications.map((notification) => (
              <div
                key={notification.id}
                className={`p-4 rounded-lg border cursor-pointer transition-colors ${
                  notification.is_read
                    ? 'bg-slate-700/50 border-slate-600 text-slate-300'
                    : 'bg-blue-900/30 border-blue-700/50 text-slate-200'
                }`}
                onClick={() => handleNotificationClick(notification)}
              >
                <div className="flex items-start gap-3">
                  {notification.icon && (
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${
                      notification.is_read 
                        ? 'bg-slate-600 text-slate-400' 
                        : 'bg-blue-600 text-white'
                    }`}>
                      {React.createElement(
                        iconMap[notification.icon] || Bell,
                        { className: "w-4 h-4" }
                      )}
                    </div>
                  )}
                  <div className="flex-1 min-w-0">
                    <h4 className="font-medium text-sm">{notification.title}</h4>
                    {notification.description && (
                      <p className="text-sm text-slate-400 mt-1">
                        {notification.description}
                      </p>
                    )}
                    <p className="text-xs text-slate-500 mt-2">
                      {formatDistanceToNow(new Date(notification.created_date))} ago
                    </p>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </SheetContent>
    </Sheet>
  );
}

