            <span className="text-slate-400">{placeholder}</span>
          )}
          <div className="flex items-center gap-1">
            {selectedContact && (
              <X
                className="h-4 w-4 shrink-0 opacity-50 hover:opacity-100"
                onClick={handleClear}
