import React, { useState, useEffect } from "react";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { AlertTriangle, Loader2, CheckCircle } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

export default function SystemStatusIndicator({ user }) {
  const [status, setStatus] = useState({ type: 'normal', message: null, cooldown: 0 });
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    // Only show for admins and superadmins
    if (!user || (user.role !== 'admin' && user.role !== 'superadmin')) {
      return;
    }

    const handleRateLimit = (event) => {
      const { cooldownMs, queueLength } = event.detail;
      setStatus({
        type: 'rate-limit',
        message: `Rate limit active. Cooldown: ${Math.ceil(cooldownMs / 1000)}s. Queue: ${queueLength}`,
        cooldown: cooldownMs
      });
      setVisible(true);

      // Auto-hide after cooldown + 2 seconds
      setTimeout(() => {
        setVisible(false);
      }, cooldownMs + 2000);
    };

    window.addEventListener('ratelimit', handleRateLimit);

    return () => {
      window.removeEventListener('ratelimit', handleRateLimit);
    };
  }, [user]);

  // Don't render anything if user is not admin/superadmin
  if (!user || (user.role !== 'admin' && user.role !== 'superadmin')) {
    return null;
  }

  return (
    <AnimatePresence>
      {visible && (
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          className="fixed top-20 right-4 z-50 max-w-md"
        >
          <Alert 
            variant="destructive" 
            className="bg-yellow-900/90 border-yellow-700 text-yellow-100 backdrop-blur-sm"
          >
            <AlertTriangle className="h-4 w-4" />
            <AlertDescription className="text-sm">
              <strong>System Status (Admin Only):</strong> {status.message}
            </AlertDescription>
          </Alert>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

import React, { useState, useEffect } from 'react';
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { X, Filter, Search, Hash } from 'lucide-react';
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";

export default function TagFilter({
  allTags = [],
  selectedTags = [],
  onTagsChange,
  placeholder = "Filter by tags...",
  disabled = false,
  className = "",
  compact = false
}) {
  const [searchTerm, setSearchTerm] = useState('');
  const [isOpen, setIsOpen] = useState(false);

  const filteredTags = allTags.filter(tag =>
    tag.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const toggleTag = (tagName) => {
    const updatedTags = selectedTags.includes(tagName)
      ? selectedTags.filter(tag => tag !== tagName)
      : [...selectedTags, tagName];
    onTagsChange(updatedTags);
  };

  const clearAllTags = () => {
    onTagsChange([]);
  };

  return (
    <TooltipProvider>
      <div className={`flex items-center gap-2 ${className}`}>
        <Popover open={isOpen} onOpenChange={setIsOpen}>
          <Tooltip>
            <TooltipTrigger asChild>
              <PopoverTrigger asChild>
                <Button
                  variant="outline"
                  disabled={disabled}
                  className={`bg-slate-700 hover:bg-slate-600 text-slate-200 border-slate-600 ${compact ? 'h-7 text-xs px-3' : 'px-4'}`}
                >
                  <Filter className={`${compact ? 'w-3 h-3' : 'w-4 h-4'} mr-2`} />
                  Tags
                  {selectedTags.length > 0 && (
                    <Badge variant="secondary" className={`ml-2 bg-blue-600 text-white ${compact ? 'text-[10px] px-1 py-0' : ''}`}>
                      {selectedTags.length}
                    </Badge>
                  )}
                </Button>
              </PopoverTrigger>
            </TooltipTrigger>
            <TooltipContent>
              <p>Filter by tags</p>
            </TooltipContent>
          </Tooltip>
          <PopoverContent className="w-80 bg-slate-800 border-slate-700 text-slate-200" align="start">
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <h4 className="font-medium text-slate-100 flex items-center gap-2">
                  <Hash className="w-4 h-4" />
                  Filter by Tags
                </h4>
                {selectedTags.length > 0 && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={clearAllTags}
                    className="h-6 px-2 text-slate-400 hover:text-slate-200 rounded-full"
                  >
                    Clear all
                  </Button>
                )}
              </div>

              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-slate-400" />
                <Input
                  placeholder="Search tags..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 bg-slate-700 border-slate-600 text-slate-200 placeholder:text-slate-400 rounded-full"
                />
              </div>

              <div className="max-h-60 overflow-y-auto space-y-1">
                {filteredTags.length > 0 ? (
                  filteredTags.map((tag) => (
                    <div
                      key={tag.name}
                      className={`flex items-center justify-between p-2 rounded-full cursor-pointer transition-colors ${
                        selectedTags.includes(tag.name)
                          ? 'bg-blue-600/20 border border-blue-500/50'
                          : 'hover:bg-slate-700'
                      }`}
                      onClick={() => toggleTag(tag.name)}
                    >
                      <div className="flex items-center gap-2">
                        <div
                          className={`w-3 h-3 rounded-full border ${
                            selectedTags.includes(tag.name)
                              ? 'bg-blue-600 border-blue-500'
                              : 'border-slate-500'
                          }`}
                        />
                        <span className="text-sm text-slate-200">{tag.name}</span>
                      </div>
                      <Badge variant="outline" className="text-xs bg-slate-700 text-slate-300 border-slate-600 rounded-full">
                        {tag.count}
                      </Badge>
                    </div>
                  ))
                ) : (
                  <div className="text-center py-4 text-slate-400">
                    <Hash className="w-8 h-8 mx-auto mb-2 opacity-50" />
                    <p className="text-sm">
                      {allTags.length === 0 ? 'No tags available' : 'No tags match your search'}
                    </p>
                  </div>
                )}
              </div>
            </div>
          </PopoverContent>
        </Popover>

        {selectedTags.length > 0 && !compact && (
          <div className="flex items-center gap-2 flex-wrap">
            {selectedTags.map((tag) => (
              <Badge
                key={tag}
                variant="secondary"
                className="bg-blue-600 text-white hover:bg-blue-700 cursor-pointer rounded-full"
                onClick={() => toggleTag(tag)}
              >
                {tag}
                <X className="w-3 h-3 ml-1" />
              </Badge>
            ))}
          </div>
        )}
      </div>
    </TooltipProvider>
  );
}

import React, { useState, useRef } from 'react';
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Command, CommandEmpty, CommandGroup, CommandInput, CommandItem, CommandList } from "@/components/ui/command";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { X, Plus, Hash, Tag } from 'lucide-react';
import { cn } from "@/lib/utils";

export default function TagInput({ 
  selectedTags = [], 
  onTagsChange, 
  allTags = [], 
  placeholder = "Add or search for tags...",
  darkMode = false 
}) {
  const [open, setOpen] = useState(false);
  const [inputValue, setInputValue] = useState("");

  const addTag = (tagName) => {
    const trimmedTag = tagName.trim();
    if (trimmedTag && !selectedTags.includes(trimmedTag)) {
      onTagsChange([...selectedTags, trimmedTag]);
    }
    setInputValue("");
    setOpen(false);
  };

  const removeTag = (tagToRemove) => {
    onTagsChange(selectedTags.filter(tag => tag !== tagToRemove));
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' || e.key === ',') {
      e.preventDefault();
      if (inputValue.trim()) {
        addTag(inputValue);
      }
    }
  };

  const availableTags = allTags.filter(tag => 
    !selectedTags.includes(tag.name) && 
    tag.name.toLowerCase().includes(inputValue.toLowerCase())
  );

  const triggerClasses = cn(
    "flex flex-wrap items-center gap-2 w-full min-h-10 px-3 py-2 text-sm text-left rounded-md border",
    darkMode ? "bg-slate-700 border-slate-600 text-slate-200" : "bg-white border-input",
    "hover:bg-accent hover:text-accent-foreground"
  );

  const popoverContentClasses = cn(
    "w-80 p-0",
    darkMode ? "bg-slate-800 border-slate-700 text-slate-200" : "bg-popover text-popover-foreground"
  );

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <button type="button" className={triggerClasses}>
          {selectedTags.length > 0 ? (
            selectedTags.map((tag) => (
              <Badge 
                key={tag} 
                variant="secondary" 
                className={cn(
                  "flex items-center gap-1",
                  darkMode ? "bg-slate-600 text-slate-100 hover:bg-slate-500" : "bg-secondary text-secondary-foreground"
                )}
              >
                {tag}
                <X 
                  className="w-3 h-3 cursor-pointer" 
                  onClick={(e) => {
                    e.stopPropagation(); // prevent popover from opening
                    removeTag(tag);
                  }}
                />
              </Badge>
            ))
          ) : (
            <span className={cn("text-sm", darkMode ? "text-slate-400" : "text-muted-foreground")}>
              {placeholder}
            </span>
          )}
        </button>
      </PopoverTrigger>

      <PopoverContent className={popoverContentClasses} align="start" side="bottom">
        <Command onKeyDown={handleKeyDown} className={darkMode ? "bg-slate-800" : ""}>
          <CommandInput 
            placeholder="Search or create a tag..." 
            value={inputValue}
            onValueChange={setInputValue}
            className={darkMode ? "text-slate-200" : ""}
          />
          <CommandList>
            <CommandEmpty className={cn("py-6 text-center text-sm", darkMode ? "text-slate-400" : "text-muted-foreground")}>
              {inputValue ? (
                <div className="space-y-2">
                  <Tag className="w-6 h-6 mx-auto opacity-50" />
                  <p>No existing tags found</p>
                  <Button
                    size="sm"
                    onClick={() => addTag(inputValue)}
                    className={cn(darkMode ? "bg-blue-600 hover:bg-blue-700" : "bg-primary text-primary-foreground")}
                  >
                    Create "{inputValue}"
                  </Button>
                </div>
              ) : (
                <p>Start typing to see existing tags</p>
              )}
            </CommandEmpty>

            {availableTags.length > 0 && (
              <CommandGroup heading="Existing Tags">
                {availableTags.slice(0, 10).map((tag) => (
                  <CommandItem
                    key={tag.name}
                    value={tag.name}
                    onSelect={() => addTag(tag.name)}
                    className={cn("cursor-pointer", darkMode ? "text-slate-200 aria-selected:bg-slate-700" : "")}
                  >
                    <div className="flex items-center justify-between w-full">
                      <span className="flex items-center gap-2">
                        <Hash className="w-4 h-4 opacity-50" />
                        {tag.name}
                      </span>
                      <Badge variant="secondary" className="text-xs">
                        {tag.count}
                      </Badge>
                    </div>
                  </CommandItem>
                ))}
              </CommandGroup>
            )}

            {inputValue && !availableTags.some(tag => tag.name.toLowerCase() === inputValue.toLowerCase()) && (
              <CommandGroup heading="Create New">
                <CommandItem
                  value={inputValue}
                  onSelect={() => addTag(inputValue)}
                  className={cn("cursor-pointer", darkMode ? "text-slate-200 aria-selected:bg-slate-700" : "")}
                >
                  <div className="flex items-center gap-2">
                    <Plus className="w-4 h-4 opacity-50" />
                    Create "{inputValue}"
                  </div>
                </CommandItem>
              </CommandGroup>
            )}
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );
}

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Tenant } from "@/api/entities"; // Internal entity, remains Tenant
import { User } from "@/api/entities"; // Internal entity, remains User
import { Loader2, Copy, Building2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { toast } from "sonner";

export default function TenantIdViewer() { // Component name remains TenantIdViewer, as per existing file
  const [tenants, setTenants] = useState([]); // Internal state variable, remains tenants
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      try {
        const user = await User.me();
        setCurrentUser(user);

        if (user.role === 'admin' || user.role === 'superadmin') {
          // Admins can see all tenants (internal logic)
          const tenantsData = await Tenant.list(); // Internal entity method, remains Tenant.list
          setTenants(tenantsData);
        } else if (user.tenant_id) { // Internal ID, remains tenant_id
          // Non-admins should only see their own client ID
          try {
            const myTenant = await Tenant.get(user.tenant_id); // Internal entity method, remains Tenant.get
            setTenants(myTenant ? [myTenant] : [{ id: user.tenant_id, name: 'Your Client' }]); // User-facing text changed
          } catch {
            setTenants([{ id: user.tenant_id, name: "Your Client" }]); // User-facing text changed
          }
        }
      } catch (error) {
        console.error("Error loading client information:", error); // User-facing text changed
        toast.error("Failed to load client information."); // User-facing text changed
        setTenants([]);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    toast.success("Client ID copied to clipboard!"); // User-facing text changed
  };

  if (loading) {
    return (
      <Card>
        <CardContent className="flex items-center justify-center p-12">
          <Loader2 className="w-8 h-8 animate-spin mr-3" />
          <span>Loading Client Information...</span> {/* User-facing text changed */}
        </CardContent>
      </Card>
    );
  }

  const isSuperOrAdmin = currentUser?.role === 'admin' || currentUser?.role === 'superadmin';

  return (
    <>
      <div className="mb-8"> {/* Added for the new heading as per outline */}
        <h2 className="text-2xl font-bold text-slate-100">Client Management</h2>
        <p className="text-slate-400 mt-1">View and manage client information.</p>
      </div>

      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-slate-100">
            <Building2 className="w-6 h-6 text-blue-600" />
            Client IDs {/* User-facing text changed */}
          </CardTitle>
          <CardDescription className="text-slate-400">
            {isSuperOrAdmin
              ? "List of all client IDs in the system for administrative purposes." // User-facing text changed
              : "Your organization's Client ID for integrations and support."} {/* User-facing text changed */}
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow className="border-slate-700 hover:bg-slate-700/50">
                <TableHead className="text-slate-300">Client Name</TableHead> {/* User-facing text changed */}
                <TableHead className="text-slate-300">Client ID</TableHead> {/* User-facing text changed */}
                <TableHead className="text-slate-300">Industry</TableHead> {/* New column from outline */}
                <TableHead className="text-slate-300">Created Date</TableHead> {/* New column from outline */}
                <TableHead className="text-slate-300">Actions</TableHead> {/* New column from outline */}
              </TableRow>
            </TableHeader>
            <TableBody>
              {tenants.map(tenant => (
                <TableRow key={tenant.id} className="border-slate-700 hover:bg-slate-700/30">
                  <TableCell className="font-medium text-slate-200">{tenant.name || 'N/A'}</TableCell>
                  <TableCell className="font-mono text-sm text-slate-300">{tenant.id}</TableCell>
                  <TableCell className="text-slate-400">N/A</TableCell> {/* Placeholder for Industry, as tenant entity doesn't provide this */}
                  <TableCell className="text-slate-400">N/A</TableCell> {/* Placeholder for Created Date, as tenant entity doesn't provide this */}
                  <TableCell>
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => copyToClipboard(tenant.id)}
                      className="text-slate-300 hover:text-slate-100 hover:bg-slate-600"
                    >
                      <Copy className="w-4 h-4" />
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
          {tenants.length === 0 && ( // Conditional rendering for empty state
            <div className="text-center py-8 text-slate-400"> {/* Replaced existing empty state row with new div from outline */}
              <Building2 className="w-12 h-12 mx-auto mb-4 text-slate-500" />
              <p>No clients found</p> {/* User-facing text changed */}
              <p className="text-sm">Clients will appear here once they are created</p> {/* User-facing text changed */}
            </div>
          )}
        </CardContent>
      </Card>
    </>
  );
}

