                  <Badge variant={formData.ai_calling_providers?.thoughtly?.is_active ? "default" : "outline"}>
                    {formData.ai_calling_providers?.thoughtly?.is_active ? "Active" : "Inactive"}
                  </Badge>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                  <div>
                    <Label className="text-xs text-slate-300">API Key</Label>
                    <Input
                      type="password"
                      placeholder="thoughtly_api_key_..."
                      value={formData.ai_calling_providers?.thoughtly?.api_key || ''}
