import { createContext, useContext, useState, useEffect, useCallback, useRef } from 'react';
import { isValidId } from './tenantUtils';

window.__fixPrototype = (obj) => {
  if (!obj || typeof obj !== 'object') return obj;
  if (typeof obj.hasOwnProperty !== 'function') {
    return JSON.parse(JSON.stringify(obj));
  }
  return obj;
};

if (typeof Object.prototype.hasOwnProperty !== 'function') {
  const goodProto = {}.__proto__;
  Object.setPrototypeOf(Object.prototype, goodProto);
}

const sanitizeObject = (obj) => {
  if (!obj) return obj;
  try {
    return JSON.parse(JSON.stringify(obj));
  } catch (e) {
    console.error("Sanitization failed:", e);
    return null;
  }
};

// Removed unused `safeGet` function to resolve the lint error.

const TenantContext = createContext(null);

// Import logger functionality
let loggerInstance = null;

// Helper to log without causing circular dependencies
function logTenantEvent(level, message, metadata) {
  if (!loggerInstance) {
    // Lazy load logger to avoid circular deps
    import('./Logger').then(module => {
      // Assuming module.useLogger is the actual logger instance/object
      // that has info, warn, error methods.
      if (module && typeof module.useLogger === 'object' &&
          typeof module.useLogger.info === 'function' &&
          typeof module.useLogger.warn === 'function' &&
          typeof module.useLogger.error === 'function') {
        loggerInstance = module.useLogger;
      } else {
        console.warn('[TenantContext] Logger module did not export expected methods via `useLogger`. Falling back to console only.');
      }
    }).catch(e => {
      console.error('[TenantContext] Failed to load logger module:', e);
    });
  }

  // Also keep console logging - FIX: ensure we always have a valid method
  const consoleMethodMap = {
    'INFO': 'info',
    'WARNING': 'warn',
    'ERROR': 'error',
    'DEBUG': 'debug'
  };

  const methodName = consoleMethodMap[level] || 'log';
  const consoleMethod = console[methodName];

  // Only call if the method exists
  if (typeof consoleMethod === 'function') {
    consoleMethod.call(console, `[TenantContext] ${message}`, metadata);
  } else {
    // Fallback to console.log
    console.log(`[TenantContext] [${level}] ${message}`, metadata);
  }

  // If external logger methods are loaded and available, use them too.
  // Note: This might not run on the very first log if loggerInstance is still loading.
  if (loggerInstance && typeof loggerInstance[level.toLowerCase()] === 'function') {
    // Ensure metadata is stringifiable if the logger expects it.
    const logMetadata = sanitizeObject(metadata); 
    loggerInstance[level.toLowerCase()](`[TenantContext] ${message}`, logMetadata);
  }
}

export const TenantProvider = ({ children }) => {
  const [selectedTenantId, setSelectedTenantIdState] = useState(null);
  const [lastSyncedTenantId, setLastSyncedTenantId] = useState(null);
  const persistenceAttempted = useRef(false);

  const setSelectedTenantId = useCallback((newTenantId) => {
    const sanitized = newTenantId === null || newTenantId === undefined || newTenantId === '' 
      ? null 
      : String(newTenantId);

    if (sanitized === selectedTenantId) {
      return;
    }

    logTenantEvent('INFO', 'Tenant selection changed', {
      from: selectedTenantId,
      to: sanitized
    });

    setSelectedTenantIdState(sanitized);

    try {
      if (sanitized === null) {
        localStorage.removeItem('selected_tenant_id');
      } else {
        localStorage.setItem('selected_tenant_id', sanitized);
      }
    } catch (error) {
      logTenantEvent('ERROR', 'Failed to persist tenant selection', {
        error: error.message
      });
    }
  }, [selectedTenantId]); // Dependency on selectedTenantId ensures we compare against the *current* state

  useEffect(() => {
    if (persistenceAttempted.current) return;
    persistenceAttempted.current = true;

    try {
      const saved = localStorage.getItem('selected_tenant_id');
      if (saved && saved !== 'null' && saved !== 'undefined') {
        const sanitized = String(saved);
        // Use shared validation function
        if (isValidId(sanitized)) {
          setSelectedTenantIdState(sanitized);
          logTenantEvent('INFO', 'Restored tenant from localStorage', {
            tenantId: sanitized
          });
        } else {
          logTenantEvent('WARNING', 'Invalid tenant ID format in localStorage', {
            savedValue: saved
          });
          // Reset to a default tenant ID
          const defaultTenantId = '68f84f58558dd2a4d4bdd545';
          setSelectedTenantIdState(defaultTenantId);
          localStorage.setItem('selected_tenant_id', defaultTenantId);
          logTenantEvent('INFO', 'Reset tenant ID to default', {
            tenantId: defaultTenantId
          });
        }
      }
    } catch (error) {
      logTenantEvent('ERROR', 'Failed to load tenant from storage', {
        error: error.message
      });
    }
  }, []); // Empty dependency array means this runs once on mount

  useEffect(() => {
    if (selectedTenantId && selectedTenantId !== lastSyncedTenantId) {
      setLastSyncedTenantId(selectedTenantId);

      logTenantEvent('INFO', 'Tenant context synchronized', {
        tenantId: selectedTenantId
      });

      window.dispatchEvent(
        new CustomEvent('tenant-changed', {
          detail: { tenantId: selectedTenantId }
        })
      );
    }
  }, [selectedTenantId, lastSyncedTenantId]); // Dependencies: selectedTenantId and lastSyncedTenantId

  return (
    <TenantContext.Provider value={{ selectedTenantId, setSelectedTenantId }}>
      {children}
    </TenantContext.Provider>
  );
};

export const useTenant = () => {
  const context = useContext(TenantContext);
  if (!context) {
    logTenantEvent('WARNING', 'useTenant called outside TenantProvider', {});
    // Return a default, non-functional object to prevent crashes
    return { selectedTenantId: null, setSelectedTenantId: () => {} };
  }
  return context;
};

import { format } from 'date-fns';

// On Save (convert local time + offset -> UTC) 
export function localToUtc(dateString, timeString, offsetMinutes) {
  // Validate inputs
  if (!dateString || !timeString || typeof dateString !== 'string' || typeof timeString !== 'string') {
    throw new Error('dateString and timeString are required and must be strings');
  }

  // Calculate offset string from offsetMinutes
  // getTimezoneOffset returns positive for zones behind UTC (e.g., America), so the sign in the ISO string is negative.
  // Our offsetMinutes are defined such that positive means 'behind UTC' (e.g., EDT is +240 minutes from UTC),
  // so for ISO format, we need to negate it to get the UTC offset.
  // For example, if offsetMinutes is 240 (EDT, 4 hours behind UTC), the ISO offset should be -04:00.
  // So, we use a negative sign if offsetMinutes is positive, and a positive sign if offsetMinutes is negative (e.g., for times ahead of UTC).
  const sign = offsetMinutes > 0 ? '-' : '+';
  const absOffset = Math.abs(offsetMinutes);
  const hours = Math.floor(absOffset / 60).toString().padStart(2, '0');
  const minutes = (absOffset % 60).toString().padStart(2, '0');
  const offsetString = `${sign}${hours}:${minutes}`;

  // Construct a full ISO 8601 string with the correct offset
  const isoStringWithOffset = `${dateString}T${timeString}:00${offsetString}`;

  // Let the Date object parse this unambiguous string
  const finalDate = new Date(isoStringWithOffset);

  // Check for invalid date
  if (isNaN(finalDate.getTime())) {
    throw new Error(`Failed to parse constructed ISO string: ${isoStringWithOffset}`);
  }

  // No console.log here, as it's not in the new implementation outline.
  // The original console.log:
  // console.log('localToUtc conversion:', {
  //   input: `${dateString} ${timeString}`,
  //   offsetMinutes,
  //   timezone: offsetMinutes === 240 ? 'EDT' : offsetMinutes === 300 ? 'EST' : 'Other',
  //   local_datetime: localDate.toISOString(),
  //   utc_datetime: utcDate.toISOString(),
  //   localTime: `${hour}:${minute.toString().padStart(2, '0')}`,
  //   utcTime: utcDate.toISOString().substr(11, 5)
  // });

  // Return the standard UTC ISO string (e.g., "2025-09-24T17:30:00.000Z")
  return finalDate.toISOString();
}

// On Display (convert stored UTC time -> user local) 
export function utcToLocal(utcString, offsetMinutes) {
  // Validate inputs
  if (!utcString) {
    throw new Error('utcString is required');
  }

  const utcDate = new Date(utcString);

  // Check if date is valid
  if (isNaN(utcDate.getTime())) {
    throw new Error(`Invalid UTC string: ${utcString}`);
  }

  // Convert UTC to local by subtracting the offset
  const localTimeValue = utcDate.getTime() - (offsetMinutes * 60 * 1000);
  const tempDate = new Date(localTimeValue);

  // Create a new date object using UTC methods to prevent the browser from re-applying its own timezone offset.
  // This effectively "tricks" the formatting library into displaying the correct local time.
  const displayDate = new Date(
    tempDate.getUTCFullYear(),
    tempDate.getUTCMonth(),
    tempDate.getUTCDate(),
    tempDate.getUTCHours(),
    tempDate.getUTCMinutes(),
    tempDate.getUTCSeconds()
  );

  return displayDate;
}

// Get timezone offset in minutes for a given timezone
export function getCurrentTimezoneOffset(timezone) {
  // Define timezone mappings with DST handling
  const timezoneMap = {
    'America/New_York': () => {
      const now = new Date();
      const jan = new Date(now.getFullYear(), 0, 1);
      const jul = new Date(now.getFullYear(), 6, 1);
      const stdOffset = Math.max(jan.getTimezoneOffset(), jul.getTimezoneOffset());
      const isDST = now.getTimezoneOffset() < stdOffset;
      return isDST ? 240 : 300; // EDT: 240, EST: 300
    },
    'America/Chicago': () => {
      const now = new Date();
      const jan = new Date(now.getFullYear(), 0, 1);
      const jul = new Date(now.getFullYear(), 6, 1);
      const stdOffset = Math.max(jan.getTimezoneOffset(), jul.getTimezoneOffset());
      const isDST = now.getTimezoneOffset() < stdOffset;
      return isDST ? 300 : 360; // CDT: 300, CST: 360
    },
    'America/Denver': () => {
      const now = new Date();
      const jan = new Date(now.getFullYear(), 0, 1);
      const jul = new Date(now.getFullYear(), 6, 1);
      const stdOffset = Math.max(jan.getTimezoneOffset(), jul.getTimezoneOffset());
      const isDST = now.getTimezoneOffset() < stdOffset;
      return isDST ? 360 : 420; // MDT: 360, MST: 420
    },
    'America/Los_Angeles': () => {
      const now = new Date();
      const jan = new Date(now.getFullYear(), 0, 1);
      const jul = new Date(now.getFullYear(), 6, 1);
      const stdOffset = Math.max(jan.getTimezoneOffset(), jul.getTimezoneOffset());
      const isDST = now.getTimezoneOffset() < stdOffset;
      return isDST ? 420 : 480; // PDT: 420, PST: 480
    },
    'UTC': () => 0,
    'Europe/London': () => {
      const now = new Date();
      const jan = new Date(now.getFullYear(), 0, 1);
      const jul = new Date(now.getFullYear(), 6, 1);
      const stdOffset = Math.max(jan.getTimezoneOffset(), jul.getTimezoneOffset());
      const isDST = now.getTimezoneOffset() < stdOffset;
      return isDST ? -60 : 0; // BST: -60, GMT: 0
    },
    'Europe/Paris': () => {
      const now = new Date();
      const jan = new Date(now.getFullYear(), 0, 1);
      const jul = new Date(now.getFullYear(), 6, 1);
      const stdOffset = Math.max(jan.getTimezoneOffset(), jul.getTimezoneOffset());
      const isDST = now.getTimezoneOffset() < stdOffset;
      return isDST ? -120 : -60; // CEST: -120, CET: -60
    },
    'Asia/Tokyo': () => -540,
    'Asia/Shanghai': () => -480,
    'Australia/Sydney': () => {
      const now = new Date();
      const jan = new Date(now.getFullYear(), 0, 1);
      const jul = new Date(now.getFullYear(), 6, 1);
      const stdOffset = Math.min(jan.getTimezoneOffset(), jul.getTimezoneOffset());
      const isDST = now.getTimezoneOffset() > stdOffset;
      return isDST ? -660 : -600; // AEDT: -660, AEST: -600
    }
  };

  if (!timezone || !timezoneMap[timezone]) {
    // Fallback to system timezone
    return new Date().getTimezoneOffset();
  }

  return timezoneMap[timezone]();
}

// Validate date string format
function isValidDateString(dateStr) {
  if (!dateStr || typeof dateStr !== 'string') return false;
  // Allow YYYY-MM-DD or full ISO string by just checking the start
  const dateRegex = /^\d{4}-\d{2}-\d{2}/;
  return dateRegex.test(dateStr);
}

// Validate time string format
function isValidTimeString(timeStr) {
  if (!timeStr || typeof timeStr !== 'string') return false;
  const timeRegex = /^\d{2}:\d{2}$/;
  return timeRegex.test(timeStr);
}

// Helper function to format activity date/time for display
export function formatActivityDateTime(activity, offsetMinutes = null) {
  // Early validation (quiet)
  if (!activity) {
    return 'Not set';
  }

  if (!activity.due_date) {
    return 'Not set';
  }

  // FIXED: Handle full ISO strings by taking only the date part
  const datePart = activity.due_date.split('T')[0];

  // Validate date format
  if (!isValidDateString(datePart)) {
    return 'Invalid date';
  }

  try {
    // If no offset provided, try to get from system/user settings
    if (offsetMinutes === null) {
      offsetMinutes = new Date().getTimezoneOffset();
    }

    if (activity.due_time) {
      // Validate time format
      if (!isValidTimeString(activity.due_time)) {
        return 'Invalid time';
      }

      // Create UTC datetime string from stored values
      const utcString = `${datePart}T${activity.due_time}:00.000Z`;

      // Convert to local time using user's timezone offset
      const displayDate = utcToLocal(utcString, offsetMinutes);

      return format(displayDate, 'PPP p'); // e.g., "September 26, 2025 at 5:00 AM"
    } else {
      // Date only - treat as local date without time conversion
      const dateParts = datePart.split('-');
      const year = parseInt(dateParts[0]);
      const month = parseInt(dateParts[1]) - 1; // JS months are 0-indexed
      const day = parseInt(dateParts[2]);

      // Validate parsed date parts
      if (isNaN(year) || isNaN(month) || isNaN(day)) {
        return 'Invalid date';
      }

      const dateOnly = new Date(year, month, day);

      // Check if constructed date is valid
      if (isNaN(dateOnly.getTime())) {
        return 'Invalid date';
      }

      return format(dateOnly, 'PPP'); // e.g., "September 26, 2025"
    }
  } catch (_error) {
    return 'Invalid date/time';
  }
}

// Get timezone display name with offset
export function getTimezoneDisplayName(timezone) {
  const offsetMinutes = getCurrentTimezoneOffset(timezone);
  const hours = Math.abs(Math.floor(offsetMinutes / 60));
  const minutes = Math.abs(offsetMinutes % 60);
  const sign = offsetMinutes <= 0 ? '+' : '-';
  const offsetString = `UTC${sign}${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;

  const displayNames = {
    'America/New_York': 'Eastern Time',
    'America/Chicago': 'Central Time', 
    'America/Denver': 'Mountain Time',
    'America/Los_Angeles': 'Pacific Time',
    'UTC': 'Coordinated Universal Time',
    'Europe/London': 'Greenwich Mean Time',
    'Europe/Paris': 'Central European Time',
    'Asia/Tokyo': 'Japan Standard Time',
    'Asia/Shanghai': 'China Standard Time',
    'Australia/Sydney': 'Australian Eastern Time'
  };

  return `${displayNames[timezone] || timezone} (${offsetString})`;
}

import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { 
  Play, 
  CheckCircle2, 
  XCircle, 
  AlertTriangle,
  Clock,
  FileText
} from "lucide-react";

export default function TestRunner({ testSuites }) {
  const [results, setResults] = useState([]);
  const [running, setRunning] = useState(false);
  const [currentTest, setCurrentTest] = useState(null);

  const runTests = async () => {
    setRunning(true);
    setResults([]);

    const allResults = [];

    for (const suite of testSuites) {
      for (const test of suite.tests) {
        setCurrentTest(`${suite.name} - ${test.name}`);

        const startTime = Date.now();
        let result = {
          suite: suite.name,
          test: test.name,
          status: 'running',
          duration: 0,
          error: null
        };

        try {
          await test.fn();
          result.status = 'passed';
          result.duration = Date.now() - startTime;
        } catch (error) {
          result.status = 'failed';
          result.duration = Date.now() - startTime;
          result.error = error.message;
        }

        allResults.push(result);
        setResults([...allResults]);
      }
    }

    setCurrentTest(null);
    setRunning(false);
  };

  const totalTests = testSuites.reduce((sum, suite) => sum + suite.tests.length, 0);
  const passedTests = results.filter(r => r.status === 'passed').length;
  const failedTests = results.filter(r => r.status === 'failed').length;
  const passRate = totalTests > 0 ? ((passedTests / results.length) * 100).toFixed(1) : 0;

  return (
    <div className="space-y-6">
      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="text-slate-100 flex items-center justify-between">
            <span>Test Suite Runner</span>
            <Button 
              onClick={runTests} 
              disabled={running}
              className="bg-blue-600 hover:bg-blue-700"
            >
              {running ? (
                <>
                  <Clock className="w-4 h-4 mr-2 animate-spin" />
                  Running...
                </>
              ) : (
                <>
                  <Play className="w-4 h-4 mr-2" />
                  Run All Tests
                </>
              )}
            </Button>
          </CardTitle>
        </CardHeader>
        <CardContent>
          {running && currentTest && (
            <Alert className="mb-4 bg-blue-900/30 border-blue-700">
              <Clock className="h-4 w-4 animate-spin" />
              <AlertDescription className="text-blue-300">
                Running: {currentTest}
              </AlertDescription>
            </Alert>
          )}

          {results.length > 0 && (
            <div className="grid grid-cols-4 gap-4 mb-6">
              <Card className="bg-slate-700 border-slate-600">
                <CardContent className="p-4">
                  <div className="text-sm text-slate-400">Total Tests</div>
                  <div className="text-2xl font-bold text-slate-100">{results.length}</div>
                </CardContent>
              </Card>
              <Card className="bg-green-900/30 border-green-700">
                <CardContent className="p-4">
                  <div className="text-sm text-green-400">Passed</div>
                  <div className="text-2xl font-bold text-green-300">{passedTests}</div>
                </CardContent>
              </Card>
              <Card className="bg-red-900/30 border-red-700">
                <CardContent className="p-4">
                  <div className="text-sm text-red-400">Failed</div>
                  <div className="text-2xl font-bold text-red-300">{failedTests}</div>
                </CardContent>
              </Card>
              <Card className="bg-blue-900/30 border-blue-700">
                <CardContent className="p-4">
                  <div className="text-sm text-blue-400">Pass Rate</div>
                  <div className="text-2xl font-bold text-blue-300">{passRate}%</div>
                </CardContent>
              </Card>
            </div>
          )}

          <div className="space-y-2">
            {results.map((result, index) => (
              <div 
                key={index}
                className={`p-4 rounded-lg border ${
                  result.status === 'passed' 
                    ? 'bg-green-900/20 border-green-700' 
                    : 'bg-red-900/20 border-red-700'
                }`}
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-3">
                    {result.status === 'passed' ? (
                      <CheckCircle2 className="w-5 h-5 text-green-400" />
                    ) : (
                      <XCircle className="w-5 h-5 text-red-400" />
                    )}
                    <div>
                      <div className="font-medium text-slate-200">
                        {result.suite} - {result.test}
                      </div>
                      {result.error && (
                        <div className="text-sm text-red-400 mt-1">
                          Error: {result.error}
                        </div>
                      )}
                    </div>
                  </div>
                  <Badge variant="outline" className="text-slate-400">
                    {result.duration}ms
                  </Badge>
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

import { assert, createSpy } from './testUtils';
