    setSaving(true);
    try {
      const workflowData = {
        tenant_id: user.tenant_id,
        name,
        description,
        is_active: true,
        trigger: {
          type: 'webhook',
          config: {}
        },
        nodes,
        connections,
        webhook_url: workflow?.webhook_url || `https://base44.app/api/apps/${user.app_id}/functions/executeWorkflow?workflow_id=PENDING`
      };

      let savedWorkflow;
      if (workflow) {
        savedWorkflow = await Workflow.update(workflow.id, workflowData);
      } else {
        savedWorkflow = await Workflow.create(workflowData);
        await Workflow.update(savedWorkflow.id, {
          webhook_url: `https://base44.app/api/apps/${user.app_id}/functions/executeWorkflow?workflow_id=${savedWorkflow.id}`
        });
      }

      toast.success('Workflow saved successfully');
      onSave();
    } catch (error) {
      console.error('Failed to save workflow:', error);
      toast.error('Failed to save workflow');
    } finally {
      setSaving(false);
    }
  };

  const selectedNode = nodes.find(node => node.id === selectedNodeId);

  return (
    <div className="flex flex-col h-full">
      <div className="p-4 border-b border-slate-700">
        <div className="grid grid-cols-2 gap-4 mb-4">
          <div>
            <Label htmlFor="workflow-name" className="text-slate-300">Workflow Name</Label>
            <Input
              id="workflow-name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="e.g., Update Lead from Webhook"
              className="bg-slate-800 border-slate-700 text-slate-100"
            />
          </div>
          <div>
            <Label htmlFor="workflow-description" className="text-slate-300">Description</Label>
            <Input
              id="workflow-description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="What does this workflow do?"
              className="bg-slate-800 border-slate-700 text-slate-100"
            />
          </div>
        </div>
      </div>

      <div className="flex flex-1 overflow-hidden">
        <div className="w-64 border-r border-slate-700 overflow-y-auto p-4 flex-shrink-0">
          <NodeLibrary onAddNode={handleAddNode} />
        </div>

        <div className="flex-1 bg-slate-950 overflow-auto">
          <WorkflowCanvas
            nodes={nodes}
            connections={connections}
            onUpdateNode={handleUpdateNode}
            onDeleteNode={handleDeleteNode}
            onConnect={handleConnect}
            onSelectNode={handleSelectNode}
            selectedNodeId={selectedNodeId}
          />
        </div>

        <div className="w-80 border-l border-slate-700 overflow-y-auto p-4 bg-slate-900 flex-shrink-0">
          <h3 className="text-lg font-semibold text-slate-100 mb-4">
            {selectedNode ? `${selectedNode.type.replace(/_/g, ' ').replace(/\b\w/g, char => char.toUpperCase())} Config` : 'Node Configuration'}
          </h3>
          {selectedNode ? (
            renderNodeConfig(selectedNode)
          ) : (
            <p className="text-slate-400 text-sm">Select a node to configure</p>
          )}
        </div>
      </div>

      <div className="p-4 border-t border-slate-700 flex justify-between">
        <Button variant="outline" onClick={onCancel} className="border-slate-600 text-slate-300">
          Cancel
        </Button>
        <div className="flex gap-2">
          <Button
            onClick={handleSave}
            disabled={saving}
            className="bg-purple-600 hover:bg-purple-700"
          >
            <Save className="w-4 h-4 mr-2" />
            {saving ? 'Saving...' : 'Save Workflow'}
          </Button>
        </div>
      </div>
    </div>
  );
}

import React, { useState } from 'react';
import WorkflowNode from './WorkflowNode';
import { ArrowDown } from 'lucide-react';

export default function WorkflowCanvas({ nodes, connections, onUpdateNode, onDeleteNode, onConnect, onSelectNode, selectedNodeId }) {
  const [connectingFrom, setConnectingFrom] = useState(null);

  const handleNodeClick = (nodeId) => {
    if (connectingFrom) {
      if (connectingFrom !== nodeId) {
        onConnect(connectingFrom, nodeId);
      }
      setConnectingFrom(null);
    } else {
      onSelectNode(nodeId);
    }
  };

  const handleStartConnect = (nodeId) => {
    setConnectingFrom(nodeId);
  };

  return (
    <div className="relative p-8 min-h-full">
      {connectingFrom && (
        <div className="fixed top-4 left-1/2 transform -translate-x-1/2 bg-purple-600 text-white px-4 py-2 rounded-lg shadow-lg z-50">
          Click on another node to connect
        </div>
      )}

      {/* Render nodes */}
      <div className="space-y-6">
        {nodes.map((node, index) => (
          <React.Fragment key={node.id}>
            <WorkflowNode
              node={node}
              isSelected={selectedNodeId === node.id}
              isConnecting={connectingFrom === node.id}
              onClick={() => handleNodeClick(node.id)}
              onUpdate={(updates) => onUpdateNode(node.id, updates)}
              onDelete={() => onDeleteNode(node.id)}
              onStartConnect={() => handleStartConnect(node.id)}
            />

            {/* Show arrow if there's a connection from this node */}
            {connections.find(c => c.from === node.id) && (
              <div className="flex justify-center">
                <ArrowDown className="w-6 h-6 text-purple-400" />
              </div>
            )}
          </React.Fragment>
        ))}
      </div>
    </div>
  );
}

import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Webhook, Search, Edit, Trash2, Link as LinkIcon, UserPlus, GitBranch } from 'lucide-react';

const nodeIcons = {
  webhook_trigger: Webhook,
  find_lead: Search,
  create_lead: UserPlus,
  update_lead: Edit,
  condition: GitBranch,
  find_contact: Search,
  update_contact: Edit,
};

const nodeColors = {
  webhook_trigger: 'bg-purple-600',
  find_lead: 'bg-blue-600',
  create_lead: 'bg-green-600',
  update_lead: 'bg-emerald-600',
  condition: 'bg-yellow-600',
  find_contact: 'bg-cyan-600',
  update_contact: 'bg-teal-600',
};

export default function WorkflowNode({ node, isSelected, isConnecting, onClick, onUpdate, onDelete, onStartConnect }) {
  const Icon = nodeIcons[node.type] || Edit;
  const colorClass = nodeColors[node.type] || 'bg-gray-600';

  const getNodeTitle = () => {
    switch (node.type) {
      case 'webhook_trigger': return 'Webhook Trigger';
      case 'find_lead': return 'Find Lead';
      case 'create_lead': return 'Create Lead';
      case 'update_lead': return 'Update Lead';
      case 'condition': return 'Condition';
      case 'find_contact': return 'Find Contact';
      case 'update_contact': return 'Update Contact';
      default: return node.type;
    }
  };

  const getNodeDescription = () => {
    switch (node.type) {
      case 'webhook_trigger': return 'Receives webhook data';
      case 'find_lead': return 'Search for an existing lead';
      case 'create_lead': return 'Create a new lead record';
      case 'update_lead': return 'Update lead fields';
      case 'condition': 
        if (node.config?.field && node.config?.operator) {
          return `If ${node.config.field} ${node.config.operator} ${node.config.value || ''}`;
        }
        return 'Branch based on condition';
      case 'find_contact': return 'Search for a contact';
      case 'update_contact': return 'Update contact fields';
      default: return '';
    }
  };

  return (
    <Card 
      className={`w-80 cursor-pointer transition-all ${
        isSelected 
          ? 'ring-2 ring-purple-500 shadow-xl' 
          : 'hover:shadow-lg'
      } ${
        isConnecting 
          ? 'ring-2 ring-yellow-500' 
          : ''
      } bg-slate-800 border-slate-700`}
      onClick={onClick}
    >
      <CardHeader className={`${colorClass} text-white`}>
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Icon className="w-5 h-5" />
            <CardTitle className="text-base">{getNodeTitle()}</CardTitle>
          </div>
          <div className="flex gap-1">
            {node.type !== 'webhook_trigger' && (
              <Button
                variant="ghost"
                size="icon"
                className="h-6 w-6 text-white hover:bg-white/20"
                onClick={(e) => {
                  e.stopPropagation();
                  onStartConnect();
                }}
              >
                <LinkIcon className="w-3 h-3" />
              </Button>
            )}
            {node.type !== 'webhook_trigger' && (
              <Button
                variant="ghost"
                size="icon"
                className="h-6 w-6 text-white hover:bg-red-600"
                onClick={(e) => {
                  e.stopPropagation();
                  onDelete();
                }}
              >
                <Trash2 className="w-3 h-3" />
              </Button>
            )}
          </div>
        </div>
      </CardHeader>
      <CardContent className="pt-4">
        <div className="text-sm text-slate-400">
          {getNodeDescription()}
        </div>
      </CardContent>
    </Card>
  );
}

