    partner: 'bg-purple-900/20 text-purple-300 border-purple-700',
    competitor: 'bg-red-900/20 text-red-300 border-red-700',
    vendor: 'bg-amber-900/20 text-amber-300 border-amber-700',
    inactive: 'bg-gray-900/20 text-gray-300 border-gray-700'
  };

  // Local getTenantFilter function that incorporates employee scope and test data
  const getTenantFilter = useCallback(() => {
    if (!user) return {};

    let filter = {};

    // Tenant filtering
    if (user.role === 'superadmin' || user.role === 'admin') {
      if (selectedTenantId) {
        filter.tenant_id = selectedTenantId;
      }
    } else if (user.tenant_id) {
      filter.tenant_id = user.tenant_id;
    }

    // Employee scope filtering from context
    if (selectedEmail && selectedEmail !== 'all') {
      if (selectedEmail === 'unassigned') {
        filter.$or = [{ assigned_to: null }, { assigned_to: '' }];
      } else {
        filter.assigned_to = selectedEmail;
      }
    } else if (user.employee_role === 'employee' && user.role !== 'admin' && user.role !== 'superadmin') {
      // Regular employees only see their own data
      filter.assigned_to = user.email;
    }

    // Test data filtering
    if (!showTestData) {
      filter.is_test_data = { $ne: true };
    }

    return filter;
  }, [user, selectedTenantId, showTestData, selectedEmail]);

  // Load user once
  useEffect(() => {
    const loadUser = async () => {
      try {
        const currentUser = await User.me();
        setUser(currentUser);
      } catch (error) {
        console.error("Failed to load user:", error);
        toast.error("Failed to load user information");
      }
    };
    loadUser();
  }, []);

  // Load supporting data (contacts, users, employees) ONCE with delays and error handling
  useEffect(() => {
    // CRITICAL: Only load once
    if (supportingDataLoaded.current || !user) return;

    const loadSupportingData = async () => {
      try {
        // Use the local getTenantFilter without employee scope specific filter for supporting data
        // since supporting data like employees/contacts are usually loaded for all within the tenant
        // for selection in other forms, not necessarily to be filtered by assigned_to.
        // We revert to basic tenant filter for supporting data for now.
        const baseTenantFilter = {};
        if (user.role === 'superadmin' || user.role === 'admin') {
          if (selectedTenantId) {
            baseTenantFilter.tenant_id = selectedTenantId;
          }
        } else if (user.tenant_id) {
          baseTenantFilter.tenant_id = user.tenant_id;
        }

        // Load contacts
        const contactsData = await cachedRequest('Contact', 'filter', { filter: baseTenantFilter }, () => Contact.filter(baseTenantFilter));
        setContacts(contactsData || []);

        await delay(300);

        // Load users safely
        const usersData = await loadUsersSafely(user, selectedTenantId, cachedRequest);
        setUsers(usersData || []);

        await delay(300);

        // Load employees
        const employeesData = await cachedRequest('Employee', 'filter', { filter: baseTenantFilter }, () => Employee.filter(baseTenantFilter));
        setEmployees(employeesData || []);

        supportingDataLoaded.current = true; // Mark as loaded
      } catch (error) {
        console.error("[Accounts] Failed to load supporting data:", error);
        // Don't toast here - the page will still function
      }
    };

    loadSupportingData();
  }, [user, selectedTenantId, cachedRequest]); // REMOVED cachedRequest from deps to prevent loops - but it is used inside, so it should be there

  // Handle opening account from URL parameter
  useEffect(() => {
    const loadAccountFromUrl = async () => {
      const urlParams = new URLSearchParams(window.location.search);
      const accountId = urlParams.get('accountId');

      if (accountId) {
        try {
          // Fetch the specific account by ID
          const account = await Account.get(accountId);
          if (account) {
            setDetailAccount(account);
            setIsDetailOpen(true);
          }
        } catch (error) {
          console.error('[Accounts] Failed to load account from URL:', error);
          toast.error("Account not found");
        } finally {
          // Clear the URL parameter
          window.history.replaceState({}, '', '/Accounts');
        }
      }
    };

    if (user) {
      loadAccountFromUrl();
    }
  }, [user]); // Only depend on user, not accounts array

  const loadTotalStats = useCallback(async () => {
    if (!user) return;

    try {
      const currentTenantFilter = getTenantFilter();
      const allAccounts = await cachedRequest(
        'Account',
        'filter',
        { filter: currentTenantFilter },
        () => Account.filter(currentTenantFilter)
      );

      const stats = {
        total: allAccounts.length,
        customer: allAccounts.filter(a => a.type === 'customer').length,
        prospect: allAccounts.filter(a => a.type === 'prospect').length,
        partner: allAccounts.filter(a => a.type === 'partner').length,
        inactive: allAccounts.filter(a => a.type === 'inactive').length || 0
      };

      setTotalStats(stats);
    } catch (error) {
      console.error('[Accounts] Failed to load stats:', error);
    }
  }, [user, selectedTenantId, cachedRequest, getTenantFilter]);

  // Load accounts with pagination
  const loadAccounts = useCallback(async () => {
    if (!user) return;

    setLoading(true);
    try {
      const currentTenantFilter = getTenantFilter();

      const allAccounts = await cachedRequest(
        'Account',
        'filter',
        { filter: currentTenantFilter },
        () => Account.filter(currentTenantFilter)
      );

      let filtered = allAccounts || [];

      // Apply client-side filters
      if (searchTerm) {
        const search = searchTerm.toLowerCase();
        filtered = filtered.filter(account =>
          account.name?.toLowerCase().includes(search) ||
          account.website?.toLowerCase().includes(search) ||
          account.email?.toLowerCase().includes(search) ||
          account.phone?.includes(searchTerm)
        );
      }

      if (typeFilter !== "all") {
        filtered = filtered.filter(account => account.type === typeFilter);
      }

      if (selectedTags.length > 0) {
        filtered = filtered.filter(account =>
          Array.isArray(account.tags) && selectedTags.every(tag => account.tags.includes(tag))
        );
      }

      // Sort by created_date descending
      filtered.sort((a, b) => new Date(b.created_date) - new Date(a.created_date));

      setTotalItems(filtered.length);

      // Apply pagination
      const startIndex = (currentPage - 1) * pageSize;
      const endIndex = startIndex + pageSize;
      const paginatedAccounts = filtered.slice(startIndex, endIndex);

      setAccounts(paginatedAccounts);
    } catch (error) {
      console.error("[Accounts] Failed to load accounts:", error);
      toast.error("Failed to load accounts");
      setAccounts([]);
    } finally {
      setLoading(false);
      if (!initialLoadDone.current) {
        initialLoadDone.current = true;
      }
    }
  }, [user, selectedTenantId, searchTerm, typeFilter, selectedTags, currentPage, pageSize, cachedRequest, getTenantFilter]);

  // Load accounts when dependencies change
  useEffect(() => {
    loadAccounts();
  }, [loadAccounts]);

  // Load stats once when user/tenant/scope changes
  useEffect(() => {
    if (user) {
      loadTotalStats();
    }
  }, [user, selectedTenantId, selectedEmail, loadTotalStats]);

  // Reset to page 1 when filters change
  useEffect(() => {
    if (initialLoadDone.current) {
      setCurrentPage(1);
    }
  }, [searchTerm, typeFilter, selectedTags, selectedEmail]);

  // Handle page change
  const handlePageChange = useCallback((newPage) => {
    setCurrentPage(newPage);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }, []);

  // Handle page size change
  const handlePageSizeChange = useCallback((newSize) => {
    setPageSize(newSize);
    setCurrentPage(1);
  }, []);

  // Extract all tags from accounts for TagFilter
  const allTags = useMemo(() => {
    if (!Array.isArray(accounts)) return [];

    const tagCounts = {};
    accounts.forEach(account => {
      if (Array.isArray(account.tags)) {
        account.tags.forEach(tag => {
          if (tag && typeof tag === 'string') {
            tagCounts[tag] = (tagCounts[tag] || 0) + 1;
          }
        });
      }
    });

    return Object.entries(tagCounts)
      .map(([name, count]) => ({ name, count }))
      .sort((a, b) => b.count - a.count);
  }, [accounts]);

  // Create lookup maps for denormalized fields
  const usersMap = useMemo(() => {
    return users.reduce((acc, user) => {
      acc[user.email] = user.full_name || user.email;
      return acc;
    }, {});
  }, [users]);

  const employeesMap = useMemo(() => {
    return employees.reduce((acc, employee) => {
      if (employee.email) {
        acc[employee.email] = `${employee.first_name} ${employee.last_name}`;
      }
      return acc;
    }, {});
  }, [employees]);

  const handleSave = async () => {
    setIsFormOpen(false);
    setEditingAccount(null);
    clearCacheByKey('Account');
    await Promise.all([
      loadAccounts(),
      loadTotalStats()
    ]);
    toast.success(editingAccount ? "Account updated successfully" : "Account created successfully");
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this account?")) return;

    try {
      await Account.delete(id);
      clearCacheByKey('Account');
      await Promise.all([
        loadAccounts(),
        loadTotalStats()
      ]);
      toast.success("Account deleted successfully");
    } catch (error) {
      console.error("Failed to delete account:", error);
      toast.error("Failed to delete account");
    }
  };

  const handleBulkDelete = async () => {
    if (selectAllMode) {
      if (!window.confirm(`Delete ALL ${totalItems} account(s) matching current filters? This cannot be undone!`)) return;

      try {
        let currentTenantFilter = getTenantFilter();
        // The employee scope filter is already applied within getTenantFilter()

        if (typeFilter !== "all") {
          currentTenantFilter = { ...currentTenantFilter, type: typeFilter };
        }

        if (searchTerm) {
          const searchRegex = { $regex: searchTerm, $options: 'i' };
          currentTenantFilter = {
            ...currentTenantFilter,
            $or: [
              { name: searchRegex },
              { email: searchRegex },
              { phone: searchRegex },
              { website: searchRegex },
              { city: searchRegex }
            ]
          };
        }

        if (selectedTags.length > 0) {
          currentTenantFilter = { ...currentTenantFilter, tags: { $all: selectedTags } };
        }

        const allAccountsToDelete = await cachedRequest('Account', 'filter', { filter: currentTenantFilter, sort: 'id', limit: 10000 }, () => Account.filter(currentTenantFilter, 'id', 10000));
        const deleteCount = allAccountsToDelete.length;

        // Delete in batches to avoid overwhelming the system
        const BATCH_SIZE = 50;
        for (let i = 0; i < allAccountsToDelete.length; i += BATCH_SIZE) {
          const batch = allAccountsToDelete.slice(i, i + BATCH_SIZE);
          await Promise.all(batch.map(a => Account.delete(a.id)));
        }

        setSelectedAccounts(new Set());
        setSelectAllMode(false);
        clearCacheByKey('Account');
        await Promise.all([
          loadAccounts(),
          loadTotalStats()
        ]);
        toast.success(`${deleteCount} account(s) deleted`);
      } catch (error) {
        console.error("Failed to delete accounts:", error);
        toast.error("Failed to delete accounts");
      }
    } else {
      if (!selectedAccounts || selectedAccounts.size === 0) {
        toast.error("No accounts selected");
        return;
      }

      if (!window.confirm(`Delete ${selectedAccounts.size} account(s)?`)) return;

      try {
        await Promise.all([...selectedAccounts].map(id => Account.delete(id)));
        setSelectedAccounts(new Set());
        clearCacheByKey('Account');
        await Promise.all([
          loadAccounts(),
          loadTotalStats()
        ]);
        toast.success(`${selectedAccounts.size} account(s) deleted`);
      } catch (error) {
        console.error("Failed to delete accounts:", error);
        toast.error("Failed to delete accounts");
      }
    }
  };

  const handleBulkTypeChange = async (newType) => {
    if (selectAllMode) {
      if (!window.confirm(`Update type for ALL ${totalItems} account(s) matching current filters to ${newType}?`)) return;

      try {
        let currentTenantFilter = getTenantFilter();
        // The employee scope filter is already applied within getTenantFilter()

        if (typeFilter !== "all") {
          currentTenantFilter = { ...currentTenantFilter, type: typeFilter };
        }

        if (searchTerm) {
          const searchRegex = { $regex: searchTerm, $options: 'i' };
          currentTenantFilter = {
            ...currentTenantFilter,
            $or: [
              { name: searchRegex },
              { email: searchRegex },
              { phone: searchRegex },
              { website: searchRegex },
              { city: searchRegex }
            ]
          };
        }

        if (selectedTags.length > 0) {
          currentTenantFilter = { ...currentTenantFilter, tags: { $all: selectedTags } };
        }

        const allAccountsToUpdate = await cachedRequest('Account', 'filter', { filter: currentTenantFilter, sort: 'id', limit: 10000 }, () => Account.filter(currentTenantFilter, 'id', 10000));
        const updateCount = allAccountsToUpdate.length;

        // Update in batches
        const BATCH_SIZE = 50;
        for (let i = 0; i < allAccountsToUpdate.length; i += BATCH_SIZE) {
          const batch = allAccountsToUpdate.slice(i, i + BATCH_SIZE);
          await Promise.all(batch.map(a => Account.update(a.id, { type: newType })));
        }

        setSelectedAccounts(new Set());
        setSelectAllMode(false);
        clearCacheByKey('Account');
        await Promise.all([
          loadAccounts(),
          loadTotalStats()
        ]);
        toast.success(`Updated ${updateCount} account(s) to ${newType}`);
      } catch (error) {
        console.error("Failed to update accounts:", error);
        toast.error("Failed to update accounts");
      }
    } else {
      if (!selectedAccounts || selectedAccounts.size === 0) {
        toast.error("No accounts selected");
        return;
      }

      try {
        const promises = [...selectedAccounts].map(id =>
          Account.update(id, { type: newType })
        );

        await Promise.all(promises);
        setSelectedAccounts(new Set());
        clearCacheByKey('Account');
        await Promise.all([
          loadAccounts(),
          loadTotalStats()
        ]);
        toast.success(`Updated ${promises.length} account(s) to ${newType}`);
      } catch (error) {
        console.error("Failed to update accounts:", error);
        toast.error("Failed to update accounts");
      }
    }
  };

  const handleBulkAssign = async (assignedTo) => {
    if (selectAllMode) {
      if (!window.confirm(`Assign ALL ${totalItems} account(s) matching current filters?`)) return;

      try {
        let currentTenantFilter = getTenantFilter();
        // The employee scope filter is already applied within getTenantFilter()

        if (typeFilter !== "all") {
          currentTenantFilter = { ...currentTenantFilter, type: typeFilter };
        }

        if (searchTerm) {
          const searchRegex = { $regex: searchTerm, $options: 'i' };
          currentTenantFilter = {
            ...currentTenantFilter,
            $or: [
              { name: searchRegex },
              { email: searchRegex },
              { phone: searchRegex },
              { website: searchRegex },
              { city: searchRegex }
            ]
          };
        }

        if (selectedTags.length > 0) {
          currentTenantFilter = { ...currentTenantFilter, tags: { $all: selectedTags } };
        }

        const allAccountsToAssign = await cachedRequest('Account', 'filter', { filter: currentTenantFilter, sort: 'id', limit: 10000 }, () => Account.filter(currentTenantFilter, 'id', 10000));
        const updateCount = allAccountsToAssign.length;

        // Update in batches
        const BATCH_SIZE = 50;
        for (let i = 0; i < allAccountsToAssign.length; i += BATCH_SIZE) {
          const batch = allAccountsToAssign.slice(i, i + BATCH_SIZE);
          await Promise.all(batch.map(a => Account.update(a.id, { assigned_to: assignedTo || null })));
        }

        setSelectedAccounts(new Set());
        setSelectAllMode(false);
        clearCacheByKey('Account');
        await Promise.all([
          loadAccounts(),
          loadTotalStats()
        ]);
        toast.success(`Assigned ${updateCount} account(s)`);
      } catch (error) {
        console.error("Failed to assign accounts:", error);
        toast.error("Failed to assign accounts");
      }
    } else {
      if (!selectedAccounts || selectedAccounts.size === 0) {
        toast.error("No accounts selected");
        return;
      }

      try {
        const promises = [...selectedAccounts].map(id =>
          Account.update(id, { assigned_to: assignedTo || null })
        );

        await Promise.all(promises);
        setSelectedAccounts(new Set());
        clearCacheByKey('Account');
        await Promise.all([
          loadAccounts(),
          loadTotalStats()
        ]);
        toast.success(`Assigned ${promises.length} account(s)`);
      } catch (error) {
        console.error("Failed to assign accounts:", error);
        toast.error("Failed to assign accounts");
      }
    }
  };

  const toggleSelection = (id) => {
    const newSet = new Set(selectedAccounts);
    if (newSet.has(id)) {
      newSet.delete(id);
    } else {
      newSet.add(id);
    }
    setSelectedAccounts(newSet);
    setSelectAllMode(false);
  };

  const toggleSelectAll = () => {
    if (selectedAccounts.size === accounts.length && accounts.length > 0) {
      setSelectedAccounts(new Set());
      setSelectAllMode(false);
    } else {
      setSelectedAccounts(new Set(accounts.map(a => a.id)));
      setSelectAllMode(false);
    }
  };

  const handleSelectAllRecords = () => {
    setSelectAllMode(true);
    setSelectedAccounts(new Set(accounts.map(a => a.id)));
  };

  const handleClearSelection = () => {
    setSelectedAccounts(new Set());
    setSelectAllMode(false);
  };

  const handleViewDetails = (account) => {
    setDetailAccount(account);
    setIsDetailOpen(true);
  };

  const handleRefresh = async () => {
    clearCacheByKey('Account');
    clearCacheByKey('Employee');
    clearCacheByKey('User'); // Clear User cache as well since it's loaded with cachedRequest
    clearCacheByKey('Contact'); // Clear Contact cache
    // Also reset supportingDataLoaded ref so it can reload
    supportingDataLoaded.current = false;
    await Promise.all([
      loadAccounts(),
      loadTotalStats()
    ]);
    toast.success("Accounts refreshed");
  };

  const toggleTag = useCallback((tagName) => {
    setSelectedTags(prev => {
      const newTags = prev.includes(tagName)
        ? prev.filter(t => t !== tagName)
        : [...prev, tagName];
      // currentPage reset handled by useEffect for filters
      return newTags;
    });
  }, []);

  const clearTags = useCallback(() => {
    setSelectedTags([]);
    // currentPage reset handled by useEffect for filters
  }, []);

  const handleTypeFilterClick = (type) => {
    setTypeFilter(type);
    // currentPage reset handled by useEffect for filters
  };

  const handleClearFilters = () => {
    setSearchTerm("");
    setTypeFilter("all");
    setSelectedTags([]);
    // currentPage reset handled by useEffect for filters
    handleClearSelection();
  };

  const hasActiveFilters = useMemo(() => {
    return searchTerm !== "" || typeFilter !== "all" || selectedTags.length > 0;
  }, [searchTerm, typeFilter, selectedTags]);

  if (!user) {
    return (
      <div className="min-h-screen bg-slate-900 p-6 flex items-center justify-center">
        <Loader2 className="w-8 h-8 animate-spin text-blue-400" />
      </div>
    );
  }

  return (
    <TooltipProvider>
      <div className="min-h-screen bg-slate-900 p-4 sm:p-6">
        <Dialog open={isFormOpen} onOpenChange={setIsFormOpen}>
          <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto bg-slate-800 border-slate-700 text-slate-200">
            <DialogHeader>
              <DialogTitle className="text-slate-100">
                {editingAccount ? "Edit Account" : "Add New Account"}
              </DialogTitle>
            </DialogHeader>
            <AccountForm
              account={editingAccount}
              onSuccess={handleSave}
              onCancel={() => {
                setIsFormOpen(false);
                setEditingAccount(null);
              }}
              user={user}
            />
          </DialogContent>
        </Dialog>

        <CsvImportDialog
          open={isImportOpen}
          onOpenChange={setIsImportOpen}
          schema={Account.schema ? Account.schema() : null}
          onSuccess={async () => {
            clearCacheByKey('Account');
            await Promise.all([
              loadAccounts(),
              loadTotalStats()
            ]);
          }}
        />

        <AccountDetailPanel
          account={detailAccount}
          assignedUserName={employeesMap[detailAccount?.assigned_to] || usersMap[detailAccount?.assigned_to]}
          open={isDetailOpen}
          onOpenChange={() => {
            setIsDetailOpen(false);
            setDetailAccount(null);
          }}
          onEdit={(account) => {
            setEditingAccount(account);
            setIsFormOpen(true);
            setIsDetailOpen(false);
          }}
          onDelete={async (id) => {
            await handleDelete(id);
            setIsDetailOpen(false);
          }}
          onRefresh={() => loadAccounts()}
          user={user}
        />

        <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-6 gap-4">
          <div>
            <h1 className="text-3xl font-bold text-slate-100 mb-2">Accounts</h1>
            <p className="text-slate-400">
              Manage your company accounts and partnerships.
            </p>
          </div>
          <div className="flex flex-wrap items-center gap-2">
            <RefreshButton onClick={handleRefresh} loading={loading} />
            <Tooltip>
              <TooltipTrigger asChild>
                <Button
                  variant="outline"
                  onClick={() => setViewMode(viewMode === "list" ? "grid" : "list")}
                  className="bg-slate-800 border-slate-700 text-slate-200 hover:bg-slate-700"
                >
                  {viewMode === "list" ? <Grid className="w-4 h-4" /> : <List className="w-4 h-4" />}
                </Button>
              </TooltipTrigger>
              <TooltipContent>
                <p>Switch to {viewMode === "list" ? "card" : "list"} view</p>
              </TooltipContent>
            </Tooltip>
            <Tooltip>
              <TooltipTrigger asChild>
                <Button
                  variant="outline"
                  onClick={() => setIsImportOpen(true)}
                  className="bg-slate-800 border-slate-700 text-slate-200 hover:bg-slate-700"
                >
                  <Upload className="w-4 h-4 mr-2" />
                  Import
                </Button>
              </TooltipTrigger>
              <TooltipContent>
                <p>Import accounts from CSV</p>
              </TooltipContent>
            </Tooltip>
            <CsvExportButton
              entityName="Account"
              data={accounts}
              filename="accounts_export"
            />
            {(selectedAccounts.size > 0 || selectAllMode) && (
              <BulkActionsMenu
                selectedCount={selectAllMode ? totalItems : selectedAccounts.size}
                onBulkTypeChange={handleBulkTypeChange}
                onBulkAssign={handleBulkAssign}
                onBulkDelete={handleBulkDelete}
                employees={employees}
                selectAllMode={selectAllMode}
                totalCount={totalItems}
              />
            )}
            <Tooltip>
              <TooltipTrigger asChild>
                <Button
                  onClick={() => {
                    setEditingAccount(null);
                    setIsFormOpen(true);
                  }}
                  className="bg-blue-600 hover:bg-blue-700"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  Add Account
                </Button>
              </TooltipTrigger>
              <TooltipContent>
                <p>Create new account</p>
              </TooltipContent>
            </Tooltip>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-2 md:grid-cols-5 gap-4 mb-6">
          {[
            {
              label: 'Total Accounts',
              value: totalStats.total,
              filter: 'all',
              bgColor: 'bg-slate-800',
              tooltip: 'total_all'
            },
            {
              label: 'Prospects',
              value: totalStats.prospect,
              filter: 'prospect',
              bgColor: 'bg-blue-900/20',
              borderColor: 'border-blue-700',
              tooltip: 'account_prospect'
            },
            {
              label: 'Customers',
              value: totalStats.customer,
              filter: 'customer',
              bgColor: 'bg-emerald-900/20',
              borderColor: 'border-emerald-700',
              tooltip: 'account_customer'
            },
            {
              label: 'Partners',
              value: totalStats.partner,
              filter: 'partner',
              bgColor: 'bg-purple-900/20',
              borderColor: 'border-purple-700',
              tooltip: 'account_partner'
            },
            {
              label: 'Inactive',
              value: totalStats.inactive,
              filter: 'inactive',
              bgColor: 'bg-gray-900/20',
              borderColor: 'border-gray-700',
              tooltip: 'account_inactive'
            },
          ].map((stat) => (
            <div
              key={stat.label}
              className={`${stat.bgColor} ${stat.borderColor || 'border-slate-700'} border rounded-lg p-4 cursor-pointer hover:scale-105 transition-all ${
                typeFilter === stat.filter ? 'ring-2 ring-blue-500 ring-offset-2 ring-offset-slate-900' : ''
              }`}
              onClick={() => handleTypeFilterClick(stat.filter)}
            >
              <div className="flex items-center justify-between mb-1">
                <p className="text-sm text-slate-400">{stat.label}</p>
                <StatusHelper statusKey={stat.tooltip} />
              </div>
              <p className="text-2xl font-bold text-slate-100">{stat.value}</p>
            </div>
          ))}
        </div>

        <div className="flex flex-col lg:flex-row gap-4 mb-6">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-3 w-5 h-5 text-slate-500" />
            <Input
              placeholder="Search accounts by name, website, email, phone, city or industry..."
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value);
                // currentPage reset handled by useEffect for filters
              }}
              className="pl-10 bg-slate-800 border-slate-700 text-slate-200"
            />
          </div>

          <div className="flex flex-wrap gap-2">
            <TagFilter
              allTags={allTags}
              selectedTags={selectedTags}
              onTagsChange={(newTags) => {
                setSelectedTags(newTags);
                // currentPage reset handled by useEffect for filters
              }}
            />

            {hasActiveFilters && (
              <Tooltip>
                <TooltipTrigger asChild>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleClearFilters}
                    className="bg-slate-800 border-slate-700 text-slate-200 hover:bg-slate-700"
                  >
                    <X className="w-4 h-4 mr-1" />
                    Clear
                  </Button>
                </TooltipTrigger>
                <TooltipContent>
                  <p>Clear all filters</p>
                </TooltipContent>
              </Tooltip>
            )}
          </div>
        </div>

        {/* Select All Banner */}
        {selectedAccounts.size === accounts.length && accounts.length > 0 && !selectAllMode && totalItems > accounts.length && (
          <div className="mb-4 bg-blue-900/20 border border-blue-700 rounded-lg p-4 flex items-center justify-between">
            <div className="flex items-center gap-2">
              <AlertCircle className="w-5 h-5 text-blue-400" />
              <span className="text-blue-200">
                All {accounts.length} accounts on this page are selected.
              </span>
              <Button
                variant="link"
                onClick={handleSelectAllRecords}
                className="text-blue-400 hover:text-blue-300 p-0 h-auto"
              >
                Select all {totalItems} accounts matching current filters
              </Button>
            </div>
            <Button
              variant="ghost"
              size="sm"
              onClick={handleClearSelection}
              className="text-slate-400 hover:text-slate-200"
            >
              <X className="w-4 h-4" />
            </Button>
          </div>
        )}

        {selectAllMode && (
          <div className="mb-4 bg-blue-900/20 border border-blue-700 rounded-lg p-4 flex items-center justify-between">
            <div className="flex items-center gap-2">
              <AlertCircle className="w-5 h-5 text-blue-400" />
              <span className="text-blue-200 font-semibold">
                All {totalItems} accounts matching current filters are selected.
              </span>
            </div>
            <Button
              variant="ghost"
              size="sm"
              onClick={handleClearSelection}
              className="text-slate-400 hover:text-slate-200"
            >
              Clear selection
            </Button>
          </div>
        )}

        {loading && !initialLoadDone.current ? (
          <div className="flex items-center justify-center py-20">
            <div className="text-center">
              <Loader2 className="w-8 h-8 animate-spin text-blue-400 mx-auto mb-4" />
              <p className="text-slate-400">Loading accounts...</p>
            </div>
          </div>
        ) : accounts.length === 0 ? (
          <div className="bg-slate-800/50 border border-slate-700 rounded-lg p-12 text-center">
            <AlertCircle className="w-12 h-12 text-slate-600 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-slate-300 mb-2">No accounts found</h3>
            <p className="text-slate-500 mb-6">
              {hasActiveFilters
                ? "Try adjusting your filters or search term"
                : "Get started by adding your first account"}
            </p>
            {!hasActiveFilters && (
              <Button
                onClick={() => setIsFormOpen(true)}
                className="bg-blue-600 hover:bg-blue-700"
              >
                <Plus className="w-4 h-4 mr-2" />
                Add Your First Account
              </Button>
            )}
          </div>
        ) : viewMode === "list" ? (
          <>
            {/* List/Table View */}
            <div className="bg-slate-800 border border-slate-700 rounded-lg overflow-hidden">
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-slate-700/50">
                    <tr>
                      <th className="px-4 py-3 text-left">
                        <Checkbox
                          checked={selectedAccounts.size === accounts.length && accounts.length > 0 && !selectAllMode}
                          onCheckedChange={toggleSelectAll}
                          className="border-slate-600"
                        />
                      </th>
                      <th className="px-4 py-3 text-left text-sm font-medium text-slate-300">Name</th>
                      <th className="px-4 py-3 text-left text-sm font-medium text-slate-300">Website</th>
                      <th className="px-4 py-3 text-left text-sm font-medium text-slate-300">Phone</th>
                      <th className="px-4 py-3 text-left text-sm font-medium text-slate-300">Industry</th>
                      <th className="px-4 py-3 text-left text-sm font-medium text-slate-300">Assigned To</th>
                      <th className="px-4 py-3 text-left text-sm font-medium text-slate-300">Type</th>
                      <th className="px-4 py-3 text-left text-sm font-medium text-slate-300">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-slate-700">
                    {accounts.map((account) => (
                      <tr
                        key={account.id}
                        className="hover:bg-slate-700/30 transition-colors"
                      >
                        <td className="px-4 py-3">
                          <Checkbox
                            checked={selectedAccounts.has(account.id) || selectAllMode}
                            onCheckedChange={() => toggleSelection(account.id)}
                            className="border-slate-600"
                          />
                        </td>
                        <td className="px-4 py-3 text-sm text-slate-300">
                          {account.name}
                        </td>
                        <td className="px-4 py-3 text-sm text-slate-300">
                          {account.website ? (
                            <a href={account.website} target="_blank" rel="noopener noreferrer" className="text-blue-400 hover:text-blue-300">
                              {account.website}
                            </a>
                          ) : (
                            <span className="text-slate-500"></span>
                          )}
                        </td>
                        <td className="px-4 py-3 text-sm text-slate-300">
                          {account.phone || <span className="text-slate-500"></span>}
                        </td>
                        <td className="px-4 py-3 text-sm text-slate-300">
                          {account.industry || <span className="text-slate-500"></span>}
                        </td>
                        <td className="px-4 py-3 text-sm text-slate-300">
                          {employeesMap[account.assigned_to] || usersMap[account.assigned_to] || <span className="text-slate-500">Unassigned</span>}
                        </td>
                        <td className="cursor-pointer p-3" onClick={() => handleViewDetails(account)}>
                          <Badge className={`${typeBadgeColors[account.type]} border capitalize text-xs font-semibold whitespace-nowrap`}>
                            {account.type?.replace(/_/g, ' ')}
                          </Badge>
                        </td>
                        <td className="px-4 py-3">
                          <div className="flex items-center gap-2">
                            <Tooltip>
                              <TooltipTrigger asChild>
                                <Button
                                  variant="ghost"
                                  size="icon"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    handleViewDetails(account);
                                  }}
                                  className="h-8 w-8 text-slate-400 hover:text-blue-400"
                                >
                                  <Eye className="w-4 h-4" />
                                </Button>
                              </TooltipTrigger>
                              <TooltipContent>
                                <p>View details</p>
                              </TooltipContent>
                            </Tooltip>
                            <Tooltip>
                              <TooltipTrigger asChild>
                                <Button
                                  variant="ghost"
                                  size="icon"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    setEditingAccount(account);
                                    setIsFormOpen(true);
                                  }}
                                  className="h-8 w-8 text-slate-400 hover:text-blue-400"
                                >
                                  <Edit className="w-4 h-4" />
                                </Button>
                              </TooltipTrigger>
                              <TooltipContent>
                                <p>Edit account</p>
                              </TooltipContent>
                            </Tooltip>
                            <Tooltip>
                              <TooltipTrigger asChild>
                                <Button
                                  variant="ghost"
                                  size="icon"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    handleDelete(account.id);
                                  }}
                                  className="h-8 w-8 text-red-400 hover:text-red-300 hover:bg-red-900/20"
                                >
                                  <Trash2 className="w-4 h-4" />
                                </Button>
                              </TooltipTrigger>
                              <TooltipContent>
                                <p>Delete account</p>
                              </TooltipContent>
                            </Tooltip>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            <Pagination
              currentPage={currentPage}
              totalPages={Math.ceil(totalItems / pageSize)}
              totalItems={totalItems}
              pageSize={pageSize}
              onPageChange={handlePageChange}
              onPageSizeChange={handlePageSizeChange}
              loading={loading}
            />
          </>
        ) : (
          <>
            {/* Card View */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {accounts.map((account) => (
                <AccountCard
                  key={account.id}
                  account={account}
                  assignedUserName={employeesMap[account.assigned_to] || usersMap[account.assigned_to]}
                  onEdit={(a) => {
                    setEditingAccount(a);
                    setIsFormOpen(true);
                  }}
                  onDelete={handleDelete}
                  onViewDetails={handleViewDetails}
                  onClick={() => handleViewDetails(account)}
                  isSelected={selectedAccounts.has(account.id) || selectAllMode}
                  onSelect={() => toggleSelection(account.id)}
                  user={user}
                />
              ))}
            </div>

            <Pagination
              currentPage={currentPage}
              totalPages={Math.ceil(totalItems / pageSize)}
              totalItems={totalItems}
              pageSize={pageSize}
              onPageChange={handlePageChange}
              onPageSizeChange={handlePageSizeChange}
              loading={loading}
            />
          </>
        )}
      </div>
    </TooltipProvider>
  );
}

import React, { useState, useEffect, useCallback, useMemo, useRef } from "react";
import { Activity } from "@/api/entities";
import { Account } from "@/api/entities";
import { Contact } from "@/api/entities";
import { Lead } from "@/api/entities";
import { Opportunity } from "@/api/entities";
import { User } from "@/api/entities";
import { Employee } from "@/api/entities";
import { useApiManager } from "../components/shared/ApiManager";
import ActivityCard from "../components/activities/ActivityCard";
import ActivityForm from "../components/activities/ActivityForm";
import ActivityDetailPanel from "../components/activities/ActivityDetailPanel";
import BulkActionsMenu from "../components/activities/BulkActionsMenu";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Plus, Search, Upload, Loader2, Grid, List, AlertCircle, X, Edit, Eye, Trash2, Calendar as CalendarIcon } from "lucide-react";
import { AnimatePresence } from "framer-motion";
import CsvExportButton from "../components/shared/CsvExportButton";
import CsvImportDialog from "../components/shared/CsvImportDialog";
import { useTenant } from '../components/shared/tenantContext';
import Pagination from "../components/shared/Pagination";
import { toast } from "sonner";
import TagFilter from "../components/shared/TagFilter";
import { useEmployeeScope } from "../components/shared/EmployeeScopeContext";
import RefreshButton from "../components/shared/RefreshButton";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { format } from "date-fns";
import SimpleModal from "../components/shared/SimpleModal";
import StatusHelper from "../components/shared/StatusHelper";
import { createPageUrl } from "@/utils";
import { Link } from "react-router-dom";
import { utcToLocal, getCurrentTimezoneOffset } from '../components/shared/timezoneUtils';
import { useTimezone } from '../components/shared/TimezoneContext';

const statusColors = {
  scheduled: "bg-blue-900/20 text-blue-300 border-blue-700",
  in_progress: "bg-yellow-900/20 text-yellow-300 border-yellow-700",
  overdue: "bg-red-900/20 text-red-300 border-red-700",
  completed: "bg-emerald-900/20 text-emerald-300 border-emerald-700",
  cancelled: "bg-slate-900/20 text-slate-300 border-slate-700"
};

const typeColors = {
  call: "bg-indigo-600 text-white",
  email: "bg-purple-600 text-white",
  meeting: "bg-blue-600 text-white",
  task: "bg-green-600 text-white",
  note: "bg-slate-600 text-white",
  demo: "bg-orange-600 text-white",
  proposal: "bg-pink-600 text-white"
};

export default function ActivitiesPage() {
  const [activities, setActivities] = useState([]);
  const [accounts, setAccounts] = useState([]);
  const [contacts, setContacts] = useState([]);
  const [leads, setLeads] = useState([]);
  const [opportunities, setOpportunities] = useState([]);
  const [users, setUsers] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [typeFilter, setTypeFilter] = useState("all");
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [editingActivity, setEditingActivity] = useState(null);
  const [viewMode, setViewMode] = useState("list");
  const [selectedActivities, setSelectedActivities] = useState(() => new Set());
  const [selectAllMode, setSelectAllMode] = useState(false);
  const [isImportOpen, setIsImportOpen] = useState(false);
  const [user, setUser] = useState(null);
  const { selectedTenantId } = useTenant();
  const [detailActivity, setDetailActivity] = useState(null);
  const [isDetailOpen, setIsDetailOpen] = useState(false);
  const [selectedTags, setSelectedTags] = useState([]);
  const [dateRange, setDateRange] = useState({ start: null, end: null });
  const [showTestData, setShowTestData] = useState(false);
  const { selectedEmail } = useEmployeeScope();

  const { selectedTimezone } = useTimezone();
  const offsetMinutes = getCurrentTimezoneOffset(selectedTimezone);

  const [totalStats, setTotalStats] = useState({
    total: 0,
    scheduled: 0,
    in_progress: 0,
    overdue: 0,
    completed: 0,
    cancelled: 0
  });

  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(25);
  const [totalItems, setTotalItems] = useState(0);

  const { cachedRequest, clearCacheByKey } = useApiManager();

  const initialLoadDone = useRef(false);

  useEffect(() => {
    const loadUser = async () => {
      try {
        const currentUser = await User.me();
        setUser(currentUser);
      } catch (error) {
        console.error("Failed to load user:", error);
        toast.error("Failed to load user information");
      }
    };
    loadUser();
  }, []);

  const buildFilter = useCallback(() => {
    if (!user) return {};

    let filter = {};

    if (user.role === 'superadmin' || user.role === 'admin') {
      if (selectedTenantId) {
        filter.tenant_id = selectedTenantId;
      }
    } else if (user.tenant_id) {
      filter.tenant_id = user.tenant_id;
    }

    if (selectedEmail && selectedEmail !== 'all') {
      if (selectedEmail === 'unassigned') {
        filter.$or = [{ assigned_to: null }, { assigned_to: '' }];
      } else {
        filter.assigned_to = selectedEmail;
      }
    } else if (user.employee_role === 'employee' && user.role !== 'admin' && user.role !== 'superadmin') {
      filter.assigned_to = user.email;
    }

    if (statusFilter !== 'all') {
      if (statusFilter === "overdue") {
        filter.due_date = { $lt: new Date().toISOString() };
        filter.status = { $nin: ['completed', 'cancelled'] };
      } else {
        filter.status = statusFilter;
      }
    }

    if (typeFilter !== 'all') {
      filter.type = typeFilter;
    }

    if (dateRange.start) {
      filter.due_date = filter.due_date || {};
      filter.due_date.$gte = dateRange.start;
    }
    if (dateRange.end) {
      filter.due_date = filter.due_date || {};
      filter.due_date.$lte = dateRange.end;
    }

    if (!showTestData) {
      filter.is_test_data = { $ne: true };
    }

    return filter;
  }, [user, selectedTenantId, selectedEmail, statusFilter, typeFilter, dateRange, showTestData]);


  useEffect(() => {
    const loadSupportingData = async () => {
      if (!user) return;

      try {
        const supportingDataTenantFilter = {};
        if (user.role === 'superadmin' || user.role === 'admin') {
          if (selectedTenantId) {
            supportingDataTenantFilter.tenant_id = selectedTenantId;
          }
        } else if (user.tenant_id) {
          supportingDataTenantFilter.tenant_id = user.tenant_id;
        }

        const [usersData, employeesData, accountsData, contactsData, leadsData, opportunitiesData] = await Promise.all([
          cachedRequest('User', 'list', {}, () => User.list()),
          cachedRequest('Employee', 'filter', { filter: supportingDataTenantFilter }, () => Employee.filter(supportingDataTenantFilter)),
          cachedRequest('Account', 'filter', { filter: supportingDataTenantFilter }, () => Account.filter(supportingDataTenantFilter)),
          cachedRequest('Contact', 'filter', { filter: supportingDataTenantFilter }, () => Contact.filter(supportingDataTenantFilter)),
          cachedRequest('Lead', 'filter', { filter: supportingDataTenantFilter }, () => Lead.filter(supportingDataTenantFilter)),
          cachedRequest('Opportunity', 'filter', { filter: supportingDataTenantFilter }, () => Opportunity.filter(supportingDataTenantFilter))
        ]);

        setUsers(usersData || []);
        setEmployees(employeesData || []);
        setAccounts(accountsData || []);
        setContacts(contactsData || []);
        setLeads(leadsData || []);
        setOpportunities(opportunitiesData || []);
      } catch (error) {
        console.error("Failed to load supporting data:", error);
      }
    };

    loadSupportingData();
  }, [user, selectedTenantId, cachedRequest]);

  const loadTotalStats = useCallback(async () => {
    if (!user) return;

    try {
      const baseFilter = buildFilter();

      const allActivities = await Activity.filter(baseFilter, 'id', 10000);

      const now = new Date();
      const stats = {
        total: allActivities?.length || 0,
        scheduled: allActivities?.filter(a => a.status === 'scheduled' && (!a.due_date || new Date(a.due_date) >= now)).length || 0,
        in_progress: allActivities?.filter(a => a.status === 'in_progress' || a.status === 'in-progress').length || 0,
        overdue: allActivities?.filter(a => {
          if (a.status === 'completed' || a.status === 'cancelled') return false;
          if (!a.due_date) return false;
          return new Date(a.due_date) < now;
        }).length || 0,
        completed: allActivities?.filter(a => a.status === 'completed').length || 0,
        cancelled: allActivities?.filter(a => a.status === 'cancelled').length || 0
      };

      setTotalStats(stats);
    } catch (error) {
      console.error("Failed to load total stats:", error);
    }
  }, [user, buildFilter]);

  useEffect(() => {
    if (user) {
      loadTotalStats();
    }
  }, [user, buildFilter, loadTotalStats]);

  const loadActivities = useCallback(async (page = 1, size = 25) => {
    if (!user) return;

    setLoading(true);
    try {
      let currentFilter = buildFilter();

      if (searchTerm) {
        const searchRegex = { $regex: searchTerm, $options: 'i' };
        currentFilter = {
          ...currentFilter,
          $or: [
            { subject: searchRegex },
            { description: searchRegex },
            { related_name: searchRegex }
          ]
        };
      }

      if (selectedTags.length > 0) {
        currentFilter = { ...currentFilter, tags: { $all: selectedTags } };
      }

      const skip = (page - 1) * size;

      console.log('[Activities] Loading page:', page, 'size:', size, 'skip:', skip, 'filter:', currentFilter);

      const activitiesData = await Activity.filter(currentFilter, '-due_date', size, skip);

      const countQuery = await Activity.filter(currentFilter, 'id', 10000);
      const totalCount = countQuery?.length || 0;

      console.log('[Activities] Loaded:', activitiesData?.length, 'Total:', totalCount);

      setActivities(activitiesData || []);
      setTotalItems(totalCount);
      setCurrentPage(page);
      initialLoadDone.current = true;
    } catch (error) {
      console.error("Failed to load activities:", error);
      toast.error("Failed to load activities");
      setActivities([]);
      setTotalItems(0);
    } finally {
      setLoading(false);
    }
  }, [user, searchTerm, selectedTags, buildFilter]);

  useEffect(() => {
    if (user) {
      loadActivities(currentPage, pageSize);
    }
  }, [user, currentPage, pageSize, loadActivities]);

  const handlePageChange = useCallback((newPage) => {
    setCurrentPage(newPage);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }, []);

  const handlePageSizeChange = useCallback((newSize) => {
    setPageSize(newSize);
    setCurrentPage(1);
  }, []);

  const usersMap = useMemo(() => {
    return users.reduce((acc, user) => {
      acc[user.email] = user.full_name || user.email;
      return acc;
    }, {});
  }, [users]);

  const employeesMap = useMemo(() => {
    return employees.reduce((acc, employee) => {
      if (employee.email) {
        acc[employee.email] = `${employee.first_name} ${employee.last_name}`;
      }
      return acc;
    }, {});
  }, [employees]);

  const accountsMap = useMemo(() => {
    return accounts.reduce((acc, account) => {
      acc[account.id] = account.name;
      return acc;
    }, {});
  }, [accounts]);

  const contactsMap = useMemo(() => {
    return contacts.reduce((acc, contact) => {
      acc[contact.id] = `${contact.first_name} ${contact.last_name}`;
      return acc;
    }, {});
  }, [contacts]);

  const leadsMap = useMemo(() => {
    return leads.reduce((acc, lead) => {
      acc[lead.id] = `${lead.first_name} ${lead.last_name}`;
      return acc;
    }, {});
  }, [leads]);

  const opportunitiesMap = useMemo(() => {
    return opportunities.reduce((acc, opportunity) => {
      acc[opportunity.id] = opportunity.name;
      return acc;
    }, {});
  }, [opportunities]);

  const allTags = useMemo(() => {
    if (!Array.isArray(activities)) return [];

    const tagCounts = {};
    activities.forEach(activity => {
      if (Array.isArray(activity.tags)) {
        activity.tags.forEach(tag => {
          if (tag && typeof tag === 'string') {
            tagCounts[tag] = (tagCounts[tag] || 0) + 1;
          }
        });
      }
    });

    return Object.entries(tagCounts)
      .map(([name, count]) => ({ name, count }))
      .sort((a, b) => b.count - a.count);
  }, [activities]);

  const handleSave = async () => {
    setIsFormOpen(false);
    setEditingActivity(null);
    clearCacheByKey('Activity');
    await Promise.all([
      loadActivities(currentPage, pageSize),
      loadTotalStats()
    ]);
    toast.success(editingActivity ? "Activity updated successfully" : "Activity created successfully");
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this activity?")) return;

    try {
      await Activity.delete(id);
      clearCacheByKey('Activity');
      await Promise.all([
        loadActivities(currentPage, pageSize),
        loadTotalStats()
      ]);
      toast.success("Activity deleted successfully");
    } catch (error) {
      console.error("Failed to delete activity:", error);
      toast.error("Failed to delete activity");
    }
  };

  const handleBulkDelete = async () => {
    if (selectAllMode) {
      if (!window.confirm(`Delete ALL ${totalItems} activity/activities? This cannot be undone!`)) return;

      try {
        let currentFilter = buildFilter();

        if (searchTerm) {
          const searchRegex = { $regex: searchTerm, $options: 'i' };
          currentFilter = {
            ...currentFilter,
            $or: [
              { subject: searchRegex },
              { description: searchRegex },
              { related_name: searchRegex }
            ]
          };
        }

        const allActivities = await Activity.filter(currentFilter, 'id', 10000);
        const deleteCount = allActivities.length;

        const BATCH_SIZE = 50;
        for (let i = 0; i < allActivities.length; i += BATCH_SIZE) {
          const batch = allActivities.slice(i, i + BATCH_SIZE);
          await Promise.all(batch.map(a => Activity.delete(a.id)));
        }

        setSelectedActivities(new Set());
        setSelectAllMode(false);
        clearCacheByKey('Activity');
        await Promise.all([
          loadActivities(1, pageSize),
          loadTotalStats()
        ]);
        toast.success(`${deleteCount} activity/activities deleted`);
      } catch (error) {
        console.error("Failed to delete activities:", error);
        toast.error("Failed to delete activities");
      }
    } else {
      if (!selectedActivities || selectedActivities.size === 0) {
        toast.error("No activities selected");
        return;
      }

      if (!window.confirm(`Delete ${selectedActivities.size} activity/activities?`)) return;

      try {
        await Promise.all([...selectedActivities].map(id => Activity.delete(id)));
        setSelectedActivities(new Set());
        clearCacheByKey('Activity');
        await Promise.all([
          loadActivities(currentPage, pageSize),
          loadTotalStats()
        ]);
        toast.success(`${selectedActivities.size} activity/activities deleted`);
      } catch (error) {
        console.error("Failed to delete activities:", error);
        toast.error("Failed to delete activities");
      }
    }
  };

  const handleBulkStatusChange = async (newStatus) => {
    if (selectAllMode) {
      if (!window.confirm(`Update status for ALL ${totalItems} activity/activities to ${newStatus}?`)) return;

      try {
        let currentFilter = buildFilter();

        if (searchTerm) {
          const searchRegex = { $regex: searchTerm, $options: 'i' };
          currentFilter = {
            ...currentFilter,
            $or: [
              { subject: searchRegex },
              { description: searchRegex },
              { related_name: searchRegex }
            ]
          };
        }

        const allActivities = await Activity.filter(currentFilter, 'id', 10000);
        const updateCount = allActivities.length;

        const BATCH_SIZE = 50;
        for (let i = 0; i < allActivities.length; i += BATCH_SIZE) {
          const batch = allActivities.slice(i, i + BATCH_SIZE);
          await Promise.all(batch.map(a => Activity.update(a.id, { status: newStatus })));
        }

        setSelectedActivities(new Set());
        setSelectAllMode(false);
        clearCacheByKey('Activity');
        await Promise.all([
          loadActivities(currentPage, pageSize),
          loadTotalStats()
        ]);
        toast.success(`Updated ${updateCount} activity/activities to ${newStatus}`);
      } catch (error) {
        console.error("Failed to update activities:", error);
        toast.error("Failed to update activities");
      }
    } else {
      if (!selectedActivities || selectedActivities.size === 0) {
        toast.error("No activities selected");
        return;
      }

      try {
        const promises = [...selectedActivities].map(id => 
          Activity.update(id, { status: newStatus })
        );

        await Promise.all(promises);
        setSelectedActivities(new Set());
        clearCacheByKey('Activity');
        await Promise.all([
          loadActivities(currentPage, pageSize),
          loadTotalStats()
        ]);
        toast.success(`Updated ${promises.length} activity/activities to ${newStatus}`);
      } catch (error) {
        console.error("Failed to update activities:", error);
        toast.error("Failed to update activities");
      }
    }
  };

  const handleBulkAssign = async (assignedTo) => {
    if (selectAllMode) {
      if (!window.confirm(`Assign ALL ${totalItems} activity/activities?`)) return;

      try {
        let currentFilter = buildFilter();

        if (searchTerm) {
          const searchRegex = { $regex: searchTerm, $options: 'i' };
          currentFilter = {
            ...currentFilter,
            $or: [
              { subject: searchRegex },
              { description: searchRegex },
              { related_name: searchRegex }
            ]
          };
        }

        const allActivities = await Activity.filter(currentFilter, 'id', 10000);
        const updateCount = allActivities.length;

        const BATCH_SIZE = 50;
        for (let i = 0; i < allActivities.length; i += BATCH_SIZE) {
          const batch = allActivities.slice(i, i + BATCH_SIZE);
          await Promise.all(batch.map(a => Activity.update(a.id, { assigned_to: assignedTo || null })));
        }

        setSelectedActivities(new Set());
        setSelectAllMode(false);
        clearCacheByKey('Activity');
        await Promise.all([
          loadActivities(currentPage, pageSize),
          loadTotalStats()
        ]);
        toast.success(`Assigned ${updateCount} activity/activities`);
      } catch (error) {
        console.error("Failed to assign activities:", error);
        toast.error("Failed to assign activities");
      }
    } else {
      if (!selectedActivities || selectedActivities.size === 0) {
        toast.error("No activities selected");
        return;
      }

      try {
        const promises = [...selectedActivities].map(id => 
          Activity.update(id, { assigned_to: assignedTo || null })
        );

        await Promise.all(promises);
        setSelectedActivities(new Set());
        clearCacheByKey('Activity');
        await Promise.all([
          loadActivities(currentPage, pageSize),
          loadTotalStats()
        ]);
        toast.success(`Assigned ${promises.length} activity/activities`);
      } catch (error) {
        console.error("Failed to assign activities:", error);
        toast.error("Failed to assign activities");
      }
    }
  };

  const toggleSelection = (id) => {
    const newSet = new Set(selectedActivities);
    if (newSet.has(id)) {
      newSet.delete(id);
    } else {
      newSet.add(id);
    }
    setSelectedActivities(newSet);
    setSelectAllMode(false);
  };

  const toggleSelectAll = () => {
    if (selectedActivities.size === activities.length && activities.length > 0) {
      setSelectedActivities(new Set());
      setSelectAllMode(false);
    } else {
      setSelectedActivities(new Set(activities.map(a => a.id)));
      setSelectAllMode(false);
    }
  };

  const handleSelectAllRecords = () => {
    setSelectAllMode(true);
    setSelectedActivities(new Set(activities.map(a => a.id)));
  };

  const handleClearSelection = () => {
    setSelectedActivities(new Set());
    setSelectAllMode(false);
  };

  const handleViewDetails = (activity) => {
    setDetailActivity(activity);
    setIsDetailOpen(true);
  };

  const handleRefresh = async () => {
    clearCacheByKey('Activity');
    clearCacheByKey('Employee');
    clearCacheByKey('Account');
    clearCacheByKey('Contact');
    clearCacheByKey('Lead');
    clearCacheByKey('Opportunity');
    await Promise.all([
      loadActivities(currentPage, pageSize),
      loadTotalStats()
    ]);
    toast.success("Activities refreshed");
  };

  const handleStatusFilterClick = (status) => {
    setStatusFilter(status);
    setCurrentPage(1);
  };

  const handleClearFilters = () => {
    setSearchTerm("");
    setStatusFilter("all");
    setTypeFilter("all");
    setSelectedTags([]);
    setDateRange({ start: null, end: null });
    setShowTestData(false);
    setCurrentPage(1);
    handleClearSelection();
  };

  const hasActiveFilters = useMemo(() => {
    return searchTerm !== "" 
      || statusFilter !== "all" 
      || typeFilter !== "all" 
      || selectedTags.length > 0
      || dateRange.start !== null
      || dateRange.end !== null
      || showTestData;
  }, [searchTerm, statusFilter, typeFilter, selectedTags, dateRange, showTestData]);

  const getRelatedEntityLink = (activity) => {
    if (!activity.related_to || !activity.related_id) return null;

    const entityMap = {
      contact: { url: createPageUrl('Contacts'), label: 'Contact' },
      account: { url: createPageUrl('Accounts'), label: 'Account' },
      lead: { url: createPageUrl('Leads'), label: 'Lead' },
      opportunity: { url: createPageUrl('Opportunities'), label: 'Opportunity' }
    };

    const entity = entityMap[activity.related_to];
    if (!entity) return null;

    return (
      <Link 
        to={entity.url} 
        className="text-blue-400 hover:text-blue-300 hover:underline"
        onClick={(e) => e.stopPropagation()}
      >
        {activity.related_name || `View ${entity.label}`}
      </Link>
    );
  };

  const statusDescriptions = useMemo(() => ({
    total_all: "Total number of activities.",
    activity_scheduled: "Activities planned for a future date or time, not yet started.",
    activity_in_progress: "Activities that are currently being worked on.",
    activity_overdue: "Activities that have passed their due date and are not yet completed.",
    activity_completed: "Activities that have been successfully finished.",
    activity_cancelled: "Activities that were planned but later cancelled."
  }), []);

  const formatDisplayDate = useCallback((activity) => {
    if (!activity.due_date) return '';

    try {
      if (activity.due_time) {
        const datePart = activity.due_date.split('T')[0];
        const utcString = `${datePart}T${activity.due_time}:00.000Z`;
        const localDate = utcToLocal(utcString, offsetMinutes);
        return format(localDate, 'MMM d, yyyy h:mm a');
      } else {
        const parts = activity.due_date.split('-').map(Number);
        const localCalendarDate = new Date(parts[0], parts[1] - 1, parts[2]);
        return format(localCalendarDate, 'MMM d, yyyy');
      }
    } catch (error) {
      console.error('Error formatting date:', error, 'Activity:', activity);
      return activity.due_date;
    }
  }, [offsetMinutes]);

  if (!user) {
    return (
      <div className="min-h-screen bg-slate-900 p-6 flex items-center justify-center">
        <Loader2 className="w-8 h-8 animate-spin text-blue-400" />
      </div>
    );
  }

  return (
    <TooltipProvider>
      <div className="min-h-screen bg-slate-900 p-4 sm:p-6">
        <SimpleModal
          open={isFormOpen}
          onOpenChange={(open) => {
            console.log('[Activities] Modal onOpenChange:', open);
            setIsFormOpen(open);
            if (!open) {
              setEditingActivity(null);
            }
          }}
          title={editingActivity ? "Edit Activity" : "Add New Activity"}
          size="lg"
        >
          <ActivityForm
            activity={editingActivity}
            accounts={accounts}
            contacts={contacts}
            leads={leads}
            opportunities={opportunities}
            users={users}
            tenantId={user?.tenant_id}
            onSubmit={async (payload) => {
              try {
                console.log('[Activities] Form submitted:', { isEdit: !!editingActivity, payload });
                if (editingActivity) {
                  await Activity.update(editingActivity.id, payload);
                } else {
                  await Activity.create(payload);
                }
                await handleSave();
              } catch (error) {
                console.error("Error saving activity:", error);
                toast.error("Failed to save activity");
              }
            }}
            onCancel={() => {
              console.log('[Activities] Form cancelled');
              setIsFormOpen(false);
              setEditingActivity(null);
            }}
          />
        </SimpleModal>

        <CsvImportDialog
          open={isImportOpen}
          onOpenChange={setIsImportOpen}
          schema={Activity.schema ? Activity.schema() : null}
          onSuccess={async () => {
            clearCacheByKey('Activity');
            await Promise.all([
              loadActivities(1, pageSize),
              loadTotalStats()
            ]);
          }}
        />

        {isDetailOpen && detailActivity && (
          <ActivityDetailPanel
            activity={detailActivity}
            accounts={accounts}
            contacts={contacts}
            leads={leads}
            opportunities={opportunities}
            users={users}
            assignedUserName={employeesMap[detailActivity.assigned_to] || usersMap[detailActivity.assigned_to] || detailActivity.assigned_to_name}
            relatedName={detailActivity.related_name}
            open={isDetailOpen}
            onOpenChange={() => {
              setIsDetailOpen(false);
              setDetailActivity(null);
            }}
            onEdit={(activity) => {
              setEditingActivity(activity);
              setIsFormOpen(true);
              setIsDetailOpen(false);
            }}
            onDelete={async (id) => {
              await handleDelete(id);
