import React, { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { 
  AlertTriangle, 
  CheckCircle, 
  XCircle, 
  Users, 
  Building2, 
  TrendingUp,
  Mail,
  Phone,
  FileText,
  RefreshCw,
  ChevronDown,
  ChevronUp
} from "lucide-react";
import { User } from "@/api/entities";
import { useTenant } from "../components/shared/tenantContext";
import { analyzeDataQuality } from "@/api/functions";
import { Progress } from "@/components/ui/progress";
import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from "@/components/ui/collapsible";

export default function DataQualityReport() {
  const [loading, setLoading] = useState(true);
  const [analyzing, setAnalyzing] = useState(false);
  const [report, setReport] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);
  const [error, setError] = useState(null);
  const [expandedSections, setExpandedSections] = useState({
    contacts: false,
    accounts: false,
    leads: false
  });

  const { selectedTenantId } = useTenant();

  useEffect(() => {
    loadUser();
  }, []);

  useEffect(() => {
    if (currentUser) {
      runAnalysis();
    }
  }, [currentUser, selectedTenantId]);

  const loadUser = async () => {
    try {
      const user = await User.me();
      setCurrentUser(user);
    } catch (error) {
      console.error("Failed to load user:", error);
      setError("Failed to load user information");
    }
  };

  const runAnalysis = async () => {
    setLoading(true);
    setAnalyzing(true);
    setError(null);

    try {
      const tenantId = selectedTenantId || currentUser?.tenant_id;

      if (!tenantId && currentUser?.role !== 'superadmin') {
        setError("No tenant selected");
        return;
      }

      const { data } = await analyzeDataQuality({ tenant_id: tenantId });

      if (data.success) {
        setReport(data.report);
      } else {
        setError(data.error || "Analysis failed");
      }
    } catch (error) {
      console.error("Error analyzing data quality:", error);
      setError(error.message || "Failed to analyze data quality");
    } finally {
      setLoading(false);
      setAnalyzing(false);
    }
  };

  const toggleSection = (section) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  const getQualityColor = (percentage) => {
    if (percentage >= 90) return "text-green-400";
    if (percentage >= 70) return "text-yellow-400";
    return "text-red-400";
  };

  const getQualityBadge = (percentage) => {
    if (percentage >= 90) return <Badge className="bg-green-900/50 text-green-300 border-green-700">Excellent</Badge>;
    if (percentage >= 70) return <Badge className="bg-yellow-900/50 text-yellow-300 border-yellow-700">Fair</Badge>;
    return <Badge className="bg-red-900/50 text-red-300 border-red-700">Needs Attention</Badge>;
  };

  const renderEntitySection = (entityType, entityData, IconComponent) => {
    const qualityPercentage = 100 - entityData.issues_percentage;
    const isExpanded = expandedSections[entityType.toLowerCase()];

    return (
      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="p-2 rounded-lg bg-slate-700">
                <IconComponent className="w-5 h-5 text-slate-300" />
              </div>
              <div>
                <CardTitle className="text-slate-100">{entityType}</CardTitle>
                <CardDescription className="text-slate-400">
                  {entityData.total_records} total records
                </CardDescription>
              </div>
            </div>
            <div className="text-right">
              <div className={`text-3xl font-bold ${getQualityColor(qualityPercentage)}`}>
                {qualityPercentage.toFixed(1)}%
              </div>
              {getQualityBadge(qualityPercentage)}
            </div>
          </div>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Progress Bar */}
          <div>
            <div className="flex justify-between text-sm text-slate-400 mb-2">
              <span>Data Quality Score</span>
              <span>{entityData.records_with_issues} issues found</span>
            </div>
            <Progress 
              value={qualityPercentage} 
              className="h-2"
            />
          </div>

          {/* Issues Summary */}
          <Collapsible open={isExpanded} onOpenChange={() => toggleSection(entityType.toLowerCase())}>
            <CollapsibleTrigger asChild>
              <Button variant="ghost" className="w-full justify-between text-slate-300 hover:text-slate-100 hover:bg-slate-700">
                <span>View Detailed Issues</span>
                {isExpanded ? <ChevronUp className="w-4 h-4" /> : <ChevronDown className="w-4 h-4" />}
              </Button>
            </CollapsibleTrigger>
            <CollapsibleContent className="space-y-3 mt-4">
              {entityData.issues.missing_first_name > 0 && (
                <div className="flex items-center justify-between p-3 rounded-lg bg-slate-700/50 border border-slate-600">
                  <div className="flex items-center gap-2">
                    <AlertTriangle className="w-4 h-4 text-yellow-400" />
                    <span className="text-slate-300">Missing First Name</span>
                  </div>
                  <Badge variant="outline" className="bg-slate-800 text-slate-200 border-slate-600">
                    {entityData.issues.missing_first_name} records
                  </Badge>
                </div>
              )}

              {entityData.issues.missing_last_name > 0 && (
                <div className="flex items-center justify-between p-3 rounded-lg bg-slate-700/50 border border-slate-600">
                  <div className="flex items-center gap-2">
                    <AlertTriangle className="w-4 h-4 text-yellow-400" />
                    <span className="text-slate-300">Missing Last Name</span>
                  </div>
                  <Badge variant="outline" className="bg-slate-800 text-slate-200 border-slate-600">
                    {entityData.issues.missing_last_name} records
                  </Badge>
                </div>
              )}

              {entityData.issues.invalid_name_characters > 0 && (
                <div className="flex items-center justify-between p-3 rounded-lg bg-slate-700/50 border border-slate-600">
                  <div className="flex items-center gap-2">
                    <XCircle className="w-4 h-4 text-red-400" />
                    <span className="text-slate-300">Invalid Name Characters</span>
                  </div>
                  <Badge variant="outline" className="bg-slate-800 text-slate-200 border-slate-600">
                    {entityData.issues.invalid_name_characters} records
                  </Badge>
                </div>
              )}

              {entityData.issues.invalid_email > 0 && (
                <div className="flex items-center justify-between p-3 rounded-lg bg-slate-700/50 border border-slate-600">
                  <div className="flex items-center gap-2">
                    <Mail className="w-4 h-4 text-red-400" />
                    <span className="text-slate-300">Invalid Email Format</span>
                  </div>
                  <Badge variant="outline" className="bg-slate-800 text-slate-200 border-slate-600">
                    {entityData.issues.invalid_email} records
                  </Badge>
                </div>
              )}

              {entityData.issues.missing_contact_info > 0 && (
                <div className="flex items-center justify-between p-3 rounded-lg bg-slate-700/50 border border-slate-600">
                  <div className="flex items-center gap-2">
                    <Phone className="w-4 h-4 text-red-400" />
                    <span className="text-slate-300">No Contact Info (Email & Phone)</span>
                  </div>
                  <Badge variant="outline" className="bg-slate-800 text-slate-200 border-slate-600">
                    {entityData.issues.missing_contact_info} records
                  </Badge>
                </div>
              )}

              {entityData.records_with_issues === 0 && (
                <div className="flex items-center gap-2 p-3 rounded-lg bg-green-900/20 border border-green-700/50">
                  <CheckCircle className="w-4 h-4 text-green-400" />
                  <span className="text-green-300">No data quality issues found!</span>
                </div>
              )}
            </CollapsibleContent>
          </Collapsible>
        </CardContent>
      </Card>
    );
  };

  if (loading && !report) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="text-center">
          <RefreshCw className="w-12 h-12 text-blue-400 animate-spin mx-auto mb-4" />
          <p className="text-slate-300">Analyzing data quality...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <Alert className="bg-red-900/20 border-red-700/50">
        <AlertTriangle className="w-4 h-4 text-red-400" />
        <AlertDescription className="text-red-300">
          {error}
        </AlertDescription>
      </Alert>
    );
  }

  if (!report) {
    return (
      <Alert className="bg-slate-800 border-slate-700">
        <AlertTriangle className="w-4 h-4 text-slate-400" />
        <AlertDescription className="text-slate-300">
          No data quality report available.
        </AlertDescription>
      </Alert>
    );
  }

  const overallQuality = 100 - report.overall.issues_percentage;

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-start">
        <div>
          <h1 className="text-3xl font-bold text-slate-100 flex items-center gap-3">
            <div className="w-12 h-12 flex items-center justify-center rounded-full bg-blue-900/30 border border-blue-700/50">
              <FileText className="w-7 h-7 text-blue-400" />
            </div>
            Data Quality Report
          </h1>
          <p className="text-slate-400 mt-2">
            Analyze and monitor data quality across your CRM entities
          </p>
        </div>
        <Button
          onClick={runAnalysis}
          disabled={analyzing}
          className="bg-blue-600 hover:bg-blue-700"
        >
          {analyzing ? (
            <>
              <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
              Analyzing...
            </>
          ) : (
            <>
              <RefreshCw className="w-4 h-4 mr-2" />
              Refresh Report
            </>
          )}
        </Button>
      </div>

      {/* Overall Summary */}
      <Card className="bg-gradient-to-br from-slate-800 to-slate-900 border-slate-700">
        <CardHeader>
          <CardTitle className="text-slate-100">Overall Data Quality</CardTitle>
          <CardDescription className="text-slate-400">
            Aggregated score across all entities
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="flex items-center justify-between">
            <div>
              <div className={`text-6xl font-bold ${getQualityColor(overallQuality)}`}>
                {overallQuality.toFixed(1)}%
              </div>
              <p className="text-slate-400 mt-2">
                {report.overall.total_records} total records analyzed
              </p>
              <p className="text-slate-500 text-sm">
                {report.overall.records_with_issues} records with issues
              </p>
            </div>
            <div className="text-right">
              {getQualityBadge(overallQuality)}
              <div className="mt-4 space-y-2">
                {overallQuality >= 90 && (
                  <p className="text-sm text-green-400">
                     Excellent data quality!
                  </p>
                )}
                {overallQuality < 90 && overallQuality >= 70 && (
                  <p className="text-sm text-yellow-400">
                     Some issues need attention
                  </p>
                )}
                {overallQuality < 70 && (
                  <p className="text-sm text-red-400">
                     Critical issues detected
                  </p>
                )}
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Entity-Specific Reports */}
      <div className="grid gap-6">
        {report.contacts && renderEntitySection("Contacts", report.contacts, Users)}
        {report.accounts && renderEntitySection("Accounts", report.accounts, Building2)}
        {report.leads && renderEntitySection("Leads", report.leads, TrendingUp)}
      </div>

      {/* Recommendations */}
      <Card className="bg-slate-800 border-slate-700">
        <CardHeader>
          <CardTitle className="text-slate-100">Recommendations</CardTitle>
          <CardDescription className="text-slate-400">
            Actions to improve your data quality
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-3">
          <div className="flex items-start gap-3 p-3 rounded-lg bg-blue-900/20 border border-blue-700/50">
            <CheckCircle className="w-5 h-5 text-blue-400 flex-shrink-0 mt-0.5" />
            <div>
              <p className="text-blue-300 font-medium">Review Records with Missing Names</p>
              <p className="text-slate-400 text-sm mt-1">
                Update records that are missing first or last names to ensure complete contact information.
              </p>
            </div>
          </div>

          <div className="flex items-start gap-3 p-3 rounded-lg bg-blue-900/20 border border-blue-700/50">
            <CheckCircle className="w-5 h-5 text-blue-400 flex-shrink-0 mt-0.5" />
            <div>
              <p className="text-blue-300 font-medium">Validate Email Addresses</p>
              <p className="text-slate-400 text-sm mt-1">
                Correct or remove invalid email addresses to improve communication reliability.
              </p>
            </div>
          </div>

          <div className="flex items-start gap-3 p-3 rounded-lg bg-blue-900/20 border border-blue-700/50">
            <CheckCircle className="w-5 h-5 text-blue-400 flex-shrink-0 mt-0.5" />
            <div>
              <p className="text-blue-300 font-medium">Add Contact Information</p>
              <p className="text-slate-400 text-sm mt-1">
                Ensure all records have at least one method of contact (email or phone number).
              </p>
            </div>
          </div>

          <div className="flex items-start gap-3 p-3 rounded-lg bg-blue-900/20 border border-blue-700/50">
            <CheckCircle className="w-5 h-5 text-blue-400 flex-shrink-0 mt-0.5" />
            <div>
              <p className="text-blue-300 font-medium">Clean Invalid Name Characters</p>
              <p className="text-slate-400 text-sm mt-1">
                Remove numbers and special characters from name fields (except hyphens, apostrophes, and language-specific characters).
              </p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

import React, { useState, useCallback, useEffect } from "react";
import { User } from "@/api/entities";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Upload, FileText, CreditCard, X, FolderOpen, AlertCircle } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";
import BusinessCardProcessor from "../components/documents/BusinessCardProcessor";
import DocumentExtractor from "../components/documents/DocumentExtractor";
import ProcessingHistory from "../components/documents/ProcessingHistory";
import CashFlowExtractor from "../components/documents/CashFlowExtractor"; // New import
import { ArrowRightLeft } from "lucide-react"; // New icon import

export default function DocumentProcessing() {
  const [activeProcessor, setActiveProcessor] = useState(null);
  const [uploadMode, setUploadMode] = useState(null); // 'extract' or 'storage'
  const [isProcessing, setIsProcessing] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const loadUser = async () => {
      try {
        const user = await User.me();
        setCurrentUser(user);
      } catch (error) {
        console.error("Failed to load user in DocumentProcessing:", error);
      }
    };
    loadUser();
  }, []);

  const handleCancel = useCallback(() => {
    setActiveProcessor(null);
    setUploadMode(null);
    setIsProcessing(false);
  }, []);

  const handleProcessingStateChange = useCallback((processing) => {
    setIsProcessing(processing);
  }, []);

  return (
    <div className="min-h-screen bg-slate-900 p-4 lg:p-8 space-y-4 lg:space-y-6">
      <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center gap-4">
        <div>
          <h1 className="text-2xl lg:text-3xl font-bold text-slate-100 flex items-center gap-3">
            <div className="w-10 h-10 lg:w-12 lg:h-12 flex items-center justify-center rounded-full bg-emerald-900/30 border border-emerald-700/50">
              <FileText className="w-5 h-5 lg:w-7 lg:h-7 text-emerald-400" />
            </div>
            Document Processing
          </h1>
          <p className="text-slate-400 mt-1 text-sm lg:text-base">
            Process business cards and documents with AI, or upload files for storage.
          </p>
        </div>

        {(activeProcessor || isProcessing) && (
          <Button
            variant="outline" 
            onClick={handleCancel}
            className="bg-red-700 hover:bg-red-600 text-white border-red-600 flex items-center gap-2"
          >
            <X className="w-4 h-4" />
            Cancel
          </Button>
        )}
      </div>

      {!activeProcessor && !isProcessing && (
        <>
          <Alert className="bg-blue-900/30 border-blue-700/50">
            <AlertCircle className="h-4 w-4 text-blue-400" />
            <AlertDescription className="text-blue-300">
              Choose between AI-powered extraction (which analyzes and extracts data) or simple storage upload (no processing).
            </AlertDescription>
          </Alert>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 lg:gap-6">
            {/* Business Card Processing */}
            <Card className="bg-slate-800 border-slate-700 hover:bg-slate-700/50 transition-all cursor-pointer">
              <CardHeader>
                <CardTitle className="flex items-center gap-3 text-slate-100">
                  <div className="p-2 rounded-lg bg-blue-900/30 border border-blue-700/50">
                    <CreditCard className="w-6 h-6 text-blue-400" />
                  </div>
                  Business Card Scanner
                </CardTitle>
                <CardDescription className="text-slate-400">
                  Extract contact information from business cards using AI
                </CardDescription>
              </CardHeader>
              <CardContent>
                <Button
                  onClick={() => {
                    setActiveProcessor('business-card');
                    setUploadMode('extract');
                  }}
                  className="w-full bg-blue-600 hover:bg-blue-700 text-white"
                >
                  <Upload className="w-4 h-4 mr-2" />
                  Scan Business Card
                </Button>
              </CardContent>
            </Card>

            {/* Document Extraction */}
            <Card className="bg-slate-800 border-slate-700 hover:bg-slate-700/50 transition-all cursor-pointer">
              <CardHeader>
                <CardTitle className="flex items-center gap-3 text-slate-100">
                  <div className="p-2 rounded-lg bg-emerald-900/30 border border-emerald-700/50">
                    <FileText className="w-6 h-6 text-emerald-400" />
                  </div>
                  Document Extractor
                </CardTitle>
                <CardDescription className="text-slate-400">
                  Extract data from receipts, invoices, and other documents
                </CardDescription>
              </CardHeader>
              <CardContent>
                <Button
                  onClick={() => {
                    setActiveProcessor('document-extractor');
                    setUploadMode('extract');
                  }}
                  className="w-full bg-emerald-600 hover:bg-emerald-700 text-white"
                >
                  <Upload className="w-4 h-4 mr-2" />
                  Process Document
                </Button>
              </CardContent>
            </Card>

            {/* NEW: Cash Flow Extractor */}
            <Card className="bg-slate-800 border-slate-700 hover:bg-slate-700/50 transition-all cursor-pointer">
              <CardHeader>
                <CardTitle className="flex items-center gap-3 text-slate-100">
                  <div className="p-2 rounded-lg bg-green-900/30 border border-green-700/50">
                    <ArrowRightLeft className="w-6 h-6 text-green-400" />
                  </div>
                  Financial Document Extractor
                </CardTitle>
                <CardDescription className="text-slate-400">
                  Extract income & expenses from spreadsheets, PDFs, etc.
                </CardDescription>
              </CardHeader>
              <CardContent>
                <Button
                  onClick={() => {
                    setActiveProcessor('cash-flow');
                    setUploadMode('extract');
                  }}
                  className="w-full bg-green-600 hover:bg-green-700 text-white"
                >
                  <Upload className="w-4 h-4 mr-2" />
                  Extract Transactions
                </Button>
              </CardContent>
            </Card>

            {/* Storage Only Upload */}
            <Card className="bg-slate-800 border-slate-700 hover:bg-slate-700/50 transition-all cursor-pointer">
              <CardHeader>
                <CardTitle className="flex items-center gap-3 text-slate-100">
                  <div className="p-2 rounded-lg bg-purple-900/30 border border-purple-700/50">
                    <FolderOpen className="w-6 h-6 text-purple-400" />
                  </div>
                  Storage Upload
                </CardTitle>
                <CardDescription className="text-slate-400">
                  Upload documents for storage without AI processing
                </CardDescription>
              </CardHeader>
              <CardContent>
                <Button
                  onClick={() => {
                    setActiveProcessor('storage-only');
                    setUploadMode('storage');
                  }}
                  className="w-full bg-purple-600 hover:bg-purple-700 text-white"
                >
                  <Upload className="w-4 h-4 mr-2" />
                  Upload for Storage
                </Button>
              </CardContent>
            </Card>
          </div>
        </>
      )}

      {/* Active Processor */}
      {activeProcessor === 'business-card' && (
        <BusinessCardProcessor 
          user={currentUser}
          onCancel={handleCancel}
          onProcessingChange={handleProcessingStateChange}
        />
      )}

      {activeProcessor === 'document-extractor' && (
        <DocumentExtractor 
          onCancel={handleCancel}
          onProcessingChange={handleProcessingStateChange}
        />
      )}

      {activeProcessor === 'cash-flow' && (
        <CashFlowExtractor
          user={currentUser}
          onCancel={handleCancel}
          onProcessingChange={handleProcessingStateChange}
        />
      )}

      {activeProcessor === 'storage-only' && (
        <StorageUploader 
          onCancel={handleCancel}
          onProcessingChange={handleProcessingStateChange}
        />
      )}

      {/* Processing History */}
      {!activeProcessor && !isProcessing && (
        <ProcessingHistory user={currentUser} />
      )}
    </div>
  );
}

// New Storage-Only Upload Component
function StorageUploader({ onCancel, onProcessingChange }) {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadResult, setUploadResult] = useState(null);

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
    setUploadResult(null);
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    setUploading(true);
    onProcessingChange(true);

    try {
      const { UploadPrivateFile } = await import("@/api/integrations");
      const { DocumentationFile } = await import("@/api/entities");
      const { User } = await import("@/api/entities");

      // Upload file to private storage
      const uploadResult = await UploadPrivateFile({ file: selectedFile });

      if (!uploadResult.file_uri) {
        throw new Error('File upload failed - no URI returned');
      }

      // Get current user for tenant info
      const currentUser = await User.me();

      // Create document record for storage
      const documentRecord = await DocumentationFile.create({
        title: selectedFile.name,
        description: "Document uploaded for storage only",
        file_name: selectedFile.name,
        file_uri: uploadResult.file_uri,
        file_type: selectedFile.type.split('/')[1] || 'unknown',
        category: 'other',
        tenant_id: currentUser.tenant_id,
        tags: ['storage-upload']
      });

      setUploadResult({
        success: true,
        message: 'Document uploaded successfully for storage!',
        documentId: documentRecord.id
      });

    } catch (error) {
      console.error('Storage upload error:', error);
      setUploadResult({
        success: false,
        message: `Upload failed: ${error.message}`
      });
    } finally {
      setUploading(false);
      onProcessingChange(false);
    }
  };

  return (
    <Card className="bg-slate-800 border-slate-700">
      <CardHeader>
        <CardTitle className="flex items-center justify-between text-slate-100">
          <span className="flex items-center gap-2">
            <FolderOpen className="w-6 h-6 text-purple-400" />
            Upload for Storage Only
          </span>
          <Button variant="ghost" onClick={onCancel} className="text-slate-400 hover:text-slate-200">
            <X className="w-5 h-5" />
          </Button>
        </CardTitle>
        <CardDescription className="text-slate-400">
          Upload documents to store them without any AI processing or data extraction.
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        {!uploadResult && (
          <>
            <div className="border-2 border-dashed border-slate-600 rounded-lg p-8 text-center">
              <input
                type="file"
                accept=".pdf,.jpg,.jpeg,.png,.doc,.docx,.txt"
                onChange={handleFileSelect}
                className="hidden"
                id="storage-file-input"
              />
              <label htmlFor="storage-file-input" className="cursor-pointer">
                <Upload className="w-12 h-12 text-slate-400 mx-auto mb-4" />
                <p className="text-slate-300 font-medium mb-2">
                  Click to select a file for storage
                </p>
                <p className="text-slate-500 text-sm">
                  Supports PDF, images, Word documents, and text files
                </p>
              </label>
            </div>

            {selectedFile && (
              <Alert className="bg-slate-700 border-slate-600">
                <FileText className="h-4 w-4 text-slate-400" />
                <AlertDescription className="text-slate-300">
                  Selected: <strong>{selectedFile.name}</strong> ({(selectedFile.size / 1024 / 1024).toFixed(2)} MB)
                </AlertDescription>
              </Alert>
            )}

            <div className="flex gap-3">
              <Button
                onClick={handleUpload}
                disabled={!selectedFile || uploading}
                className="flex-1 bg-purple-600 hover:bg-purple-700 text-white"
              >
                {uploading ? 'Uploading...' : 'Upload for Storage'}
              </Button>
              <Button
                variant="outline"
                onClick={onCancel}
                className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600"
              >
                Cancel
              </Button>
            </div>
          </>
        )}

        {uploadResult && (
          <Alert className={uploadResult.success ? 'bg-green-900/30 border-green-700/50' : 'bg-red-900/30 border-red-700/50'}>
            <AlertCircle className={`h-4 w-4 ${uploadResult.success ? 'text-green-400' : 'text-red-400'}`} />
            <AlertDescription className={uploadResult.success ? 'text-green-300' : 'text-red-300'}>
              {uploadResult.message}
            </AlertDescription>
          </Alert>
        )}

        {uploadResult?.success && (
          <div className="flex gap-3">
            <Button
              onClick={() => {
                setSelectedFile(null);
                setUploadResult(null);
              }}
              className="flex-1 bg-purple-600 hover:bg-purple-700 text-white"
            >
              Upload Another File
            </Button>
            <Button
              variant="outline"
              onClick={onCancel}
              className="bg-slate-700 border-slate-600 text-slate-200 hover:bg-slate-600"
            >
              Done
            </Button>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

import React, { useState, useEffect, useCallback } from "react";
import { Account } from "@/api/entities";
import { User } from "@/api/entities";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { Loader2, Building2, Trash2, Eye, AlertTriangle, Mail, Phone, ArrowDownCircle } from "lucide-react";
import { useTenant } from "../components/shared/tenantContext";
import { findDuplicates } from "@/api/functions";
import { bulkDeleteAccounts } from "@/api/functions";
import { consolidateDuplicateAccounts } from "@/api/functions";
import AccountDetailPanel from "../components/accounts/AccountDetailPanel";
import OperationOverlay from "../components/shared/OperationOverlay";
import { useToast } from "@/components/ui/use-toast";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

export default function DuplicateAccounts() {
  const [loading, setLoading] = useState(true);
  const [duplicateGroups, setDuplicateGroups] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [deleting, setDeleting] = useState(false);
  const [deleteTarget, setDeleteTarget] = useState(null);
  const [selectedForDeletion, setSelectedForDeletion] = useState(new Set());
  const [showBulkDeleteConfirm, setShowBulkDeleteConfirm] = useState(false);
  const [operationProgress, setOperationProgress] = useState({ show: false, current: 0, total: 0 });
  const { selectedTenantId } = useTenant();
  const { toast } = useToast();

  const [consolidating, setConsolidating] = useState(false);
  const [consolidateTarget, setConsolidateTarget] = useState(null);

  useEffect(() => {
    loadUser();
  }, []);

  const loadUser = async () => {
    try {
      const user = await User.me();
      setCurrentUser(user);
    } catch (error) {
      console.error("Failed to load user:", error);
    }
  };

  const loadDuplicates = useCallback(async () => {
    if (!currentUser) return;

    setLoading(true);
    try {
      const response = await findDuplicates({
        entity_type: 'Account',
        tenant_id: selectedTenantId || currentUser?.tenant_id
      });

      if (response?.data?.success) {
        const groups = Array.isArray(response.data.groups) ? response.data.groups : [];
        setDuplicateGroups(groups);
      } else {
        setDuplicateGroups([]);
      }
    } catch (error) {
      console.error("Failed to load duplicates:", error);
      toast({
        title: "Error",
        description: "Failed to load duplicate accounts",
        variant: "destructive"
      });
      setDuplicateGroups([]);
    } finally {
      setLoading(false);
    }
  }, [currentUser, selectedTenantId, toast]);

  useEffect(() => {
    if (currentUser) {
      loadDuplicates();
    }
  }, [currentUser, loadDuplicates]);

  const handleDelete = async (account) => {
    setDeleting(true);
    try {
      await Account.delete(account.id);
      toast({
        title: "Success",
        description: "Account deleted successfully"
      });
      setDeleteTarget(null);
      await loadDuplicates();
    } catch (error) {
      console.error("Failed to delete account:", error);
      toast({
        title: "Error",
        description: "Failed to delete account",
        variant: "destructive"
      });
    } finally {
      setDeleting(false);
    }
  };

  const handleBulkDelete = async () => {
    setShowBulkDeleteConfirm(false);
    setDeleting(true);

    const accountIds = Array.from(selectedForDeletion);
    const totalCount = accountIds.length;

    setOperationProgress({
      show: true,
      current: 0,
      total: totalCount
    });

    try {
      // For small batches (< 10), delete directly without backend function
      if (totalCount < 10) {
        for (let i = 0; i < accountIds.length; i++) {
          try {
            await Account.delete(accountIds[i]);
            setOperationProgress(prev => ({ ...prev, current: i + 1 }));
          } catch (error) {
            console.error(`Failed to delete account ${accountIds[i]}:`, error);
          }
        }

        toast({
          title: "Success",
          description: `Successfully deleted ${totalCount} account(s)`
        });
      } else {
        // For larger batches, use the backend function for chunked processing
        const response = await bulkDeleteAccounts({ accountIds });

        if (response.data?.status === 'success') {
          toast({
            title: "Success",
            description: response.data.message
          });
        } else {
          throw new Error(response.data?.message || 'Bulk delete failed');
        }
      }

      setSelectedForDeletion(new Set());
      await loadDuplicates();
    } catch (error) {
      console.error("Failed to delete accounts:", error);
      toast({
        title: "Error",
        description: error.message || "Failed to delete some accounts",
        variant: "destructive"
      });
    } finally {
      setDeleting(false);
      setOperationProgress({ show: false, current: 0, total: 0 });
    }
  };

  const handleConsolidateGroup = async (records, masterAccountId = null) => {
    if (!records || records.length < 2) {
      toast({
        title: "Error",
        description: "Need at least 2 accounts to consolidate",
        variant: "destructive"
      });
      return;
    }

    const accountIds = records.map(r => r?.id).filter(Boolean);

    if (!confirm(`Consolidate ${accountIds.length} accounts into one master record?\n\nThis will:\n Merge all data into the oldest account\n Re-link all contacts, opportunities, and activities\n Delete duplicate records\n\nThis cannot be undone.`)) {
      return;
    }

    setConsolidating(true);
    setOperationProgress({
      show: true,
      current: 0,
      total: accountIds.length
    });

    try {
      const response = await consolidateDuplicateAccounts({ 
        accountIds,
        masterAccountId 
      });

      if (response.data?.success) {
        toast({
          title: "Success",
          description: response.data.message,
          duration: 5000
        });

        // Clear any selections for deleted accounts
        const deletedIds = new Set(response.data.deleted_account_ids || []);
        setSelectedForDeletion(prev => {
          const newSet = new Set(prev);
          deletedIds.forEach(id => newSet.delete(id));
          return newSet;
        });

        await loadDuplicates();
      } else {
        throw new Error(response.data?.error || 'Consolidation failed');
      }
    } catch (error) {
      console.error("Failed to consolidate accounts:", error);
      toast({
        title: "Error",
        description: error.message || "Failed to consolidate accounts",
        variant: "destructive",
        duration: 5000
      });
    } finally {
      setConsolidating(false);
      setOperationProgress({ show: false, current: 0, total: 0 });
      setConsolidateTarget(null);
    }
  };

  const toggleSelection = (accountId) => {
    if (!accountId) return; // Don't add invalid IDs

    const newSelection = new Set(selectedForDeletion);
    if (newSelection.has(accountId)) {
      newSelection.delete(accountId);
    } else {
      newSelection.add(accountId);
    }
    setSelectedForDeletion(newSelection);
  };

  const toggleGroupSelection = (records) => {
    // Filter out any invalid records/IDs
    const accountIds = records
      .map(r => r?.id)
      .filter(id => id && typeof id === 'string'); // Only valid string IDs

    if (accountIds.length === 0) return;

    const newSelection = new Set(selectedForDeletion);

    // Check if all valid accounts in this group are selected
    const allSelected = accountIds.every(id => newSelection.has(id));

    if (allSelected) {
      // Deselect all
      accountIds.forEach(id => newSelection.delete(id));
    } else {
      // Select all
      accountIds.forEach(id => newSelection.add(id));
    }

    setSelectedForDeletion(newSelection);
  };

  const getGroupSelectionState = (records) => {
    // Filter out any invalid records/IDs
    const accountIds = records
      .map(r => r?.id)
      .filter(id => id && typeof id === 'string');

    if (accountIds.length === 0) return 'none';

    const selectedCount = accountIds.filter(id => selectedForDeletion.has(id)).length;

    if (selectedCount === 0) return 'none';
    if (selectedCount === accountIds.length) return 'all';
    return 'some';
  };

  const handleView = async (account) => {
    if (!account?.id) {
      toast({
        title: "Error",
        description: "Invalid account selected",
        variant: "destructive"
      });
      return;
    }

    try {
      const fullAccount = await Account.get(account.id);
      setSelectedAccount(fullAccount);
    } catch (error) {
      console.error("Failed to fetch account details:", error);
      toast({
        title: "Error",
        description: "Failed to load account details: " + (error.message || 'Unknown error'),
        variant: "destructive"
      });
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-slate-900">
        <div className="text-center">
          <Loader2 className="w-12 h-12 animate-spin text-blue-400 mx-auto mb-4" />
          <p className="text-slate-400">Scanning for duplicate accounts...</p>
        </div>
      </div>
    );
  }

  const validGroups = Array.isArray(duplicateGroups) ? duplicateGroups : [];

  return (
    <div className="min-h-screen bg-slate-900 p-4 lg:p-8 space-y-4 lg:space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl lg:text-3xl font-bold text-slate-100 flex items-center gap-3">
            <Building2 className="w-8 h-8 text-green-400" />
            Review Duplicate Accounts
          </h1>
          <p className="text-slate-400 mt-2">
            Found {validGroups.length} potential duplicate group(s)
          </p>
        </div>
        <div className="flex gap-2">
          {selectedForDeletion.size > 0 && (
            <Button 
              onClick={() => setShowBulkDeleteConfirm(true)}
              variant="destructive"
              className="bg-red-600 hover:bg-red-700"
              disabled={deleting || consolidating}
            >
              <Trash2 className="w-4 h-4 mr-2" />
              Delete Selected ({selectedForDeletion.size})
            </Button>
          )}
          <Button 
            onClick={loadDuplicates} 
            variant="outline" 
            className="bg-slate-800 border-slate-700 text-slate-200" 
            disabled={deleting || consolidating}
          >
            Refresh
          </Button>
        </div>
      </div>

      <Card className="bg-blue-900/30 border-blue-700/50">
        <CardContent className="p-4">
          <div className="flex items-start gap-3">
            <AlertTriangle className="w-5 h-5 text-blue-400 mt-0.5" />
            <div className="text-sm text-blue-300">
              <p className="font-semibold mb-1">How duplicates are detected:</p>
              <ul className="list-disc list-inside space-y-1 text-blue-200">
                <li>Similar company names</li>
                <li>Matching addresses</li>
                <li>Same legacy ID from imported data</li>
              </ul>
            </div>
          </div>
        </CardContent>
      </Card>

      {validGroups.length === 0 ? (
        <Card className="bg-slate-800 border-slate-700">
          <CardContent className="p-12 text-center">
            <Building2 className="w-16 h-16 text-slate-600 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-slate-300 mb-2">No Duplicates Found</h3>
            <p className="text-slate-400">Your account records look clean!</p>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-6">
          {validGroups.map((group, groupIndex) => {
            let records = [];
            let reasons = [];

            if (Array.isArray(group?.records)) {
              records = group.records;
            } else if (Array.isArray(group?.items)) {
              records = group.items;
            } else if (Array.isArray(group?.accounts)) {
              records = group.accounts;
            } else if (group && typeof group === 'object') {
              records = [group];
            }

            if (Array.isArray(group?.reasons)) {
              reasons = group.reasons;
            } else if (group?.reason) {
              reasons = [group.reason];
            } else if (group?.match_type) {
              reasons = [group.match_type];
            }

            const selectionState = getGroupSelectionState(records);

            return (
              <Card key={groupIndex} className="bg-slate-800 border-slate-700">
                <CardHeader className="border-b border-slate-700">
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-slate-100 flex items-center gap-2">
                      <AlertTriangle className="w-5 h-5 text-yellow-400" />
                      Duplicate Group {groupIndex + 1}
                      <Badge variant="outline" className="ml-2 bg-yellow-900/30 text-yellow-300 border-yellow-700/50">
                        {records.length} records
                      </Badge>
                    </CardTitle>

                    <div className="flex items-center gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleConsolidateGroup(records)}
                        disabled={consolidating || records.length < 2 || deleting}
                        className="bg-blue-600 hover:bg-blue-700 text-white border-blue-500"
                      >
                        <ArrowDownCircle className="w-4 h-4 mr-2" />
                        Consolidate Group
                      </Button>

                      <span className="text-sm text-slate-400">Select All</span>
                      <Checkbox
                        checked={selectionState === 'all'}
                        indeterminate={selectionState === 'some'}
                        onCheckedChange={() => toggleGroupSelection(records)}
                        className="bg-slate-600 border-slate-500 data-[state=checked]:bg-blue-600 data-[state=checked]:border-blue-600"
                      />
                    </div>
                  </div>
                  {reasons.length > 0 && (
                    <p className="text-sm text-slate-400 mt-1">
                      Match reasons: {reasons.join(", ")}
                    </p>
                  )}
                </CardHeader>
                <CardContent className="p-6">
                  {records.length === 0 ? (
                    <div className="text-center py-8 text-slate-500">
                      <AlertTriangle className="w-12 h-12 mx-auto mb-3 opacity-50" />
                      <p>No records found in this group</p>
                    </div>
                  ) : (
                    <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                      {records.map((account, idx) => (
                        <Card key={account?.id || idx} className="bg-slate-700/50 border-slate-600 relative">
                          <div className="absolute top-3 right-3 z-10">
                            <Checkbox
                              checked={selectedForDeletion.has(account?.id)}
                              onCheckedChange={() => toggleSelection(account?.id)}
                              className="bg-slate-600 border-slate-500 data-[state=checked]:bg-blue-600 data-[state=checked]:border-blue-600"
                              disabled={deleting || consolidating}
                            />
                          </div>

                          <CardHeader className="pb-3">
                            <CardTitle className="text-base text-slate-100 flex items-center gap-2 pr-8">
                              <Building2 className="w-4 h-4 text-green-400" />
                              {account?.name || 'Unknown Account'}
                            </CardTitle>
                          </CardHeader>
                          <CardContent className="space-y-2">
                            {account?.email && (
