                <Button
                  variant="outline"
                  onClick={() => {
                    console.log('[Opportunities] View mode button clicked, current:', viewMode);
                    if (viewMode === "table") setViewMode("grid");
                    else if (viewMode === "grid") setViewMode("kanban");
                    else setViewMode("table");
