export default function TenantsPage() {
  const [tenants, setTenants] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editingTenant, setEditingTenant] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    setLoading(true);
    try {
      const [tenantsData, userData] = await Promise.all([
        Tenant.list('display_order'),
        User.me()
      ]);
      setTenants(tenantsData);
      setCurrentUser(userData);
    } catch (error) {
      if (import.meta.env.DEV) {
        console.error("Error loading tenants:", error);
      }
      toast.error("Failed to load tenants");
    } finally {
      setLoading(false);
    }
  };

  const handleSaveTenant = async (formData) => {
    try {
      if (editingTenant?.id) {
        await Tenant.update(editingTenant.id, formData);
        toast.success("Tenant updated successfully!");
      } else {
        await Tenant.create(formData);
        toast.success("Tenant created successfully!");
      }
      setEditingTenant(null);
      setShowCreateForm(false);
      loadData();
    } catch (error) {
      if (import.meta.env.DEV) {
        console.error("Error saving tenant:", error);
      }
      toast.error("Failed to save tenant");
    }
  };

  const handleCancelEdit = () => {
    setEditingTenant(null);
    setShowCreateForm(false);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center p-12">
        <Loader2 className="w-8 h-8 animate-spin mr-3" />
        <span>Loading tenants...</span>
      </div>
    );
  }

  if (currentUser?.role !== 'admin' && currentUser?.role !== 'superadmin') {
    return (
      <Alert variant="destructive">
        <AlertCircle className="h-4 w-4" />
        <AlertDescription>
          You do not have permission to manage tenants. Only admins and superadmins can access this page.
        </AlertDescription>
      </Alert>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold flex items-center gap-2">
            <Building2 className="w-8 h-8 text-blue-600" />
            Tenant Management
          </h1>
          <p className="text-slate-600 mt-1">Manage client organizations and their settings</p>
        </div>
        <Button onClick={() => setShowCreateForm(true)} className="bg-blue-600 hover:bg-blue-700">
          <Plus className="w-4 h-4 mr-2" />
          Create Tenant
        </Button>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Active Tenants</CardTitle>
          <CardDescription>
            Manage tenant organizations, their branding, and ElevenLabs AI agent configuration
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Name</TableHead>
                <TableHead>Domain</TableHead>
                <TableHead>Industry</TableHead>
                <TableHead>AI Agent</TableHead>
                <TableHead>Users</TableHead>
                <TableHead className="text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {tenants.map((tenant) => (
                <TableRow key={tenant.id}>
                  <TableCell>
                    <div className="flex items-center gap-3">
                      {tenant.logo_url ? (
                        <img src={tenant.logo_url} alt={tenant.name} className="w-8 h-8 rounded object-cover" />
                      ) : (
                        <div className="w-8 h-8 bg-slate-200 rounded flex items-center justify-center">
                          <Building2 className="w-4 h-4 text-slate-600" />
                        </div>
                      )}
                      <div>
                        <div className="font-medium">{tenant.name}</div>
                        <div className="text-sm text-slate-500">ID: {tenant.id}</div>
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    {tenant.domain ? (
                      <Badge variant="outline">{tenant.domain}</Badge>
                    ) : (
                      <span className="text-slate-400">No domain</span>
                    )}
                  </TableCell>
                  <TableCell>
                    <Badge variant="secondary">
                      {tenant.industry ? tenant.industry.replace(/_/g, ' ') : 'Not set'}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    {tenant.elevenlabs_agent_id ? (
                      <Badge className="bg-green-100 text-green-800">Configured</Badge>
                    ) : (
                      <Badge variant="outline">Not set</Badge>
                    )}
                  </TableCell>
                  <TableCell>
                    <Badge variant="outline">View Users</Badge>
                  </TableCell>
                  <TableCell className="text-right">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setEditingTenant(tenant)}
                    >
                      <Edit className="w-4 h-4 mr-1" />
                      Edit
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
          {tenants.length === 0 && (
            <div className="text-center py-8 text-slate-500">
              No tenants found. Create your first tenant to get started.
            </div>
          )}
        </CardContent>
      </Card>

      {editingTenant && (
        <TenantForm
          tenant={editingTenant}
          onSave={handleSaveTenant}
          onCancel={handleCancelEdit}
        />
      )}

      {showCreateForm && (
        <TenantForm
          tenant={{}}
          onSave={handleSaveTenant}
          onCancel={handleCancelEdit}
        />
      )}
    </div>
  );
}

